// Error severity for monitoring & alerting
export enum ErrorSeverity {
	LOW = 'low',
	MEDIUM = 'medium',
	HIGH = 'high',
	CRITICAL = 'critical'
}

// Structured error codes with categories
export enum ErrorCodes {
	// HTTP Standard (400-499)
	BAD_REQUEST = 'BAD_REQUEST',
	UNAUTHORIZED = 'UNAUTHORIZED',
	FORBIDDEN = 'FORBIDDEN',
	NOT_FOUND = 'NOT_FOUND',
	CONFLICT = 'CONFLICT',
	VALIDATION_FAILED = 'VALIDATION_FAILED',
	RATE_LIMITED = 'RATE_LIMITED',

	// Business Logic (1000-1999)
	INSUFFICIENT_PERMISSIONS = 'INSUFFICIENT_PERMISSIONS',
	RESOURCE_LOCKED = 'RESOURCE_LOCKED',
	OPERATION_NOT_ALLOWED = 'OPERATION_NOT_ALLOWED',
	BUSINESS_RULE_VIOLATION = 'BUSINESS_RULE_VIOLATION',

	// Authentication (2000-2099)
	INVALID_CREDENTIALS = 'INVALID_CREDENTIALS',
	TOKEN_EXPIRED = 'TOKEN_EXPIRED',
	TOKEN_INVALID = 'TOKEN_INVALID',
	SESSION_EXPIRED = 'SESSION_EXPIRED',

	// Database (3000-3099)
	DB_CONNECTION_FAILED = 'DB_CONNECTION_FAILED',
	DB_QUERY_FAILED = 'DB_QUERY_FAILED',
	DB_CONSTRAINT_VIOLATION = 'DB_CONSTRAINT_VIOLATION',
	DB_TRANSACTION_FAILED = 'DB_TRANSACTION_FAILED',

	// External Services (4000-4099)
	EXTERNAL_API_ERROR = 'EXTERNAL_API_ERROR',
	PAYMENT_PROVIDER_ERROR = 'PAYMENT_PROVIDER_ERROR',
	EMAIL_SERVICE_ERROR = 'EMAIL_SERVICE_ERROR',

	// System (5000-5099)
	SERVER_ERROR = 'SERVER_ERROR',
	SERVICE_UNAVAILABLE = 'SERVICE_UNAVAILABLE',
	TIMEOUT = 'TIMEOUT',
	MEMORY_LIMIT = 'MEMORY_LIMIT',

	// Domain Specific
	WALLET_INSUFFICIENT_FUNDS = 'WALLET_INSUFFICIENT_FUNDS',
	WALLET_TRANSACTION_FAILED = 'WALLET_TRANSACTION_FAILED',
	THREAD_LOCKED = 'THREAD_LOCKED',
	USER_BANNED = 'USER_BANNED'
}

// Error context for debugging
export interface ErrorContext {
	userId?: string;
	requestId?: string;
	userAgent?: string;
	ip?: string;
	path?: string;
	method?: string;
	timestamp?: string;
	stack?: string;
}

// Structured error response
export interface ErrorResponse {
	success: false;
	error: {
		code: ErrorCodes;
		message: string;
		details?: unknown;
		requestId?: string;
		timestamp: string;
	};
}

/**
 * Enhanced base error class with monitoring integration
 */
export class AppError extends Error {
	public readonly timestamp: string;
	public readonly requestId?: string;

	constructor(
		message: string,
		public readonly httpStatus: number = 500,
		public readonly code: ErrorCodes = ErrorCodes.SERVER_ERROR,
		public readonly severity: ErrorSeverity = ErrorSeverity.MEDIUM,
		public readonly details?: unknown,
		public readonly context?: ErrorContext
	) {
		super(message);
		this.name = 'AppError';
		this.timestamp = new Date().toISOString();
		this.requestId = context?.requestId;

		// Maintain stack trace
		if (Error.captureStackTrace) {
			Error.captureStackTrace(this, AppError);
		}
	}

	toJSON(): ErrorResponse {
		return {
			success: false,
			error: {
				code: this.code,
				message: this.message,
				details: this.details,
				requestId: this.requestId,
				timestamp: this.timestamp
			}
		};
	}
}

