// Initialize the logger with default config
initLogger();

// Create and export a logger instance with convenient methods
export const logger = {
	debug,
	info,
	warn,
	error,
	critical,
	init: initLogger
};

/**
 * Health Check Endpoints with Metrics
 *
 * Provides system health monitoring and metrics collection
 */

import { Router } from 'express';
import type { Request, Response } from 'express';
import { db } from '@db';
import { sql } from 'drizzle-orm';
import { queryMonitor } from './query-performance';
import { logger } from '../logger';
import os from 'os';

const router = Router();

export interface HealthStatus {
	status: 'healthy' | 'degraded' | 'unhealthy';
	timestamp: string;
	uptime: number;
	version: string;
	services: {
		database: ServiceHealth;
		memory: ServiceHealth;
		disk: ServiceHealth;
	};
	metrics: SystemMetrics;
}

export interface ServiceHealth {
	status: 'healthy' | 'degraded' | 'unhealthy';
	responseTime?: number;
	message?: string;
	details?: any;
}

export interface SystemMetrics {
	requests: {
		total: number;
		errors: number;
		averageResponseTime: number;
	};
	database: {
		totalQueries: number;
		slowQueries: number;
		averageQueryTime: number;
		errorRate: number;
	};
	system: {
		memoryUsage: NodeJS.MemoryUsage;
		cpuUsage: number;
		loadAverage: number[];
		diskUsage?: number;
	};
}

class HealthMonitor {
	private startTime = Date.now();
	private requestCount = 0;
	private errorCount = 0;
	private responseTimes: number[] = [];

	/**
	 * Record request metrics
	 */
	recordRequest(responseTime: number, isError = false): void {
		this.requestCount++;
		if (isError) this.errorCount++;

		this.responseTimes.push(responseTime);

		// Keep only last 1000 response times
		if (this.responseTimes.length > 1000) {
			this.responseTimes = this.responseTimes.slice(-1000);
		}
	}

	/**
	 * Check database health
	 */
	async checkDatabaseHealth(): Promise<ServiceHealth> {
		const startTime = Date.now();

		try {
			await db.execute(sql`SELECT 1 as health_check`);
			const responseTime = Date.now() - startTime;

			return {
				status: responseTime < 1000 ? 'healthy' : 'degraded',
				responseTime,
				message: `Database responded in ${responseTime}ms`
			};
		} catch (error) {
			return {
				status: 'unhealthy',
				responseTime: Date.now() - startTime,
				message: `Database connection failed: ${error instanceof Error ? error.message : 'Unknown error'}`
			};
		}
	}

	/**
	 * Check memory health
	 */
	checkMemoryHealth(): ServiceHealth {
		const usage = process.memoryUsage();
		const totalMemory = os.totalmem();
		const freeMemory = os.freemem();
		const memoryUsagePercent = ((totalMemory - freeMemory) / totalMemory) * 100;

		let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';
		if (memoryUsagePercent > 90) status = 'unhealthy';
		else if (memoryUsagePercent > 80) status = 'degraded';

		return {
			status,
			message: `Memory usage: ${memoryUsagePercent.toFixed(1)}%`,
			details: {
				heapUsed: Math.round(usage.heapUsed / 1024 / 1024),
				heapTotal: Math.round(usage.heapTotal / 1024 / 1024),
				external: Math.round(usage.external / 1024 / 1024),
				systemMemoryUsage: memoryUsagePercent.toFixed(1)
			}
		};
	}

	/**
	 * Check disk health
	 */
	checkDiskHealth(): ServiceHealth {
		try {
			const stats = os.freemem();
			// This is a simplified check - in production you'd check actual disk usage
			return {
				status: 'healthy',
				message: 'Disk space available'
			};
		} catch (error) {
			return {
				status: 'unhealthy',
				message: `Failed to check disk space: ${error instanceof Error ? error.message : 'Unknown error'}`
			};
		}
	}

	/**
	 * Get CPU usage
	 */
	getCpuUsage(): number {
		const cpus = os.cpus();
		let totalIdle = 0;
		let totalTick = 0;

		cpus.forEach((cpu) => {
			for (const type in cpu.times) {
				totalTick += cpu.times[type as keyof typeof cpu.times];
			}
			totalIdle += cpu.times.idle;
		});

		return 100 - (totalIdle / totalTick) * 100;
	}

	/**
	 * Get system metrics
	 */
	getSystemMetrics(): SystemMetrics {
		const dbStats = queryMonitor.getStats();
		const averageResponseTime =
			this.responseTimes.length > 0
				? this.responseTimes.reduce((sum, time) => sum + time, 0) / this.responseTimes.length
				: 0;

		return {
			requests: {
				total: this.requestCount,
				errors: this.errorCount,
				averageResponseTime
			},
			database: dbStats,
			system: {
				memoryUsage: process.memoryUsage(),
				cpuUsage: this.getCpuUsage(),
				loadAverage: os.loadavg()
			}
		};
	}

	/**
	 * Get comprehensive health status
	 */
	async getHealthStatus(): Promise<HealthStatus> {
		const [databaseHealth, memoryHealth, diskHealth] = await Promise.all([
			this.checkDatabaseHealth(),
			Promise.resolve(this.checkMemoryHealth()),
			Promise.resolve(this.checkDiskHealth())
		]);

		const services = {
			database: databaseHealth,
			memory: memoryHealth,
			disk: diskHealth
		};

		// Determine overall status
		const statuses = Object.values(services).map((s) => s.status);
		let overallStatus: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';

		if (statuses.some((s) => s === 'unhealthy')) {
			overallStatus = 'unhealthy';
		} else if (statuses.some((s) => s === 'degraded')) {
			overallStatus = 'degraded';
		}

		return {
			status: overallStatus,
			timestamp: new Date().toISOString(),
			uptime: Date.now() - this.startTime,
			version: process.env.npm_package_version || '1.0.0',
			services,
			metrics: this.getSystemMetrics()
		};
	}
}

// Global health monitor instance
const healthMonitor = new HealthMonitor();

/**
 * Middleware to track request metrics
 */
export function requestMetricsMiddleware(req: Request, res: Response, next: Function) {
	const startTime = Date.now();

	res.on('finish', () => {
		const responseTime = Date.now() - startTime;
		const isError = res.statusCode >= 400;
		healthMonitor.recordRequest(responseTime, isError);
	});

	next();
}

/**
 * Basic health check endpoint
 */
router.get('/health', async (req: Request, res: Response) => {
	try {
		const health = await healthMonitor.getHealthStatus();
		const statusCode = health.status === 'healthy' ? 200 : 503;

		res.status(statusCode).json(health);
	} catch (error) {
		logger.error('HealthCheck', 'Health check failed', { error });

		res.status(503).json({
			status: 'unhealthy',
			timestamp: new Date().toISOString(),
			message: 'Health check failed',
			error: error instanceof Error ? error.message : 'Unknown error'
		});
	}
});

/**
 * Liveness probe - basic server responsiveness
 */
router.get('/health/live', (req: Request, res: Response) => {
	res.status(200).json({
		status: 'alive',
		timestamp: new Date().toISOString(),
		uptime: process.uptime()
	});
});

/**
 * Readiness probe - ready to serve traffic
 */
router.get('/health/ready', async (req: Request, res: Response) => {
	try {
		const dbHealth = await healthMonitor.checkDatabaseHealth();
		const isReady = dbHealth.status !== 'unhealthy';

		res.status(isReady ? 200 : 503).json({
			status: isReady ? 'ready' : 'not_ready',
			timestamp: new Date().toISOString(),
			database: dbHealth
		});
	} catch (error) {
		res.status(503).json({
			status: 'not_ready',
			timestamp: new Date().toISOString(),
			error: error instanceof Error ? error.message : 'Unknown error'
		});
	}
});

/**
 * Metrics endpoint (Prometheus-compatible format)
 */
router.get('/metrics', async (req: Request, res: Response) => {
	try {
		const metrics = healthMonitor.getSystemMetrics();
		const dbStats = queryMonitor.getStats();

		// Simple text format for metrics
		const metricsText = [
			`# HELP http_requests_total Total HTTP requests`,
			`# TYPE http_requests_total counter`,
			`http_requests_total ${metrics.requests.total}`,
			'',
			`# HELP http_request_errors_total Total HTTP request errors`,
			`# TYPE http_request_errors_total counter`,
			`http_request_errors_total ${metrics.requests.errors}`,
			'',
			`# HELP http_request_duration_ms Average HTTP request duration`,
			`# TYPE http_request_duration_ms gauge`,
			`http_request_duration_ms ${metrics.requests.averageResponseTime}`,
			'',
			`# HELP db_queries_total Total database queries`,
			`# TYPE db_queries_total counter`,
			`db_queries_total ${dbStats.totalQueries}`,
			'',
			`# HELP db_queries_slow_total Total slow database queries`,
			`# TYPE db_queries_slow_total counter`,
			`db_queries_slow_total ${dbStats.slowQueries}`,
			'',
			`# HELP db_query_duration_ms Average database query duration`,
			`# TYPE db_query_duration_ms gauge`,
			`db_query_duration_ms ${dbStats.averageQueryTime}`,
			'',
			`# HELP memory_heap_used_bytes Node.js heap memory used`,
			`# TYPE memory_heap_used_bytes gauge`,
			`memory_heap_used_bytes ${metrics.system.memoryUsage.heapUsed}`,
			'',
			`# HELP process_uptime_seconds Process uptime`,
			`# TYPE process_uptime_seconds gauge`,
			`process_uptime_seconds ${process.uptime()}`,
			''
		].join('\n');

		res.set('Content-Type', 'text/plain; version=0.0.4; charset=utf-8');
		res.send(metricsText);
	} catch (error) {
		logger.error('HealthCheck', 'Metrics collection failed', { error });
		res.status(500).send('# Metrics collection failed');
	}
});

export { healthMonitor };
export default router;

/**
 * Base Repository
 *
 * QUALITY IMPROVEMENT: Repository pattern implementation for consistent data access
 * Provides common CRUD operations with proper error handling and type safety
 */

import { db } from '@db';
import { sql, eq, and, or, SQL, count, desc, asc } from 'drizzle-orm';
import type { AnyPgColumn, AnyPgTable } from 'drizzle-orm/pg-core';
import { logger } from '../logger';
import type { EntityId } from '@shared/types/ids';

export interface PaginationOptions {
	page?: number;
	limit?: number;
	offset?: number;
}

export interface SortOptions {
	column: string;
	direction: 'asc' | 'desc';
}

export interface FilterOptions {
	[key: string]: any;
}

export interface QueryOptions {
	pagination?: PaginationOptions;
	sort?: SortOptions[];
	filters?: FilterOptions;
	include?: string[];
}

export interface PaginatedResult<T> {
	data: T[];
	total: number;
	page: number;
	limit: number;
	totalPages: number;
	hasNext: boolean;
	hasPrev: boolean;
}

export interface RepositoryTransaction {
	commit(): Promise<void>;
	rollback(): Promise<void>;
}

export class RepositoryError extends Error {
	constructor(
		message: string,
		public readonly code: string,
		public readonly statusCode: number = 500,
		public readonly context?: Record<string, any>
	) {
		super(message);
		this.name = 'RepositoryError';
	}
}

export abstract class BaseRepository<T extends Record<string, any>> {
	protected abstract table: AnyPgTable;
	protected abstract entityName: string;

	/**
	 * Find entity by ID
	 */
	async findById(id: EntityId): Promise<T | null> {
		try {
			const idColumn = this.getIdColumn();
			const [result] = await db.select().from(this.table).where(eq(idColumn, id)).limit(1);

			return result || null;
		} catch (error) {
			logger.error(`${this.entityName}Repository`, 'Error in findById', { id, error });
			throw new RepositoryError(
				`Failed to find ${this.entityName} by ID`,
				'FIND_BY_ID_ERROR',
				500,
				{ id, originalError: error }
			);
		}
	}

	/**
	 * Find multiple entities with options
	 */
	async find(options: QueryOptions = {}): Promise<T[]> {
		try {
			let query = db.select().from(this.table);

			// Apply filters
			if (options.filters) {
				const conditions = this.buildFilterConditions(options.filters);
				if (conditions.length > 0) {
					query = query.where(and(...conditions));
				}
			}

			// Apply sorting
			if (options.sort && options.sort.length > 0) {
				const orderBy = options.sort.map((sort) => {
					const column = this.getColumnByName(sort.column);
					return sort.direction === 'desc' ? desc(column) : asc(column);
				});
				query = query.orderBy(...orderBy);
			}

			// Apply pagination
			if (options.pagination) {
				const { limit = 20, offset } = options.pagination;
				const page = options.pagination.page || 1;
				const actualOffset = offset !== undefined ? offset : (page - 1) * limit;

				query = query.limit(limit).offset(actualOffset);
			}

			const results = await query;
			return results as T[];
		} catch (error) {
			logger.error(`${this.entityName}Repository`, 'Error in find', { options, error });
			throw new RepositoryError(`Failed to find ${this.entityName} entities`, 'FIND_ERROR', 500, {
				options,
				originalError: error
			});
		}
	}

	/**
	 * Find with pagination
	 */
	async findPaginated(options: QueryOptions = {}): Promise<PaginatedResult<T>> {
		try {
			const page = options.pagination?.page || 1;
			const limit = Math.min(options.pagination?.limit || 20, 100);
			const offset = (page - 1) * limit;

			// Get total count
			let countQuery = db.select({ count: count() }).from(this.table);
			if (options.filters) {
				const conditions = this.buildFilterConditions(options.filters);
				if (conditions.length > 0) {
					countQuery = countQuery.where(and(...conditions));
				}
			}
			const [{ count: total }] = await countQuery;

			// Get data
			const data = await this.find({
				...options,
				pagination: { page, limit, offset }
			});

			const totalPages = Math.ceil(total / limit);

			return {
				data,
				total,
				page,
				limit,
				totalPages,
				hasNext: page < totalPages,
				hasPrev: page > 1
			};
		} catch (error) {
			logger.error(`${this.entityName}Repository`, 'Error in findPaginated', { options, error });
			throw new RepositoryError(
				`Failed to find paginated ${this.entityName} entities`,
				'FIND_PAGINATED_ERROR',
				500,
				{ options, originalError: error }
			);
		}
	}

	/**
	 * Create new entity
	 */
	async create(data: Partial<T>): Promise<T> {
		try {
			const [result] = await db.insert(this.table).values(this.prepareCreateData(data)).returning();

			logger.info(`${this.entityName}Repository`, 'Entity created successfully', {
				id: result[this.getIdColumnName()]
			});

			return result as T;
		} catch (error) {
			logger.error(`${this.entityName}Repository`, 'Error in create', { data, error });
			throw new RepositoryError(`Failed to create ${this.entityName}`, 'CREATE_ERROR', 500, {
				data,
				originalError: error
			});
		}
	}

	/**
	 * Update entity by ID
	 */
	async update(id: EntityId, data: Partial<T>): Promise<T> {
		try {
			const idColumn = this.getIdColumn();
			const [result] = await db
				.update(this.table)
				.set(this.prepareUpdateData(data))
				.where(eq(idColumn, id))
				.returning();

			if (!result) {
				throw new RepositoryError(`${this.entityName} not found`, 'NOT_FOUND', 404, { id });
			}

			logger.info(`${this.entityName}Repository`, 'Entity updated successfully', { id });
			return result as T;
		} catch (error) {
			if (error instanceof RepositoryError) {
				throw error;
			}

			logger.error(`${this.entityName}Repository`, 'Error in update', { id, data, error });
			throw new RepositoryError(`Failed to update ${this.entityName}`, 'UPDATE_ERROR', 500, {
				id,
				data,
				originalError: error
			});
		}
	}

	/**
	 * Delete entity by ID
	 */
	async delete(id: EntityId): Promise<void> {
		try {
			const idColumn = this.getIdColumn();
			const result = await db.delete(this.table).where(eq(idColumn, id));

			logger.info(`${this.entityName}Repository`, 'Entity deleted successfully', { id });
		} catch (error) {
			logger.error(`${this.entityName}Repository`, 'Error in delete', { id, error });
			throw new RepositoryError(`Failed to delete ${this.entityName}`, 'DELETE_ERROR', 500, {
				id,
				originalError: error
			});
		}
	}

	/**
	 * Check if entity exists
	 */
	async exists(id: EntityId): Promise<boolean> {
		try {
			const entity = await this.findById(id);
			return entity !== null;
		} catch (error) {
			logger.error(`${this.entityName}Repository`, 'Error in exists', { id, error });
			return false;
		}
	}

	/**
	 * Count entities with filters
	 */
	async count(filters?: FilterOptions): Promise<number> {
		try {
			let query = db.select({ count: count() }).from(this.table);

			if (filters) {
				const conditions = this.buildFilterConditions(filters);
				if (conditions.length > 0) {
					query = query.where(and(...conditions));
				}
			}

			const [{ count: total }] = await query;
			return total;
		} catch (error) {
			logger.error(`${this.entityName}Repository`, 'Error in count', { filters, error });
			throw new RepositoryError(`Failed to count ${this.entityName} entities`, 'COUNT_ERROR', 500, {
				filters,
				originalError: error
			});
		}
	}

	/**
	 * Get ID column for the table
	 */
	protected getIdColumn(): AnyPgColumn {
		const idColumnName = this.getIdColumnName();
		return this.table[idColumnName];
	}

	/**
	 * Get ID column name (can be overridden)
	 */
	protected getIdColumnName(): string {
		return 'id';
	}

	/**
	 * Get column by name
	 */
	protected getColumnByName(name: string): AnyPgColumn {
		if (!(name in this.table)) {
			throw new RepositoryError(
				`Column '${name}' does not exist in ${this.entityName} table`,
				'INVALID_COLUMN',
				400,
				{ columnName: name }
			);
		}
		return this.table[name];
	}

	/**
	 * Build filter conditions from filter options
	 */
	protected buildFilterConditions(filters: FilterOptions): SQL[] {
		const conditions: SQL[] = [];

		for (const [key, value] of Object.entries(filters)) {
			if (value === undefined || value === null) {
				continue;
			}

			try {
				const column = this.getColumnByName(key);

				if (Array.isArray(value)) {
					// IN condition for arrays
					conditions.push(sql`${column} = ANY(${value})`);
				} else if (typeof value === 'object' && value.operator) {
					// Complex conditions like { operator: 'gt', value: 10 }
					conditions.push(this.buildComplexCondition(column, value));
				} else {
					// Simple equality
					conditions.push(eq(column, value));
				}
			} catch (error) {
				logger.warn(`${this.entityName}Repository`, 'Skipping invalid filter', { key, value });
			}
		}

		return conditions;
	}

	/**
	 * Build complex filter conditions
	 */
	protected buildComplexCondition(column: AnyPgColumn, condition: any): SQL {
		const { operator, value } = condition;

		switch (operator) {
			case 'gt':
				return sql`${column} > ${value}`;
			case 'gte':
				return sql`${column} >= ${value}`;
			case 'lt':
				return sql`${column} < ${value}`;
			case 'lte':
				return sql`${column} <= ${value}`;
			case 'ne':
				return sql`${column} != ${value}`;
			case 'like':
				return sql`${column} ILIKE ${`%${value}%`}`;
			case 'in':
				return sql`${column} = ANY(${value})`;
			default:
				return eq(column, value);
		}
	}

	/**
	 * Prepare data for creation (can be overridden)
	 */
	protected prepareCreateData(data: Partial<T>): any {
		return {
			...data,
			createdAt: new Date(),
			updatedAt: new Date()
		};
	}

	/**
	 * Prepare data for update (can be overridden)
	 */
	protected prepareUpdateData(data: Partial<T>): any {
		return {
			...data,
			updatedAt: new Date()
		};
	}
}

/**
 * Transaction Repository Implementation
 *
 * QUALITY IMPROVEMENT: Repository pattern for Transaction data access
 * Implements ITransactionRepository interface with proper error handling and type safety
 */

import { db } from '@db';
import type { UserId } from '@shared/types/ids';
import { transactions } from '@schema';
import { eq, and, desc, asc, sum, sql } from 'drizzle-orm';
import {
	BaseRepository,
	RepositoryError,
	type PaginatedResult,
	type QueryOptions
} from '../base-repository';
import type { ITransactionRepository } from '../interfaces';
import type { Transaction } from '@schema';
import { logger } from '@server/src/core/logger';

export class TransactionRepository
	extends BaseRepository<Transaction>
	implements ITransactionRepository
{
	protected table = transactions;
	protected entityName = 'Transaction';

	/**
	 * Find transactions by user ID with pagination
	 */
	async findByUserId(
		userId: UserId,
		options: QueryOptions = {}
	): Promise<PaginatedResult<Transaction>> {
		try {
			const queryOptions = {
				...options,
				filters: {
					...options.filters,
					userId
				},
				sort: options.sort || [{ column: 'createdAt', direction: 'desc' as const }]
			};

			const result = await this.findPaginated(queryOptions);

			logger.debug('TransactionRepository', 'Found transactions by user', {
				userId,
				count: result.data.length,
				total: result.total
			});

			return result;
		} catch (error) {
			logger.error('TransactionRepository', 'Error in findByUserId', { userId, options, error });
			throw new RepositoryError(
				'Failed to find transactions by user ID',
				'FIND_BY_USER_ID_ERROR',
				500,
				{ userId, options, originalError: error }
			);
		}
	}

	/**
	 * Find transactions by type with pagination
	 */
	async findByType(
		type: string,
		options: QueryOptions = {}
	): Promise<PaginatedResult<Transaction>> {
		try {
			const queryOptions = {
				...options,
				filters: {
					...options.filters,
					type
				},
				sort: options.sort || [{ column: 'createdAt', direction: 'desc' as const }]
			};

			const result = await this.findPaginated(queryOptions);

			logger.debug('TransactionRepository', 'Found transactions by type', {
				type,
				count: result.data.length,
				total: result.total
			});

			return result;
		} catch (error) {
			logger.error('TransactionRepository', 'Error in findByType', { type, options, error });
			throw new RepositoryError('Failed to find transactions by type', 'FIND_BY_TYPE_ERROR', 500, {
				type,
				options,
				originalError: error
			});
		}
	}

	/**
	 * Find transactions by status with pagination
	 */
	async findByStatus(
		status: string,
		options: QueryOptions = {}
	): Promise<PaginatedResult<Transaction>> {
		try {
			const queryOptions = {
				...options,
				filters: {
					...options.filters,
					status
				},
				sort: options.sort || [{ column: 'createdAt', direction: 'desc' as const }]
			};

			const result = await this.findPaginated(queryOptions);

			logger.debug('TransactionRepository', 'Found transactions by status', {
				status,
				count: result.data.length,
				total: result.total
			});

			return result;
		} catch (error) {
			logger.error('TransactionRepository', 'Error in findByStatus', { status, options, error });
			throw new RepositoryError(
				'Failed to find transactions by status',
				'FIND_BY_STATUS_ERROR',
				500,
				{ status, options, originalError: error }
			);
		}
	}

	/**
	 * Get total transaction amount by user and optionally by type
	 */
	async getTotalByUser(userId: UserId, type?: string): Promise<number> {
		try {
			const conditions = [eq(transactions.userId, userId)];

			if (type) {
				conditions.push(eq(transactions.type, type));
			}

			const [result] = await db
				.select({
					total: sql<number>`COALESCE(SUM(${transactions.amount}), 0)`
				})
				.from(transactions)
				.where(and(...conditions));

			const total = Number(result?.total || 0);

			logger.debug('TransactionRepository', 'Calculated total by user', {
				userId,
				type,
				total
			});

			return total;
		} catch (error) {
			logger.error('TransactionRepository', 'Error in getTotalByUser', { userId, type, error });
			throw new RepositoryError(
				'Failed to get total transactions by user',
				'GET_TOTAL_BY_USER_ERROR',
				500,
				{ userId, type, originalError: error }
			);
		}
	}

	/**
	 * Get balance by user (credits minus debits)
	 */
	async getBalanceByUser(userId: UserId): Promise<number> {
		try {
			// Calculate credits (positive transactions)
			const [creditsResult] = await db
				.select({
					credits: sql<number>`COALESCE(SUM(${transactions.amount}), 0)`
				})
				.from(transactions)
				.where(and(eq(transactions.userId, userId), sql`${transactions.amount} > 0`));

			// Calculate debits (negative transactions)
			const [debitsResult] = await db
				.select({
					debits: sql<number>`COALESCE(SUM(ABS(${transactions.amount})), 0)`
				})
				.from(transactions)
				.where(and(eq(transactions.userId, userId), sql`${transactions.amount} < 0`));

			const credits = Number(creditsResult?.credits || 0);
			const debits = Number(debitsResult?.debits || 0);
			const balance = credits - debits;

			logger.debug('TransactionRepository', 'Calculated balance by user', {
				userId,
				credits,
				debits,
				balance
			});

			return balance;
		} catch (error) {
			logger.error('TransactionRepository', 'Error in getBalanceByUser', { userId, error });
			throw new RepositoryError('Failed to get balance by user', 'GET_BALANCE_BY_USER_ERROR', 500, {
				userId,
				originalError: error
			});
		}
	}

	/**
	 * Get transactions by date range
	 */
	async findByDateRange(
		startDate: Date,
		endDate: Date,
		options: QueryOptions = {}
	): Promise<PaginatedResult<Transaction>> {
		try {
			const queryOptions = {
				...options,
				filters: {
					...options.filters,
					createdAt: {
						operator: 'gte',
						value: startDate
					}
					// Add end date filter using complex condition
				},
				sort: options.sort || [{ column: 'createdAt', direction: 'desc' as const }]
			};

			// For now, use simple date filtering - can be enhanced with proper date range queries
			const result = await this.findPaginated(queryOptions);

			// Filter by end date manually (should be optimized to database level)
			result.data = result.data.filter((transaction) => transaction.createdAt <= endDate);

			logger.debug('TransactionRepository', 'Found transactions by date range', {
				startDate,
				endDate,
				count: result.data.length
			});

			return result;
		} catch (error) {
			logger.error('TransactionRepository', 'Error in findByDateRange', {
				startDate,
				endDate,
				options,
				error
			});
			throw new RepositoryError(
				'Failed to find transactions by date range',
				'FIND_BY_DATE_RANGE_ERROR',
				500,
				{ startDate, endDate, options, originalError: error }
			);
		}
	}

	/**
	 * Get transaction statistics for a user
	 */
	async getUserStats(userId: UserId): Promise<{
		totalTransactions: number;
		totalCredits: number;
		totalDebits: number;
		balance: number;
		lastTransactionAt: Date | null;
	}> {
		try {
			const [statsResult] = await db
				.select({
					totalTransactions: sql<number>`COUNT(*)`,
					totalCredits: sql<number>`COALESCE(SUM(CASE WHEN ${transactions.amount} > 0 THEN ${transactions.amount} ELSE 0 END), 0)`,
					totalDebits: sql<number>`COALESCE(SUM(CASE WHEN ${transactions.amount} < 0 THEN ABS(${transactions.amount}) ELSE 0 END), 0)`,
					lastTransactionAt: sql<Date | null>`MAX(${transactions.createdAt})`
				})
				.from(transactions)
				.where(eq(transactions.userId, userId));

			const totalCredits = Number(statsResult?.totalCredits || 0);
			const totalDebits = Number(statsResult?.totalDebits || 0);
			const balance = totalCredits - totalDebits;

			const stats = {
				totalTransactions: Number(statsResult?.totalTransactions || 0),
				totalCredits,
				totalDebits,
				balance,
				lastTransactionAt: statsResult?.lastTransactionAt || null
			};

			logger.debug('TransactionRepository', 'Calculated user stats', {
				userId,
				stats
			});

			return stats;
		} catch (error) {
			logger.error('TransactionRepository', 'Error in getUserStats', { userId, error });
			throw new RepositoryError(
				'Failed to get user transaction statistics',
				'GET_USER_STATS_ERROR',
				500,
				{ userId, originalError: error }
			);
		}
	}

	/**
	 * Prepare data for transaction creation with validation
	 */
	protected prepareCreateData(data: Partial<Transaction>): any {
		// Validate required fields
		if (!data.userId) {
			throw new RepositoryError('User ID is required for transaction', 'VALIDATION_ERROR', 400, {
				field: 'userId'
			});
		}

		if (!data.type) {
			throw new RepositoryError('Transaction type is required', 'VALIDATION_ERROR', 400, {
				field: 'type'
			});
		}

		if (data.amount === undefined || data.amount === null) {
			throw new RepositoryError('Transaction amount is required', 'VALIDATION_ERROR', 400, {
				field: 'amount'
			});
		}

		return {
			...data,
			status: data.status || 'pending',
			metadata: data.metadata || {},
			createdAt: new Date(),
			updatedAt: new Date()
		};
	}
}

/**
 * Redis-Based Rate Limiting Service
 *
 * Production-grade rate limiting with Redis backend
 * Falls back to in-memory for development
 */

import Redis from 'ioredis';
import rateLimit from 'express-rate-limit';
import RedisStore from 'rate-limit-redis';
import type { Request, Response } from 'express';
import { userService } from './user.service';
import { env, getRateLimitConfig, isDevelopment } from '../config/environment';
import { logger } from '../logger';

/**
 * Redis connection for rate limiting
 */
let redisClient: Redis | null = null;

/**
 * Initialize Redis connection for rate limiting
 */
function initializeRedis(): Redis | null {
	const config = getRateLimitConfig();

	if (!config.redis) {
		logger.warn('RateLimit', 'Redis not configured - using memory store');
		return null;
	}

	try {
		const redis = new Redis(config.redis, {
			retryDelayOnFailover: 100,
			maxRetriesPerRequest: 3,
			lazyConnect: true,
			connectionName: 'rate-limiter',
			// Redis options for production stability
			keepAlive: 30000,
			connectTimeout: 10000,
			commandTimeout: 5000
		});

		redis.on('connect', () => {
			logger.info('RateLimit', 'Connected to Redis');
		});

		redis.on('error', (error) => {
			logger.error('RateLimit', 'Redis connection error:', error);
		});

		return redis;
	} catch (error) {
		logger.error('RateLimit', 'Failed to initialize Redis:', error);
		return null;
	}
}

/**
 * Create rate limiter with Redis or memory store
 */
function createRateLimiter(config: any) {
	const baseConfig = {
		windowMs: config.windowMs || 15 * 60 * 1000,
		max: config.max || 100,
		message: config.message || { error: 'Too many requests' },
		standardHeaders: true,
		legacyHeaders: false,
		// Custom key generator for better tracking
		keyGenerator: (req: Request) => {
			const authUser = userService.getUserFromRequest(req);
			const ip = req.ip || req.connection.remoteAddress;
			// Use user ID if authenticated, otherwise IP
			return authUser ? `user:${authUser.id}` : `ip:${ip}`;
		},
		// Skip successful requests to reduce Redis load
		skipSuccessfulRequests: false,
		skipFailedRequests: false
	};

	// Use Redis store if available
	if (redisClient && env.RATE_LIMIT_ENABLED) {
		return rateLimit({
			...baseConfig,
			store: new RedisStore({
				sendCommand: (...args: string[]) => redisClient!.call(...args),
				prefix: 'rate_limit:'
			})
		});
	}

	// Fallback to memory store with warning
	if (env.RATE_LIMIT_ENABLED) {
		logger.warn('RateLimit', 'Using memory store - not recommended for production');
	}

	return rateLimit(baseConfig);
}

/**
 * Rate limit configurations for different endpoint types
 */
export const rateLimiters = {
	// General API endpoints
	general: createRateLimiter({
		windowMs: 15 * 60 * 1000, // 15 minutes
		max: isDevelopment() ? 1000 : 100,
		message: {
			error: 'Too many requests',
			retryAfter: '15 minutes',
			type: 'RATE_LIMIT_GENERAL'
		}
	}),

	// Authentication endpoints
	auth: createRateLimiter({
		windowMs: 15 * 60 * 1000, // 15 minutes
		max: isDevelopment() ? 50 : 5,
		message: {
			error: 'Too many authentication attempts',
			retryAfter: '15 minutes',
			type: 'RATE_LIMIT_AUTH'
		}
	}),

	// Password reset (very strict)
	passwordReset: createRateLimiter({
		windowMs: 60 * 60 * 1000, // 1 hour
		max: 3,
		message: {
			error: 'Too many password reset attempts',
			retryAfter: '1 hour',
			type: 'RATE_LIMIT_PASSWORD_RESET'
		}
	}),

	// Admin endpoints
	admin: createRateLimiter({
		windowMs: 60 * 60 * 1000, // 1 hour
		max: isDevelopment() ? 500 : 50,
		message: {
			error: 'Too many admin requests',
			retryAfter: '1 hour',
			type: 'RATE_LIMIT_ADMIN'
		}
	}),

	// Financial operations
	financial: createRateLimiter({
		windowMs: 60 * 60 * 1000, // 1 hour
		max: isDevelopment() ? 100 : 10,
		message: {
			error: 'Too many financial operations',
			retryAfter: '1 hour',
			type: 'RATE_LIMIT_FINANCIAL'
		}
	}),

	// Forum posting
	posting: createRateLimiter({
		windowMs: 5 * 60 * 1000, // 5 minutes
		max: isDevelopment() ? 100 : 10,
		message: {
			error: 'Too many posts created',
			retryAfter: '5 minutes',
			type: 'RATE_LIMIT_POSTING'
		}
	}),

	// File uploads
	upload: createRateLimiter({
		windowMs: 15 * 60 * 1000, // 15 minutes
		max: isDevelopment() ? 50 : 5,
		message: {
			error: 'Too many file uploads',
			retryAfter: '15 minutes',
			type: 'RATE_LIMIT_UPLOAD'
		}
	})
};

/**
 * Custom rate limiter for specific needs
 */
export function createCustomRateLimiter(options: {
	windowMs: number;
	max: number;
	message?: any;
	keyGenerator?: (req: Request) => string;
}) {
	return createRateLimiter(options);
}

/**
 * Rate limit bypass for development
 */
export function bypassRateLimit(req: Request, res: Response, next: Function) {
	if (isDevelopment() && req.headers['x-bypass-rate-limit']) {
		logger.debug('RateLimit', 'Bypassing rate limit in development');
		return next();
	}
	next();
}

/**
 * Get rate limit statistics
 */
export async function getRateLimitStats(key: string): Promise<any> {
	if (!redisClient) {
		return { error: 'Redis not available' };
	}

	try {
		const current = await redisClient.get(`rate_limit:${key}`);
		const ttl = await redisClient.ttl(`rate_limit:${key}`);

		return {
			current: current ? parseInt(current) : 0,
			remaining: ttl,
			resetTime: new Date(Date.now() + ttl * 1000)
		};
	} catch (error) {
		logger.error('RateLimit', 'Failed to get stats:', error);
		return { error: 'Failed to get rate limit stats' };
	}
}

/**
 * Clear rate limit for a specific key (admin operation)
 */
export async function clearRateLimit(key: string): Promise<boolean> {
	if (!redisClient) {
		logger.warn('RateLimit', 'Cannot clear rate limit - Redis not available');
		return false;
	}

	try {
		await redisClient.del(`rate_limit:${key}`);
		logger.info('RateLimit', 'Cleared rate limit', { key });
		return true;
	} catch (error) {
		logger.error('RateLimit', 'Failed to clear rate limit:', error);
		return false;
	}
}

/**
 * Health check for rate limiting service
 */
export async function rateLimitHealthCheck(): Promise<{
	status: 'healthy' | 'degraded' | 'unhealthy';
	redis: boolean;
	store: 'redis' | 'memory';
}> {
	if (!env.RATE_LIMIT_ENABLED) {
		return {
			status: 'healthy',
			redis: false,
			store: 'memory'
		};
	}

	if (!redisClient) {
		return {
			status: 'degraded',
			redis: false,
			store: 'memory'
		};
	}

	try {
		await redisClient.ping();
		return {
			status: 'healthy',
			redis: true,
			store: 'redis'
		};
	} catch (error) {
		return {
			status: 'unhealthy',
			redis: false,
			store: 'memory'
		};
	}
}

// Initialize Redis connection
if (env.RATE_LIMIT_ENABLED) {
	redisClient = initializeRedis();
}

// Graceful shutdown
process.on('SIGTERM', () => {
	if (redisClient) {
		redisClient.disconnect();
		logger.info('RateLimit', 'Redis connection closed');
	}
});

export { redisClient };

/**
 * Centralized User Service
 *
 * Consolidates scattered getUser patterns across the codebase
 * Provides consistent user fetching, validation, and management
 */

import { db } from '@db';
import { users, userRoles } from '@schema';
import { eq, and } from 'drizzle-orm';
import { logger } from '../logger';
import type { UserId } from '@shared/types/ids';
import { getAuthenticatedUser } from "@server/src/core/utils/auth.helpers";

export interface User {
	id: UserId;
	username: string;
	email: string;
	role: 'user' | 'moderator' | 'admin';
	level?: number;
	dgtBalance?: number;
	isActive: boolean;
	createdAt: Date;
	updatedAt: Date;
}

export interface UserProfile extends User {
	displayName?: string;
	bio?: string;
	avatarUrl?: string;
	totalPosts: number;
	totalThreads: number;
	reputation: number;
	lastActiveAt?: Date;
}

export interface AuthenticatedUser extends User {
	sessionId?: string;
	permissions?: string[];
	lastLoginAt?: Date;
}

class UserService {
	/**
	 * Get user from Express request object with type safety
	 */
	getUserFromRequest(req: any): AuthenticatedUser | null {
		const user = getAuthenticatedUser(req);
		if (!user || !user.id) {
			return null;
		}

		return {
			id: user.id,
			username: user.username,
			email: user.email,
			role: user.role || 'user',
			level: user.level,
			dgtBalance: user.dgtBalance,
			isActive: user.isActive !== false,
			createdAt: user.createdAt,
			updatedAt: user.updatedAt,
			sessionId: req.sessionID,
			permissions: user.permissions,
			lastLoginAt: user.lastLoginAt
		};
	}

	/**
	 * Get user by ID with complete profile data
	 */
	async getUserById(userId: UserId): Promise<UserProfile | null> {
		try {
			const [user] = await db
				.select({
					id: users.id,
					username: users.username,
					email: users.email,
					role: users.role,
					level: users.level,
					dgtBalance: users.dgtBalance,
					isActive: users.isActive,
					createdAt: users.createdAt,
					updatedAt: users.updatedAt,
					displayName: users.displayName,
					bio: users.bio,
					avatarUrl: users.avatarUrl,
					totalPosts: users.totalPosts,
					totalThreads: users.totalThreads,
					reputation: users.reputation,
					lastActiveAt: users.lastActiveAt
				})
				.from(users)
				.where(eq(users.id, userId))
				.limit(1);

			return user || null;
		} catch (error) {
			logger.error('UserService', 'Error fetching user by ID', { userId, error });
			return null;
		}
	}

	/**
	 * Get user by username
	 */
	async getUserByUsername(username: string): Promise<User | null> {
		try {
			const [user] = await db
				.select({
					id: users.id,
					username: users.username,
					email: users.email,
					role: users.role,
					level: users.level,
					dgtBalance: users.dgtBalance,
					isActive: users.isActive,
					createdAt: users.createdAt,
					updatedAt: users.updatedAt
				})
				.from(users)
				.where(eq(users.username, username))
				.limit(1);

			return user || null;
		} catch (error) {
			logger.error('UserService', 'Error fetching user by username', { username, error });
			return null;
		}
	}

	/**
	 * Get user by email
	 */
	async getUserByEmail(email: string): Promise<User | null> {
		try {
			const [user] = await db
				.select({
					id: users.id,
					username: users.username,
					email: users.email,
					role: users.role,
					level: users.level,
					dgtBalance: users.dgtBalance,
					isActive: users.isActive,
					createdAt: users.createdAt,
					updatedAt: users.updatedAt
				})
				.from(users)
				.where(eq(users.email, email))
				.limit(1);

			return user || null;
		} catch (error) {
			logger.error('UserService', 'Error fetching user by email', { email, error });
			return null;
		}
	}

	/**
	 * Get multiple users by IDs
	 */
	async getUsersByIds(userIds: UserId[]): Promise<User[]> {
		if (userIds.length === 0) return [];

		try {
			const userList = await db
				.select({
					id: users.id,
					username: users.username,
					email: users.email,
					role: users.role,
					level: users.level,
					dgtBalance: users.dgtBalance,
					isActive: users.isActive,
					createdAt: users.createdAt,
					updatedAt: users.updatedAt
				})
				.from(users)
				.where(eq(users.id, userIds[0])); // TODO: Use proper IN clause when Drizzle supports it

			return userList;
		} catch (error) {
			logger.error('UserService', 'Error fetching users by IDs', { userIds, error });
			return [];
		}
	}

	/**
	 * Check if user exists and is active
	 */
	async isActiveUser(userId: UserId): Promise<boolean> {
		try {
			const [user] = await db
				.select({ isActive: users.isActive })
				.from(users)
				.where(and(eq(users.id, userId), eq(users.isActive, true)))
				.limit(1);

			return !!user;
		} catch (error) {
			logger.error('UserService', 'Error checking user active status', { userId, error });
			return false;
		}
	}

	/**
	 * Update user's last active timestamp
	 */
	async updateLastActive(userId: UserId): Promise<void> {
		try {
			await db.update(users).set({ lastActiveAt: new Date() }).where(eq(users.id, userId));
		} catch (error) {
			logger.error('UserService', 'Error updating last active', { userId, error });
		}
	}

	/**
	 * Check if user has specific role
	 */
	hasRole(user: User | AuthenticatedUser, role: 'admin' | 'moderator' | 'user'): boolean {
		if (role === 'admin') {
			return user.role === 'admin';
		}
		if (role === 'moderator') {
			return user.role === 'moderator' || user.role === 'admin';
		}
		return true; // All authenticated users have 'user' role
	}

	/**
	 * Check if user is admin
	 */
	isAdmin(user: User | AuthenticatedUser): boolean {
		return user.role === 'admin';
	}

	/**
	 * Check if user is moderator or admin
	 */
	isModerator(user: User | AuthenticatedUser): boolean {
		return user.role === 'moderator' || user.role === 'admin';
	}

	/**
	 * Validate user session and return user data
	 */
	async validateUserSession(req: any): Promise<AuthenticatedUser | null> {
		const user = this.getUserFromRequest(req);
		if (!user) return null;

		// Check if user is still active
		const isActive = await this.isActiveUser(user.id);
		if (!isActive) {
			logger.warn('UserService', 'Inactive user attempted access', { userId: user.id });
			return null;
		}

		// Update last active
		await this.updateLastActive(user.id);

		return user;
	}

	/**
	 * Get user display name (fallback to username)
	 */
	getDisplayName(user: Partial<UserProfile>): string {
		return user.displayName || user.username || `User ${user.id}`;
	}

	/**
	 * Format user for API response (remove sensitive data)
	 */
	formatForAPI(user: User | UserProfile): Omit<User | UserProfile, 'email'> {
		const { email, ...safeUser } = user;
		return safeUser;
	}

	/**
	 * Batch user operations for efficiency
	 */
	async batchGetUsers(userIds: UserId[]): Promise<Map<UserId, User>> {
		const users = await this.getUsersByIds(userIds);
		const userMap = new Map<UserId, User>();

		users.forEach((user) => {
			userMap.set(user.id, user);
		});

		return userMap;
	}
}

