/**
 * Extract userId from request consistently
 * This standardizes user ID access across the platform
 */
export function getUserId(req: Request): number {
	const user = userService.getUserFromRequest(req) as any;
	return user?.id || 0;
}

/**
 * Middleware to ensure user is an admin
 * In development mode, it auto-authenticates as DevUser
 */
export async function isAdmin(req: Request, res: Response, next: NextFunction) {
	// Skip auth check in development mode and auto-login as DevUser
	if (process.env.ALLOW_DEV_ADMIN === 'true' && !req.isAuthenticated()) {
		try {
			// Use Drizzle ORM for secure, parameterized queries
			const [devUser] = await db.select().from(users).where(eq(users.username, 'DevUser')).limit(1);

			if (devUser) {
				// For admin routes, ensure the DevUser has admin privileges
				if (devUser.role !== 'admin') {
					logger.warn(
						'AdminMiddleware',
						'DevUser exists but does not have admin role. Updating to admin role.',
						{ userId: devUser.id }
					);
					await db.update(users).set({ role: 'admin' }).where(eq(users.username, 'DevUser'));
					devUser.role = 'admin';
				}

				// Mock an authenticated session with the DevUser
				req.login(devUser, (err) => {
					if (err) {
						logger.error(
							'AdminMiddleware',
							'Error auto-authenticating as DevUser for admin route',
							{ err, userId: devUser.id || devUser.user_id }
						);
						return res.status(401).json({ message: 'Unauthorized' });
					}

					logger.info(
						'AdminMiddleware',
						`Auto-authenticated as DevUser admin (ID: ${devUser.id})`,
						{ userId: devUser.id }
					);
					return next();
				});
			} else {
				logger.warn('AdminMiddleware', 'DevUser not found in database, admin auth failed');
				return res.status(401).json({ message: 'Unauthorized' });
			}
		} catch (error) {
			logger.error('AdminMiddleware', 'Error in dev mode admin authentication', { err: error });
			return res.status(401).json({ message: 'Unauthorized' });
		}
	} else if (req.isAuthenticated()) {
		// Normal authentication - use RBAC util
		const user = userService.getUserFromRequest(req) as any;
		const { canUser } = await import('@lib/auth/canUser');
		if (user && (await canUser(user, 'canViewAdminPanel'))) {
			return next();
		}
		return res.status(403).json({ message: 'Forbidden - Admin access required' });
	} else {
		// Not authenticated and not in dev mode
		return res.status(401).json({ message: 'Unauthorized' });
	}
}

/**
 * Check if user is authenticated and has moderator or admin rights
 */
export async function isAdminOrModerator(req: Request, res: Response, next: NextFunction) {
	// Skip auth check in development mode and auto-login as DevUser
	if (process.env.ALLOW_DEV_ADMIN === 'true' && !req.isAuthenticated()) {
		try {
			// Use Drizzle ORM for secure, parameterized queries
			const [devUser] = await db.select().from(users).where(eq(users.username, 'DevUser')).limit(1);

			if (devUser) {
				// For admin routes, ensure the DevUser has admin or mod privileges
				if (devUser.role !== 'admin' && devUser.role !== 'mod') {
					logger.warn(
						'AdminMiddleware',
						'DevUser exists but does not have admin/mod role. Updating to admin role.',
						{ userId: devUser.id }
					);
					await db.update(users).set({ role: 'admin' }).where(eq(users.username, 'DevUser'));
					devUser.role = 'admin';
				}

				// Mock an authenticated session with the DevUser
				req.login(devUser, (err) => {
					if (err) {
						logger.error(
							'AdminMiddleware',
							'Error auto-authenticating as DevUser for admin/mod route',
							{ err, userId: devUser.id || devUser.user_id }
						);
						return res.status(401).json({ message: 'Unauthorized' });
					}

					logger.info(
						'AdminMiddleware',
						`Auto-authenticated as DevUser admin/mod (ID: ${devUser.id})`,
						{ userId: devUser.id }
					);
					return next();
				});
			} else {
				logger.warn('AdminMiddleware', 'DevUser not found in database, admin/mod auth failed');
				return res.status(401).json({ message: 'Unauthorized' });
			}
		} catch (error) {
			logger.error('AdminMiddleware', 'Error in dev mode admin/mod authentication', { err: error });
			return res.status(401).json({ message: 'Unauthorized' });
		}
	} else if (req.isAuthenticated()) {
		const user = userService.getUserFromRequest(req) as any;
		const { canUser } = await import('@lib/auth/canUser');
		if (user && (await canUser(user, 'canManageUsers'))) {
			return next();
		}
		return res.status(403).json({ message: 'Forbidden - Admin or moderator access required' });
	} else {
		// Not authenticated and not in dev mode
		return res.status(401).json({ message: 'Unauthorized' });
	}
}

/**
 * Async handler for error handling in admin routes
 * Wraps async route handlers to properly catch errors
 */
export const asyncHandler = (fn: Function) => (req: Request, res: Response, next: NextFunction) => {
	Promise.resolve(fn(req, res, next)).catch((error) => {
		logger.error('AdminMiddleware', 'Admin route error in asyncHandler', {
			err: error,
			path: req.path,
			method: req.method
		});

		if (error instanceof WalletError) {
			return res.status(error.httpStatus).json({
				error: error.message,
				code: error.code
			});
		}

		return res.status(500).json({
			error: 'An unexpected error occurred',
			message: error.message || 'Internal server error'
		});
	});
};

/**
 * Admin Service
 *
 * Provides centralized admin functionality
 */

import { db } from '@db';
import { count, desc, eq, sql, and, like, isNull, or, ne, sum } from 'drizzle-orm';
import { users, auditLogs, transactions } from '@schema';
import { AdminError, AdminErrorCodes } from './admin.errors';
import type { AdminId, UserId } from '@shared/types/ids';
import { logger } from '../../core/logger';

export class AdminService {
	/**
	 * Log admin action for audit purposes
	 */
	async logAdminAction(
		adminId: AdminId,
		action: string,
		entityType: string,
		entityId: string,
		details: any = {}
	) {
		try {
			await db.insert(auditLogs).values({
				userId: adminId,
				action,
				entityType,
				entityId,
				details: details || {}
			});
		} catch (error) {
			logger.error('Error logging admin action:', error);
			// Non-blocking - we continue even if logging fails
		}
	}

	/**
	 * Get admin dashboard statistics
	 */
	async getDashboardStats() {
		try {
			// User stats
			const [userStats] = await db
				.select({
					total: count(users.id),
					active: sql`COUNT(CASE WHEN ${users.isActive} = true THEN 1 END)`,
					banned: sql`COUNT(CASE WHEN ${users.isBanned} = true THEN 1 END)`,
					new24h: sql`COUNT(CASE WHEN ${users.createdAt} > NOW() - INTERVAL '1 day' THEN 1 END)`
				})
				.from(users);

			// Additional stats could be added here (posts, threads, etc.)

			return {
				users: {
					total: Number(userStats.total) || 0,
					active: Number(userStats.active) || 0,
					banned: Number(userStats.banned) || 0,
					new24h: Number(userStats.new24h) || 0
				}
			};
		} catch (error) {
			logger.error('Error fetching admin dashboard stats:', error);
			throw new AdminError('Failed to fetch dashboard statistics', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get a user by ID with standardized ID handling
	 */
	async getUserById(userId: UserId) {
		try {
			if (!userId) {
				throw new AdminError('Invalid user ID', 400, AdminErrorCodes.INVALID_REQUEST);
			}

			const [user] = await db.select().from(users).where(eq(users.id, userId));

			if (!user) {
				throw new AdminError(
					`User with ID ${userId} not found`,
					404,
					AdminErrorCodes.USER_NOT_FOUND
				);
			}

			return user;
		} catch (error) {
			if (error instanceof AdminError) {
				throw error;
			}
			logger.error('Error fetching user by ID:', error);
			throw new AdminError('Failed to fetch user', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get recent admin actions
	 */
	async getRecentAdminActions(limit: number = 10) {
		try {
			const recentActions = await db
				.select({
					id: auditLogs.id,
					userId: auditLogs.userId,
					action: auditLogs.action,
					entityType: auditLogs.entityType,
					entityId: auditLogs.entityId,
					details: auditLogs.details,
					createdAt: auditLogs.createdAt,
					username: users.username
				})
				.from(auditLogs)
				.leftJoin(users, eq(auditLogs.userId, users.id))
				.orderBy(desc(auditLogs.createdAt))
				.limit(limit);

			return recentActions;
		} catch (error) {
			logger.error('Error fetching recent admin actions:', error);
			throw new AdminError('Failed to fetch recent admin actions', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}
}

// Export singleton instance
export const adminService = new AdminService();

import type { Request, Response } from 'express';
import type { EntityId, RequestId } from '@shared/types/ids';
import { UserId } from '@shared/types/ids';
import { userService } from '@server/src/core/services/user.service';
import { logger } from '@server/src/core/logger';
import { AdminError, AdminErrorCodes } from '../admin.errors';

// Enhanced error types with context
export interface AdminErrorContext {
	operation: string;
	entityType?: string;
	entityId?: string | EntityId;
	userId?: UserId;
	timestamp: Date;
	requestId?: string;
	metadata?: Record<string, any>;
}

export interface AdminOperationResult<T = any> {
	success: boolean;
	data?: T;
	error?: AdminErrorDetails;
	context: AdminErrorContext;
}

export interface AdminErrorDetails {
	code: string;
	message: string;
	httpStatus: number;
	details?: any;
	recoverable: boolean;
	userMessage?: string;
}

/**
 * Typed error categories for better error handling
 */
export const ERROR_CATEGORIES = {
	VALIDATION: 'validation',
	AUTHORIZATION: 'authorization',
	NOT_FOUND: 'not_found',
	CONFLICT: 'conflict',
	RATE_LIMITED: 'rate_limited',
	EXTERNAL_SERVICE: 'external_service',
	DATABASE: 'database',
	BUSINESS_LOGIC: 'business_logic',
	SYSTEM: 'system'
} as const;

export type ErrorCategory = (typeof ERROR_CATEGORIES)[keyof typeof ERROR_CATEGORIES];

/**
 * Enhanced AdminError with category and recovery information
 */
export class TypedAdminError extends AdminError {
	public readonly category: ErrorCategory;
	public readonly recoverable: boolean;
	public readonly userMessage?: string;
	public readonly context?: AdminErrorContext;

	constructor(
		message: string,
		httpStatus: number = 500,
		code: string = AdminErrorCodes.INTERNAL_ERROR,
		details?: any,
		category: ErrorCategory = ERROR_CATEGORIES.SYSTEM,
		recoverable: boolean = false,
		userMessage?: string,
		context?: AdminErrorContext
	) {
		super(message, httpStatus, code, details);
		this.category = category;
		this.recoverable = recoverable;
		this.userMessage = userMessage;
		this.context = context;
	}

	toJSON() {
		return {
			...super.toJSON(),
			category: this.category,
			recoverable: this.recoverable,
			userMessage: this.userMessage,
			context: this.context
		};
	}
}

/**
 * Error factory for creating typed admin errors
 */
export class AdminErrorFactory {
	static validation(
		message: string,
		details?: any,
		userMessage?: string,
		context?: AdminErrorContext
	): TypedAdminError {
		return new TypedAdminError(
			message,
			400,
			AdminErrorCodes.VALIDATION_ERROR,
			details,
			ERROR_CATEGORIES.VALIDATION,
			true,
			userMessage || 'Please check your input and try again',
			context
		);
	}

	static notFound(
		entityType: string,
		entityId: string | EntityId,
		context?: AdminErrorContext
	): TypedAdminError {
		return new TypedAdminError(
			`${entityType} with ID ${entityId} not found`,
			404,
			AdminErrorCodes.NOT_FOUND,
			{ entityType, entityId },
			ERROR_CATEGORIES.NOT_FOUND,
			false,
			`The requested ${entityType.toLowerCase()} could not be found`,
			context
		);
	}

	static unauthorized(
		operation: string,
		requiredRole?: string,
		context?: AdminErrorContext
	): TypedAdminError {
		return new TypedAdminError(
			`Insufficient permissions for operation: ${operation}`,
			403,
			AdminErrorCodes.FORBIDDEN,
			{ operation, requiredRole },
			ERROR_CATEGORIES.AUTHORIZATION,
			false,
			'You do not have permission to perform this action',
			context
		);
	}

	static conflict(
		message: string,
		conflictType: string,
		details?: any,
		context?: AdminErrorContext
	): TypedAdminError {
		return new TypedAdminError(
			message,
			409,
			AdminErrorCodes.DUPLICATE_ENTRY,
			{ conflictType, ...details },
			ERROR_CATEGORIES.CONFLICT,
			true,
			'This operation conflicts with existing data',
			context
		);
	}

	static rateLimited(
		operation: string,
		resetTime?: Date,
		context?: AdminErrorContext
	): TypedAdminError {
		return new TypedAdminError(
			`Rate limit exceeded for operation: ${operation}`,
			429,
			AdminErrorCodes.RATE_LIMITED,
			{ operation, resetTime },
			ERROR_CATEGORIES.RATE_LIMITED,
			true,
			'Too many requests. Please try again later',
			context
		);
	}

	static database(
		operation: string,
		originalError?: Error,
		context?: AdminErrorContext
	): TypedAdminError {
		return new TypedAdminError(
			`Database error during operation: ${operation}`,
			500,
			AdminErrorCodes.DB_ERROR,
			{ operation, originalError: originalError?.message },
			ERROR_CATEGORIES.DATABASE,
			true,
			'A temporary issue occurred. Please try again',
			context
		);
	}

	static businessLogic(
		message: string,
		rule: string,
		details?: any,
		context?: AdminErrorContext
	): TypedAdminError {
		return new TypedAdminError(
			message,
			400,
			AdminErrorCodes.BUSINESS_RULE_VIOLATION,
			{ rule, ...details },
			ERROR_CATEGORIES.BUSINESS_LOGIC,
			true,
			message, // Business logic errors are usually user-facing
			context
		);
	}
}

/**
 * Admin operation wrapper with error boundaries
 */
export class AdminOperationBoundary {
	private context: AdminErrorContext;

	constructor(context: AdminErrorContext) {
		this.context = context;
	}

	/**
	 * Execute an admin operation with comprehensive error handling
	 */
	async execute<T>(
		operation: () => Promise<T>,
		options?: {
			retryAttempts?: number;
			retryDelay?: number;
			onRetry?: (attempt: number, error: Error) => void;
		}
	): Promise<AdminOperationResult<T>> {
		const { retryAttempts = 0, retryDelay = 1000, onRetry } = options || {};
		let lastError: Error | null = null;

		for (let attempt = 0; attempt <= retryAttempts; attempt++) {
			try {
				const result = await operation();

				// Log successful operation
				logger.info('AdminOperationBoundary', 'Operation completed successfully', {
					operation: this.context.operation,
					attempt: attempt + 1,
					entityType: this.context.entityType,
					userId: this.context.userId
				});

				return {
					success: true,
					data: result,
					context: this.context
				};
			} catch (error) {
				lastError = error;

				// Log the error
				logger.error('AdminOperationBoundary', `Operation failed (attempt ${attempt + 1})`, {
					operation: this.context.operation,
					error: error.message,
					attempt: attempt + 1,
					retryAttempts,
					entityType: this.context.entityType,
					userId: this.context.userId
				});

				// Check if error is retryable
				if (attempt < retryAttempts && this.isRetryableError(error)) {
					if (onRetry) {
						onRetry(attempt + 1, error);
					}

					// Wait before retry
					await new Promise((resolve) => setTimeout(resolve, retryDelay * (attempt + 1)));
					continue;
				}

				// Convert to typed error if needed
				const typedError = this.normalizeError(error);

				return {
					success: false,
					error: {
						code: typedError.code,
						message: typedError.message,
						httpStatus: typedError.httpStatus,
						details: typedError.details,
						recoverable: typedError instanceof TypedAdminError ? typedError.recoverable : false,
						userMessage: typedError instanceof TypedAdminError ? typedError.userMessage : undefined
					},
					context: this.context
				};
			}
		}

		// This shouldn't be reached, but TypeScript requires it
		throw lastError || new Error('Unknown error in operation boundary');
	}

	/**
	 * Check if an error is retryable
	 */
	private isRetryableError(error: Error): boolean {
		if (error instanceof TypedAdminError) {
			return (
				error.recoverable &&
				[
					ERROR_CATEGORIES.DATABASE,
					ERROR_CATEGORIES.EXTERNAL_SERVICE,
					ERROR_CATEGORIES.RATE_LIMITED
				].includes(error.category)
			);
		}

		if (error instanceof AdminError) {
			return [
				AdminErrorCodes.DB_ERROR,
				AdminErrorCodes.EXTERNAL_SERVICE_ERROR,
				AdminErrorCodes.RATE_LIMITED
			].includes(error.code);
		}

		// For unknown errors, assume they might be retryable
		return true;
	}

	/**
	 * Normalize any error to TypedAdminError
	 */
	private normalizeError(error: Error): TypedAdminError {
		if (error instanceof TypedAdminError) {
			return error;
		}

		if (error instanceof AdminError) {
			// Convert AdminError to TypedAdminError
			return new TypedAdminError(
				error.message,
				error.httpStatus,
				error.code,
				error.details,
				ERROR_CATEGORIES.SYSTEM,
				false,
				undefined,
				this.context
			);
		}

		// Convert generic error
		return new TypedAdminError(
			error.message || 'An unexpected error occurred',
			500,
			AdminErrorCodes.INTERNAL_ERROR,
			{ originalError: error.message },
			ERROR_CATEGORIES.SYSTEM,
			true,
			'A temporary issue occurred. Please try again',
			this.context
		);
	}
}

/**
 * Express middleware for admin error boundaries
 */
export function adminErrorBoundaryMiddleware(req: Request, res: Response, next: Function) {
	// Add error boundary helper to request
	req.adminBoundary = (operation: string, entityType?: string, entityId?: string | EntityId) => {
		const context: AdminErrorContext = {
			operation,
			entityType,
			entityId,
			userId: (userService.getUserFromRequest(req) as any)?.id,
			timestamp: new Date(),
			requestId: (req.headers['x-request-id'] as string) || generateRequestId(),
			metadata: {
				ip: req.ip,
				userAgent: req.headers['user-agent'],
				path: req.path,
				method: req.method
			}
		};

		return new AdminOperationBoundary(context);
	};

	next();
}

/**
 * Generate unique request ID for tracing
 */
function generateRequestId(): string {
	return `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
}

/**
 * Error response formatter
 */
export function formatErrorResponse(
	result: AdminOperationResult,
	includeStack: boolean = false
): {
	success: false;
	error: AdminErrorDetails;
	requestId?: string;
	timestamp: string;
	stack?: string;
} {
	if (result.success || !result.error) {
		throw new Error('Cannot format error response for successful result');
	}

	return {
		success: false,
		error: result.error,
		requestId: result.context.requestId,
		timestamp: result.context.timestamp.toISOString(),
		...(includeStack && { stack: new Error().stack })
	};
}

// Extend Express Request type
declare global {
	namespace Express {
		interface Request {
			adminBoundary?: (
				operation: string,
				entityType?: string,
				entityId?: string | EntityId
			) => AdminOperationBoundary;
		}
	}
}

/**
 * Admin Query Utilities
 *
 * Shared utilities for common database query patterns across admin services
 * Reduces code duplication and ensures consistent behavior
 */

import { SQL, and, or, like, ilike, eq, ne, gte, lte, asc, desc } from 'drizzle-orm';
import { PgColumn } from 'drizzle-orm/pg-core';

// Common filter types
export interface SearchFilter {
	search?: string;
	searchFields?: PgColumn[];
}

export interface DateRangeFilter {
	startDate?: Date;
	endDate?: Date;
	dateField?: PgColumn;
}

export interface StatusFilter {
	status?: string | string[];
	statusField?: PgColumn;
}

export interface RoleFilter {
	role?: string | string[];
	roleField?: PgColumn;
}

export interface PaginationParams {
	page?: number;
	limit?: number;
	offset?: number;
}

export interface SortParams {
	sortBy?: string;
	sortOrder?: 'asc' | 'desc';
	sortField?: PgColumn;
}

/**
 * Build search conditions for text search across multiple fields
 */
export function buildSearchConditions(filter: SearchFilter): SQL<unknown> | undefined {
	if (!filter.search || !filter.searchFields || filter.searchFields.length === 0) {
		return undefined;
	}

	const searchTerm = `%${filter.search}%`;
	const conditions = filter.searchFields.map((field) => ilike(field, searchTerm));

	return or(...conditions);
}

/**
 * Build date range conditions
 */
export function buildDateRangeConditions(filter: DateRangeFilter): SQL<unknown> | undefined {
	if (!filter.dateField) return undefined;

	const conditions: SQL<unknown>[] = [];

	if (filter.startDate) {
		conditions.push(gte(filter.dateField, filter.startDate));
	}

	if (filter.endDate) {
		conditions.push(lte(filter.dateField, filter.endDate));
	}

	return conditions.length > 0 ? and(...conditions) : undefined;
}

/**
 * Build status filter conditions
 */
export function buildStatusConditions(filter: StatusFilter): SQL<unknown> | undefined {
	if (!filter.status || !filter.statusField) return undefined;

	if (Array.isArray(filter.status)) {
		// Multiple statuses - use OR conditions
		const conditions = filter.status.map((status) => eq(filter.statusField!, status));
		return or(...conditions);
	} else {
		// Single status
		return eq(filter.statusField, filter.status);
	}
}

/**
 * Build role filter conditions
 */
export function buildRoleConditions(filter: RoleFilter): SQL<unknown> | undefined {
	if (!filter.role || !filter.roleField) return undefined;

	if (Array.isArray(filter.role)) {
		// Multiple roles
		const conditions = filter.role.map((role) => eq(filter.roleField!, role));
		return or(...conditions);
	} else {
		// Single role
		return eq(filter.roleField, filter.role);
	}
}

/**
 * Combine multiple filter conditions with AND logic
 */
export function combineFilterConditions(
	...conditions: (SQL<unknown> | undefined)[]
): SQL<unknown> | undefined {
	const validConditions = conditions.filter(Boolean) as SQL<unknown>[];
	return validConditions.length > 0 ? and(...validConditions) : undefined;
}

/**
 * Build sort conditions
 */
export function buildSortConditions(params: SortParams) {
	if (!params.sortField) return [];

	const direction = params.sortOrder === 'desc' ? desc : asc;
	return [direction(params.sortField)];
}

/**
 * Calculate pagination offset
 */
export function calculatePaginationOffset(params: PaginationParams): number {
	const page = params.page || 1;
	const limit = params.limit || 10;
	return (page - 1) * limit;
}

/**
 * Normalize pagination parameters
 */
export function normalizePaginationParams(params: PaginationParams): Required<PaginationParams> {
	const page = Math.max(1, params.page || 1);
	const limit = Math.min(100, Math.max(1, params.limit || 10));
	const offset = (page - 1) * limit;

	return { page, limit, offset };
}

/**
 * Build pagination metadata for responses
 */
export function buildPaginationMeta(
	total: number,
	params: Required<PaginationParams>
): {
	total: number;
	page: number;
	limit: number;
	totalPages: number;
	hasNextPage: boolean;
	hasPreviousPage: boolean;
} {
	const totalPages = Math.ceil(total / params.limit);

	return {
		total,
		page: params.page,
		limit: params.limit,
		totalPages,
		hasNextPage: params.page < totalPages,
		hasPreviousPage: params.page > 1
	};
}

/**
 * Generic user search utility
 */
export function buildUserSearchConditions(
	searchTerm: string,
	usernameField: PgColumn,
	emailField?: PgColumn
): SQL<unknown> | undefined {
	if (!searchTerm || searchTerm.length < 2) return undefined;

	const conditions = [ilike(usernameField, `%${searchTerm}%`)];

	if (emailField) {
		conditions.push(ilike(emailField, `%${searchTerm}%`));
	}

	return or(...conditions);
}

/**
 * Build exclusion conditions (NOT IN)
 */
export function buildExclusionConditions<T>(
	field: PgColumn,
	excludeValues: T[]
): SQL<unknown> | undefined {
	if (!excludeValues || excludeValues.length === 0) return undefined;

	const conditions = excludeValues.map((value) => ne(field, value));
	return and(...conditions);
}

/**
 * Utility for building complex filter chains
 */
export class FilterBuilder {
	private conditions: SQL<unknown>[] = [];

	addSearch(filter: SearchFilter): this {
		const condition = buildSearchConditions(filter);
		if (condition) this.conditions.push(condition);
		return this;
	}

	addDateRange(filter: DateRangeFilter): this {
		const condition = buildDateRangeConditions(filter);
		if (condition) this.conditions.push(condition);
		return this;
	}

	addStatus(filter: StatusFilter): this {
		const condition = buildStatusConditions(filter);
		if (condition) this.conditions.push(condition);
		return this;
	}

	addRole(filter: RoleFilter): this {
		const condition = buildRoleConditions(filter);
		if (condition) this.conditions.push(condition);
		return this;
	}

	addCustom(condition: SQL<unknown> | undefined): this {
		if (condition) this.conditions.push(condition);
		return this;
	}

	build(): SQL<unknown> | undefined {
		return this.conditions.length > 0 ? and(...this.conditions) : undefined;
	}

	clear(): this {
		this.conditions = [];
		return this;
	}
}

/**
 * Common sort field mappings for admin entities
 */
export const COMMON_SORT_FIELDS = {
	createdAt: 'created_at',
	updatedAt: 'updated_at',
	name: 'name',
	title: 'title',
	username: 'username',
	email: 'email',
	role: 'role',
	status: 'status'
} as const;

/**
 * Validate and normalize sort parameters
 */
export function validateSortParams(
	sortBy?: string,
	sortOrder?: string,
	allowedFields: string[] = Object.keys(COMMON_SORT_FIELDS)
): { sortBy: string; sortOrder: 'asc' | 'desc' } | null {
	if (!sortBy || !allowedFields.includes(sortBy)) {
		return null;
	}

	const normalizedOrder = sortOrder?.toLowerCase() === 'desc' ? 'desc' : 'asc';

	return {
		sortBy,
		sortOrder: normalizedOrder
	};
}

import { db } from '@db';
import { users, adminManualAirdropLogs, type NewAdminManualAirdropLog } from '@schema';
import { xpService } from '../../../xp/xp.service'; // Core XP service for XP adjustments
import { dgtService } from '../../../wallet/dgt.service'; // Core DGT service for DGT adjustments
import { eq, inArray } from 'drizzle-orm';
import { logger } from '../../../../core/logger';
import { v4 as uuidv4 } from 'uuid';
import type { AdminId } from '@shared/types/ids';

interface AirdropRequest {
	adminId: AdminId;
	tokenType: 'XP' | 'DGT';
	amount: number;
	targetCriteria: {
		type: 'group' | 'userIds' | 'role'; // For now, we primarily support 'group'
		value: number | number[]; // Group ID or array of User IDs
	};
	note?: string;
}

export class AirdropAdminService {
	async processAirdrop(request: AirdropRequest): Promise<{
		success: boolean;
		message: string;
		batchId?: string;
		processedCount: number;
		errorCount: number;
	}> {
		const { adminId, tokenType, amount, targetCriteria, note } = request;
		logger.info('AIRDROP_SERVICE', 'Processing airdrop request', { request });

		if (amount <= 0) {
			return {
				success: false,
				message: 'Airdrop amount must be positive.',
				processedCount: 0,
				errorCount: 0
			};
		}

		let targetUserIds: number[] = [];

		if (targetCriteria.type === 'group') {
			const groupId = targetCriteria.value as number;
			try {
				const usersInGroup = await db
					.select({ userId: users.id })
					.from(users)
					.where(eq(users.groupId, groupId));
				targetUserIds = usersInGroup.map((u) => u.userId);
			} catch (error) {
				logger.error('AIRDROP_SERVICE', `Error fetching users in group ${groupId}:`, error);
				return {
					success: false,
					message: 'Failed to fetch users in the target group.',
					processedCount: 0,
					errorCount: 0
				};
			}
		} else if (targetCriteria.type === 'userIds') {
			targetUserIds = Array.isArray(targetCriteria.value)
				? targetCriteria.value
				: [targetCriteria.value as number];
		} else {
			// TODO: Implement other target types like 'role' or 'all_users' if needed
			logger.warn('AIRDROP_SERVICE', `Unsupported target criteria type: ${targetCriteria.type}`);
			return {
				success: false,
				message: `Unsupported target criteria: ${targetCriteria.type}`,
				processedCount: 0,
				errorCount: 0
			};
		}

		if (targetUserIds.length === 0) {
			return {
				success: false,
				message: 'No target users found for the airdrop.',
				processedCount: 0,
				errorCount: 0
			};
		}

		const airdropBatchId = uuidv4();
		let processedCount = 0;
		let errorCount = 0;
		const airdropLogEntries: NewAdminManualAirdropLog[] = [];

		for (const userId of targetUserIds) {
			try {
				let operationSuccessful = false;
				if (tokenType === 'XP') {
					const xpResult = await xpService.updateUserXp(userId, amount, 'add', {
						reason: note || `Admin airdrop (Batch: ${airdropBatchId})`,
						adminId,
						logAdjustment: false // We will log via airdropRecords
					});
					operationSuccessful = !!xpResult; // Check if xpResult is not undefined/null
				} else if (tokenType === 'DGT') {
					// Assuming dgtService.addDgt returns an object indicating success or throws an error
					await dgtService.addDgt(userId, BigInt(amount), 'AIRDROP', {
						reason: note || `Admin airdrop (Batch: ${airdropBatchId})`,
						adminId,
						batchId: airdropBatchId
					});
					operationSuccessful = true;
				}

				if (operationSuccessful) {
					airdropLogEntries.push({
						adminId,
						userId,
						tokenType,
						amount,
						groupId: targetCriteria.type === 'group' ? (targetCriteria.value as number) : undefined,
						note,
						airdropBatchId
						// createdAt will be set by default
					});
					processedCount++;
				} else {
					logger.warn(
						'AIRDROP_SERVICE',
						`Airdrop operation for user ${userId} (token: ${tokenType}) reported failure but did not throw.`
					);
					errorCount++;
				}
			} catch (error) {
				logger.error(
					'AIRDROP_SERVICE',
					`Error processing airdrop for user ${userId} (Batch: ${airdropBatchId}):`,
					error
				);
				errorCount++;
				// Optionally, collect individual errors to return to the admin
			}
		}

		// Batch insert airdrop records
		if (airdropLogEntries.length > 0) {
			try {
				await db.insert(adminManualAirdropLogs).values(airdropLogEntries);
			} catch (error) {
				logger.error(
					'AIRDROP_SERVICE',
					`Failed to batch insert admin manual airdrop logs for batch ${airdropBatchId}:`,
					error
				);
				// This is a partial failure scenario. Some users might have received tokens.
				// Decide on atomicity or compensating transactions if this is critical.
				return {
					success: false,
					message:
						'Airdrop processed for some users, but failed to log all records. Please check logs.',
					batchId: airdropBatchId,
					processedCount,
					errorCount: errorCount + (targetUserIds.length - processedCount) // Count unlogged as errors too
				};
			}
		}

		let message = `Airdrop batch ${airdropBatchId} processed. ${processedCount} users received ${amount} ${tokenType}.`;
		if (errorCount > 0) {
			message += ` ${errorCount} users could not be processed. Check server logs for details.`;
		}

		logger.info('AIRDROP_SERVICE', message);
		return {
			success: processedCount > 0,
			message,
			batchId: airdropBatchId,
			processedCount,
			errorCount
		};
	}
}

