/**
 * Backup Service
 *
 * Handles database backup operations using PostgreSQL pg_dump
 */

import { db } from '@db';
import { adminBackups, backupSettings } from '@schema';
import { eq, desc, and, gte, lte, ilike, or } from 'drizzle-orm';
import { AdminError, AdminErrorCodes } from '../../admin.errors';
import { adminCacheService } from '../../shared';
import { spawn } from 'child_process';
import { createHash } from 'crypto';
import { createReadStream, createWriteStream, statSync, unlinkSync, existsSync } from 'fs';
import { createGzip } from 'zlib';
import { pipeline } from 'stream/promises';
import { join, dirname } from 'path';
import { mkdirSync } from 'fs';
import { z } from 'zod';
import type { BackupId } from '@shared/types/ids';
import { logger } from '../../../../core/logger';

// Validation schemas
export const createBackupSchema = z.object({
	displayName: z.string().min(1).max(255),
	description: z.string().optional(),
	backupType: z.enum(['full', 'schema', 'selective']).default('full'),
	backupFormat: z.enum(['custom', 'plain', 'tar']).default('custom'),
	includedTables: z.array(z.string()).default([]),
	includedSchemas: z.array(z.string()).default(['public']),
	excludedTables: z.array(z.string()).default([]),
	tags: z.array(z.string()).default([]),
	compressionType: z.enum(['gzip', 'none']).default('gzip'),
	isProtected: z.boolean().default(false)
});

export const listBackupsSchema = z.object({
	page: z.coerce.number().min(1).default(1),
	limit: z.coerce.number().min(1).max(100).default(20),
	status: z.enum(['pending', 'running', 'completed', 'failed']).optional(),
	backupType: z.enum(['full', 'schema', 'selective']).optional(),
	source: z.enum(['manual', 'scheduled']).optional(),
	search: z.string().optional(),
	startDate: z.string().datetime().optional(),
	endDate: z.string().datetime().optional(),
	tags: z.array(z.string()).default([])
});

export type CreateBackupInput = z.infer<typeof createBackupSchema>;
export type ListBackupsInput = z.infer<typeof listBackupsSchema>;

interface BackupProgress {
	backupId: BackupId;
	status: 'pending' | 'running' | 'completed' | 'failed';
	progressPercent: number;
	currentStep: string;
	estimatedTimeRemaining?: number;
	errorMessage?: string;
}

export class BackupService {
	private activeBackups = new Map<number, BackupProgress>();
	private readonly BACKUP_BASE_PATH = process.env.BACKUP_STORAGE_PATH || '/var/backups/degentalk';

	/**
	 * Create a new database backup
	 */
	async createBackup(
		data: CreateBackupInput,
		adminId: string
	): Promise<{ backupId: BackupId; message: string }> {
		try {
			// Validate input
			const validatedData = createBackupSchema.parse(data);

			// Get backup settings
			const settings = await this.getBackupSettings();

			// Check storage space
			await this.checkStorageSpace(settings);

			// Generate unique filename
			const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, 19);
			const filename = `degentalk_${validatedData.backupType}_${timestamp}`;
			const fileExtension = this.getFileExtension(
				validatedData.backupFormat,
				validatedData.compressionType
			);
			const fullFilename = `${filename}${fileExtension}`;
			const filePath = join(this.BACKUP_BASE_PATH, 'manual', fullFilename);

			// Ensure directory exists
			mkdirSync(dirname(filePath), { recursive: true });

			// Create backup record
			const [backup] = await db
				.insert(adminBackups)
				.values({
					filename: fullFilename,
					displayName: validatedData.displayName,
					description: validatedData.description,
					backupType: validatedData.backupType,
					source: 'manual',
					filePath,
					databaseName: this.getDatabaseName(),
					backupFormat: validatedData.backupFormat,
					compressionType: validatedData.compressionType,
					includedTables: validatedData.includedTables,
					includedSchemas: validatedData.includedSchemas,
					excludedTables: validatedData.excludedTables,
					tags: validatedData.tags,
					isProtected: validatedData.isProtected,
					status: 'pending',
					createdBy: adminId
				})
				.returning();

			// Start backup process asynchronously
			this.executeBackup(backup.id, filePath, validatedData).catch((error) => {
				logger.error(`Backup ${backup.id} failed:`, error);
				this.updateBackupStatus(backup.id, 'failed', error.message);
			});

			// Invalidate cache
			await adminCacheService.invalidateEntity('backup');

			return {
				backupId: backup.id,
				message: `Backup "${validatedData.displayName}" started successfully`
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to create backup', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get list of backups with filtering and pagination
	 */
	async getBackups(filters: ListBackupsInput) {
		try {
			const validatedFilters = listBackupsSchema.parse(filters);

			let query = db.select().from(adminBackups);
			const conditions = [];

			// Apply filters
			if (validatedFilters.status) {
				conditions.push(eq(adminBackups.status, validatedFilters.status));
			}

			if (validatedFilters.backupType) {
				conditions.push(eq(adminBackups.backupType, validatedFilters.backupType));
			}

			if (validatedFilters.source) {
				conditions.push(eq(adminBackups.source, validatedFilters.source));
			}

			if (validatedFilters.search) {
				conditions.push(
					or(
						ilike(adminBackups.displayName, `%${validatedFilters.search}%`),
						ilike(adminBackups.description, `%${validatedFilters.search}%`),
						ilike(adminBackups.filename, `%${validatedFilters.search}%`)
					)
				);
			}

			if (validatedFilters.startDate) {
				conditions.push(gte(adminBackups.createdAt, validatedFilters.startDate));
			}

			if (validatedFilters.endDate) {
				conditions.push(lte(adminBackups.createdAt, validatedFilters.endDate));
			}

			// Apply conditions
			if (conditions.length > 0) {
				query = query.where(and(...conditions));
			}

			// Apply ordering and pagination
			const backups = await query
				.orderBy(desc(adminBackups.createdAt))
				.limit(validatedFilters.limit)
				.offset((validatedFilters.page - 1) * validatedFilters.limit);

			// Get total count for pagination
			const [countResult] = await db
				.select({ count: adminBackups.id })
				.from(adminBackups)
				.where(conditions.length > 0 ? and(...conditions) : undefined);

			return {
				backups: backups.map((backup) => ({
					...backup,
					fileSizeMB: backup.fileSize
						? Math.round((backup.fileSize / (1024 * 1024)) * 100) / 100
						: null,
					progress: this.activeBackups.get(backup.id)
				})),
				pagination: {
					page: validatedFilters.page,
					limit: validatedFilters.limit,
					total: countResult?.count || 0,
					totalPages: Math.ceil((countResult?.count || 0) / validatedFilters.limit)
				}
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to fetch backups', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get backup details by ID
	 */
	async getBackup(id: Id<'id'>) {
		try {
			const [backup] = await db.select().from(adminBackups).where(eq(adminBackups.id, id));

			if (!backup) {
				throw new AdminError(`Backup not found: ${id}`, 404, AdminErrorCodes.NOT_FOUND);
			}

			// Check if file exists
			const fileExists = existsSync(backup.filePath);

			return {
				...backup,
				fileSizeMB: backup.fileSize
					? Math.round((backup.fileSize / (1024 * 1024)) * 100) / 100
					: null,
				fileExists,
				progress: this.activeBackups.get(backup.id)
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to fetch backup', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Delete a backup and its file
	 */
	async deleteBackup(id: Id<'id'>, adminId: string) {
		try {
			const backup = await this.getBackup(id);

			// Check if backup is protected
			if (backup.isProtected) {
				throw new AdminError(
					'Cannot delete protected backup',
					400,
					AdminErrorCodes.INVALID_REQUEST
				);
			}

			// Check if backup is currently running
			if (backup.status === 'running' || backup.status === 'pending') {
				throw new AdminError(
					'Cannot delete backup that is currently running',
					400,
					AdminErrorCodes.INVALID_REQUEST
				);
			}

			// Delete file if it exists
			if (backup.fileExists) {
				try {
					unlinkSync(backup.filePath);
				} catch (fileError) {
					logger.warn(`Could not delete backup file ${backup.filePath}:`, fileError);
				}
			}

			// Delete backup record
			await db.delete(adminBackups).where(eq(adminBackups.id, id));

			// Invalidate cache
			await adminCacheService.invalidateEntity('backup');

			return { success: true, message: `Backup "${backup.displayName}" deleted successfully` };
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to delete backup', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get backup progress for active operations
	 */
	getBackupProgress(id: Id<'id'>): BackupProgress | null {
		return this.activeBackups.get(id) || null;
	}

	/**
	 * Get backup storage statistics
	 */
	async getStorageStats() {
		try {
			// Get total backups and storage usage
			const backups = await db.select().from(adminBackups);

			const stats = backups.reduce(
				(acc, backup) => {
					acc.totalCount++;
					acc.totalSize += backup.fileSize || 0;

					if (backup.status === 'completed') acc.completedCount++;
					if (backup.status === 'failed') acc.failedCount++;
					if (backup.status === 'running' || backup.status === 'pending') acc.activeCount++;

					acc.byType[backup.backupType] = (acc.byType[backup.backupType] || 0) + 1;
					acc.bySource[backup.source] = (acc.bySource[backup.source] || 0) + 1;

					return acc;
				},
				{
					totalCount: 0,
					completedCount: 0,
					failedCount: 0,
					activeCount: 0,
					totalSize: 0,
					totalSizeMB: 0,
					byType: {} as Record<string, number>,
					bySource: {} as Record<string, number>
				}
			);

			stats.totalSizeMB = Math.round((stats.totalSize / (1024 * 1024)) * 100) / 100;

			// Get settings for comparison
			const settings = await this.getBackupSettings();
			const maxSizeMB = Math.round(settings.maxStorageSize / (1024 * 1024));
			const usagePercent = Math.round((stats.totalSizeMB / maxSizeMB) * 100);

			return {
				...stats,
				maxStorageMB: maxSizeMB,
				usagePercent,
				recommendations: this.generateStorageRecommendations(stats, settings)
			};
		} catch (error) {
			throw new AdminError('Failed to fetch storage statistics', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	// Private helper methods

	private async executeBackup(backupId: BackupId, filePath: string, options: CreateBackupInput) {
		try {
			// Update status to running
			await this.updateBackupStatus(backupId, 'running', null, {
				startedAt: new Date().toISOString()
			});

			this.activeBackups.set(backupId, {
				backupId,
				status: 'running',
				progressPercent: 0,
				currentStep: 'Initializing backup...'
			});

			// Build pg_dump command
			const pgDumpArgs = this.buildPgDumpArgs(options);

			// Update progress
			this.updateProgress(backupId, 10, 'Connecting to database...');

			// Execute pg_dump
			const outputPath =
				options.compressionType === 'gzip' ? filePath.replace('.gz', '') : filePath;

			await this.executePgDump(pgDumpArgs, outputPath, backupId);

			// Compress if needed
			if (options.compressionType === 'gzip') {
				this.updateProgress(backupId, 90, 'Compressing backup...');
				await this.compressFile(outputPath, filePath);
				unlinkSync(outputPath); // Remove uncompressed file
			}

			// Calculate file size and checksum
			this.updateProgress(backupId, 95, 'Calculating checksum...');
			const fileSize = statSync(filePath).size;
			const checksum = await this.calculateMD5(filePath);

			// Update backup record with completion
			await this.updateBackupStatus(backupId, 'completed', null, {
				completedAt: new Date().toISOString(),
				fileSize,
				checksumMd5: checksum
			});

			this.updateProgress(backupId, 100, 'Backup completed successfully');

			// Remove from active backups after a delay
			setTimeout(() => {
				this.activeBackups.delete(backupId);
			}, 5000);
		} catch (error) {
			await this.updateBackupStatus(backupId, 'failed', error.message);
			this.activeBackups.set(backupId, {
				backupId,
				status: 'failed',
				progressPercent: 0,
				currentStep: 'Backup failed',
				errorMessage: error.message
			});

			// Clean up partial files
			try {
				if (existsSync(filePath)) unlinkSync(filePath);
			} catch (cleanupError) {
				logger.warn(`Could not clean up failed backup file:`, cleanupError);
			}

			throw error;
		}
	}

	private buildPgDumpArgs(options: CreateBackupInput): string[] {
		const args = [
			'--verbose',
			'--no-password' // Use environment variables for auth
		];

		// Set format
		if (options.backupFormat === 'custom') {
			args.push('--format=custom');
		} else if (options.backupFormat === 'tar') {
			args.push('--format=tar');
		}

		// Backup type options
		if (options.backupType === 'schema') {
			args.push('--schema-only');
		}

		// Include/exclude tables
		if (options.includedTables.length > 0) {
			options.includedTables.forEach((table) => {
				args.push(`--table=${table}`);
			});
		}

		if (options.excludedTables.length > 0) {
			options.excludedTables.forEach((table) => {
				args.push(`--exclude-table=${table}`);
			});
		}

		// Include schemas
		if (options.includedSchemas.length > 0) {
			options.includedSchemas.forEach((schema) => {
				if (schema !== 'public') {
					// public is default
					args.push(`--schema=${schema}`);
				}
			});
		}

		return args;
	}

	private async executePgDump(
		args: string[],
		outputPath: string,
		backupId: BackupId
	): Promise<void> {
		return new Promise((resolve, reject) => {
			const pgDump = spawn('pg_dump', [...args, '--file', outputPath], {
				env: {
					...process.env,
					PGPASSWORD: this.extractPasswordFromUrl(process.env.DATABASE_URL!)
				}
			});

			pgDump.on('error', (error) => {
				reject(new Error(`pg_dump process error: ${error.message}`));
			});

			pgDump.on('close', (code) => {
				if (code === 0) {
					resolve();
				} else {
					reject(new Error(`pg_dump exited with code ${code}`));
				}
			});

			// Update progress periodically
			const progressInterval = setInterval(() => {
				this.updateProgress(
					backupId,
					Math.min(80, (this.activeBackups.get(backupId)?.progressPercent || 0) + 5),
					'Creating backup...'
				);
			}, 2000);

			pgDump.on('close', () => {
				clearInterval(progressInterval);
			});
		});
	}

	private async compressFile(inputPath: string, outputPath: string): Promise<void> {
		const input = createReadStream(inputPath);
		const output = createWriteStream(outputPath);
		const gzip = createGzip({ level: 6 });

		await pipeline(input, gzip, output);
	}

	private async calculateMD5(filePath: string): Promise<string> {
		return new Promise((resolve, reject) => {
			const hash = createHash('md5');
			const stream = createReadStream(filePath);

			stream.on('data', (data) => hash.update(data));
			stream.on('end', () => resolve(hash.digest('hex')));
			stream.on('error', reject);
		});
	}

	private updateProgress(backupId: BackupId, percent: number, step: string) {
		const progress = this.activeBackups.get(backupId);
		if (progress) {
			progress.progressPercent = percent;
			progress.currentStep = step;
			this.activeBackups.set(backupId, progress);
		}
	}

	private async updateBackupStatus(
		id: Id<'id'>,
		status: 'pending' | 'running' | 'completed' | 'failed',
		errorMessage?: string | null,
		additionalData?: Record<string, any>
	) {
		const updateData: any = { status };

		if (errorMessage !== undefined) {
			updateData.errorMessage = errorMessage;
		}

		if (additionalData) {
			Object.assign(updateData, additionalData);
		}

		await db.update(adminBackups).set(updateData).where(eq(adminBackups.id, id));
	}

	private async getBackupSettings() {
		// Get or create default backup settings
		const [settings] = await db.select().from(backupSettings).limit(1);

		if (!settings) {
			const [newSettings] = await db.insert(backupSettings).values({}).returning();
			return newSettings;
		}

		return settings;
	}

	private async checkStorageSpace(settings: any) {
		// Implementation would check available disk space
		// For now, just check against configured limits
		const stats = await this.getStorageStats();

		if (stats.totalSize >= settings.maxStorageSize) {
			throw new AdminError(
				'Storage limit exceeded. Please clean up old backups.',
				400,
				AdminErrorCodes.INVALID_REQUEST
			);
		}
	}

	private getFileExtension(format: string, compression: string): string {
		let ext = '';

		if (format === 'custom') ext = '.dump';
		else if (format === 'tar') ext = '.tar';
		else ext = '.sql';

		if (compression === 'gzip') ext += '.gz';

		return ext;
	}

	private getDatabaseName(): string {
		const dbUrl = process.env.DATABASE_URL || '';
		const match = dbUrl.match(/\/([^?]+)(\?|$)/);
		return match ? match[1] : 'degentalk';
	}

	private extractPasswordFromUrl(url: string): string {
		try {
			const parsed = new URL(url);
			return parsed.password;
		} catch {
			return '';
		}
	}

	private generateStorageRecommendations(stats: any, settings: any): string[] {
		const recommendations = [];

		if (stats.usagePercent > 80) {
			recommendations.push('Storage usage is high. Consider cleaning up old backups.');
		}

		if (stats.failedCount > stats.completedCount * 0.1) {
			recommendations.push('High failure rate detected. Check backup configuration.');
		}

		if (stats.activeCount > 3) {
			recommendations.push('Multiple backups running simultaneously may impact performance.');
		}

		return recommendations;
	}
}

// Export singleton instance
export const backupService = new BackupService();

/**
 * Restore Service
 *
 * Handles database restore operations with safety validations
 */

import { db } from '@db';
import { adminBackups, restoreOperations, backupSettings } from '@schema';
import { eq, desc } from 'drizzle-orm';
import { AdminError, AdminErrorCodes } from '../../admin.errors';
import { adminCacheService } from '../../shared';
import { backupService } from './backup.service';
import { spawn } from 'child_process';
import { existsSync, statSync } from 'fs';
import { randomUUID } from 'crypto';
import { z } from 'zod';
import type { BackupId } from '@shared/types/ids';
import { logger } from '../../../../core/logger';

// Validation schemas
export const createRestoreSchema = z.object({
	sourceBackupId: z.string().uuid(),
	displayName: z.string().min(1).max(255),
	description: z.string().optional(),
	restoreType: z.enum(['full', 'schema_only', 'data_only', 'selective']).default('full'),
	createPreBackup: z.boolean().default(true),
	includedTables: z.array(z.string()).default([]),
	excludedTables: z.array(z.string()).default([]),
	includeIndexes: z.boolean().default(true),
	includeConstraints: z.boolean().default(true),
	includeTriggers: z.boolean().default(true),
	confirmationToken: z.string().min(1) // Required for safety
});

export const listRestoreOperationsSchema = z.object({
	page: z.coerce.number().min(1).default(1),
	limit: z.coerce.number().min(1).max(100).default(20),
	status: z
		.enum(['pending', 'pre_backup', 'restoring', 'completed', 'failed', 'cancelled'])
		.optional(),
	restoreType: z.enum(['full', 'schema_only', 'data_only', 'selective']).optional()
});

export type CreateRestoreInput = z.infer<typeof createRestoreSchema>;
export type ListRestoreOperationsInput = z.infer<typeof listRestoreOperationsSchema>;

interface RestoreProgress {
	operationId: string;
	status: 'pending' | 'pre_backup' | 'restoring' | 'completed' | 'failed' | 'cancelled';
	progressPercent: number;
	currentStep: string;
	estimatedTimeRemaining?: number;
	errorMessage?: string;
	canRollback: boolean;
}

export class RestoreService {
	private activeRestores = new Map<string, RestoreProgress>();

	/**
	 * Validate backup file and get restore impact assessment
	 */
	async validateRestoreOperation(backupId: BackupId) {
		try {
			// Get backup details
			const backup = await backupService.getBackup(backupId);

			if (!backup) {
				throw new AdminError(`Backup not found: ${backupId}`, 404, AdminErrorCodes.NOT_FOUND);
			}

			if (backup.status !== 'completed') {
				throw new AdminError(
					'Can only restore from completed backups',
					400,
					AdminErrorCodes.INVALID_REQUEST
				);
			}

			if (!backup.fileExists) {
				throw new AdminError('Backup file not found on disk', 400, AdminErrorCodes.NOT_FOUND);
			}

			// Validate backup file integrity
			const validationResult = await this.validateBackupFile(backup);

			// Generate impact assessment
			const impactAssessment = await this.generateImpactAssessment(backup);

			// Generate warnings
			const warnings = this.generateRestoreWarnings(backup, impactAssessment);

			return {
				backup,
				validation: validationResult,
				impactAssessment,
				warnings,
				confirmationRequired: true,
				estimatedDuration: this.estimateRestoreDuration(backup)
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to validate restore operation', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Create a new restore operation
	 */
	async createRestoreOperation(
		data: CreateRestoreInput,
		adminId: string
	): Promise<{ operationId: string; message: string }> {
		try {
			// Validate input
			const validatedData = createRestoreSchema.parse(data);

			// Validate backup and get assessment
			const validation = await this.validateRestoreOperation(validatedData.sourceBackupId);

			// Check if user has permission for this type of restore
			await this.checkRestorePermissions(adminId, validatedData.restoreType);

			// Generate operation ID
			const operationId = randomUUID();

			// Get source backup details
			const sourceBackup = validation.backup;

			// Create restore operation record
			const [operation] = await db
				.insert(restoreOperations)
				.values({
					operationId,
					displayName: validatedData.displayName,
					description: validatedData.description,
					sourceBackupId: validatedData.sourceBackupId,
					sourceFilename: sourceBackup.filename,
					restoreType: validatedData.restoreType,
					createPreBackup: validatedData.createPreBackup,
					includedTables: validatedData.includedTables,
					excludedTables: validatedData.excludedTables,
					includeIndexes: validatedData.includeIndexes,
					includeConstraints: validatedData.includeConstraints,
					includeTriggers: validatedData.includeTriggers,
					status: 'pending',
					initiatedBy: adminId,
					confirmationToken: validatedData.confirmationToken,
					confirmationExpiresAt: new Date(Date.now() + 30 * 60 * 1000).toISOString(), // 30 minutes
					validationWarnings: validation.warnings,
					impactAssessment: validation.impactAssessment
				})
				.returning();

			// Start restore process asynchronously
			this.executeRestore(operation.id, operationId, validatedData, adminId).catch((error) => {
				logger.error(`Restore operation ${operationId} failed:`, error);
				this.updateRestoreStatus(operation.id, 'failed', error.message);
			});

			// Invalidate cache
			await adminCacheService.invalidateEntity('restore');

			return {
				operationId,
				message: `Restore operation "${validatedData.displayName}" started successfully`
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to create restore operation', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get list of restore operations
	 */
	async getRestoreOperations(filters: ListRestoreOperationsInput) {
		try {
			const validatedFilters = listRestoreOperationsSchema.parse(filters);

			const query = db.select().from(restoreOperations);
			const conditions = [];

			// Apply filters
			if (validatedFilters.status) {
				conditions.push(eq(restoreOperations.status, validatedFilters.status));
			}

			if (validatedFilters.restoreType) {
				conditions.push(eq(restoreOperations.restoreType, validatedFilters.restoreType));
			}

			// Apply conditions and pagination
			const operations = await query
				.orderBy(desc(restoreOperations.createdAt))
				.limit(validatedFilters.limit)
				.offset((validatedFilters.page - 1) * validatedFilters.limit);

			// Add progress information
			const operationsWithProgress = operations.map((operation) => ({
				...operation,
				progress: this.activeRestores.get(operation.operationId),
				durationMinutes: operation.duration ? Math.round(operation.duration / 60) : null
			}));

			return {
				operations: operationsWithProgress,
				pagination: {
					page: validatedFilters.page,
					limit: validatedFilters.limit,
					total: operations.length, // Would need count query for accurate total
					totalPages: Math.ceil(operations.length / validatedFilters.limit)
				}
			};
		} catch (error) {
			throw new AdminError('Failed to fetch restore operations', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get restore operation details
	 */
	async getRestoreOperation(operationId: string) {
		try {
			const [operation] = await db
				.select()
				.from(restoreOperations)
				.where(eq(restoreOperations.operationId, operationId));

			if (!operation) {
				throw new AdminError(
					`Restore operation not found: ${operationId}`,
					404,
					AdminErrorCodes.NOT_FOUND
				);
			}

			// Get source backup details
			const sourceBackup = await backupService.getBackup(operation.sourceBackupId);

			// Get pre-restore backup if exists
			let preRestoreBackup = null;
			if (operation.preRestoreBackupId) {
				preRestoreBackup = await backupService.getBackup(operation.preRestoreBackupId);
			}

			return {
				...operation,
				sourceBackup,
				preRestoreBackup,
				progress: this.activeRestores.get(operationId),
				durationMinutes: operation.duration ? Math.round(operation.duration / 60) : null
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to fetch restore operation', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Cancel a pending restore operation
	 */
	async cancelRestoreOperation(operationId: string, adminId: string) {
		try {
			const operation = await this.getRestoreOperation(operationId);

			if (operation.status !== 'pending') {
				throw new AdminError(
					'Can only cancel pending restore operations',
					400,
					AdminErrorCodes.INVALID_REQUEST
				);
			}

			// Update status to cancelled
			await this.updateRestoreStatus(operation.id, 'cancelled');

			// Remove from active restores
			this.activeRestores.delete(operationId);

			return { success: true, message: 'Restore operation cancelled successfully' };
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to cancel restore operation', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get restore progress for active operations
	 */
	getRestoreProgress(operationId: string): RestoreProgress | null {
		return this.activeRestores.get(operationId) || null;
	}

	// Private helper methods

	private async executeRestore(
		dbId: Id<'db'>,
		operationId: string,
		options: CreateRestoreInput,
		adminId: string
	) {
		const startTime = Date.now();

		try {
			// Initialize progress tracking
			this.activeRestores.set(operationId, {
				operationId,
				status: 'pending',
				progressPercent: 0,
				currentStep: 'Initializing restore operation...',
				canRollback: true
			});

			// Update status to starting
			await this.updateRestoreStatus(dbId, 'pre_backup', null, {
				startedAt: new Date().toISOString()
			});

			// Step 1: Create pre-restore backup if requested
			let preRestoreBackupId = null;
			if (options.createPreBackup) {
				this.updateProgress(operationId, 5, 'Creating safety backup...');
				preRestoreBackupId = await this.createPreRestoreBackup(adminId);

				await db
					.update(restoreOperations)
					.set({
						preRestoreBackupId,
						preBackupCompletedAt: new Date().toISOString()
					})
					.where(eq(restoreOperations.id, dbId));
			}

			// Step 2: Prepare for restore
			this.updateProgress(operationId, 15, 'Preparing database for restore...');
			await this.updateRestoreStatus(dbId, 'restoring', null, {
				restoreStartedAt: new Date().toISOString()
			});

			// Step 3: Execute restore
			this.updateProgress(operationId, 20, 'Restoring database...');
			await this.executePgRestore(operationId, options);

			// Step 4: Verify restore
			this.updateProgress(operationId, 90, 'Verifying restore...');
			const verificationResult = await this.verifyRestore();

			// Step 5: Update completion
			const endTime = Date.now();
			const duration = Math.round((endTime - startTime) / 1000);

			await this.updateRestoreStatus(dbId, 'completed', null, {
				completedAt: new Date().toISOString(),
				duration,
				tablesRestored: verificationResult.tablesCount,
				rowsRestored: verificationResult.rowsCount
			});

			this.updateProgress(operationId, 100, 'Restore completed successfully');

			// Remove from active restores after delay
			setTimeout(() => {
				this.activeRestores.delete(operationId);
			}, 10000);
		} catch (error) {
			const endTime = Date.now();
			const duration = Math.round((endTime - startTime) / 1000);

			await this.updateRestoreStatus(dbId, 'failed', error.message, {
				duration
			});

			this.activeRestores.set(operationId, {
				operationId,
				status: 'failed',
				progressPercent: 0,
				currentStep: 'Restore failed',
				errorMessage: error.message,
				canRollback: true
			});

			throw error;
		}
	}

	private async createPreRestoreBackup(adminId: string): Promise<number> {
		const result = await backupService.createBackup(
			{
				displayName: `Pre-restore backup ${new Date().toISOString()}`,
				description: 'Automatic backup created before restore operation',
				backupType: 'full',
				backupFormat: 'custom',
				tags: ['pre-restore', 'automatic'],
				isProtected: true
			},
			adminId
		);

		return result.backupId;
	}

	private async executePgRestore(operationId: string, options: CreateRestoreInput): Promise<void> {
		// Get source backup
		const sourceBackup = await backupService.getBackup(options.sourceBackupId);

		return new Promise((resolve, reject) => {
			const args = this.buildPgRestoreArgs(options, sourceBackup);

			const pgRestore = spawn('pg_restore', args, {
				env: {
					...process.env,
					PGPASSWORD: this.extractPasswordFromUrl(process.env.DATABASE_URL!)
				}
			});

			pgRestore.on('error', (error) => {
				reject(new Error(`pg_restore process error: ${error.message}`));
			});

			pgRestore.on('close', (code) => {
				if (code === 0) {
					resolve();
				} else {
					reject(new Error(`pg_restore exited with code ${code}`));
				}
			});

			// Update progress periodically
			const progressInterval = setInterval(() => {
				const current = this.activeRestores.get(operationId);
				if (current && current.progressPercent < 85) {
					this.updateProgress(
						operationId,
						Math.min(85, current.progressPercent + 5),
						'Restoring data...'
					);
				}
			}, 3000);

			pgRestore.on('close', () => {
				clearInterval(progressInterval);
			});
		});
	}

	private buildPgRestoreArgs(options: CreateRestoreInput, sourceBackup: any): string[] {
		const args = [
			'--verbose',
			'--no-password',
			'--clean', // Clean (drop) database objects before recreating
			'--if-exists' // Use IF EXISTS when dropping objects
		];

		// Database connection
		const dbUrl = new URL(process.env.DATABASE_URL!);
		args.push(`--host=${dbUrl.hostname}`);
		args.push(`--port=${dbUrl.port || '5432'}`);
		args.push(`--username=${dbUrl.username}`);
		args.push(`--dbname=${dbUrl.pathname.slice(1)}`);

		// Restore type options
		if (options.restoreType === 'schema_only') {
			args.push('--schema-only');
		} else if (options.restoreType === 'data_only') {
			args.push('--data-only');
		}

		// Include/exclude options
		if (!options.includeIndexes) {
			args.push('--no-indexes');
		}

		if (!options.includeConstraints) {
			args.push('--no-constraints');
		}

		if (!options.includeTriggers) {
			args.push('--no-triggers');
		}

		// Tables
		if (options.includedTables.length > 0) {
			options.includedTables.forEach((table) => {
				args.push(`--table=${table}`);
			});
		}

		// Exclude tables (for selective restore)
		if (options.excludedTables.length > 0) {
			options.excludedTables.forEach((table) => {
				args.push(`--exclude-table=${table}`);
			});
		}

		// Source file
		args.push(sourceBackup.filePath);

		return args;
	}

	private async validateBackupFile(backup: any) {
		try {
			// Check file exists and size
			if (!existsSync(backup.filePath)) {
				throw new Error('Backup file not found');
			}

			const stats = statSync(backup.filePath);
			const currentSize = stats.size;

			// Check if file size matches recorded size (within 1% tolerance)
			if (backup.fileSize && Math.abs(currentSize - backup.fileSize) > backup.fileSize * 0.01) {
				throw new Error('Backup file size mismatch - file may be corrupted');
			}

			// Verify MD5 checksum if available
			if (backup.checksumMd5) {
				// Implementation would calculate and compare MD5
				// Skipped for now due to complexity
			}

			return {
				valid: true,
				fileSize: currentSize,
				lastModified: stats.mtime,
				warnings: []
			};
		} catch (error) {
			return {
				valid: false,
				error: error.message,
				warnings: ['Backup file validation failed']
			};
		}
	}

	private async generateImpactAssessment(backup: any) {
		// This would analyze the backup and current database to assess impact
		return {
			estimatedDowntime: '5-15 minutes',
			affectedTables: backup.includedTables || ['All tables'],
			dataLossRisk: backup.backupType === 'full' ? 'Low' : 'Medium',
			rollbackTime: '2-5 minutes',
			databaseSizeChange: 'Unknown',
			userImpact: 'High - Database will be temporarily unavailable'
		};
	}

	private generateRestoreWarnings(backup: any, impact: any): string[] {
		const warnings = [];

		if (backup.backupType !== 'full') {
			warnings.push('Partial backup - some data may not be restored');
		}

		const backupAge = Date.now() - new Date(backup.createdAt).getTime();
		const daysOld = Math.floor(backupAge / (1000 * 60 * 60 * 24));

		if (daysOld > 7) {
			warnings.push(`Backup is ${daysOld} days old - recent data will be lost`);
		}

		if (backup.fileSize > 1000 * 1024 * 1024) {
			// > 1GB
			warnings.push('Large backup file - restore may take significant time');
		}

		warnings.push('Database will be unavailable during restore');
		warnings.push('All current data will be replaced');

		return warnings;
	}

	private estimateRestoreDuration(backup: any): number {
		// Rough estimation based on backup size
		const sizeGB = (backup.fileSize || 0) / (1024 * 1024 * 1024);
		return Math.max(5, Math.round(sizeGB * 3)); // ~3 minutes per GB, minimum 5 minutes
	}

	private async checkRestorePermissions(adminId: string, restoreType: string) {
		// Get backup settings to check permissions
		const [settings] = await db.select().from(backupSettings).limit(1);

		// For now, require admin role for all restores
		// This would integrate with the user role system
		return true;
	}

	private async verifyRestore() {
		// Basic verification - check if database is accessible and has expected structure
		try {
			const result = await db.execute(`
				SELECT 
					COUNT(*) as table_count,
					(SELECT COUNT(*) FROM users) as user_count
				FROM information_schema.tables 
				WHERE table_schema = 'public'
			`);

			return {
				tablesCount: result.rows[0]?.table_count || 0,
				rowsCount: result.rows[0]?.user_count || 0,
				verified: true
			};
		} catch (error) {
			throw new Error(`Restore verification failed: ${error.message}`);
		}
	}

	private updateProgress(operationId: string, percent: number, step: string) {
		const progress = this.activeRestores.get(operationId);
		if (progress) {
			progress.progressPercent = percent;
			progress.currentStep = step;
			this.activeRestores.set(operationId, progress);
		}
	}

	private async updateRestoreStatus(
		id: Id<'id'>,
		status: 'pending' | 'pre_backup' | 'restoring' | 'completed' | 'failed' | 'cancelled',
		errorMessage?: string | null,
		additionalData?: Record<string, any>
	) {
		const updateData: any = { status };

		if (errorMessage !== undefined) {
			updateData.errorMessage = errorMessage;
		}

		if (additionalData) {
			Object.assign(updateData, additionalData);
		}

		await db.update(restoreOperations).set(updateData).where(eq(restoreOperations.id, id));
	}

	private extractPasswordFromUrl(url: string): string {
		try {
			const parsed = new URL(url);
			return parsed.password;
		} catch {
			return '';
		}
	}
}

// Export singleton instance
export const restoreService = new RestoreService();

/**
 * Backup Schedule Service
 *
 * Manages scheduled backup operations and cron-based automation
 */

import { db } from '@db';
import { backupSchedules, adminBackups } from '@schema';
import { eq, desc, and, lte } from 'drizzle-orm';
import { AdminError, AdminErrorCodes } from '../../admin.errors';
import { adminCacheService } from '../../shared';
import { backupService } from './backup.service';
import { z } from 'zod';
import { logger } from '../../../../core/logger';

// Validation schemas
export const createScheduleSchema = z.object({
	name: z.string().min(1).max(255),
	description: z.string().optional(),
	cronExpression: z
		.string()
		.min(1)
		.max(100)
		.regex(/^[\d\s\*\/\-\,]+$/, 'Invalid cron expression format'),
	timezone: z.string().default('UTC'),
	backupType: z.enum(['full', 'schema', 'selective']).default('full'),
	backupFormat: z.enum(['custom', 'plain', 'tar']).default('custom'),
	compressionType: z.enum(['gzip', 'none']).default('gzip'),
	includedTables: z.array(z.string()).default([]),
	includedSchemas: z.array(z.string()).default(['public']),
	excludedTables: z.array(z.string()).default([]),
	retentionDays: z.number().min(1).max(365).default(30),
	maxBackups: z.number().min(1).max(100).default(10),
	isActive: z.boolean().default(true),
	notifyOnSuccess: z.boolean().default(false),
	notifyOnFailure: z.boolean().default(true),
	notificationEmails: z.array(z.string().email()).default([])
});

export const updateScheduleSchema = createScheduleSchema.partial();

export const listSchedulesSchema = z.object({
	page: z.coerce.number().min(1).default(1),
	limit: z.coerce.number().min(1).max(100).default(20),
	isActive: z.boolean().optional(),
	backupType: z.enum(['full', 'schema', 'selective']).optional()
});

export type CreateScheduleInput = z.infer<typeof createScheduleSchema>;
export type UpdateScheduleInput = z.infer<typeof updateScheduleSchema>;
export type ListSchedulesInput = z.infer<typeof listSchedulesSchema>;

export class BackupScheduleService {
	private schedulerRunning = false;
	private schedulerInterval: NodeJS.Timeout | null = null;

	/**
	 * Create a new backup schedule
	 */
	async createSchedule(data: CreateScheduleInput, adminId: string) {
		try {
			const validatedData = createScheduleSchema.parse(data);

			// Validate cron expression
			const nextRun = this.calculateNextRun(validatedData.cronExpression, validatedData.timezone);

			// Create schedule record
			const [schedule] = await db
				.insert(backupSchedules)
				.values({
					...validatedData,
					nextRunAt: nextRun.toISOString(),
					createdBy: adminId,
					updatedBy: adminId
				})
				.returning();

			// Start scheduler if this is the first active schedule
			await this.ensureSchedulerRunning();

			// Invalidate cache
			await adminCacheService.invalidateEntity('backupSchedule');

			return {
				schedule,
				message: `Backup schedule "${validatedData.name}" created successfully`,
				nextRun: nextRun.toISOString()
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to create backup schedule', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Update an existing backup schedule
	 */
	async updateSchedule(id: Id<'id'>, data: UpdateScheduleInput, adminId: string) {
		try {
			const validatedData = updateScheduleSchema.parse(data);

			// Get existing schedule
			const [existingSchedule] = await db
				.select()
				.from(backupSchedules)
				.where(eq(backupSchedules.id, id));

			if (!existingSchedule) {
				throw new AdminError(`Backup schedule not found: ${id}`, 404, AdminErrorCodes.NOT_FOUND);
			}

			// Calculate new next run time if cron expression changed
			let nextRunAt = existingSchedule.nextRunAt;
			if (validatedData.cronExpression || validatedData.timezone) {
				const cronExpr = validatedData.cronExpression || existingSchedule.cronExpression;
				const timezone = validatedData.timezone || existingSchedule.timezone;
				nextRunAt = this.calculateNextRun(cronExpr, timezone).toISOString();
			}

			// Update schedule
			const [updatedSchedule] = await db
				.update(backupSchedules)
				.set({
					...validatedData,
					nextRunAt,
					updatedBy: adminId,
					updatedAt: new Date().toISOString()
				})
				.where(eq(backupSchedules.id, id))
				.returning();

			// Ensure scheduler is running if schedule is active
			if (updatedSchedule.isActive) {
				await this.ensureSchedulerRunning();
			}

			// Invalidate cache
			await adminCacheService.invalidateEntity('backupSchedule');

			return {
				schedule: updatedSchedule,
				message: `Backup schedule "${updatedSchedule.name}" updated successfully`
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to update backup schedule', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Delete a backup schedule
	 */
	async deleteSchedule(id: Id<'id'>, adminId: string) {
		try {
			const [schedule] = await db.select().from(backupSchedules).where(eq(backupSchedules.id, id));

			if (!schedule) {
				throw new AdminError(`Backup schedule not found: ${id}`, 404, AdminErrorCodes.NOT_FOUND);
			}

			// Delete the schedule
			await db.delete(backupSchedules).where(eq(backupSchedules.id, id));

			// Invalidate cache
			await adminCacheService.invalidateEntity('backupSchedule');

			return {
				success: true,
				message: `Backup schedule "${schedule.name}" deleted successfully`
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to delete backup schedule', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get list of backup schedules
	 */
	async getSchedules(filters: ListSchedulesInput) {
		try {
			const validatedFilters = listSchedulesSchema.parse(filters);

			let query = db.select().from(backupSchedules);
			const conditions = [];

			// Apply filters
			if (validatedFilters.isActive !== undefined) {
				conditions.push(eq(backupSchedules.isActive, validatedFilters.isActive));
			}

			if (validatedFilters.backupType) {
				conditions.push(eq(backupSchedules.backupType, validatedFilters.backupType));
			}

			// Apply conditions and pagination
			if (conditions.length > 0) {
				query = query.where(and(...conditions));
			}

			const schedules = await query
				.orderBy(desc(backupSchedules.createdAt))
				.limit(validatedFilters.limit)
				.offset((validatedFilters.page - 1) * validatedFilters.limit);

			// Enhance with additional information
			const enhancedSchedules = await Promise.all(
				schedules.map(async (schedule) => {
					// Get last backup info
					let lastBackup = null;
					if (schedule.lastBackupId) {
						try {
							lastBackup = await backupService.getBackup(schedule.lastBackupId);
						} catch (error) {
							// Backup may have been deleted
						}
					}

					return {
						...schedule,
						lastBackup,
						nextRunIn: this.getTimeUntilNextRun(schedule.nextRunAt),
						status: this.getScheduleStatus(schedule)
					};
				})
			);

			return {
				schedules: enhancedSchedules,
				pagination: {
					page: validatedFilters.page,
					limit: validatedFilters.limit,
					total: schedules.length, // Would need count query for accurate total
					totalPages: Math.ceil(schedules.length / validatedFilters.limit)
				}
			};
		} catch (error) {
			throw new AdminError('Failed to fetch backup schedules', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Get schedule details by ID
	 */
	async getSchedule(id: Id<'id'>) {
		try {
			const [schedule] = await db.select().from(backupSchedules).where(eq(backupSchedules.id, id));

			if (!schedule) {
				throw new AdminError(`Backup schedule not found: ${id}`, 404, AdminErrorCodes.NOT_FOUND);
			}

			// Get recent backups for this schedule
			const recentBackups = await db
				.select()
				.from(adminBackups)
				.where(
					and(
						eq(adminBackups.source, 'scheduled'),
						eq(adminBackups.metadata, JSON.stringify({ scheduleId: id }))
					)
				)
				.orderBy(desc(adminBackups.createdAt))
				.limit(10);

			// Get statistics
			const stats = this.calculateScheduleStats(recentBackups);

			return {
				...schedule,
				recentBackups,
				stats,
				nextRunIn: this.getTimeUntilNextRun(schedule.nextRunAt),
				status: this.getScheduleStatus(schedule)
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to fetch backup schedule', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Trigger a schedule to run immediately
	 */
	async triggerSchedule(id: Id<'id'>, adminId: string) {
		try {
			const schedule = await this.getSchedule(id);

			if (!schedule.isActive) {
				throw new AdminError(
					'Cannot trigger inactive schedule',
					400,
					AdminErrorCodes.INVALID_REQUEST
				);
			}

			// Execute the backup
			const result = await this.executeScheduledBackup(schedule, adminId);

			return {
				success: true,
				message: `Schedule "${schedule.name}" triggered successfully`,
				backupId: result.backupId
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			throw new AdminError('Failed to trigger backup schedule', 500, AdminErrorCodes.DB_ERROR, {
				originalError: error.message
			});
		}
	}

	/**
	 * Start the backup scheduler
	 */
	async startScheduler() {
		if (this.schedulerRunning) {
			return { message: 'Backup scheduler is already running' };
		}

		this.schedulerRunning = true;

		// Check for due schedules every minute
		this.schedulerInterval = setInterval(async () => {
			try {
				await this.processDueSchedules();
			} catch (error) {
				logger.error('Error processing backup schedules:', error);
			}
		}, 60 * 1000); // 1 minute

		logger.info('Backup scheduler started');
		return { message: 'Backup scheduler started successfully' };
	}

	/**
	 * Stop the backup scheduler
	 */
	stopScheduler() {
		if (this.schedulerInterval) {
			clearInterval(this.schedulerInterval);
			this.schedulerInterval = null;
		}

		this.schedulerRunning = false;
		logger.info('Backup scheduler stopped');
		return { message: 'Backup scheduler stopped' };
	}

	/**
	 * Get scheduler status
	 */
	getSchedulerStatus() {
		return {
			running: this.schedulerRunning,
			uptime: this.schedulerRunning ? process.uptime() : 0,
			nextCheck: this.schedulerRunning ? new Date(Date.now() + 60000).toISOString() : null
		};
	}

	// Private helper methods

	private async ensureSchedulerRunning() {
		if (!this.schedulerRunning) {
			await this.startScheduler();
		}
	}

	private async processDueSchedules() {
		const now = new Date().toISOString();

		// Get schedules that are due for execution
		const dueSchedules = await db
			.select()
			.from(backupSchedules)
			.where(and(eq(backupSchedules.isActive, true), lte(backupSchedules.nextRunAt, now)));

		for (const schedule of dueSchedules) {
			try {
				logger.info(`Executing scheduled backup: ${schedule.name}`);

				// Execute the backup
				const result = await this.executeScheduledBackup(schedule, schedule.createdBy);

				// Update schedule with next run time and last backup info
				const nextRun = this.calculateNextRun(schedule.cronExpression, schedule.timezone);

				await db
					.update(backupSchedules)
					.set({
						lastRunAt: new Date().toISOString(),
						nextRunAt: nextRun.toISOString(),
						lastBackupId: result.backupId,
						consecutiveFailures: 0,
						lastError: null
					})
					.where(eq(backupSchedules.id, schedule.id));

				logger.info(`Scheduled backup completed: ${schedule.name}, next run: ${nextRun}`);
			} catch (error) {
				logger.error(`Scheduled backup failed: ${schedule.name}`, error);

				// Update failure count and error
				await db
					.update(backupSchedules)
					.set({
						consecutiveFailures: schedule.consecutiveFailures + 1,
						lastError: error.message,
						lastRunAt: new Date().toISOString(),
						nextRunAt: this.calculateNextRun(
							schedule.cronExpression,
							schedule.timezone
						).toISOString()
					})
					.where(eq(backupSchedules.id, schedule.id));

				// Send notification if configured
				if (schedule.notifyOnFailure) {
					// Implementation would send email/webhook notification
					logger.info(`Backup failure notification for schedule: ${schedule.name}`);
				}
			}
		}
	}

	private async executeScheduledBackup(schedule: any, adminId: string) {
		// Create backup using the backup service
		const result = await backupService.createBackup(
			{
				displayName: `${schedule.name} - ${new Date().toISOString()}`,
				description: `Scheduled backup from: ${schedule.name}`,
				backupType: schedule.backupType,
				backupFormat: schedule.backupFormat,
				compressionType: schedule.compressionType,
				includedTables: schedule.includedTables,
				includedSchemas: schedule.includedSchemas,
				excludedTables: schedule.excludedTables,
				tags: ['scheduled', `schedule-${schedule.id}`],
				isProtected: false
			},
			adminId
		);

		// Clean up old backups according to retention policy
		await this.cleanupOldBackups(schedule);

		return result;
	}

	private async cleanupOldBackups(schedule: any) {
		try {
			// Get all backups for this schedule
			const scheduleBackups = await db
				.select()
				.from(adminBackups)
				.where(
					and(
						eq(adminBackups.source, 'scheduled'),
						eq(adminBackups.metadata, JSON.stringify({ scheduleId: schedule.id }))
					)
				)
				.orderBy(desc(adminBackups.createdAt));

			// Delete backups exceeding max count
			if (scheduleBackups.length > schedule.maxBackups) {
				const toDelete = scheduleBackups.slice(schedule.maxBackups);
				for (const backup of toDelete) {
					await backupService.deleteBackup(backup.id, schedule.createdBy);
				}
			}

			// Delete backups older than retention period
			const cutoffDate = new Date();
			cutoffDate.setDate(cutoffDate.getDate() - schedule.retentionDays);

			const expiredBackups = scheduleBackups.filter(
				(backup) => new Date(backup.createdAt) < cutoffDate && !backup.isProtected
			);

			for (const backup of expiredBackups) {
				await backupService.deleteBackup(backup.id, schedule.createdBy);
			}
		} catch (error) {
			logger.warn(`Failed to cleanup old backups for schedule ${schedule.name}:`, error);
		}
	}

	private calculateNextRun(cronExpression: string, timezone: string = 'UTC'): Date {
		// Simple cron calculation - in production, use a proper cron library like 'node-cron'
		// For now, just add appropriate intervals based on common patterns

		const now = new Date();
		const parts = cronExpression.split(' ');

		// Handle common patterns
		if (cronExpression === '0 2 * * *') {
			// Daily at 2 AM
			const next = new Date(now);
			next.setHours(2, 0, 0, 0);
			if (next <= now) {
				next.setDate(next.getDate() + 1);
			}
			return next;
		}

		if (cronExpression === '0 2 * * 0') {
			// Weekly on Sunday at 2 AM
			const next = new Date(now);
			next.setHours(2, 0, 0, 0);
			const daysUntilSunday = (7 - next.getDay()) % 7 || 7;
			next.setDate(next.getDate() + daysUntilSunday);
			return next;
		}

		if (cronExpression === '0 2 1 * *') {
			// Monthly on 1st at 2 AM
			const next = new Date(now);
			next.setDate(1);
			next.setHours(2, 0, 0, 0);
			if (next <= now) {
				next.setMonth(next.getMonth() + 1);
			}
			return next;
		}

		// Default: add 1 hour
		return new Date(now.getTime() + 60 * 60 * 1000);
	}

	private getTimeUntilNextRun(nextRunAt: string | null): string | null {
		if (!nextRunAt) return null;

		const now = new Date();
		const nextRun = new Date(nextRunAt);
		const diff = nextRun.getTime() - now.getTime();

		if (diff <= 0) return 'Overdue';

		const hours = Math.floor(diff / (1000 * 60 * 60));
		const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

		if (hours > 24) {
			const days = Math.floor(hours / 24);
			return `${days}d ${hours % 24}h`;
		}

		return `${hours}h ${minutes}m`;
	}

	private getScheduleStatus(schedule: any): 'healthy' | 'warning' | 'error' {
		if (!schedule.isActive) return 'warning';
		if (schedule.consecutiveFailures >= 3) return 'error';
		if (schedule.consecutiveFailures > 0) return 'warning';
		return 'healthy';
	}

	private calculateScheduleStats(backups: any[]) {
		const total = backups.length;
		const successful = backups.filter((b) => b.status === 'completed').length;
		const failed = backups.filter((b) => b.status === 'failed').length;
		const avgSize =
			backups.length > 0
				? backups.reduce((sum, b) => sum + (b.fileSize || 0), 0) / backups.length
				: 0;

		return {
			totalBackups: total,
			successfulBackups: successful,
			failedBackups: failed,
			successRate: total > 0 ? Math.round((successful / total) * 100) : 0,
			averageSizeMB: Math.round((avgSize / (1024 * 1024)) * 100) / 100
		};
	}
}

