		});
	}
}

/**
 * Export query results as CSV
 */
export async function exportQueryResults(req: Request, res: Response) {
	try {
		const userId = userService.getUserFromRequest(req);
		const { query } = z.object({ query: z.string().min(1) }).parse(req.body);

		// Execute query
		const result = await queryService.executeQuery(query, userId);

		if (!result.success) {
			return res.status(400).json({
				success: false,
				error: result.error
			});
		}

		// Convert to CSV
		const csvData = queryService.formatResultAsCSV(result);

		// Log export
		await adminCreateAuditLogEntry({
			adminUserId: userId,
			action: 'database_query_exported',
			details: `Exported query results as CSV (${result.rowCount} rows)`,
			metadata: {
				queryLength: query.length,
				rowCount: result.rowCount,
				exportSize: csvData.length
			}
		});

		res.setHeader('Content-Type', 'text/csv');
		res.setHeader('Content-Disposition', `attachment; filename="query_results_${Date.now()}.csv"`);
		res.send(csvData);
	} catch (error: any) {
		logger.error('QueryController', 'Error exporting query results', { error: error.message });
		res.status(500).json({
			success: false,
			error: 'Failed to export query results'
		});
	}
}

/**
 * Get query performance metrics
 */
export async function getQueryMetrics(req: Request, res: Response) {
	try {
		const userId = userService.getUserFromRequest(req);

		const history = queryService.getQueryHistory();

		// Calculate metrics
		const totalQueries = history.length;
		const successfulQueries = history.filter((h) => h.success).length;
		const failedQueries = totalQueries - successfulQueries;
		const avgExecutionTime =
			history.length > 0
				? history.reduce((sum, h) => sum + h.executionTime, 0) / history.length
				: 0;
		const totalRowsReturned = history.reduce((sum, h) => sum + h.rowCount, 0);

		const metrics = {
			totalQueries,
			successfulQueries,
			failedQueries,
			successRate: totalQueries > 0 ? ((successfulQueries / totalQueries) * 100).toFixed(2) : 0,
			avgExecutionTime: Math.round(avgExecutionTime),
			totalRowsReturned,
			recentActivity: history.slice(0, 10)
		};

		// Log access
		await adminCreateAuditLogEntry({
			adminUserId: userId,
			action: 'database_query_metrics_viewed',
			details: 'Viewed query performance metrics'
		});

		res.json({
			success: true,
			data: metrics
		});
	} catch (error: any) {
		logger.error('QueryController', 'Error getting query metrics', { error: error.message });
		res.status(500).json({
			success: false,
			error: 'Failed to retrieve query metrics'
		});
	}
}

import type { Request, Response } from 'express';
import { userService } from '@server/src/core/services/user.service';
import { z } from 'zod';
import { emailTemplateService, emailTemplateSchema } from './email-templates.service';
import { formatAdminResponse, AdminOperationBoundary } from '../../shared';
import { AdminError, AdminErrorCodes } from '../../admin.errors';

// Query parameters schema for filtering
const filterTemplatesSchema = z.object({
	category: z.enum(['auth', 'notification', 'marketing', 'transactional', 'general']).optional(),
	isActive: z.coerce.boolean().optional(),
	search: z.string().optional(),
	page: z.coerce.number().min(1).default(1),
	limit: z.coerce.number().min(1).max(100).default(20)
});

const previewTemplateSchema = z
	.object({
		variables: z.record(z.any()).default({}),
		templateId: z.coerce.number().optional(),
		templateKey: z.string().optional()
	})
	.refine((data) => data.templateId || data.templateKey, {
		message: 'Either templateId or templateKey must be provided'
	});

const sendEmailSchema = z.object({
	templateKey: z.string().min(1),
	recipientEmail: z.string().email(),
	variables: z.record(z.any()).default({}),
	recipientUserId: z.string().uuid().optional()
});

export class EmailTemplateController {
	/**
	 * GET /api/admin/email-templates
	 * Get all email templates with filtering
	 */
	async getAllTemplates(req: Request, res: Response) {
		const boundary = new AdminOperationBoundary({
			operation: 'FETCH_EMAIL_TEMPLATES',
			entityType: 'emailTemplate'
		});

		return boundary.execute(async () => {
			const filters = filterTemplatesSchema.parse(req.query);

			const templates = await emailTemplateService.getAllTemplates({
				category: filters.category,
				isActive: filters.isActive,
				search: filters.search
			});

			// Pagination
			const startIndex = (filters.page - 1) * filters.limit;
			const endIndex = startIndex + filters.limit;
			const paginatedTemplates = templates.slice(startIndex, endIndex);

			return formatAdminResponse(
				{
					templates: paginatedTemplates,
					pagination: {
						page: filters.page,
						limit: filters.limit,
						total: templates.length,
						totalPages: Math.ceil(templates.length / filters.limit)
					}
				},
				'FETCH_EMAIL_TEMPLATES',
				'emailTemplate'
			);
		});
	}

	/**
	 * GET /api/admin/email-templates/:id
	 * Get a single email template by ID or key
	 */
	async getTemplate(req: Request, res: Response) {
		const boundary = new AdminOperationBoundary({
			operation: 'FETCH_EMAIL_TEMPLATE',
			entityType: 'emailTemplate'
		});

		return boundary.execute(async () => {
			const { id } = req.params;
			const templateId = isNaN(id) ? id : id;

			const template = await emailTemplateService.getTemplate(templateId);

			return formatAdminResponse({ template }, 'FETCH_EMAIL_TEMPLATE', 'emailTemplate');
		});
	}

	/**
	 * POST /api/admin/email-templates
	 * Create a new email template
	 */
	async createTemplate(req: Request, res: Response) {
		const boundary = new AdminOperationBoundary({
			operation: 'CREATE_EMAIL_TEMPLATE',
			entityType: 'emailTemplate'
		});

		return boundary.execute(async () => {
			const templateData = emailTemplateSchema.parse(req.body);
			const adminId = userService.getUserFromRequest(req)?.id;

			if (!adminId) {
				throw new AdminError('Admin ID required', 401, AdminErrorCodes.UNAUTHORIZED);
			}

			const template = await emailTemplateService.createTemplate(templateData, adminId);

			return formatAdminResponse({ template }, 'CREATE_EMAIL_TEMPLATE', 'emailTemplate');
		});
	}

	/**
	 * PUT /api/admin/email-templates/:id
	 * Update an email template
	 */
	async updateTemplate(req: Request, res: Response) {
		const boundary = new AdminOperationBoundary({
			operation: 'EMAIL_TEMPLATE_OPERATION',
			entityType: 'emailTemplate'
		});
		return boundary.execute(async () => {
			const { id } = req.params;
			const templateId = id;
			const updateData = emailTemplateSchema.partial().parse(req.body);
			const { changeDescription } = req.body;
			const adminId = userService.getUserFromRequest(req)?.id;

			if (!adminId) {
				throw new AdminError('Admin ID required', 401, AdminErrorCodes.UNAUTHORIZED);
			}

			if (isNaN(templateId)) {
				throw new AdminError('Invalid template ID', 400, AdminErrorCodes.INVALID_REQUEST);
			}

			const template = await emailTemplateService.updateTemplate(
				templateId,
				updateData,
				adminId,
				changeDescription
			);

			return formatAdminResponse({ template }, 'UPDATE_EMAIL_TEMPLATE', 'emailTemplate');
		});
	}

	/**
	 * DELETE /api/admin/email-templates/:id
	 * Delete (deactivate) an email template
	 */
	async deleteTemplate(req: Request, res: Response) {
		const boundary = new AdminOperationBoundary({
			operation: 'EMAIL_TEMPLATE_OPERATION',
			entityType: 'emailTemplate'
		});
		return boundary.execute(async () => {
			const { id } = req.params;
			const templateId = id;
			const adminId = userService.getUserFromRequest(req)?.id;

			if (!adminId) {
				throw new AdminError('Admin ID required', 401, AdminErrorCodes.UNAUTHORIZED);
			}

			if (isNaN(templateId)) {
				throw new AdminError('Invalid template ID', 400, AdminErrorCodes.INVALID_REQUEST);
			}

			const result = await emailTemplateService.deleteTemplate(templateId, adminId);

			return formatAdminResponse(result, 'DELETE_EMAIL_TEMPLATE', 'emailTemplate');
		});
	}

	/**
	 * POST /api/admin/email-templates/preview
	 * Preview template with sample data
	 */
	async previewTemplate(req: Request, res: Response) {
		const boundary = new AdminOperationBoundary({
			operation: 'EMAIL_TEMPLATE_OPERATION',
			entityType: 'emailTemplate'
		});
		return boundary.execute(async () => {
			const { variables, templateId, templateKey } = previewTemplateSchema.parse(req.body);

			const identifier = templateId || templateKey;
