import { Router } from 'express';
import { userService } from '@server/src/core/services/user.service';
import { z } from 'zod';
import { db } from '@db';
import { moderatorNotes, moderatorNoteTypeEnum } from '@schema';
import { eq, and, desc } from 'drizzle-orm';
import { isAdminOrModerator } from '../../../auth/middleware/auth.middleware';
import { getUserIdFromRequest } from '@server/src/utils/auth';
import { logger } from '@server/src/core/logger';
import type { EntityId } from '@shared/types/ids';

const router = Router();

// Validation schemas
const createModeratorNoteSchema = z.object({
	type: z.enum(['thread', 'post', 'user']),
	itemId: z.union([z.string(), z.number()]).transform(String),
	note: z.string().min(1).max(1000)
});

const getModeratorNotesSchema = z.object({
	type: z.enum(['thread', 'post', 'user']),
	itemId: z.union([z.string(), z.number()]).transform(String)
});

// Create a new moderator note
router.post('/', isAdminOrModerator, async (req, res) => {
	try {
		const validatedData = createModeratorNoteSchema.parse(req.body);
		const userId = getUserIdFromRequest(req);

		if (!userId) {
			return res.status(401).json({ error: 'Unauthorized' });
		}

		const [note] = await db
			.insert(moderatorNotes)
			.values({
				...validatedData,
				createdBy: userId
			})
			.returning();

		logger.info('ModeratorNotes', 'Created moderator note', {
			noteId: note.id,
			type: note.type,
			itemId: note.itemId,
			createdBy: userId
		});

		res.status(201).json(note);
	} catch (error) {
		if (error instanceof z.ZodError) {
			return res.status(400).json({
				error: 'Invalid request data',
				details: error.flatten()
			});
		}

		logger.error('ModeratorNotes', 'Error creating moderator note', {
			error,
			body: req.body
		});

		res.status(500).json({ error: 'Internal server error' });
	}
});

// Get moderator notes for a specific item
router.get('/', isAdminOrModerator, async (req, res) => {
	try {
		const validatedQuery = getModeratorNotesSchema.parse(req.query);

		const notes = await db
			.select()
			.from(moderatorNotes)
			.where(
				and(
					eq(moderatorNotes.type, validatedQuery.type as any),
					eq(moderatorNotes.itemId, validatedQuery.itemId)
				)
			)
			.orderBy(desc(moderatorNotes.createdAt));

		res.json(notes);
	} catch (error) {
		if (error instanceof z.ZodError) {
			return res.status(400).json({
				error: 'Invalid query parameters',
				details: error.flatten()
			});
		}

		logger.error('ModeratorNotes', 'Error fetching moderator notes', {
			error,
			query: req.query
		});

		res.status(500).json({ error: 'Internal server error' });
	}
});

// Delete a moderator note (admin only)
router.delete('/:id', isAdminOrModerator, async (req, res) => {
	try {
		const noteId = req.params.id as EntityId;
		const userId = getUserIdFromRequest(req);

		// Check if note exists and user can delete it
		const [existingNote] = await db
			.select()
			.from(moderatorNotes)
			.where(eq(moderatorNotes.id, noteId))
			.limit(1);

		if (!existingNote) {
			return res.status(404).json({ error: 'Note not found' });
		}

		// Only allow deletion by the creator or admins
		const isAdmin = userService.getUserFromRequest(req)?.role === 'admin';
		if (existingNote.createdBy !== userId && !isAdmin) {
			return res.status(403).json({ error: 'You can only delete your own notes' });
		}

		await db.delete(moderatorNotes).where(eq(moderatorNotes.id, noteId));

		logger.info('ModeratorNotes', 'Deleted moderator note', {
			noteId,
			deletedBy: userId
		});

		res.status(204).send();
	} catch (error) {
		logger.error('ModeratorNotes', 'Error deleting moderator note', {
			error,
			noteId: req.params.id
		});

		res.status(500).json({ error: 'Internal server error' });
	}
});

export default router;

import { eq, sql } from 'drizzle-orm';
import type { UserId } from '@shared/types/ids';
import { db } from '../../../../core/db';
import { referralSources } from '@schema/system/referralSources';
import { userReferrals } from '@schema/system/userReferrals';
import { users } from '@schema/user/users';
import { EntityId } from "@shared/types";
import { logger } from '../../../../core/logger';

/**
 * Service for managing referral sources and user referrals
 */
export class ReferralsService {
	/**
	 * Create a new referral source
	 *
	 * @param name Display name of the referral source
	 * @param slug Unique slug for the referral source
	 * @param metadata Optional metadata for the referral source
	 * @param createdBy User ID of the admin who created the source
	 * @returns The created referral source
	 */
	async createReferralSource(
		name: string,
		slug: string,
		metadata: Record<string, any> = {},
		createdBy?: number
	) {
		try {
			const [source] = await db
				.insert(referralSources)
				.values({
					name,
					slug,
					metadata,
					createdBy
				})
				.returning();

			return source;
		} catch (error) {
			if (error.message?.includes('unique constraint')) {
				if (error.message.includes('referral_sources_slug_unique')) {
					throw new Error(`Referral source with slug "${slug}" already exists`);
				} else if (error.message.includes('referral_sources_name_unique')) {
					throw new Error(`Referral source with name "${name}" already exists`);
				}
			}
			throw error;
		}
	}

	/**
	 * Record a user referral
	 *
	 * @param params Parameters for recording a user referral
	 * @returns The created user referral or null if already exists
	 */
	async recordUserReferral(params: {
		userId: UserId;
		referredByUserId?: EntityId;
		sourceSlug: string;
	}) {
		const { userId, referredByUserId, sourceSlug } = params;

		// First, find the referral source by slug
		const source = await db.query.referralSources.findFirst({
			where: eq(referralSources.slug, sourceSlug)
		});

		if (!source) {
			throw new Error(`Referral source with slug "${sourceSlug}" not found`);
		}

		try {
			// Insert with ON CONFLICT DO NOTHING
			const result = await db
				.insert(userReferrals)
				.values({
					userId,
					referredByUserId,
					referralSourceId: source.id
				})
				.onConflictDoNothing({ target: [userReferrals.userId] })
				.returning();

			return result[0] || null;
		} catch (error) {
			// Log error but don't throw, as we want to silently handle duplicates
			logger.error('Error recording user referral:', error);
			return null;
		}
	}

	/**
	 * Get all referral sources
	 */
	async getAllReferralSources() {
		return db.query.referralSources.findMany({
			orderBy: (sources) => [sources.name]
		});
	}

	/**
	 * Get referral source statistics
	 */
	async getReferralSourceStats() {
		const stats = await db.execute(sql`
      SELECT 
        rs.name AS source_name, 
        rs.slug AS source_slug,
        COUNT(ur.id) AS referral_count
      FROM 
        referral_sources rs
      LEFT JOIN 
        user_referrals ur ON rs.id = ur.referral_source_id
      GROUP BY 
        rs.id, rs.name, rs.slug
      ORDER BY 
        referral_count DESC
    `);

		return stats.rows;
	}

	/**
	 * Get user-to-user referral statistics
	 */
	async getUserReferralStats() {
		const stats = await db.execute(sql`
      SELECT 
        u.username AS user_username,
        u.id AS user_id,
        ref_user.username AS referred_by_username,
        ref_user.id AS referred_by_id,
        rs.name AS source_name,
        rs.slug AS source_slug,
        ur.created_at AS referral_date
      FROM 
        user_referrals ur
      JOIN 
        users u ON ur.user_id = u.id
      LEFT JOIN 
        users ref_user ON ur.referred_by_user_id = ref_user.id
      LEFT JOIN 
        referral_sources rs ON ur.referral_source_id = rs.id
      ORDER BY 
        ur.created_at DESC
    `);

		return stats.rows;
	}

	/**
	 * Get referral count by user
	 */
	async getReferralCountByUser() {
		const stats = await db.execute(sql`
      SELECT 
        u.username AS referrer_username,
        u.id AS referrer_id,
        COUNT(ur.id) AS referral_count
      FROM 
        users u
      JOIN 
        user_referrals ur ON u.id = ur.referred_by_user_id
      GROUP BY 
        u.id, u.username
      ORDER BY 
        referral_count DESC
      LIMIT 50
    `);

		return stats.rows;
	}
}

export const referralsService = new ReferralsService();

import type { Request, Response } from 'express';
import { userService } from '@server/src/core/services/user.service';
import type { ReportId, MessageId } from '@shared/types/ids';
import { adminReportsService } from './reports.service';
import { AdminError, AdminErrorCodes } from '../../admin.errors';
import { getUserId } from '../../admin.middleware';
import { adminController } from '../../admin.controller';
import {
	GetReportsQuerySchema,
	ReportActionSchema,
	BanUserSchema,
	DeleteContentSchema
} from './reports.validators';
import { validateRequestBody, validateQueryParams } from '../../admin.validation';

export class AdminReportsController {
	async getReports(req: Request, res: Response) {
		try {
			const query = validateQueryParams(req, res, GetReportsQuerySchema);
			if (!query) return;
			const result = await adminReportsService.getReports(query);
			res.json(result);
		} catch (error) {
			if (error instanceof AdminError)
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			res.status(500).json({ error: 'Failed to fetch reports' });
		}
	}

	async getReportById(req: Request, res: Response) {
		try {
			const reportId = req.params.id as ReportId;

			const report = await adminReportsService.getReportById(reportId);
			res.json(report);
		} catch (error) {
			if (error instanceof AdminError)
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			res.status(500).json({ error: 'Failed to fetch report details' });
		}
	}

	async resolveReport(req: Request, res: Response) {
		try {
			const reportId = req.params.id as ReportId;

			const body = validateRequestBody(req, res, ReportActionSchema);
			if (!body) return;
			const adminId = userService.getUserFromRequest(req);
			const updatedReport = await adminReportsService.updateReportStatus(
				reportId,
				'resolved',
				adminId,
				body.notes
			);

			await adminController.logAction(req, 'RESOLVE_REPORT', 'report', reportId.toString(), {
				notes: body.notes,
				finalStatus: 'resolved'
			});
			res.json({ message: 'Report resolved successfully', data: updatedReport });
		} catch (error) {
			if (error instanceof AdminError)
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			res.status(500).json({ error: 'Failed to resolve report' });
		}
	}

	async dismissReport(req: Request, res: Response) {
		try {
			const reportId = req.params.id as ReportId;

			const body = validateRequestBody(req, res, ReportActionSchema);
			if (!body) return;
			const adminId = userService.getUserFromRequest(req);
			const updatedReport = await adminReportsService.updateReportStatus(
				reportId,
				'dismissed',
				adminId,
				body.notes
			);

			await adminController.logAction(req, 'DISMISS_REPORT', 'report', reportId.toString(), {
				notes: body.notes,
				finalStatus: 'dismissed'
			});
			res.json({ message: 'Report dismissed successfully', data: updatedReport });
		} catch (error) {
			if (error instanceof AdminError)
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			res.status(500).json({ error: 'Failed to dismiss report' });
		}
	}

	async banUser(req: Request, res: Response) {
		try {
			const userIdToBan = req.params.userId as string; // User ID as string

			const body = validateRequestBody(req, res, BanUserSchema);
			if (!body) return;
			const adminId = userService.getUserFromRequest(req);
			const banResult = await adminReportsService.banUser(userIdToBan, body, adminId);

			await adminController.logAction(req, 'BAN_USER', 'user', userIdToBan.toString(), {
				reason: body.reason,
				duration: body.duration
			});
			res.json({ message: 'User banned successfully', data: banResult });
		} catch (error) {
			if (error instanceof AdminError)
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			res.status(500).json({ error: 'Failed to ban user' });
		}
	}

	async deleteContent(req: Request, res: Response) {
		try {
			const contentType = req.params.contentType as 'post' | 'thread' | 'message';
			const contentId = req.params.contentId as MessageId;

			if (!['post', 'thread', 'message'].includes(contentType)) {
				throw new AdminError(
					'Invalid content type for deletion',
					400,
					AdminErrorCodes.INVALID_REQUEST
				);
			}

			const body = validateRequestBody(req, res, DeleteContentSchema);
			if (!body) return;
			const adminId = userService.getUserFromRequest(req);
			const deleteResult = await adminReportsService.deleteContent(
				contentType,
				contentId,
				body,
				adminId
			);

			await adminController.logAction(
				req,
				`DELETE_${contentType.toUpperCase()}`,
				contentType,
				contentId.toString(),
				{ reason: body.reason }
			);
			res.json(deleteResult);
		} catch (error) {
			if (error instanceof AdminError)
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			res.status(500).json({ error: 'Failed to delete content' });
		}
	}
}

