					.select({ content: posts.content })
					.from(posts)
					.where(eq(posts.id, contentId));
				return post?.content || 'Content not found';
			}
			case 'thread': {
				const [thread] = await db
					.select({ title: threads.title })
					.from(threads)
					.where(eq(threads.id, contentId));
				return thread?.title || 'Content not found';
			}
			case 'message': {
				const [message] = await db
					.select({ content: shoutboxMessages.content })
					.from(shoutboxMessages)
					.where(eq(shoutboxMessages.id, contentId));
				return message?.content || 'Content not found';
			}
			case 'user':
				return 'User profile reported'; // Or fetch user profile snippet
			default:
				return 'Unknown content type';
		}
	} catch (error) {
		logger.error(`Error getting content preview for ${type} ${contentId}:`, error);
		return 'Error retrieving content preview';
	}
}

export class AdminReportsService {
	async getReports(params: GetReportsQueryInput) {
		const { page, limit, status, type, search, sortBy, sortOrder } = params;
		const offset = (page - 1) * limit;

		let query = db
			.select({
				id: reportedContent.id,
				contentType: reportedContent.contentType,
				contentId: reportedContent.contentId,
				reason: reportedContent.reason,
				status: reportedContent.status,
				createdAt: reportedContent.createdAt,
				resolvedAt: reportedContent.resolvedAt,
				reporterId: reportedContent.reporterId,
				reportedUserId: reportedContent.reportedUserId, // User who authored the content
				resolvedById: reportedContent.resolvedById,
				notes: reportedContent.notes
			})
			.from(reportedContent)
			.leftJoin(users, eq(reportedContent.reportedUserId, users.id)) // Join with users who created the content
			.$dynamic(); // Required for conditional where clauses with Drizzle

		const conditions = [];
		if (status && status !== 'all') {
			conditions.push(eq(reportedContent.status, status));
		}
		if (type && type !== 'all') {
			conditions.push(eq(reportedContent.contentType, type));
		}
		if (search) {
			const searchTerm = `%${search}%`;
			conditions.push(
				or(
					ilike(reportedContent.reason, searchTerm),
					ilike(reportedContent.notes, searchTerm),
					ilike(users.username, searchTerm) // Search by reported user's username
				)
			);
		}
		if (conditions.length > 0) {
			query = query.where(and(...conditions));
		}

		const countQuery = db
			.select({ count: sql<number>`count(*)` })
			.from(reportedContent)
			.leftJoin(users, eq(reportedContent.reportedUserId, users.id))
			.$dynamic();
		if (conditions.length > 0) {
			countQuery.where(and(...conditions));
		}
		const [totalResult] = await countQuery;
		const totalReports = Number(totalResult?.count) || 0;

		const sortColumn = sortBy === 'reporter' ? users.username : reportedContent.createdAt; // Example, adapt as needed
		const sortDirection = sortOrder === 'asc' ? asc : desc;

		const reportsData = await query.orderBy(sortDirection(sortColumn)).limit(limit).offset(offset);

		// Enhance reports with usernames and previews
		const enhancedReports = await Promise.all(
			reportsData.map(async (report) => {
				let reporterUsername: string | null = null;
				let reportedUsername: string | null = null;
				let resolvedByUsername: string | null = null;

				if (report.reporterId) {
					const [rUser] = await db
						.select({ username: users.username })
						.from(users)
						.where(eq(users.id, report.reporterId));
					reporterUsername = rUser?.username || 'Unknown';
				}
				if (report.reportedUserId) {
					const [ruUser] = await db
						.select({ username: users.username })
						.from(users)
						.where(eq(users.id, report.reportedUserId));
					reportedUsername = ruUser?.username || 'System/Unknown';
				}
				if (report.resolvedById) {
					const [resUser] = await db
						.select({ username: users.username })
						.from(users)
						.where(eq(users.id, report.resolvedById));
					resolvedByUsername = resUser?.username || 'Unknown';
				}

				const preview = await getContentPreview(report.contentType, report.contentId);
				return {
					...report,
					reporterUsername,
					reportedUsername,
					resolvedByUsername,
					contentPreview: preview
				};
			})
		);

		return {
			data: enhancedReports,
			pagination: {
				total: totalReports,
				page,
				limit,
				totalPages: Math.ceil(totalReports / limit)
			}
		};
	}

	async getReportById(reportId: ReportId) {
		const [report] = await db
			.select()
			.from(reportedContent)
			.where(eq(reportedContent.id, reportId));
		if (!report) {
			throw new AdminError('Report not found', 404, AdminErrorCodes.NOT_FOUND);
		}

		let reporterUsername: string | null = null;
		let reportedUsername: string | null = null;
		let resolvedByUsername: string | null = null;

		if (report.reporterId) {
			const [rUser] = await db
				.select({ username: users.username })
				.from(users)
				.where(eq(users.id, report.reporterId));
			reporterUsername = rUser?.username || 'Unknown';
		}
		// reportedUserId might be null if the content itself represents a user report
		if (report.reportedUserId) {
			const [ruUser] = await db
				.select({ username: users.username })
				.from(users)
				.where(eq(users.id, report.reportedUserId));
			reportedUsername = ruUser?.username || 'System/Unknown';
		}
		if (report.resolvedById) {
			const [resUser] = await db
				.select({ username: users.username })
				.from(users)
				.where(eq(users.id, report.resolvedById));
			resolvedByUsername = resUser?.username || 'Unknown';
		}

		const contentPreview = await getContentPreview(report.contentType, report.contentId);
		return { ...report, reporterUsername, reportedUsername, resolvedByUsername, contentPreview };
	}

	async updateReportStatus(
		reportId: ReportId,
		newStatus: 'resolved' | 'dismissed',
		adminUserId: AdminUserId,
		notes?: string
	) {
		const [existingReport] = await db
			.select()
			.from(reportedContent)
			.where(eq(reportedContent.id, reportId));
		if (!existingReport) {
			throw new AdminError('Report not found', 404, AdminErrorCodes.NOT_FOUND);
		}
		if (existingReport.status !== 'pending') {
			throw new AdminError(
				`Report is already ${existingReport.status}`,
				400,
				AdminErrorCodes.OPERATION_FAILED
			);
		}

		const [updatedReport] = await db
			.update(reportedContent)
			.set({
				status: newStatus,
				resolvedAt: new Date(),
				resolvedById: adminUserId,
				notes: notes || null
			})
			.where(eq(reportedContent.id, reportId))
			.returning();

		// Log moderation action
		await db.insert(contentModerationActions).values({
			moderatorId: adminUserId, // Changed from userId to moderatorId
			action: newStatus === 'resolved' ? 'resolve_report' : 'dismiss_report',
			contentType: existingReport.contentType,
			contentId: existingReport.contentId,
			targetUserId: existingReport.reportedUserId, // Assuming reportedUserId is the author of content
			reason: notes || 'No reason provided'
			// reportId: reportId // contentModerationActions might not have reportId, check schema
		});

		return updatedReport;
	}

	async banUser(userIdToBan: number, input: BanUserInput, adminUserId: AdminUserId) {
		const { reason, duration } = input;
		const [user] = await db.select().from(users).where(eq(users.id, userIdToBan));
		if (!user) {
			throw new AdminError('User to ban not found', 404, AdminErrorCodes.USER_NOT_FOUND);
		}

		const [existingBan] = await db
			.select()
			.from(userBans)
			.where(and(eq(userBans.userId, userIdToBan), eq(userBans.isActive, true)));
		if (existingBan) {
			throw new AdminError('User is already actively banned', 400, AdminErrorCodes.DUPLICATE_ENTRY);
		}

		let expiresAt: Date | null = null;
		let isPermanent = false;
		if (!duration || duration.toLowerCase() === 'permanent') {
			isPermanent = true;
		} else {
			const match = duration.match(/^(\d+)([dhm])$/i);
			if (!match)
				throw new AdminError(
					'Invalid ban duration format. Use N[d|h|m] or permanent.',
					400,
					AdminErrorCodes.VALIDATION_ERROR
				);
			const value = parseInt(match[1]);
			const unit = match[2].toLowerCase();
			expiresAt = new Date();
			if (unit === 'd') expiresAt.setDate(expiresAt.getDate() + value);
			else if (unit === 'h') expiresAt.setHours(expiresAt.getHours() + value);
			else if (unit === 'm') expiresAt.setMinutes(expiresAt.getMinutes() + value);
		}

		const [ban] = await db
			.insert(userBans)
			.values({
				userId: userIdToBan,
				reason,
				bannedBy: adminUserId,
				isActive: true,
				isPermanent,
				expiresAt
			})
			.returning();

		await db.update(users).set({ isBanned: true }).where(eq(users.id, userIdToBan));

		// Log moderation action
		await db.insert(contentModerationActions).values({
			moderatorId: adminUserId,
			action: 'ban_user',
			targetUserId: userIdToBan,
			reason
			// details: JSON.stringify({ duration, isPermanent, expiresAt }) // Check schema for details field
		});
		return ban;
	}

	async deleteContent(
		contentType: 'post' | 'thread' | 'message',
		contentId: ContentId,
		input: DeleteContentInput,
		adminUserId: AdminUserId
	) {
		const { reason } = input;
		let contentAuthorId: AuthorId | null = null;

		if (contentType === 'post') {
			const [postData] = await db
				.select({ userId: posts.userId })
				.from(posts)
				.where(eq(posts.id, contentId));
			if (!postData) throw new AdminError('Post not found', 404, AdminErrorCodes.NOT_FOUND);
			contentAuthorId = postData.userId;
			await db
				.update(posts)
				.set({
					isDeleted: true,
					content: '[Post removed by moderator]',
					deletedAt: new Date(),
					deletedBy: adminUserId
					// deletionReason: reason // Check schema for deletionReason field
				})
				.where(eq(posts.id, contentId));
		} else if (contentType === 'thread') {
			const [threadData] = await db
				.select({ userId: threads.userId })
				.from(threads)
				.where(eq(threads.id, contentId));
			if (!threadData) throw new AdminError('Thread not found', 404, AdminErrorCodes.NOT_FOUND);
			contentAuthorId = threadData.userId;
			await db
				.update(threads)
				.set({
					isDeleted: true,
					title: '[Thread removed by moderator]',
					// content: '[Content removed by moderator]', // Check schema for content field on threads
					deletedAt: new Date(),
					deletedBy: adminUserId
					// deletionReason: reason
				})
				.where(eq(threads.id, contentId));
		} else if (contentType === 'message') {
			const [msgData] = await db
				.select({ userId: shoutboxMessages.userId })
				.from(shoutboxMessages)
				.where(eq(shoutboxMessages.id, contentId));
			if (!msgData)
				throw new AdminError('Shoutbox message not found', 404, AdminErrorCodes.NOT_FOUND);
			contentAuthorId = msgData.userId;
			await db
				.update(shoutboxMessages)
				.set({
					isDeleted: true,
					content: '[Message removed by moderator]'
					// deletedAt: new Date(), // Check schema for deletedAt on shoutboxMessages
					// deletedBy: adminUserId,
					// deletionReason: reason
				})
				.where(eq(shoutboxMessages.id, contentId));
		} else {
			throw new AdminError(
				'Invalid content type for deletion',
				400,
				AdminErrorCodes.INVALID_REQUEST
			);
		}

		// Log moderation action
		if (contentAuthorId !== null) {
			await db.insert(contentModerationActions).values({
				moderatorId: adminUserId,
				action: `delete_${contentType}`,
				contentType,
				contentId,
				targetUserId: contentAuthorId,
				reason
			});
		}
		return { success: true, message: `${contentType} deleted successfully.` };
	}
}

export const adminReportsService = new AdminReportsService();

/**
 * Settings Command Service
 *
 * Handles all write operations for settings management
 * Focused on create, update, delete operations with proper validation
 */

import { db } from '@db';
import { siteSettings, featureFlags } from '@schema';
import { eq } from 'drizzle-orm';
import { logger } from '@server/src/core/logger';
import { AdminError, AdminErrorCodes } from '../../../admin.errors';
import { adminCacheService } from '../../../shared/admin-cache.service';
import { settingsQueryService } from './settings-query.service';
import { settingsValidationService } from './settings-validation.service';
import type {
	UpdateSettingInput,
	UpdateSettingsInput,
	CreateSettingInput,
	UpdateSettingMetadataInput
} from '../settings.validators';

// For the feature flag input type
export type ToggleFeatureFlagInput = {
	key: string;
	enabled?: boolean;
	rolloutPercentage?: number;
};

export class SettingsCommandService {
	/**
	 * Update a single setting value
	 */
	async updateSetting(data: UpdateSettingInput) {
		try {
			const { key, value, description } = data;

			// Validate setting exists
			await settingsValidationService.validateSettingExists(key);

			// Validate the new value
			await settingsValidationService.validateSettingValue(key, value);

			// Prepare update data
			const updateData: any = {
				value: String(value),
				updatedAt: new Date()
			};

			if (description !== undefined) {
				updateData.description = description;
			}

			// Update the setting
			const [updatedSetting] = await db
				.update(siteSettings)
				.set(updateData)
				.where(eq(siteSettings.key, key))
				.returning();

			// Invalidate cache after successful update
			await adminCacheService.invalidateEntity('setting', key);

			logger.info('SettingsCommandService', 'Setting updated successfully', {
				key,
				oldValue: 'redacted',
				newValue: 'redacted'
			});

			return updatedSetting;
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsCommandService', 'Error updating setting', {
				error: error.message,
				key: data.key
			});
			throw new AdminError('Failed to update setting', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Update multiple settings in a transaction
	 */
	async updateSettings(data: UpdateSettingsInput) {
		try {
			const { settings } = data;

			if (!settings || settings.length === 0) {
				throw new AdminError(
					'No settings provided for update',
					400,
					AdminErrorCodes.INVALID_REQUEST
				);
			}

			// Validate all settings before updating
			for (const setting of settings) {
				await settingsValidationService.validateSettingExists(setting.key);
				await settingsValidationService.validateSettingValue(setting.key, setting.value);
			}

			const updatedSettings = [];

			// Use transaction for multiple updates
			await db.transaction(async (tx) => {
				for (const setting of settings) {
					const [updated] = await tx
						.update(siteSettings)
						.set({
							value: String(setting.value),
							updatedAt: new Date()
						})
						.where(eq(siteSettings.key, setting.key))
						.returning();

					updatedSettings.push(updated);
				}
			});

			logger.info('SettingsCommandService', 'Bulk settings update completed', {
				settingsCount: settings.length,
				keys: settings.map((s) => s.key)
			});

			return updatedSettings;
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsCommandService', 'Error updating multiple settings', {
				error: error.message,
				settingsCount: data.settings?.length
			});
			throw new AdminError('Failed to update settings', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Create a new setting
	 */
	async createSetting(data: CreateSettingInput) {
		try {
			const { key, name, value, type, group, description, isPublic } = data;

			// Validate setting doesn't already exist
			await settingsValidationService.validateSettingNotExists(key);

			// Validate the setting data
			await settingsValidationService.validateNewSettingData(data);

			// Create the setting
			const [newSetting] = await db
				.insert(siteSettings)
				.values({
					key,
					name,
					value: String(value),
					type: type || 'string',
					group: group || null,
					description: description || null,
					isPublic: isPublic ?? false,
					createdAt: new Date(),
					updatedAt: new Date()
				})
				.returning();

			logger.info('SettingsCommandService', 'Setting created successfully', {
				key,
				type: type || 'string',
				group: group || 'none'
			});

			return newSetting;
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsCommandService', 'Error creating setting', {
				error: error.message,
				key: data.key
			});
			throw new AdminError('Failed to create setting', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Update setting metadata (name, description, type, etc.)
	 */
	async updateSettingMetadata(key: string, data: UpdateSettingMetadataInput) {
		try {
			// Validate setting exists
			await settingsValidationService.validateSettingExists(key);

			// Validate metadata
			await settingsValidationService.validateSettingMetadata(data);

			// Prepare update data
			const updateData: any = {
				updatedAt: new Date()
			};

			if (data.name !== undefined) updateData.name = data.name;
			if (data.description !== undefined) updateData.description = data.description;
			if (data.type !== undefined) updateData.type = data.type;
			if (data.group !== undefined) updateData.group = data.group;
			if (data.isPublic !== undefined) updateData.isPublic = data.isPublic;

			// Update metadata
			const [updatedSetting] = await db
				.update(siteSettings)
				.set(updateData)
				.where(eq(siteSettings.key, key))
				.returning();

			logger.info('SettingsCommandService', 'Setting metadata updated', {
				key,
				updatedFields: Object.keys(updateData).filter((k) => k !== 'updatedAt')
			});

			return updatedSetting;
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsCommandService', 'Error updating setting metadata', {
				error: error.message,
				key
			});
			throw new AdminError('Failed to update setting metadata', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Delete a setting
	 */
	async deleteSetting(key: string) {
		try {
			// Validate setting exists and can be deleted
			await settingsValidationService.validateSettingCanBeDeleted(key);

			// Delete the setting
			const [deletedSetting] = await db
				.delete(siteSettings)
				.where(eq(siteSettings.key, key))
				.returning();

			if (!deletedSetting) {
				throw new AdminError(`Setting with key "${key}" not found`, 404, AdminErrorCodes.NOT_FOUND);
			}

			logger.info('SettingsCommandService', 'Setting deleted successfully', { key });

			return { success: true, deletedSetting };
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsCommandService', 'Error deleting setting', {
				error: error.message,
				key
			});
			throw new AdminError('Failed to delete setting', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Toggle feature flag status
	 */
	async updateFeatureFlag({ key, enabled, rolloutPercentage }: ToggleFeatureFlagInput) {
		try {
			// Validate feature flag exists
			const existingFlag = await this.getFeatureFlag(key);

			// Validate rollout percentage
			if (rolloutPercentage !== undefined && (rolloutPercentage < 0 || rolloutPercentage > 100)) {
				throw new AdminError(
					'Rollout percentage must be between 0 and 100',
					400,
					AdminErrorCodes.INVALID_REQUEST
				);
			}

			// Prepare update data
			const updateData: any = {
				updatedAt: new Date()
			};

			if (enabled !== undefined) updateData.enabled = enabled;
			if (rolloutPercentage !== undefined) updateData.rolloutPercentage = rolloutPercentage;

			// Update feature flag
			const [updatedFlag] = await db
				.update(featureFlags)
				.set(updateData)
				.where(eq(featureFlags.key, key))
				.returning();

			logger.info('SettingsCommandService', 'Feature flag updated', {
				key,
				enabled: updateData.enabled,
				rolloutPercentage: updateData.rolloutPercentage
			});

			return updatedFlag;
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsCommandService', 'Error updating feature flag', {
				error: error.message,
				key
			});
			throw new AdminError('Failed to update feature flag', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Private helper to get feature flag
	 */
	private async getFeatureFlag(key: string) {
		const [flag] = await db.select().from(featureFlags).where(eq(featureFlags.key, key));

		if (!flag) {
			throw new AdminError(`Feature flag "${key}" not found`, 404, AdminErrorCodes.NOT_FOUND);
		}

		return flag;
	}
}

// Export singleton instance
export const settingsCommandService = new SettingsCommandService();

/**
 * Settings Group Service
 *
 * Handles setting group management operations
 * Focused on organizing settings into logical groups for better admin UX
 */

import { db } from '@db';
import { siteSettings } from '@schema';
import { eq, sql, and, ne } from 'drizzle-orm';
import { logger } from '@server/src/core/logger';
import { AdminError, AdminErrorCodes } from '../../../admin.errors';
import type { SettingGroupInput } from '../settings.validators';

export class SettingsGroupService {
	/**
	 * Get all setting groups with their setting counts
	 */
	async getAllSettingGroups() {
		try {
			// Get groups with setting counts
			const groupStats = await db
				.select({
					group: siteSettings.group,
					count: sql<number>`count(*)::integer`,
					publicCount: sql<number>`sum(case when ${siteSettings.isPublic} then 1 else 0 end)::integer`
				})
				.from(siteSettings)
				.where(ne(siteSettings.group, null))
				.groupBy(siteSettings.group)
				.orderBy(siteSettings.group);

			// Format the results
			const groups = groupStats.map((stat) => ({
				key: stat.group,
				name: this.formatGroupName(stat.group),
				settingsCount: stat.count,
				publicSettingsCount: stat.publicCount,
				privateSettingsCount: stat.count - stat.publicCount
			}));

			// Add ungrouped settings count
			const [ungroupedCount] = await db
				.select({
					count: sql<number>`count(*)::integer`
				})
				.from(siteSettings)
				.where(eq(siteSettings.group, null));

			if (ungroupedCount.count > 0) {
				groups.push({
					key: null,
					name: 'Ungrouped',
					settingsCount: ungroupedCount.count,
					publicSettingsCount: 0,
					privateSettingsCount: ungroupedCount.count
				});
			}

			return groups;
		} catch (error) {
			logger.error('SettingsGroupService', 'Error fetching setting groups', {
				error: error.message
			});
			throw new AdminError('Failed to fetch setting groups', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Create a new setting group
	 */
	async createSettingGroup(data: SettingGroupInput) {
		try {
			const { groupKey, name, description } = data;

			// Validate group doesn't already exist
			await this.validateGroupNotExists(groupKey);

			// Validate group key format
			if (!/^[a-z0-9_]+$/.test(groupKey)) {
				throw new AdminError(
					'Group key must contain only lowercase letters, numbers, and underscores',
					400,
					AdminErrorCodes.VALIDATION_ERROR
				);
			}

			// Since we don't have a separate groups table, we create a placeholder setting
			// This is a design choice - groups are implicit based on settings that reference them
			const groupInfo = {
				group: groupKey,
				name: name || this.formatGroupName(groupKey),
				description,
				createdAt: new Date()
			};

			logger.info('SettingsGroupService', 'Setting group created', {
				groupKey,
				name: groupInfo.name
			});

			return groupInfo;
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsGroupService', 'Error creating setting group', {
				error: error.message,
				groupKey: data.groupKey
			});
			throw new AdminError('Failed to create setting group', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Update a setting group (rename all settings in the group)
	 */
	async updateSettingGroup(oldGroupKey: string, data: SettingGroupInput) {
		try {
			const { groupKey: newGroupKey, name } = data;

			// Validate old group exists
			await this.validateGroupExists(oldGroupKey);

			// If changing group key, validate new key doesn't exist
			if (newGroupKey !== oldGroupKey) {
				await this.validateGroupNotExists(newGroupKey);

				// Update all settings in the group
				await db
					.update(siteSettings)
					.set({
						group: newGroupKey,
						updatedAt: new Date()
					})
					.where(eq(siteSettings.group, oldGroupKey));

				logger.info('SettingsGroupService', 'Setting group key updated', {
					oldGroupKey,
					newGroupKey,
					name
				});
			}

			return {
				group: newGroupKey,
				name: name || this.formatGroupName(newGroupKey),
				updatedAt: new Date()
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsGroupService', 'Error updating setting group', {
				error: error.message,
				oldGroupKey,
				newGroupKey: data.groupKey
			});
			throw new AdminError('Failed to update setting group', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Delete a setting group (move all settings to another group or ungrouped)
	 */
	async deleteSettingGroup(groupKey: string, newGroupKey?: string) {
		try {
			// Validate group exists
			await this.validateGroupExists(groupKey);

			// If moving to another group, validate it exists
			if (newGroupKey) {
				await this.validateGroupExists(newGroupKey);
			}

			// Update all settings in the group
			await db
				.update(siteSettings)
				.set({
					group: newGroupKey || null,
					updatedAt: new Date()
				})
				.where(eq(siteSettings.group, groupKey));

			logger.info('SettingsGroupService', 'Setting group deleted', {
				deletedGroup: groupKey,
				movedToGroup: newGroupKey || 'ungrouped'
			});

			return {
				success: true,
				deletedGroup: groupKey,
				movedToGroup: newGroupKey || null
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsGroupService', 'Error deleting setting group', {
				error: error.message,
				groupKey,
				newGroupKey
			});
			throw new AdminError('Failed to delete setting group', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Move settings between groups
	 */
	async moveSettingsToGroup(settingKeys: string[], targetGroupKey: string | null) {
		try {
			if (!settingKeys || settingKeys.length === 0) {
				throw new AdminError('No settings provided for move', 400, AdminErrorCodes.INVALID_REQUEST);
			}

			// Validate target group exists (if not null)
			if (targetGroupKey) {
				await this.validateGroupExists(targetGroupKey);
			}

			// Update the settings
			const results = await db
				.update(siteSettings)
				.set({
					group: targetGroupKey,
					updatedAt: new Date()
				})
				.where(sql`${siteSettings.key} = ANY(${settingKeys})`)
				.returning({ key: siteSettings.key });

			if (results.length !== settingKeys.length) {
				logger.warn('SettingsGroupService', 'Not all settings were found for group move', {
					requested: settingKeys.length,
					moved: results.length
				});
			}

			logger.info('SettingsGroupService', 'Settings moved to group', {
				settingsCount: results.length,
				targetGroup: targetGroupKey || 'ungrouped'
			});

			return {
				success: true,
				movedCount: results.length,
				targetGroup: targetGroupKey
			};
		} catch (error) {
			if (error instanceof AdminError) throw error;

			logger.error('SettingsGroupService', 'Error moving settings to group', {
				error: error.message,
				settingsCount: settingKeys?.length,
				targetGroupKey
			});
			throw new AdminError('Failed to move settings to group', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Get settings for a specific group
	 */
	async getSettingsInGroup(groupKey: string | null) {
		try {
			const settings = await db
				.select()
				.from(siteSettings)
				.where(groupKey ? eq(siteSettings.group, groupKey) : eq(siteSettings.group, null))
				.orderBy(siteSettings.key);

			return settings;
		} catch (error) {
			logger.error('SettingsGroupService', 'Error fetching settings in group', {
				error: error.message,
				groupKey
			});
			throw new AdminError('Failed to fetch group settings', 500, AdminErrorCodes.DB_ERROR);
		}
	}

	/**
	 * Validate that a group exists
	 */
	private async validateGroupExists(groupKey: string) {
		const [setting] = await db
			.select({ group: siteSettings.group })
			.from(siteSettings)
			.where(eq(siteSettings.group, groupKey))
			.limit(1);

		if (!setting) {
			throw new AdminError(`Setting group "${groupKey}" not found`, 404, AdminErrorCodes.NOT_FOUND);
		}
	}

	/**
	 * Validate that a group does not exist
	 */
	private async validateGroupNotExists(groupKey: string) {
		const [setting] = await db
			.select({ group: siteSettings.group })
			.from(siteSettings)
			.where(eq(siteSettings.group, groupKey))
			.limit(1);

		if (setting) {
			throw new AdminError(
				`Setting group "${groupKey}" already exists`,
				409,
				AdminErrorCodes.DUPLICATE_ENTRY
			);
		}
	}

	/**
	 * Format group key into a display name
	 */
	private formatGroupName(groupKey: string): string {
		return groupKey
			.split('_')
			.map((word) => word.charAt(0).toUpperCase() + word.slice(1))
			.join(' ');
	}
}

