		logger.info('Social config update attempted (disabled):', newConfig);

		return newConfig;
	}

	/**
	 * Get social feature usage statistics
	 * TODO: Temporarily returning mock data until schema imports are fixed
	 */
	static async getSocialStats() {
		// TODO: Implement database queries once schema imports are fixed
		return {
			mentions: { total: 0, unread: 0 },
			follows: { total: 0 },
			friendships: { accepted: 0, pending: 0, blocked: 0 },
			activeUsers: { last24h: 0 },
			lastUpdated: new Date().toISOString()
		};
	}

	/**
	 * Reset social configuration to defaults
	 * TODO: Temporarily disabled until schema imports are fixed
	 */
	static async resetToDefaults(adminUserId: string): Promise<SocialConfig> {
		// TODO: Implement database reset once schema imports are fixed
		logger.info('Social config reset attempted (disabled) by:', adminUserId);
		return defaultSocialConfig;
	}

	/**
	 * Get current status of all social features
	 */
	static async getFeatureStatus() {
		const config = await this.getSocialConfig();
		const stats = await this.getSocialStats();

		return {
			mentions: {
				enabled: config.mentions.enabled,
				activeUsers: stats.activeUsers.last24h,
				totalMentions: stats.mentions.total,
				unreadMentions: stats.mentions.unread,
				healthStatus: this.getHealthStatus('mentions', config, stats)
			},
			whaleWatch: {
				enabled: config.whaleWatch.enabled,
				totalFollows: stats.follows.total,
				healthStatus: this.getHealthStatus('whaleWatch', config, stats)
			},
			friends: {
				enabled: config.friends.enabled,
				totalFriendships: stats.friendships.accepted,
				pendingRequests: stats.friendships.pending,
				healthStatus: this.getHealthStatus('friends', config, stats)
			},
			overall: {
				status:
					config.mentions.enabled || config.whaleWatch.enabled || config.friends.enabled
						? 'active'
						: 'disabled',
				lastChecked: new Date().toISOString()
			}
		};
	}

	/**
	 * Emergency disable all social features
	 */
	static async emergencyDisable(adminUserId: string) {
		const emergencyConfig = await this.getSocialConfig();

		// Disable all features
		emergencyConfig.mentions.enabled = false;
		emergencyConfig.whaleWatch.enabled = false;
		emergencyConfig.friends.enabled = false;
		emergencyConfig.adminOverrides.enableEmergencyDisable = true;

		await this.updateSocialConfig(emergencyConfig, adminUserId);

		return {
			success: true,
			message: 'All social features have been emergency disabled',
			disabledAt: new Date().toISOString(),
			disabledBy: adminUserId
		};
	}

	/**
	 * Merge partial config updates with current config
	 */
	private static deepMerge(target: any, source: any): any {
		const result = { ...target };

		for (const key in source) {
			if (source[key] && typeof source[key] === 'object' && !Array.isArray(source[key])) {
				result[key] = this.deepMerge(target[key] || {}, source[key]);
			} else {
				result[key] = source[key];
			}
		}

		return result;
	}

	/**
	 * Merge stored config with defaults to ensure all fields exist
	 */
	private static mergeWithDefaults(storedConfig: any): SocialConfig {
		return this.deepMerge(defaultSocialConfig, storedConfig);
	}

	/**
	 * Validate configuration values
	 */
	private static validateConfig(config: SocialConfig): void {
		// Validate mention settings
		if (config.mentions.minLevel < 1 || config.mentions.minLevel > 100) {
			throw new Error('Mentions minimum level must be between 1 and 100');
		}

		if (config.mentions.settings.maxMentionsPerPost > 50) {
			throw new Error('Maximum mentions per post cannot exceed 50');
		}

		// Validate whale watch settings
		if (config.whaleWatch.settings.maxFollowsPerUser > 10000) {
			throw new Error('Maximum follows per user cannot exceed 10,000');
		}

		// Validate friends settings
		if (config.friends.settings.maxFriendsPerUser > 5000) {
			throw new Error('Maximum friends per user cannot exceed 5,000');
		}
	}

	/**
	 * Get health status for a specific feature
	 */
	private static getHealthStatus(feature: string, config: SocialConfig, stats: any): string {
		if (!config[feature as keyof SocialConfig].enabled) {
			return 'disabled';
		}

		// Simple health checks - can be expanded
		switch (feature) {
			case 'mentions':
				return stats.mentions.total > 0 ? 'healthy' : 'inactive';
			case 'whaleWatch':
				return stats.follows.total > 0 ? 'healthy' : 'inactive';
			case 'friends':
				return stats.friendships.accepted > 0 ? 'healthy' : 'inactive';
			default:
				return 'unknown';
		}
	}
}

import { userService } from '@server/src/core/services/user.service';
/**
 * Admin Subscription Controller
 *
 * Admin interface for managing subscriptions, cosmetic drops, and subscription analytics
 */

import type { Request, Response } from 'express';
import { subscriptionService } from '../../../subscriptions/subscription.service';
import { logger } from '../../../../core/logger';
import { db } from '@db';
import { subscriptions, cosmeticDrops, users } from '@schema';
import { eq, sql, desc, count, sum } from 'drizzle-orm';

export class AdminSubscriptionController {
	/**
	 * Get subscription analytics
	 * GET /api/admin/subscriptions/analytics
	 */
	async getSubscriptionAnalytics(req: Request, res: Response): Promise<void> {
		try {
			// Get subscription statistics
			const [
				totalSubscriptions,
				vipPassCount,
				degenPassCount,
				activeSubscriptions,
				monthlyRevenue,
				cosmeticDropStats
			] = await Promise.all([
				// Total subscriptions
				db
					.select({ count: sql<number>`COUNT(*)` })
					.from(subscriptions)
					.where(eq(subscriptions.isDeleted, false)),

				// VIP Pass count
				db
					.select({ count: sql<number>`COUNT(*)` })
					.from(subscriptions)
					.where(sql`${subscriptions.type} = 'vip_pass' AND ${subscriptions.isDeleted} = false`),

				// Degen Pass count
				db
					.select({ count: sql<number>`COUNT(*)` })
					.from(subscriptions)
					.where(sql`${subscriptions.type} = 'degen_pass' AND ${subscriptions.isDeleted} = false`),

				// Active subscriptions
				db
					.select({ count: sql<number>`COUNT(*)` })
					.from(subscriptions)
					.where(
						sql`${subscriptions.status} IN ('active', 'lifetime') AND ${subscriptions.isDeleted} = false`
					),

				// Monthly revenue (current month)
				db
					.select({ revenue: sql<number>`SUM(${subscriptions.pricePaid})` })
					.from(subscriptions)
					.where(
						sql`EXTRACT(MONTH FROM ${subscriptions.createdAt}) = EXTRACT(MONTH FROM CURRENT_DATE) 
                        AND EXTRACT(YEAR FROM ${subscriptions.createdAt}) = EXTRACT(YEAR FROM CURRENT_DATE)
                        AND ${subscriptions.isDeleted} = false`
					),

				// Cosmetic drop statistics
				db
					.select({
						totalDrops: sql<number>`COUNT(*)`,
						totalValue: sql<number>`SUM(${cosmeticDrops.cosmeticValue})`
					})
					.from(cosmeticDrops)
			]);

			const analytics = {
				subscriptions: {
					total: totalSubscriptions[0]?.count || 0,
					vip_pass: vipPassCount[0]?.count || 0,
					degen_pass: degenPassCount[0]?.count || 0,
					active: activeSubscriptions[0]?.count || 0
				},
				revenue: {
					monthly: monthlyRevenue[0]?.revenue || 0,
					currency: 'DGT'
				},
				cosmetics: {
					totalDrops: cosmeticDropStats[0]?.totalDrops || 0,
					totalValue: cosmeticDropStats[0]?.totalValue || 0
				}
			};

			res.json({
				success: true,
				data: { analytics }
			});
		} catch (error) {
			logger.error('ADMIN_SUBSCRIPTION', 'Error getting analytics:', error);
			res.status(500).json({
				success: false,
				error: 'Failed to retrieve subscription analytics'
			});
		}
	}

	/**
	 * Get all subscriptions with pagination
	 * GET /api/admin/subscriptions
	 */
	async getAllSubscriptions(req: Request, res: Response): Promise<void> {
		try {
			const page = parseInt(req.query.page as string) || 1;
			const limit = parseInt(req.query.limit as string) || 50;
			const status = req.query.status as string;
			const type = req.query.type as string;

			const offset = (page - 1) * limit;

			// Build where conditions
			const whereConditions = [eq(subscriptions.isDeleted, false)];

			if (status) {
				whereConditions.push(eq(subscriptions.status, status));
			}

			if (type) {
				whereConditions.push(eq(subscriptions.type, type));
			}

			// Get subscriptions with user information
			const results = await db
				.select({
					subscription: subscriptions,
					user: {
						id: users.id,
						username: users.username,
						email: users.email
					}
				})
				.from(subscriptions)
				.leftJoin(users, eq(subscriptions.userId, users.id))
				.where(sql`${whereConditions.join(' AND ')}`)
				.orderBy(desc(subscriptions.createdAt))
				.limit(limit)
				.offset(offset);

			// Get total count
			const [totalCount] = await db
				.select({ count: sql<number>`COUNT(*)` })
				.from(subscriptions)
				.where(sql`${whereConditions.join(' AND ')}`);

			res.json({
				success: true,
				data: {
					subscriptions: results,
					pagination: {
						page,
						limit,
						total: totalCount?.count || 0,
						pages: Math.ceil((totalCount?.count || 0) / limit)
					}
				}
			});
		} catch (error) {
			logger.error('ADMIN_SUBSCRIPTION', 'Error getting all subscriptions:', error);
			res.status(500).json({
				success: false,
				error: 'Failed to retrieve subscriptions'
			});
		}
	}

	/**
	 * Process monthly cosmetic drops manually
	 * POST /api/admin/subscriptions/process-cosmetics
	 */
	async processMonthlyCosmetics(req: Request, res: Response): Promise<void> {
		try {
			const results = await subscriptionService.processMonthlyCosmetics();

			res.json({
				success: true,
				data: {
					results,
					message: `Cosmetic drops processed: ${results.processed} successful, ${results.failed} failed`
				}
			});
		} catch (error) {
			logger.error('ADMIN_SUBSCRIPTION', 'Error processing cosmetics:', error);
			res.status(500).json({
				success: false,
				error: 'Failed to process monthly cosmetic drops'
			});
		}
	}

	/**
	 * Get cosmetic drop history with pagination
	 * GET /api/admin/subscriptions/cosmetic-drops
	 */
	async getCosmeticDropHistory(req: Request, res: Response): Promise<void> {
		try {
			const page = parseInt(req.query.page as string) || 1;
			const limit = parseInt(req.query.limit as string) || 50;
			const month = parseInt(req.query.month as string);
			const year = parseInt(req.query.year as string);

			const offset = (page - 1) * limit;

			// Build where conditions
			const whereConditions = [];

			if (month && year) {
				whereConditions.push(eq(cosmeticDrops.dropMonth, month));
				whereConditions.push(eq(cosmeticDrops.dropYear, year));
			}

			// Get cosmetic drops with user information
			const results = await db
				.select({
					drop: cosmeticDrops,
					user: {
						id: users.id,
						username: users.username
					}
				})
				.from(cosmeticDrops)
				.leftJoin(users, eq(cosmeticDrops.userId, users.id))
				.where(whereConditions.length > 0 ? sql`${whereConditions.join(' AND ')}` : undefined)
				.orderBy(desc(cosmeticDrops.createdAt))
				.limit(limit)
				.offset(offset);

			// Get total count
			const [totalCount] = await db
				.select({ count: sql<number>`COUNT(*)` })
				.from(cosmeticDrops)
				.where(whereConditions.length > 0 ? sql`${whereConditions.join(' AND ')}` : undefined);

			res.json({
				success: true,
				data: {
					drops: results,
					pagination: {
						page,
						limit,
						total: totalCount?.count || 0,
						pages: Math.ceil((totalCount?.count || 0) / limit)
					}
				}
			});
		} catch (error) {
			logger.error('ADMIN_SUBSCRIPTION', 'Error getting cosmetic drops:', error);
			res.status(500).json({
				success: false,
				error: 'Failed to retrieve cosmetic drop history'
			});
		}
	}

	/**
	 * Cancel a subscription (admin override)
	 * POST /api/admin/subscriptions/:id/cancel
	 */
	async cancelSubscription(req: Request, res: Response): Promise<void> {
		try {
			const { id } = req.params;
			const { reason } = req.body;
			const adminUserId = userService.getUserFromRequest(req)?.id;

			const subscriptionId = id;
			if (isNaN(subscriptionId)) {
				res.status(400).json({
					success: false,
					error: 'Valid subscription ID required'
				});
				return;
			}

			// Get subscription details
			const subscription = await db
				.select()
				.from(subscriptions)
				.where(eq(subscriptions.id, subscriptionId))
				.limit(1);

			if (subscription.length === 0) {
				res.status(404).json({
					success: false,
					error: 'Subscription not found'
				});
				return;
			}

			// Cancel the subscription (admin override - can cancel VIP Pass)
			await db
				.update(subscriptions)
				.set({
					status: 'cancelled',
					autoRenew: false,
					notes: `Admin cancellation by ${adminUserId}: ${reason || 'No reason provided'}`,
					updatedAt: new Date()
				})
				.where(eq(subscriptions.id, subscriptionId));

			logger.info(
				'ADMIN_SUBSCRIPTION',
				`Subscription ${subscriptionId} cancelled by admin ${adminUserId}: ${reason}`
			);

			res.json({
				success: true,
				data: {
					message: 'Subscription cancelled successfully'
				}
			});
		} catch (error) {
			logger.error('ADMIN_SUBSCRIPTION', 'Error cancelling subscription:', error);
			res.status(500).json({
				success: false,
				error: 'Failed to cancel subscription'
			});
		}
	}

	/**
	 * Grant subscription to user (admin action)
	 * POST /api/admin/subscriptions/grant
	 */
	async grantSubscription(req: Request, res: Response): Promise<void> {
		try {
			const { userId, type, reason } = req.body;
			const adminUserId = userService.getUserFromRequest(req)?.id;

			if (!userId || !type || !['vip_pass', 'degen_pass'].includes(type)) {
				res.status(400).json({
					success: false,
					error: 'Valid userId and subscription type required'
				});
				return;
			}

			// Check if user already has this subscription type
			const existingSubscription = await subscriptionService.getUserActiveSubscription(
				userId,
				type
			);
			if (existingSubscription) {
				res.status(400).json({
					success: false,
					error: `User already has an active ${type.replace('_', ' ')} subscription`
				});
				return;
			}

			// Grant the subscription
			const endDate = type === 'vip_pass' ? null : new Date(Date.now() + 30 * 24 * 60 * 60 * 1000); // 30 days
			const nextBillingDate = type === 'vip_pass' ? null : endDate;
			const status = type === 'vip_pass' ? 'lifetime' : 'active';

			const [subscription] = await db
				.insert(subscriptions)
				.values({
					userId,
					type,
					status,
					pricePaid: 0, // Admin granted - no payment
					currency: 'DGT',
					startDate: new Date(),
					endDate,
					nextBillingDate,
					autoRenew: type === 'degen_pass',
					benefits: subscriptionService['getDefaultBenefits'](type),
					metadata: {
						grantedBy: adminUserId,
						grantReason: reason || 'Admin grant',
						source: 'admin_grant'
					},
					notes: `Admin grant by ${adminUserId}: ${reason || 'No reason provided'}`
				})
				.returning();

			logger.info(
				'ADMIN_SUBSCRIPTION',
				`${type} granted to user ${userId} by admin ${adminUserId}: ${reason}`
			);

			res.json({
				success: true,
				data: {
					subscription,
					message: `${type.replace('_', ' ')} granted successfully`
				}
			});
		} catch (error) {
			logger.error('ADMIN_SUBSCRIPTION', 'Error granting subscription:', error);
			res.status(500).json({
				success: false,
				error: 'Failed to grant subscription'
			});
		}
	}
}

