import { db } from '@db';
import { titles as titlesTable, roles as rolesTable } from '@schema';
import type { CreateTitleInput, UpdateTitleInput } from './titles.validators';
import { eq, desc, asc, isNull, isNotNull } from 'drizzle-orm';

export class AdminTitlesService {
	async list() {
		// Get titles with their associated role information
		const titles = await db
			.select({
				id: titlesTable.id,
				name: titlesTable.name,
				description: titlesTable.description,
				iconUrl: titlesTable.iconUrl,
				rarity: titlesTable.rarity,
				emoji: titlesTable.emoji,
				fontFamily: titlesTable.fontFamily,
				fontSize: titlesTable.fontSize,
				fontWeight: titlesTable.fontWeight,
				textColor: titlesTable.textColor,
				backgroundColor: titlesTable.backgroundColor,
				borderColor: titlesTable.borderColor,
				borderWidth: titlesTable.borderWidth,
				borderStyle: titlesTable.borderStyle,
				borderRadius: titlesTable.borderRadius,
				glowColor: titlesTable.glowColor,
				glowIntensity: titlesTable.glowIntensity,
				shadowColor: titlesTable.shadowColor,
				shadowBlur: titlesTable.shadowBlur,
				shadowOffsetX: titlesTable.shadowOffsetX,
				shadowOffsetY: titlesTable.shadowOffsetY,
				gradientStart: titlesTable.gradientStart,
				gradientEnd: titlesTable.gradientEnd,
				gradientDirection: titlesTable.gradientDirection,
				animation: titlesTable.animation,
				animationDuration: titlesTable.animationDuration,
				roleId: titlesTable.roleId,
				isShopItem: titlesTable.isShopItem,
				isUnlockable: titlesTable.isUnlockable,
				unlockConditions: titlesTable.unlockConditions,
				shopPrice: titlesTable.shopPrice,
				shopCurrency: titlesTable.shopCurrency,
				createdAt: titlesTable.createdAt,
				roleName: rolesTable.name
			})
			.from(titlesTable)
			.leftJoin(rolesTable, eq(titlesTable.roleId, rolesTable.id))
			.orderBy(asc(titlesTable.rarity), asc(titlesTable.name));

		return titles;
	}

	async getByRole(roleId: string) {
		return db
			.select()
			.from(titlesTable)
			.where(eq(titlesTable.roleId, roleId))
			.orderBy(asc(titlesTable.name));
	}

	async getCustomTitles() {
		return db
			.select()
			.from(titlesTable)
			.where(isNull(titlesTable.roleId))
			.orderBy(asc(titlesTable.rarity), asc(titlesTable.name));
	}

	async create(data: CreateTitleInput) {
		// Validate role exists if roleId is provided
		if (data.roleId) {
			const [role] = await db
				.select({ id: rolesTable.id })
				.from(rolesTable)
				.where(eq(rolesTable.id, data.roleId));
			if (!role) throw new Error('Role not found');
		}

		const [title] = await db.insert(titlesTable).values(data).returning();
		return title;
	}

	async update(id: Id<'id'>, data: UpdateTitleInput) {
		// Validate role exists if roleId is provided
		if (data.roleId) {
			const [role] = await db
				.select({ id: rolesTable.id })
				.from(rolesTable)
				.where(eq(rolesTable.id, data.roleId));
			if (!role) throw new Error('Role not found');
		}

		const [title] = await db
			.update(titlesTable)
			.set(data)
			.where(eq(titlesTable.id, id))
			.returning();

		if (!title) throw new Error('Title not found');
		return title;
	}

	async delete(id: Id<'id'>) {
		const [title] = await db.select().from(titlesTable).where(eq(titlesTable.id, id));
		if (!title) throw new Error('Title not found');

		await db.delete(titlesTable).where(eq(titlesTable.id, id));
		return { success: true };
	}
}

import type { Request, Response } from 'express';
import { userService } from '@server/src/core/services/user.service';
import { adminTreasuryService } from './treasury.service';
import { AdminError, AdminErrorCodes } from '../../admin.errors';
import { getUserId } from '../../admin.middleware';
import { adminController } from '../../admin.controller';
import {
	TreasuryDepositSchema,
	TreasuryWithdrawalSchema,
	TreasurySettingsUpdateSchema,
	MassAirdropSchema,
	type TreasuryDepositInput,
	type TreasuryWithdrawalInput,
	type TreasurySettingsUpdateInput,
	type MassAirdropInput
} from './treasury.validators';
import { validateRequestBody } from '../../admin.validation';

export class AdminTreasuryController {
	async getDgtSupplyStats(req: Request, res: Response) {
		try {
			const stats = await adminTreasuryService.getDgtSupplyStats();
			res.json(stats);
		} catch (error) {
			if (error instanceof AdminError) {
				return res.status(error.httpStatus).json({ error: error.message, code: error.code });
			}
			res.status(500).json({ error: 'Failed to get DGT supply statistics' });
		}
	}

	async sendFromTreasury(req: Request, res: Response) {
		try {
			const data = validateRequestBody(req, res, TreasuryDepositSchema);
			if (!data) return;
			const adminId = userService.getUserFromRequest(req);
			const result = await adminTreasuryService.sendFromTreasury(data, adminId);

			await adminController.logAction(req, 'TREASURY_SEND', 'treasury', data.userId.toString(), {
				amount: data.amount,
				recipientUserId: data.userId,
				description: data.description
			});
			res.json({ message: 'DGT sent from treasury successfully', data: result });
		} catch (error) {
			if (error instanceof AdminError) {
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			}
			res.status(500).json({ error: 'Failed to send DGT from treasury' });
		}
	}

	async recoverToTreasury(req: Request, res: Response) {
		try {
			const data = validateRequestBody(req, res, TreasuryWithdrawalSchema);
			if (!data) return;
			const adminId = userService.getUserFromRequest(req);
			const result = await adminTreasuryService.recoverToTreasury(data, adminId);

			await adminController.logAction(req, 'TREASURY_RECOVER', 'treasury', data.userId.toString(), {
				amount: data.amount,
				sourceUserId: data.userId,
				description: data.description
			});
			res.json({ message: 'DGT recovered to treasury successfully', data: result });
		} catch (error) {
			if (error instanceof AdminError) {
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			}
			res.status(500).json({ error: 'Failed to recover DGT to treasury' });
		}
	}

	async massAirdrop(req: Request, res: Response) {
		try {
			const dataAirdrop = validateRequestBody(req, res, MassAirdropSchema);
			if (!dataAirdrop) return;
			const adminId = userService.getUserFromRequest(req);
			const result = await adminTreasuryService.massAirdrop(dataAirdrop, adminId);

			await adminController.logAction(req, 'TREASURY_MASS_AIRDROP', 'treasury', 'multiple_users', {
				amountPerUser: dataAirdrop.amountPerUser,
				userCount: dataAirdrop.userIds.length,
				reason: dataAirdrop.reason,
				missingUserIds: result.missingUserIds
			});
			res.json({ message: 'Mass airdrop process completed.', data: result });
		} catch (error) {
			if (error instanceof AdminError) {
				return res
					.status(error.httpStatus)
					.json({ error: error.message, code: error.code, details: error.details });
			}
			res.status(500).json({ error: 'Failed to process mass airdrop' });
		}
	}

	async getTreasurySettings(req: Request, res: Response) {
		try {
			const settings = await adminTreasuryService.getDgtEconomyParameters();
			if (!settings) {
				throw new AdminError('Treasury settings not found.', 404, AdminErrorCodes.NOT_FOUND);
			}
