/**
 * High-performance ad serving engine with sub-100ms response time
 * Implements privacy-preserving targeting and real-time optimization
 */
export class AdServingService {
	/**
	 * Main ad serving endpoint - optimized for speed
	 * Target: <50ms response time for 95% of requests
	 */
	async serveAd(request: AdRequest): Promise<AdResponse | null> {
		const startTime = Date.now();

		try {
			// 1. Get placement configuration (5ms)
			const placement = await this.getPlacement(request.placementSlug);
			if (!placement) return null;

			// 2. Get eligible campaigns (15ms)
			const campaigns = await this.getEligibleCampaigns(placement, request);
			if (campaigns.length === 0) return null;

			// 3. Apply targeting and rules (20ms)
			const scoredCampaigns = await this.scoreAndRankCampaigns(campaigns, request, placement);

			// 4. Select winning campaign (5ms)
			const winningCampaign = this.selectWinningCampaign(scoredCampaigns);
			if (!winningCampaign) return null;

			// 5. Generate ad response (5ms)
			const adResponse = await this.generateAdResponse(winningCampaign, placement, request);

			// 6. Track impression asynchronously
			this.trackImpressionAsync(winningCampaign, placement, request, adResponse);

			const responseTime = Date.now() - startTime;
			logger.info(`Ad served in ${responseTime}ms`);

			return adResponse;
		} catch (error) {
			logger.error('Ad serving error:', error);
			return null;
		}
	}

	/**
	 * Get placement configuration with caching
	 */
	private async getPlacement(placementSlug: string): Promise<AdPlacement | null> {
		// TODO: Add Redis caching layer
		const [placement] = await db
			.select()
			.from(adPlacements)
			.where(and(eq(adPlacements.slug, placementSlug), eq(adPlacements.isActive, true)))
			.limit(1);

		return placement || null;
	}

	/**
	 * Get campaigns eligible for the placement
	 */
	private async getEligibleCampaigns(
		placement: AdPlacement,
		request: AdRequest
	): Promise<Campaign[]> {
		const now = new Date();

		return await db
			.select()
			.from(campaigns)
			.where(
				and(
					eq(campaigns.status, 'active'),
					eq(campaigns.isActive, true),
					// Budget check
					sql`${campaigns.spentAmount} < ${campaigns.totalBudget}`,
					// Date range check
					sql`${campaigns.startDate} <= ${now}`,
					sql`(${campaigns.endDate} IS NULL OR ${campaigns.endDate} >= ${now})`
				)
			)
			.orderBy(desc(campaigns.bidAmount))
			.limit(20); // Limit for performance
	}

	/**
	 * Score and rank campaigns based on targeting and rules
	 */
	private async scoreAndRankCampaigns(
		campaigns: Campaign[],
		request: AdRequest,
		placement: AdPlacement
	): Promise<Array<{ campaign: Campaign; score: number; bidPrice: number }>> {
		const scoredCampaigns = [];

		for (const campaign of campaigns) {
			// Check targeting rules
			const targetingScore = await this.calculateTargetingScore(campaign, request);
			if (targetingScore === 0) continue; // Not targeted

			// Apply campaign rules
			const rulesResult = await this.applyCampaignRules(campaign, request, placement);
			if (rulesResult.blocked) continue;

			// Calculate final score
			const qualityScore = campaign.qualityScore?.toNumber() || 1.0;
			const bidPrice = rulesResult.adjustedBid || campaign.bidAmount?.toNumber() || 0;
			const finalScore = targetingScore * qualityScore * bidPrice;

			scoredCampaigns.push({
				campaign,
				score: finalScore,
				bidPrice
			});
		}

		return scoredCampaigns.sort((a, b) => b.score - a.score);
	}

	/**
	 * Calculate targeting score (0-1) based on user profile
	 */
	private async calculateTargetingScore(campaign: Campaign, request: AdRequest): Promise<number> {
		const targetingRules = campaign.targetingRules as any;
		if (!targetingRules || Object.keys(targetingRules).length === 0) {
			return 1.0; // No targeting = everyone
		}

		let score = 1.0;

		// Device targeting
		if (targetingRules.deviceTypes?.length > 0) {
			if (!targetingRules.deviceTypes.includes(request.deviceInfo.type)) {
				return 0;
			}
		}

		// Geographic targeting
		if (targetingRules.geoRegions?.length > 0) {
			if (!targetingRules.geoRegions.includes(request.geoData.region)) {
				return 0;
			}
		}

		// Interest targeting
		if (targetingRules.interestSegments?.length > 0 && request.userContext.interestSegments) {
			const overlap = targetingRules.interestSegments.filter((segment: string) =>
				request.userContext.interestSegments!.includes(segment)
			);
			score *= overlap.length / targetingRules.interestSegments.length;
		}

		// DGT balance targeting
		if (targetingRules.dgtBalanceTiers?.length > 0) {
			if (!targetingRules.dgtBalanceTiers.includes(request.userContext.dgtBalanceTier)) {
				score *= 0.5; // Partial match
			}
		}

		// XP level targeting
		if (targetingRules.minXpLevel && request.userContext.xpLevel) {
			if (request.userContext.xpLevel < targetingRules.minXpLevel) {
				return 0;
			}
		}

		return Math.max(0, Math.min(1, score));
	}

	/**
	 * Apply campaign rules for dynamic configuration
	 */
	private async applyCampaignRules(
		campaign: Campaign,
		request: AdRequest,
		placement: AdPlacement
	): Promise<{ blocked: boolean; adjustedBid?: Id<'adjustedBid'>; metadata?: any }> {
		// Get applicable rules
		const rules = await db
			.select()
			.from(campaignRules)
			.where(and(eq(campaignRules.campaignId, campaign.id), eq(campaignRules.isActive, true)))
			.orderBy(asc(campaignRules.priority));

		let blocked = false;
		let bidMultiplier = 1.0;
		const metadata: any = {};

		for (const rule of rules) {
			const ruleResult = this.evaluateRule(rule, request, placement);

			if (ruleResult.block) {
				blocked = true;
				break;
			}

			if (ruleResult.bidAdjustment) {
				bidMultiplier *= ruleResult.bidAdjustment;
			}

			if (ruleResult.metadata) {
				Object.assign(metadata, ruleResult.metadata);
			}
		}

		const originalBid = campaign.bidAmount?.toNumber() || 0;
		const adjustedBid = originalBid * bidMultiplier;

		return {
			blocked,
			adjustedBid: adjustedBid !== originalBid ? adjustedBid : undefined,
			metadata: Object.keys(metadata).length > 0 ? metadata : undefined
		};
	}

	/**
	 * Evaluate individual campaign rule
	 */
	private evaluateRule(
		rule: any,
		request: AdRequest,
		placement: AdPlacement
	): { block?: boolean; bidAdjustment?: number; metadata?: any } {
		const conditions = rule.conditions as any[];
		const actions = rule.actions as any[];

		// Check all conditions
		for (const condition of conditions) {
			if (!this.evaluateCondition(condition, request, placement)) {
				return {}; // Condition not met, skip rule
			}
		}

		// All conditions met, apply actions
		const result: any = {};

		for (const action of actions) {
			switch (action.type) {
				case 'block_ad':
					result.block = true;
					break;
				case 'adjust_bid':
					result.bidAdjustment = action.parameters.multiplier;
					break;
				case 'set_metadata':
					result.metadata = { ...result.metadata, ...action.parameters };
					break;
			}
		}

		return result;
	}

	/**
	 * Evaluate rule condition
	 */
	private evaluateCondition(condition: any, request: AdRequest, placement: AdPlacement): boolean {
		const { field, operator, value } = condition;

		let fieldValue: any;

		// Extract field value from request
		switch (field) {
			case 'device.type':
				fieldValue = request.deviceInfo.type;
				break;
			case 'geo.region':
				fieldValue = request.geoData.region;
				break;
			case 'user.xpLevel':
				fieldValue = request.userContext.xpLevel;
				break;
			case 'user.dgtBalanceTier':
				fieldValue = request.userContext.dgtBalanceTier;
				break;
			case 'placement.priority':
				fieldValue = placement.priority;
				break;
			default:
				return false;
		}

		// Apply operator
		switch (operator) {
			case 'equals':
				return fieldValue === value;
			case 'not_equals':
				return fieldValue !== value;
			case 'greater_than':
				return Number(fieldValue) > Number(value);
			case 'less_than':
				return Number(fieldValue) < Number(value);
			case 'in':
				return Array.isArray(value) && value.includes(fieldValue);
			case 'contains':
				return Array.isArray(fieldValue) && fieldValue.includes(value);
			default:
				return false;
		}
	}

	/**
	 * Select winning campaign using auction logic
	 */
	private selectWinningCampaign(
		scoredCampaigns: Array<{ campaign: Campaign; score: number; bidPrice: number }>
	): { campaign: Campaign; bidPrice: number } | null {
		if (scoredCampaigns.length === 0) return null;

		// Simple highest score wins (could implement second-price auction)
		return scoredCampaigns[0];
	}

	/**
	 * Generate final ad response
	 */
	private async generateAdResponse(
		winner: { campaign: Campaign; bidPrice: number },
		placement: AdPlacement,
		request: AdRequest
	): Promise<AdResponse> {
		const { campaign, bidPrice } = winner;
		const creativeAssets = campaign.creativeAssets as any;

		// Select appropriate creative based on placement
		const creative = this.selectCreative(creativeAssets, placement);

		// Generate tracking URLs
		const trackingUrls = this.generateTrackingUrls(campaign.id, placement.id, request.sessionId);

		// Calculate DGT reward if applicable
		const dgtReward = this.calculateDgtReward(campaign, request);

		return {
			adId: `ad_${campaign.id}_${Date.now()}`,
			campaignId: campaign.id,
			creative: {
				type: creative.type,
				content: creative.content,
				dimensions: placement.dimensions || '300x250',
				clickUrl: trackingUrls.clickUrl
			},
			tracking: trackingUrls,
			dgtReward,
			metadata: {
				bidPrice,
				currency: 'DGT',
				qualityScore: campaign.qualityScore?.toNumber() || 1.0
			}
		};
	}

	/**
	 * Select appropriate creative for placement
	 */
	private selectCreative(creativeAssets: any[], placement: AdPlacement): any {
		// Find creative that matches placement dimensions
		const matchingCreative = creativeAssets.find(
			(creative) => creative.dimensions === placement.dimensions
		);

		return (
			matchingCreative ||
			creativeAssets[0] || {
				type: 'banner',
				content: '<div>Default Ad</div>',
				dimensions: placement.dimensions
			}
		);
	}

	/**
	 * Generate tracking URLs for analytics
	 */
	private generateTrackingUrls(
		campaignId: string,
		placementId: string,
		sessionId: string
	): { impressionUrl: string; clickUrl: string; conversionUrl?: string } {
		const baseUrl = process.env.DOMAIN || 'https://degentalk.com';
		const trackingParams = `campaign=${campaignId}&placement=${placementId}&session=${sessionId}`;

		return {
			impressionUrl: `${baseUrl}/api/ad/track/impression?${trackingParams}`,
			clickUrl: `${baseUrl}/api/ad/track/click?${trackingParams}`,
			conversionUrl: `${baseUrl}/api/ad/track/conversion?${trackingParams}`
		};
	}

	/**
	 * Calculate DGT reward for user engagement
	 */
	private calculateDgtReward(
		campaign: Campaign,
		request: AdRequest
	): { amount: number; condition: string } | undefined {
		const campaignType = campaign.type;

		if (campaignType === 'user_reward') {
			// User gets DGT for viewing/clicking ads
			const baseReward = 0.1; // 0.1 DGT base
			const xpMultiplier = Math.min(2.0, 1 + (request.userContext.xpLevel || 1) / 100);

			return {
				amount: baseReward * xpMultiplier,
				condition: 'click'
			};
		}

		return undefined;
	}

	/**
	 * Track impression asynchronously to avoid blocking response
	 */
	private async trackImpressionAsync(
		winner: { campaign: Campaign; bidPrice: number },
		placement: AdPlacement,
		request: AdRequest,
		response: AdResponse
	): Promise<void> {
		// Use setTimeout to avoid blocking the response
		setTimeout(async () => {
			try {
				await db.insert(adImpressions).values({
					id: BigInt(Date.now()), // Simple ID generation
					campaignId: winner.campaign.id,
					placementId: placement.id,
					userHash: request.userHash || null,
					sessionId: request.sessionId,
					interactionType: 'impression',
					bidPrice: winner.bidPrice.toString(),
					paidPrice: winner.bidPrice.toString(),
					revenue: (winner.bidPrice * 0.7).toString(), // 70% revenue share
					currency: 'DGT',
					forumSlug: request.forumSlug || null,
					threadId: request.threadId || null,
					deviceInfo: request.deviceInfo,
					geoData: request.geoData,
					dgtRewardAmount: response.dgtReward?.amount?.toString() || null,
					qualityScore: response.metadata.qualityScore.toString()
				});
			} catch (error) {
				logger.error('Failed to track impression:', error);
			}
		}, 0);
	}
}

export const adServingService = new AdServingService();

import { eq, and, sql, desc, count } from 'drizzle-orm';
import { db } from '@server/src/core/db';
import {
	campaigns,
	campaignRules,
	adPlacements,
	cryptoPayments,
	campaignMetrics,
	type Campaign,
	type CampaignRule,
	type CryptoPayment
} from '@schema';

export interface CreateCampaignRequest {
	name: string;
	description?: string;
	type:
		| 'display_banner'
		| 'sponsored_thread'
		| 'forum_spotlight'
		| 'user_reward'
		| 'native_content';
	totalBudget: number;
	dailyBudget?: number;
	paymentMethod: 'dgt_tokens' | 'usdt' | 'bitcoin' | 'ethereum' | 'stripe';
	pricingModel: 'CPM' | 'CPC' | 'CPA' | 'FLAT';
	bidAmount: number;
	startDate?: Date;
	endDate?: Date;
	targetingRules: any;
	placementRules: any;
	frequencyCap: any;
	creativeAssets: any[];
	optimizationGoal?: string;
}

export interface UpdateCampaignRequest extends Partial<CreateCampaignRequest> {
	status?: 'draft' | 'active' | 'paused' | 'completed' | 'cancelled';
}

export interface CampaignAnalytics {
	impressions: number;
	clicks: number;
	conversions: number;
	spend: number;
	revenue: number;
	ctr: number;
	cpm: number;
	cpc: number;
	roas: number;
	dgtRewards: number;
	timeSeriesData: {
		date: string;
		impressions: number;
		clicks: number;
		spend: number;
	}[];
}

/**
 * Campaign Management Service
 * Handles CRUD operations, budget management, and performance optimization
 */
export class CampaignManagementService {
	/**
	 * Create a new advertising campaign
	 */
	async createCampaign(
		advertiserUserId: string,
		request: CreateCampaignRequest
	): Promise<{ campaign: Campaign; paymentRequired?: CryptoPayment }> {
		// Validate campaign configuration
		await this.validateCampaignRequest(request);

		// Create campaign in draft status
		const [campaign] = await db
			.insert(campaigns)
			.values({
				name: request.name,
				description: request.description,
				advertiserUserId,
				type: request.type,
				status: 'draft',
				totalBudget: request.totalBudget.toString(),
				dailyBudget: request.dailyBudget?.toString(),
				paymentMethod: request.paymentMethod,
				pricingModel: request.pricingModel,
				bidAmount: request.bidAmount.toString(),
				startDate: request.startDate,
				endDate: request.endDate,
				targetingRules: request.targetingRules,
				placementRules: request.placementRules,
				frequencyCap: request.frequencyCap,
				creativeAssets: request.creativeAssets,
				optimizationGoal: request.optimizationGoal
			})
			.returning();

		// Handle payment if required
		let paymentRequired: CryptoPayment | undefined;
		if (request.paymentMethod !== 'stripe' && request.totalBudget > 0) {
			paymentRequired = await this.createCryptoPayment(campaign, advertiserUserId);
		}

		return { campaign, paymentRequired };
	}

	/**
	 * Update existing campaign
	 */
	async updateCampaign(
		campaignId: string,
		advertiserUserId: string,
		request: UpdateCampaignRequest
	): Promise<Campaign> {
		// Verify ownership
		await this.verifyCampaignOwnership(campaignId, advertiserUserId);

		// Validate status transitions
		if (request.status) {
			await this.validateStatusTransition(campaignId, request.status);
		}

		const [updatedCampaign] = await db
			.update(campaigns)
			.set({
				...request,
				totalBudget: request.totalBudget?.toString(),
				dailyBudget: request.dailyBudget?.toString(),
				bidAmount: request.bidAmount?.toString(),
				updatedAt: new Date()
			})
			.where(eq(campaigns.id, campaignId))
			.returning();

		return updatedCampaign;
	}

	/**
	 * Get campaign details with analytics
	 */
	async getCampaign(
		campaignId: string,
		advertiserUserId: string
	): Promise<Campaign & { analytics: CampaignAnalytics }> {
		// Verify ownership
		await this.verifyCampaignOwnership(campaignId, advertiserUserId);

		const [campaign] = await db
			.select()
			.from(campaigns)
			.where(eq(campaigns.id, campaignId))
			.limit(1);

		if (!campaign) {
			throw new Error('Campaign not found');
		}

		const analytics = await this.getCampaignAnalytics(campaignId);

		return { ...campaign, analytics };
	}

	/**
	 * List campaigns for advertiser
	 */
	async listCampaigns(
		advertiserUserId: string,
		filters: {
			status?: string;
			type?: string;
			limit?: number;
			offset?: number;
		} = {}
	): Promise<{ campaigns: Campaign[]; total: number }> {
		const { limit = 20, offset = 0 } = filters;

		let query = db.select().from(campaigns).where(eq(campaigns.advertiserUserId, advertiserUserId));

		if (filters.status) {
			query = query.where(eq(campaigns.status, filters.status as any));
		}

		if (filters.type) {
			query = query.where(eq(campaigns.type, filters.type as any));
		}

		const campaignsList = await query
			.orderBy(desc(campaigns.createdAt))
			.limit(limit)
			.offset(offset);

		const [{ total }] = await db
			.select({ total: count() })
			.from(campaigns)
			.where(eq(campaigns.advertiserUserId, advertiserUserId));

		return { campaigns: campaignsList, total };
	}

	/**
	 * Delete campaign (soft delete)
	 */
	async deleteCampaign(campaignId: string, advertiserUserId: string): Promise<void> {
		await this.verifyCampaignOwnership(campaignId, advertiserUserId);

		// Check if campaign is running
		const [campaign] = await db
			.select({ status: campaigns.status })
			.from(campaigns)
			.where(eq(campaigns.id, campaignId))
			.limit(1);

		if (campaign?.status === 'active') {
			throw new Error('Cannot delete active campaign. Pause it first.');
		}

		// Soft delete by setting status to cancelled
		await db
			.update(campaigns)
			.set({ status: 'cancelled', isActive: false, updatedAt: new Date() })
			.where(eq(campaigns.id, campaignId));
	}

	/**
	 * Create campaign rule for dynamic configuration
	 */
	async createCampaignRule(
		campaignId: string,
		advertiserUserId: string,
		rule: {
			name: string;
			description?: string;
			ruleType: string;
			conditions: any[];
			actions: any[];
			priority?: number;
			validFrom?: Date;
			validUntil?: Date;
		}
	): Promise<CampaignRule> {
		await this.verifyCampaignOwnership(campaignId, advertiserUserId);

		const [campaignRule] = await db
			.insert(campaignRules)
			.values({
				name: rule.name,
				description: rule.description,
				campaignId,
				ruleType: rule.ruleType,
				conditions: rule.conditions,
				actions: rule.actions,
				priority: rule.priority || 1,
				validFrom: rule.validFrom,
				validUntil: rule.validUntil
			})
			.returning();

		return campaignRule;
	}

	/**
	 * Get campaign analytics
	 */
	async getCampaignAnalytics(
		campaignId: string,
		timeRange?: { from: Date; to: Date }
	): Promise<CampaignAnalytics> {
		const from = timeRange?.from || new Date(Date.now() - 30 * 24 * 60 * 60 * 1000); // 30 days ago
		const to = timeRange?.to || new Date();

		// Get aggregated metrics
		const [metricsResult] = await db
			.select({
				impressions: sql<number>`COALESCE(SUM(${campaignMetrics.impressions}), 0)`,
				clicks: sql<number>`COALESCE(SUM(${campaignMetrics.clicks}), 0)`,
				conversions: sql<number>`COALESCE(SUM(${campaignMetrics.conversions}), 0)`,
				spend: sql<number>`COALESCE(SUM(${campaignMetrics.spend}), 0)`,
				revenue: sql<number>`COALESCE(SUM(${campaignMetrics.revenue}), 0)`,
				dgtRewards: sql<number>`COALESCE(SUM(${campaignMetrics.dgtRewards}), 0)`
			})
			.from(campaignMetrics)
			.where(
				and(
					eq(campaignMetrics.campaignId, campaignId),
					sql`${campaignMetrics.dateHour} >= ${from}`,
					sql`${campaignMetrics.dateHour} <= ${to}`
				)
			);

		// Calculate derived metrics
		const ctr =
			metricsResult.impressions > 0 ? metricsResult.clicks / metricsResult.impressions : 0;
		const cpm =
			metricsResult.impressions > 0 ? (metricsResult.spend / metricsResult.impressions) * 1000 : 0;
		const cpc = metricsResult.clicks > 0 ? metricsResult.spend / metricsResult.clicks : 0;
		const roas = metricsResult.spend > 0 ? metricsResult.revenue / metricsResult.spend : 0;

		// Get time series data (daily aggregation)
		const timeSeriesData = await db
			.select({
				date: sql<string>`DATE(${campaignMetrics.dateHour})`,
				impressions: sql<number>`SUM(${campaignMetrics.impressions})`,
				clicks: sql<number>`SUM(${campaignMetrics.clicks})`,
				spend: sql<number>`SUM(${campaignMetrics.spend})`
			})
			.from(campaignMetrics)
			.where(
				and(
					eq(campaignMetrics.campaignId, campaignId),
					sql`${campaignMetrics.dateHour} >= ${from}`,
					sql`${campaignMetrics.dateHour} <= ${to}`
				)
			)
			.groupBy(sql`DATE(${campaignMetrics.dateHour})`)
			.orderBy(sql`DATE(${campaignMetrics.dateHour})`);

		return {
			...metricsResult,
			ctr,
			cpm,
			cpc,
			roas,
			timeSeriesData
		};
	}

	/**
	 * Get bid recommendations based on performance data
	 */
	async getBidRecommendations(campaignId: string): Promise<{
		currentBid: Id<'currentBid'>;
		recommendedBid: Id<'recommendedBid'>;
		reasoning: string;
		expectedImprovement: {
			impressions: number;
			clicks: number;
			conversions: number;
		};
	}> {
		const [campaign] = await db
			.select()
			.from(campaigns)
			.where(eq(campaigns.id, campaignId))
			.limit(1);

		if (!campaign) {
			throw new Error('Campaign not found');
		}

		const analytics = await this.getCampaignAnalytics(campaignId);
		const currentBid = campaign.bidAmount?.toNumber() || 0;

		// Simple bid optimization logic
		let recommendedBid = currentBid;
		let reasoning = 'Current bid is optimal';

		if (analytics.ctr > 0.02) {
			// High CTR, can afford to bid higher
			recommendedBid = currentBid * 1.2;
			reasoning = 'High click-through rate suggests you can bid higher to get more volume';
		} else if (analytics.ctr < 0.005) {
			// Low CTR, need to improve targeting or creative
			recommendedBid = currentBid * 0.8;
			reasoning = 'Low click-through rate suggests reducing bid or improving targeting';
		}

		// Cap bid adjustments
		recommendedBid = Math.max(0.01, Math.min(currentBid * 2, recommendedBid));

		return {
			currentBid,
			recommendedBid: Math.round(recommendedBid * 100) / 100,
			reasoning,
			expectedImprovement: {
				impressions: Math.round((recommendedBid / currentBid - 1) * analytics.impressions),
				clicks: Math.round((recommendedBid / currentBid - 1) * analytics.clicks * 0.8),
				conversions: Math.round((recommendedBid / currentBid - 1) * analytics.conversions * 0.6)
			}
		};
	}

	/**
	 * Optimize campaign performance automatically
	 */
	async optimizeCampaign(
		campaignId: string,
		advertiserUserId: string
	): Promise<{
		changes: string[];
		expectedImprovement: string;
	}> {
		await this.verifyCampaignOwnership(campaignId, advertiserUserId);

		const analytics = await this.getCampaignAnalytics(campaignId);
		const bidRecommendation = await this.getBidRecommendations(campaignId);

		const changes: string[] = [];

		// Apply bid optimization
		if (Math.abs(bidRecommendation.recommendedBid - bidRecommendation.currentBid) > 0.01) {
			await db
				.update(campaigns)
				.set({
					bidAmount: bidRecommendation.recommendedBid.toString(),
					updatedAt: new Date()
				})
				.where(eq(campaigns.id, campaignId));

			changes.push(
				`Adjusted bid from $${bidRecommendation.currentBid} to $${bidRecommendation.recommendedBid}`
			);
		}

		// Pause underperforming campaigns
		if (analytics.spend > 50 && analytics.roas < 0.5) {
			await db
				.update(campaigns)
				.set({ status: 'paused', updatedAt: new Date() })
				.where(eq(campaigns.id, campaignId));

			changes.push('Paused campaign due to poor performance (ROAS < 0.5)');
		}

		return {
			changes,
			expectedImprovement: bidRecommendation.reasoning
		};
	}

	/**
	 * Private helper methods
	 */
	private async validateCampaignRequest(request: CreateCampaignRequest): Promise<void> {
		if (!request.name?.trim()) {
			throw new Error('Campaign name is required');
		}

		if (request.totalBudget <= 0) {
			throw new Error('Total budget must be greater than 0');
		}

		if (request.bidAmount <= 0) {
			throw new Error('Bid amount must be greater than 0');
		}

		if (request.dailyBudget && request.dailyBudget > request.totalBudget) {
			throw new Error('Daily budget cannot exceed total budget');
		}

		if (request.endDate && request.startDate && request.endDate <= request.startDate) {
			throw new Error('End date must be after start date');
		}

		// Validate creative assets
		if (!request.creativeAssets || request.creativeAssets.length === 0) {
			throw new Error('At least one creative asset is required');
		}
	}

	private async verifyCampaignOwnership(
		campaignId: string,
		advertiserUserId: string
	): Promise<void> {
		const [campaign] = await db
			.select({ advertiserUserId: campaigns.advertiserUserId })
			.from(campaigns)
			.where(eq(campaigns.id, campaignId))
			.limit(1);

		if (!campaign || campaign.advertiserUserId !== advertiserUserId) {
			throw new Error('Campaign not found or access denied');
		}
	}

	private async validateStatusTransition(campaignId: string, newStatus: string): Promise<void> {
		const [campaign] = await db
			.select({ status: campaigns.status })
			.from(campaigns)
			.where(eq(campaigns.id, campaignId))
			.limit(1);

		if (!campaign) {
			throw new Error('Campaign not found');
		}

		const currentStatus = campaign.status;

		// Define valid transitions
		const validTransitions: Record<string, string[]> = {
			draft: ['active', 'cancelled'],
			active: ['paused', 'completed', 'cancelled'],
			paused: ['active', 'cancelled'],
			completed: [],
			cancelled: []
		};

		if (!validTransitions[currentStatus]?.includes(newStatus)) {
			throw new Error(`Cannot transition from ${currentStatus} to ${newStatus}`);
		}
	}

	private async createCryptoPayment(
		campaign: Campaign,
		advertiserUserId: string
	): Promise<CryptoPayment> {
		const [payment] = await db
			.insert(cryptoPayments)
			.values({
				paymentHash: `payment_${campaign.id}_${Date.now()}`,
				campaignId: campaign.id,
				payerUserId: advertiserUserId,
				currency: campaign.paymentMethod === 'dgt_tokens' ? 'DGT' : 'USDT',
				amount: campaign.totalBudget!.toString(),
				status: 'pending',
				requiredConfirmations: campaign.paymentMethod === 'dgt_tokens' ? 1 : 3,
				expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000) // 24 hours
			})
			.returning();

		return payment;
	}
}

