	async getPendingPromotions(
		filters: {
			type?: string;
			limit?: number;
			offset?: number;
		} = {}
	): Promise<UserPromotion[]> {
		let query = db.select().from(userPromotions).where(eq(userPromotions.status, 'pending'));

		if (filters.type) {
			query = query.where(eq(userPromotions.type, filters.type));
