/**
 * Handle user registration
 */
export async function register(req: Request, res: Response, next: NextFunction) {
	try {
		// Validate registration data
		const registerSchema = insertUserSchema
			.extend({
				confirmPassword: z.string()
			})
			.refine((data) => data.password === data.confirmPassword, {
				message: "Passwords don't match",
				path: ['confirmPassword']
			});

		const validatedData = registerSchema.parse(req.body);
		const { confirmPassword, ...userData } = validatedData as any;

		// Check if username already exists
		const existingUser = await storage.getUserByUsername(userData.username);
		if (existingUser) {
			return res.status(400).json({ message: 'Username already exists' });
		}

		// Store temporary dev metadata if beta tools are enabled
		const tempDevMetadata = await storeTempDevMetadata(userData.password);

		// Create the user with hashed password
		const user = await storage.createUser({
			...userData,
			password: await hashPassword(userData.password),
			tempDevMeta: tempDevMetadata,
			isActive: isDevMode() ? true : false // Automatically active in dev mode
		});

		// Initialize wallet for new user
		if (walletConfig.WALLET_ENABLED) {
			try {
				// Initialize DGT wallet
				await dgtService.initializeUserWallet(user.id);
				logger.info('AuthController', 'DGT wallet initialized for new user', { userId: user.id });

				// Initialize CCPayment wallet
				const ccpaymentId = await walletService.ensureCcPaymentWallet(user.id);
				logger.info('AuthController', 'CCPayment wallet initialized for new user', {
					userId: user.id,
					ccpaymentId
				});
			} catch (walletError) {
				logger.error('AuthController', 'Error initializing wallet for new user', {
					err: walletError,
					userId: user.id
				});
				// Continue with registration even if wallet creation fails
			}
		}

		// Create default settings for the new user
		try {
			// TODO: Implement or verify createDefaultSettings functionality
			// Import the settings service
			// const { createDefaultSettings } = await import('../../admin/sub-domains/settings/settings.service');
			// await createDefaultSettings(user.id);
			logger.info(
				'AuthController',
				'Skipping default settings creation for new user - to be implemented/verified.',
				{ userId: user.id }
			);
		} catch (settingsError) {
			logger.error('AuthController', 'Error during (attempted) default user settings creation', {
				err: settingsError,
				userId: user.id
			});
			// Continue with registration even if settings creation fails
		}

		// In dev mode, we skip verification
		if (isDevMode()) {
			return res.status(201).json({
				message: 'Registration successful in development mode. User is automatically activated.',
				devMode: true
			});
		}

		// Generate verification token
		const verificationToken = randomBytes(20).toString('hex');
		// Store token in database (replace with secure token storage later)
		await storage.storeVerificationToken(user.id, verificationToken);

		// Send verification email (replace with actual email sending logic)
		logger.info('AuthController', `Verification email sent to ${userData.email}`, {
			email: userData.email,
			token: verificationToken
		});

		res.status(201).json({
			message: 'Registration successful. Please check your email to verify your account.'
		});
	} catch (err) {
		if (err instanceof z.ZodError) {
			return res.status(400).json({
				message: 'Validation error',
				errors: err.errors
			});
		}
		next(err);
	}
}

/**
 * Handle user login
 */
export function login(req: Request, res: Response, next: NextFunction) {
	logger.info('Login attempt', { username: req.body.username });
	passport.authenticate('local', (err: Error, user: any, info: any) => {
		logger.debug('Passport authenticate callback', { hasError: !!err, hasUser: !!user, info });
		if (err) {
			logger.error('Authentication error', err);
			return next(err);
		}
		if (!user) {
			logger.warn('Authentication failed', { message: info?.message || 'No user returned' });
			return res.status(401).json({ message: info?.message || 'Authentication failed' });
		}

		logger.info('User authenticated', { username: user.username, userId: user.id });
		req.login(user, async (err) => {
			if (err) {
				logger.error('Login session error', err);
				return next(err);
			}

			// Ensure wallet is initialized for existing users
			if (walletConfig.WALLET_ENABLED && user.id) {
				try {
					// Initialize DGT wallet if needed
					await dgtService.initializeUserWallet(user.id);

					// Initialize CCPayment wallet if needed
					await walletService.ensureCcPaymentWallet(user.id);
				} catch (walletError) {
					logger.error('AuthController', 'Error ensuring wallet for user login', {
						err: walletError,
						userId: user.id
					});
					// Continue with login even if wallet check fails
				}
			}

			// Remove password from response
			const userResponse = { ...user };
			delete userResponse.password;

			res.status(200).json(userResponse);
		});
	})(req, res, next);
}

/**
 * Handle user logout
 */
export function logout(req: Request, res: Response, next: NextFunction) {
	req.logout((err) => {
		if (err) return next(err);
		res.sendStatus(200);
	});
}

/**
 * Get current user profile
 */
export function getCurrentUser(req: Request, res: Response) {
	if (!req.isAuthenticated()) return res.sendStatus(401);

	// Remove password from response
	const userResponse = { ...(userService.getUserFromRequest(req) as any) };
	delete userResponse.password;

	// Add computed role properties for frontend compatibility
	userResponse.isAdmin = ['admin', 'super_admin'].includes(userResponse.role);
	userResponse.isModerator = ['moderator', 'mod'].includes(userResponse.role);
	userResponse.isSuperAdmin = userResponse.role === 'super_admin';

	res.json(UserTransformer.toAuthenticatedSelf(userResponse));
}

/**
 * Handle email verification
 */
export async function verifyEmail(req: Request, res: Response, next: NextFunction) {
	try {
		const { token } = req.query;

		if (!token || typeof token !== 'string') {
			return res.status(400).json({ message: 'Invalid verification token' });
		}

		// Find and validate token
		const isValid = await verifyEmailToken(token);

		if (!isValid) {
			return res.status(400).json({
				message: 'Invalid or expired verification token. Please request a new one.'
			});
		}

		// Activate the user account
		const userId = isValid.userId;
		await storage.updateUser(userId, { isActive: true });

		return res.status(200).json({
			message: 'Email verified successfully. You can now log in to your account.'
		});
	} catch (err) {
		next(err);
	}
}

/**
 * Resend verification email
 */
export async function resendVerification(req: Request, res: Response, next: NextFunction) {
	try {
		const { email } = req.body;

		if (!email) {
			return res.status(400).json({ message: 'Email is required' });
		}

		// Find user by email
		const user = await storage.getUserByEmail(email);
		if (!user) {
			// For security reasons, don't reveal if email exists or not
			return res.status(200).json({
				message:
					'If your email exists in our system, you will receive a verification email shortly.'
			});
		}

		// Check if account is already active
		if (user.isActive) {
			return res.status(400).json({
				message: 'This account is already active. Please try logging in.'
			});
		}

		// Generate new verification token
		const verificationToken = randomBytes(20).toString('hex');

		// Store the new token
		await storage.storeVerificationToken(user.id, verificationToken);

		// Send verification email (replace with actual email sending logic)
		logger.info('AuthController', `Verification email re-sent to ${email}`, {
			email,
			token: verificationToken
		});

		res.status(200).json({
			message: 'If your email exists in our system, you will receive a verification email shortly.'
		});
	} catch (err) {
		next(err);
	}
}

import { userService } from '@server/src/core/services/user.service';
import type { Request, Response, NextFunction } from 'express';
import { env, isDevelopment, isProduction } from '@server/src/core/config/environment';
import { createMockUser } from '../services/auth.service';
import { logger } from '@server/src/core/logger';
import { shouldBypassAuth } from '@server/src/utils/environment';

/**
 * Determines if a user has a specific role
 */
export function hasRole(req: Request, role: string): boolean {
	if (!userService.getUserFromRequest(req)) return false;
	return (userService.getUserFromRequest(req) as any).role === role;
}

/**
 * Authentication middleware that enforces login requirement
 * Secure development bypass with strict production protection
 */
export function isAuthenticated(req: Request, res: Response, next: NextFunction) {
	if (req.isAuthenticated()) {
		return next();
	}

	// SECURITY: Strict production check - no bypass allowed
	if (isProduction()) {
		return res.status(401).json({
			message: 'Unauthorized',
			error: 'PRODUCTION_AUTH_REQUIRED'
		});
	}

	// Development mode bypass with strict validation
	if (isDevelopment() && env.DEV_BYPASS_PASSWORD && !env.DEV_FORCE_AUTH) {
		logger.warn('AuthMiddleware', 'DEVELOPMENT: Bypassing authentication', {
			path: req.path,
			ip: req.ip,
			userAgent: req.get('User-Agent')
		});

		// Validate development role
		const devRole = (req.headers['x-dev-role'] as string) || 'user';
		const validRoles = ['user', 'moderator', 'admin'];

		if (!validRoles.includes(devRole)) {
			return res.status(400).json({
				message: 'Invalid development role',
				validRoles
			});
		}

		const roleId = req.headers['x-dev-role-id']
			? parseInt(req.headers['x-dev-role-id'] as string, 10)
			: devRole === 'admin'
				? 1
				: devRole === 'moderator'
					? 2
					: 3;

		// Create mock user with audit trail
		(req as any).user = createMockUser(roleId, devRole as any);

		// Add development flag to request for audit purposes
		(req as any).isDevelopmentBypass = true;

		return next();
	}

	res.status(401).json({
		message: 'Unauthorized',
		hint: isDevelopment() ? 'Set DEV_BYPASS_PASSWORD=true for development bypass' : undefined
	});
}

/**
 * Similar to isAuthenticated, but allows non-authenticated requests to pass through
 * In development mode, it will create a mock user if auth bypassing is enabled
 */
export function isAuthenticatedOptional(req: Request, res: Response, next: NextFunction) {
	if (req.isAuthenticated()) {
		return next();
	}

	// Check if we should bypass auth in development mode
	if (shouldBypassAuth()) {
		logger.info('AuthMiddleware', 'Bypassing optional authentication in development mode', {
			path: req.path
		});
		// Get the dev mode role from headers or default to 'user'
		const devRole = (req.headers['x-dev-role'] as string) || 'user';
		const roleId = req.headers['x-dev-role-id']
			? parseInt(req.headers['x-dev-role-id'] as string, 10)
			: devRole === 'admin'
				? 1
				: devRole === 'moderator'
					? 2
					: 3;

		// Create a mock user with the specified role
		(req as any).user = createMockUser(roleId, devRole as any);
	}

	// Allow request to proceed even if not authenticated
	return next();
}

/**
 * Admin role middleware - requires admin privileges
 * Secure development bypass with strict production protection
 */
export function isAdmin(req: Request, res: Response, next: NextFunction) {
	// Check if user has admin role
	if (hasRole(req, 'admin')) {
		return next();
	}

	// SECURITY: Strict production check - no bypass allowed
	if (isProduction()) {
		logger.warn('AuthMiddleware', 'PRODUCTION: Admin access denied', {
			path: req.path,
			userId: (userService.getUserFromRequest(req) as any)?.id,
			userRole: (userService.getUserFromRequest(req) as any)?.role,
			ip: req.ip
		});
		return res.status(403).json({
			message: 'Forbidden - Admin access required',
			error: 'PRODUCTION_ADMIN_REQUIRED'
		});
	}

	// Development mode bypass with strict validation
	if (isDevelopment() && env.DEV_BYPASS_PASSWORD && req.headers['x-dev-role'] === 'admin') {
		logger.warn('AuthMiddleware', 'DEVELOPMENT: Bypassing admin authentication', {
			path: req.path,
			ip: req.ip,
			userAgent: req.get('User-Agent')
		});

		const roleId = req.headers['x-dev-role-id']
			? parseInt(req.headers['x-dev-role-id'] as string, 10)
			: 1;

		(req as any).user = createMockUser(roleId, 'admin');
		(req as any).isDevelopmentBypass = true;

		return next();
	}

	res.status(403).json({
		message: 'Forbidden - Admin access required',
		hint: isDevelopment() ? 'Set x-dev-role header to "admin" for development bypass' : undefined
	});
}

/**
 * Moderator role middleware - requires moderator or admin privileges
 */
export function isModerator(req: Request, res: Response, next: NextFunction) {
	if (hasRole(req, 'moderator') || hasRole(req, 'admin')) {
		return next();
	}

	// Check if we should bypass auth in development mode with moderator role
	if (
		shouldBypassAuth() &&
		(req.headers['x-dev-role'] === 'moderator' || req.headers['x-dev-role'] === 'admin')
	) {
		logger.info('AuthMiddleware', 'Bypassing moderator authentication in development mode', {
			path: req.path,
			devRoleHeader: req.headers['x-dev-role']
		});
		const devRole = req.headers['x-dev-role'] as 'moderator' | 'admin';
		const roleId = req.headers['x-dev-role-id']
			? parseInt(req.headers['x-dev-role-id'] as string, 10)
			: devRole === 'admin'
				? 1
				: 2;

		(req as any).user = createMockUser(roleId, devRole);
		return next();
	}

	res.status(403).json({ message: 'Forbidden' });
}

/**
 * Middleware for routes that should be accessible by either admins or moderators
 */
export function isAdminOrModerator(req: Request, res: Response, next: NextFunction) {
	// For compatibility with existing code, this is a combination function
	return isModerator(req, res, next);
}

/**
 * Middleware for handling developer mode authentication switching
 * SECURITY: Only available in development with bypass enabled
 */
export function devModeAuthHandler(req: Request, res: Response, next: NextFunction) {
	// SECURITY: Strict production check
	if (isProduction()) {
		return res.status(404).json({ message: 'Not found' });
	}

	// Only available in development mode with bypass enabled
	if (!isDevelopment() || !env.DEV_BYPASS_PASSWORD) {
		return res.status(404).json({ message: 'Not found' });
	}

	// Log security-sensitive operation
	logger.warn('AuthMiddleware', 'DEVELOPMENT: Role switching requested', {
		ip: req.ip,
		userAgent: req.get('User-Agent'),
		requestedRole: req.query.role
	});

	// Get the requested role from the query parameters
	const role = (req.query.role as string) || 'user';
	const validRoles = ['user', 'moderator', 'admin'];

	if (!validRoles.includes(role)) {
		return res.status(400).json({
			message: 'Invalid role',
			validRoles
		});
	}

	// Set a session cookie with the selected role
	(req.session as any).devRole = role;

	res.json({
		message: `Development mode authentication set to: ${role}`,
		role: role,
		warning: 'DEVELOPMENT ONLY - This endpoint is disabled in production'
	});
}

/**
 * CCPayment Webhook Controller
 *
 * This controller handles webhook requests from CCPayment.
 * It verifies the webhook signature and passes verified events to the service.
 */

import type { Request, Response } from 'express';
import { logger } from '../../core/logger';
import { ccpaymentService } from '../wallet/ccpayment.service';
import { ccpaymentWebhookService } from './ccpayment-webhook.service';
import { WalletError, ErrorCodes as WalletErrorCodes } from '../../core/errors';
import crypto from 'crypto';

/**
 * CCPayment webhook controller
 */
export class CCPaymentWebhookController {
	/**
	 * Handle webhook POST request from CCPayment
	 * @param req Express request
	 * @param res Express response
	 */
	async handleWebhook(req: Request, res: Response): Promise<void> {
		try {
			logger.info('Received CCPayment webhook', {
				headers: {
					signature: req.header('X-Signature') ? 'present' : 'missing',
					timestamp: req.header('X-Timestamp') || 'missing',
					appId: req.header('X-App-Id') || 'missing'
				}
			});

			// Extract signature headers
			const signature = req.header('X-Signature');
			const timestamp = req.header('X-Timestamp');
			const appId = req.header('X-App-Id');

			// Verify required headers are present
			if (!signature || !timestamp || !appId) {
				logger.warn('Missing required webhook headers');
				res.status(400).json({
					success: false,
					message: 'Missing required webhook headers'
				});
				return;
			}

			// Verify webhook signature
			const isValid = ccpaymentService.verifyWebhookSignature(req.body, signature, timestamp);

			if (!isValid) {
				logger.warn('Invalid webhook signature');
				res.status(401).json({
					success: false,
					message: 'Invalid webhook signature'
				});
				return;
			}

			// Process the webhook event
			const webhookEvent = req.body;

			// Log the webhook event
			logger.info('Processing verified webhook event', {
				eventType: webhookEvent.eventType,
				orderId: webhookEvent.orderId,
				merchantOrderId: webhookEvent.merchantOrderId,
				status: webhookEvent.status
			});

			// Process the event (asynchronously, but don't wait for completion)
			// This allows us to respond quickly to CCPayment while processing in the background
			const processPromise = ccpaymentWebhookService.processWebhookEvent(webhookEvent);

			// Immediately respond to the webhook
			res.status(200).json({
				success: true,
				message: 'Webhook received and validated'
			});

			// Wait for processing to complete (for logging purposes)
			const result = await processPromise;
			logger.info('Webhook processing completed', result);
		} catch (error) {
			logger.error('Error handling webhook', error);

			// Still respond with 200 to CCPayment to prevent retries
			// CCPayment expects 200 OK even if processing fails
			res.status(200).json({
				success: false,
				message: 'Webhook received but processing failed'
			});
		}
	}
}

