			operation: 'UPLOAD_STICKER_FILE',
			entityType: 'sticker'
		});

		return boundary.execute(async () => {
			// Parse upload parameters from request body
			const uploadSchema = z.object({
				fileName: z.string().min(1),
				fileType: z.string().min(1),
				fileSize: z.number().positive(),
				uploadType: z.enum([
					'sticker_static',
