			res.json({
				success: true,
				data: transformedReplies
			});
		} catch (error) {
			logger.error('PostRoutes', 'Error in GET /posts/:postId/replies', { error });
			res.status(500).json({
				success: false,
				error: 'Failed to fetch post replies'
			});
		}
	})
);

