	forumRules,
	userRulesAgreements,
	contentEditStatusEnum,
	type ForumRule,
	type UserRulesAgreement,
	type User
} from '@schema';
import crypto from 'crypto';
import { z } from 'zod';
import { isAuthenticated } from '../../auth/middleware/auth.middleware';
import { storage } from '../../../../storage';
import { asyncHandler } from '@server/src/core/errors'; // Assuming asyncHandler is in core errors
import { getUserIdFromRequest } from '@server/src/utils/auth';
import { logger } from "../../../core/logger";

const router = Router();

// Get all published forum rules
router.get(
	'/',
	asyncHandler(async (req: Request, res: Response) => {
		try {
			const { section, status } = req.query;

			// Build query conditionally based on request
			let query = db.select().from(forumRules);

			// Filter by status (default to 'published' if not specified)
			const ruleStatus = (status as string) || 'published';
			if (ruleStatus && ruleStatus in contentEditStatusEnum.enumValues) {
				query = query.where(eq(forumRules.status, ruleStatus as any));
			}

			// Filter by section if provided
			if (section) {
				query = query.where(eq(forumRules.section, section as string));
			}

			// Execute query and sort by position
			const rules = await query.orderBy(forumRules.position);

			return res.status(200).json({
				data: rules,
				count: rules.length
			});
		} catch (error) {
			logger.error('Error fetching forum rules:', error);
			return res.status(500).json({ error: 'Failed to fetch forum rules' });
		}
	})
);

// Get a specific rule
router.get(
	'/:id',
	asyncHandler(async (req: Request, res: Response) => {
		try {
			const ruleId = req.params.id;
			if (!ruleId || typeof ruleId !== 'string') {
				return res.status(400).json({ error: 'Invalid rule ID' });
			}

			const rule = await storage.getForumRule(ruleId);

			if (!rule) {
				return res.status(404).json({ error: 'Rule not found' });
			}

			// Only allow published rules to be viewed, unless the user is an admin
			if (
				rule.status !== 'published' &&
				(!userService.getUserFromRequest(req) ||
					(userService.getUserFromRequest(req) as any).role !== 'admin')
			) {
				// Fixed isAdmin check
				return res.status(403).json({ error: 'This rule is not published' });
			}

			return res.status(200).json({ rule });
		} catch (error) {
			logger.error('Error fetching forum rule:', error);
			return res.status(500).json({ error: 'Failed to fetch forum rule' });
		}
	})
);

// Get user agreements (requires authentication)
router.get(
	'/user-agreements',
	asyncHandler(async (req: Request, res: Response) => {
		if (!req.isAuthenticated() || !userService.getUserFromRequest(req)) {
			return res.status(401).json({ error: 'Authentication required' });
		}

		try {
			const userId = getUserIdFromRequest(req);
			if (userId === undefined) {
				// This case should ideally be caught by !req.isAuthenticated() already,
				// but as a safeguard if req.user exists but ID doesn't.
				return res.status(401).json({ error: 'User ID not found after authentication' });
			}

			// Get all user rule agreements
			const agreements = await db
				.select()
				.from(userRulesAgreements)
				.where(eq(userRulesAgreements.userId, userId))
				.orderBy(desc(userRulesAgreements.agreedAt));

			// Get all published rules
			const rules = await db.select().from(forumRules).where(eq(forumRules.status, 'published'));

			// Find which rules require agreement
			const requiredRules = rules.filter((rule: ForumRule) => rule.isRequired);

			// Check which required rules have been agreed to
			const agreedRequiredRuleIds = agreements
				.filter((agreement: UserRulesAgreement) => {
					const rule = rules.find((r: ForumRule) => r.id === agreement.ruleId);
					return rule && rule.isRequired;
				})
				.map((agreement: UserRulesAgreement) => agreement.ruleId);

			const allRequiredRulesAgreed = requiredRules.every((rule: ForumRule) =>
				agreedRequiredRuleIds.includes(rule.id)
			);

			return res.status(200).json({
				agreements,
				requiredRules,
				allRequiredRulesAgreed,
				requiresAction: !allRequiredRulesAgreed
			});
		} catch (error) {
			logger.error('Error fetching user rule agreements:', error);
			return res.status(500).json({ error: 'Failed to fetch user rule agreements' });
		}
	})
);

// Agree to rules (requires authentication)
router.post(
	'/agree',
	asyncHandler(async (req: Request, res: Response) => {
		if (!req.isAuthenticated() || !userService.getUserFromRequest(req)) {
			return res.status(401).json({ error: 'Authentication required' });
		}

		// Validate request body using Zod
		const agreeSchema = z.object({
			ruleIds: z.array(z.string().uuid('Invalid ruleId format'))
		});

		const parsed = agreeSchema.safeParse(req.body);
		if (!parsed.success) {
			return res.status(400).json({
				error: 'Invalid request body',
				details: parsed.error.issues
			});
		}

		const { ruleIds } = parsed.data;

		try {
			const userId = getUserIdFromRequest(req);
			if (userId === undefined) {
				// This case should ideally be caught by !req.isAuthenticated() already.
				return res.status(401).json({ error: 'User ID not found after authentication' });
			}

			// Get rules
			const rules = await db
				.select()
				.from(forumRules)
				.where(and(inArray(forumRules.id, ruleIds), eq(forumRules.status, 'published')));

			if (rules.length !== ruleIds.length) {
				return res.status(404).json({
					error: 'One or more rules not found or not published'
				});
			}

			// Get existing agreements
			const existingAgreements = await db
				.select()
				.from(userRulesAgreements)
				.where(
					and(eq(userRulesAgreements.userId, userId), inArray(userRulesAgreements.ruleId, ruleIds))
				);

			// Create map of existing agreements for faster lookup
			const existingAgreementMap = new Map<number, UserRulesAgreement>();
			existingAgreements.forEach((agreement: UserRulesAgreement) => {
				existingAgreementMap.set(agreement.ruleId, agreement);
			});

			// Process each rule agreement
			for (const rule of rules) {
				const existingAgreement = existingAgreementMap.get(rule.id);

				// If agreement already exists and hash is the same, skip
				if (existingAgreement && existingAgreement.versionHash === rule.versionHash) {
					continue;
				}

				// If agreement exists but hash is different (rule was updated), update the agreement
				if (existingAgreement) {
					await db
						.update(userRulesAgreements)
						.set({
							versionHash: rule.versionHash,
							agreedAt: new Date()
						})
						.where(
							and(
								eq(userRulesAgreements.userId, existingAgreement.userId),
								eq(userRulesAgreements.ruleId, existingAgreement.ruleId)
							)
						);

					// Also update the rule's lastAgreedVersionHash if not already set
					if (!rule.lastAgreedVersionHash) {
						await db
							.update(forumRules)
							.set({ lastAgreedVersionHash: rule.versionHash })
							.where(eq(forumRules.id, rule.id));
					}
				}
				// If no agreement exists, create a new one
				else {
					await db.insert(userRulesAgreements).values({
						userId,
						ruleId: rule.id,
						versionHash: rule.versionHash,
						agreedAt: new Date()
					});

					// Also update the rule's lastAgreedVersionHash if not already set
					if (!rule.lastAgreedVersionHash) {
						await db
							.update(forumRules)
							.set({ lastAgreedVersionHash: rule.versionHash })
							.where(eq(forumRules.id, rule.id));
					}
				}
			}

			return res.status(200).json({
				success: true,
				message: 'Successfully agreed to rules',
				ruleIds
			});
		} catch (error) {
			logger.error('Error agreeing to rules:', error);
			return res.status(500).json({ error: 'Failed to process rule agreements' });
		}
	})
);

export default router;

/**
 * Forum Category Service
 *
 * QUALITY IMPROVEMENT: Extracted from forum.service.ts god object
 * Handles category-specific operations with proper separation of concerns
 */

import { db } from '@db';
import { logger } from '@server/src/core/logger';
import { forumStructure, threads, posts, users as usersTable } from '@schema';
import { sql, desc, eq, count, isNull } from 'drizzle-orm';
import type { ForumCategoryWithStats } from '../../../../db/types/forum.types';
import type { CategoryId } from '@shared/types/ids';

// Simple in-memory cache for categories
const CACHE_DURATION_MS = 30 * 1000; // 30 seconds
let categoriesCache: {
	timestamp: number;
	data: ForumCategoryWithStats[];
} | null = null;

export class CategoryService {
	/**
	 * Get all categories with forum statistics
	 */
	async getCategoriesWithStats(): Promise<ForumCategoryWithStats[]> {
		try {
			// Check cache first
			if (categoriesCache && Date.now() - categoriesCache.timestamp < CACHE_DURATION_MS) {
				logger.info('CategoryService', 'Returning cached categories');
				return categoriesCache.data;
			}

			logger.info('CategoryService', 'Fetching categories with stats from database');

			// Simplified query without complex joins that might be causing issues
			const categoriesWithStats = await db
				.select({
					id: forumStructure.id,
					name: forumStructure.name,
					slug: forumStructure.slug,
					description: forumStructure.description,
					type: forumStructure.type,
					position: forumStructure.position,
					isVisible: sql<boolean>`NOT ${forumStructure.isHidden}`,
					parentId: forumStructure.parentId,
					threadCount: sql<number>`0`, // Simplified for now
					postCount: sql<number>`0`, // Simplified for now
					lastPostAt: sql<Date | null>`NULL`,
					createdAt: forumStructure.createdAt,
					updatedAt: forumStructure.updatedAt,
					pluginData: forumStructure.pluginData
				})
				.from(forumStructure)
				.orderBy(forumStructure.position, forumStructure.name);

			// Update cache
			categoriesCache = {
				timestamp: Date.now(),
				data: categoriesWithStats
			};

			logger.info(
				'CategoryService',
				`Successfully fetched ${categoriesWithStats.length} categories`
			);
			return categoriesWithStats;
		} catch (error) {
			logger.error('CategoryService', 'Error fetching categories with stats', { error });
			throw error;
		}
	}

	/**
	 * Get hierarchical category tree
	 */
	async getCategoriesTree(
		options: {
			includeHidden?: boolean;
			includeEmptyStats?: boolean;
		} = {}
	): Promise<ForumCategoryWithStats[]> {
		try {
			const { includeHidden = false } = options;

			const baseQuery = db
				.select({
					id: forumStructure.id,
					name: forumStructure.name,
					slug: forumStructure.slug,
					description: forumStructure.description,
					type: forumStructure.type,
					position: forumStructure.position,
					isHidden: forumStructure.isHidden,
					parentId: forumStructure.parentId,
					threadCount: sql<number>`COALESCE(${count(threads.id)}, 0)`,
					postCount: sql<number>`COALESCE(SUM(${threads.postCount}), 0)`,
					lastPostAt: sql<Date | null>`MAX(${posts.createdAt})`,
					createdAt: forumStructure.createdAt,
					updatedAt: forumStructure.updatedAt,
					pluginData: forumStructure.pluginData
				})
				.from(forumStructure)
				.leftJoin(threads, eq(forumStructure.id, threads.structureId))
				.leftJoin(posts, eq(threads.id, posts.threadId))
				.groupBy(forumStructure.id);

			if (!includeHidden) {
				baseQuery.where(eq(forumStructure.isVisible, true));
			}

			const categories = await baseQuery.orderBy(forumStructure.position, forumStructure.name);

			// Build tree structure
			const rootCategories = categories.filter((cat) => cat.parentId === null);
			const childCategories = categories.filter((cat) => cat.parentId !== null);

			// Attach children to parents
			const buildTree = (parentCategories: ForumCategoryWithStats[]): ForumCategoryWithStats[] => {
				return parentCategories.map((parent) => ({
					...parent,
					children: childCategories.filter((child) => child.parentId === parent.id)
				}));
			};

			return buildTree(rootCategories);
		} catch (error) {
			logger.error('CategoryService', 'Error fetching category tree', { error });
			throw error;
		}
	}

	/**
	 * Get category by slug
	 */
	async getCategoryBySlug(slug: string): Promise<ForumCategoryWithStats | null> {
		try {
			const [category] = await db
				.select({
					id: forumStructure.id,
					name: forumStructure.name,
					slug: forumStructure.slug,
					description: forumStructure.description,
					type: forumStructure.type,
					position: forumStructure.position,
					isHidden: forumStructure.isHidden,
					parentId: forumStructure.parentId,
					threadCount: sql<number>`COALESCE(${count(threads.id)}, 0)`,
					postCount: sql<number>`COALESCE(SUM(${threads.postCount}), 0)`,
					lastPostAt: sql<Date | null>`MAX(${posts.createdAt})`,
					createdAt: forumStructure.createdAt,
					updatedAt: forumStructure.updatedAt,
					pluginData: forumStructure.pluginData
				})
				.from(forumStructure)
				.leftJoin(threads, eq(forumStructure.id, threads.structureId))
				.leftJoin(posts, eq(threads.id, posts.threadId))
				.where(eq(forumStructure.slug, slug))
				.groupBy(forumStructure.id);

			return category || null;
		} catch (error) {
			logger.error('CategoryService', 'Error fetching category by slug', { slug, error });
			throw error;
		}
	}

	/**
	 * Clear categories cache
	 */
	clearCache(): void {
		categoriesCache = null;
		logger.info('CategoryService', 'Categories cache cleared');
	}

	/**
	 * Get category statistics
	 */
	async getCategoryStats(categoryId: CategoryId): Promise<{
		threadCount: number;
		postCount: number;
		lastPostAt: Date | null;
	}> {
		try {
			const [stats] = await db
				.select({
					threadCount: sql<number>`COALESCE(${count(threads.id)}, 0)`,
					postCount: sql<number>`COALESCE(SUM(${threads.postCount}), 0)`,
					lastPostAt: sql<Date | null>`MAX(${posts.createdAt})`
				})
				.from(forumStructure)
				.leftJoin(threads, eq(forumStructure.id, threads.structureId))
				.leftJoin(posts, eq(threads.id, posts.threadId))
				.where(eq(forumStructure.id, categoryId))
				.groupBy(forumStructure.id);

			return stats || { threadCount: 0, postCount: 0, lastPostAt: null };
		} catch (error) {
			logger.error('CategoryService', 'Error fetching category stats', { categoryId, error });
			throw error;
		}
	}
}

