	/**
	 * Manually award achievement to user(s)
	 */
	async manuallyAwardAchievement(
		achievementId: AchievementId,
		userIds: string[],
		reason?: string
	): Promise<void> {
		try {
			const achievement = await db
				.select()
				.from(achievements)
				.where(eq(achievements.id, achievementId))
				.limit(1);

			if (!achievement.length) {
				throw new Error(`Achievement with id ${achievementId} not found`);
			}

			// Award to each user
			for (const userId of userIds) {
				await db
					.insert(userAchievements)
					.values({
						userId,
						achievementId,
						isCompleted: true,
						completedAt: new Date(),
						progressPercentage: '100',
						completionData: {
							manuallyAwarded: true,
							awardedBy: 'admin',
							reason: reason || 'Manually awarded by admin',
							awardedAt: new Date().toISOString()
						}
					})
					.onConflictDoUpdate({
						target: [userAchievements.userId, userAchievements.achievementId],
						set: {
							isCompleted: true,
							completedAt: new Date(),
							progressPercentage: '100',
							completionData: {
								manuallyAwarded: true,
								awardedBy: 'admin',
								reason: reason || 'Manually awarded by admin',
								awardedAt: new Date().toISOString()
							}
						}
					});
			}

			logger.info('ACHIEVEMENT_ADMIN', 'Manually awarded achievement', {
				achievementId,
				userIds,
				reason,
				userCount: userIds.length
			});
		} catch (error) {
			logger.error('ACHIEVEMENT_ADMIN', 'Failed to manually award achievement', {
				achievementId,
				userIds,
				reason,
				error: error instanceof Error ? error.message : String(error)
			});
			throw error;
		}
	}

	/**
	 * Validate trigger configuration
	 */
	private validateTriggerConfig(triggerType: string, config: any): void {
		switch (triggerType) {
			case 'count':
				if (!config.action || typeof config.target !== 'number') {
					throw new Error('Count trigger requires action and numeric target');
				}
				break;

			case 'threshold':
				if (!config.metric || typeof config.target !== 'number') {
					throw new Error('Threshold trigger requires metric and numeric target');
				}
				break;

			case 'event':
				if (!config.eventType) {
					throw new Error('Event trigger requires eventType');
				}
				break;

			case 'composite':
				if (!Array.isArray(config.requirements) || config.requirements.length === 0) {
					throw new Error('Composite trigger requires requirements array');
				}
				break;

			case 'custom':
				if (!config.evaluator || !this.evaluators.canHandle(config.evaluator)) {
					throw new Error('Custom trigger requires valid evaluator');
				}
				break;

			case 'manual':
				// Manual triggers don't need validation
				break;

			default:
				throw new Error(`Unknown trigger type: ${triggerType}`);
		}
	}

	/**
	 * Generate a unique key from achievement name
	 */
	private generateAchievementKey(name: string): string {
		return name
			.toLowerCase()
			.replace(/[^a-z0-9\s]/g, '')
			.replace(/\s+/g, '_')
			.substring(0, 100);
	}
}

/**
 * Achievement Processor Service
 *
 * Processes achievement events and updates user progress.
 * Handles the core logic for checking requirements and awarding achievements.
 */

import { db } from '@db';
import { eq, and, desc, count, sql } from 'drizzle-orm';
import { achievements, userAchievements, achievementEvents } from '@schema';
import type { Achievement, UserAchievement } from '@schema';
import type { AchievementEventType } from '@schema';
import { users } from '@schema';
import { logger } from '../../../core/logger';
import { XpService } from '../../xp/xp.service';
import { DegenAchievementEvaluators } from './evaluators/degen-evaluators';
import type { UserId, AchievementId } from '@shared/types/ids';

export interface AchievementProgress {
	current: number;
	target: number;
	percentage: number;
	isCompleted: boolean;
	data?: any;
}

export class AchievementProcessorService {
	private xpService = new XpService();
	private evaluators = new DegenAchievementEvaluators();

	/**
	 * Process a single achievement event
	 */
	async processEvent(
		eventType: AchievementEventType,
		userId: UserId,
		eventData: any
	): Promise<void> {
		try {
			// Get all active achievements that could be triggered by this event
			const triggeredAchievements = await this.getTriggeredAchievements(eventType);

			for (const achievement of triggeredAchievements) {
				await this.updateUserProgress(userId, achievement, eventData);
			}

			// Mark event as processed
			await this.markEventProcessed(eventType, userId, eventData);

			logger.info('ACHIEVEMENT_PROCESSOR', `Processed ${eventType} for user ${userId}`, {
				eventType,
				userId,
				achievementCount: triggeredAchievements.length
			});
		} catch (error) {
			logger.error('ACHIEVEMENT_PROCESSOR', `Failed to process ${eventType} event`, {
				eventType,
				userId,
				error: error instanceof Error ? error.message : String(error)
			});
		}
	}

	/**
	 * Get achievements that can be triggered by an event type
	 */
	private async getTriggeredAchievements(eventType: AchievementEventType): Promise<Achievement[]> {
		// Get achievements where the trigger config matches this event type
		const allAchievements = await db
			.select()
			.from(achievements)
			.where(eq(achievements.isActive, true));

		return allAchievements.filter((achievement) => {
			const config = achievement.triggerConfig as any;

			// Check different trigger types
			switch (achievement.triggerType) {
				case 'count':
				case 'threshold':
					return config.action && this.mapEventToAction(eventType) === config.action;

				case 'event':
					return config.eventType === eventType;

				case 'custom':
					return (
						config.eventTypes?.includes(eventType) ||
						this.evaluators.canHandle(config.evaluator, eventType)
					);

				case 'composite':
					return config.requirements?.some(
						(req: any) => this.mapEventToAction(eventType) === req.action
					);

				default:
					return false;
			}
		});
	}

	/**
	 * Map event types to action names for trigger matching
	 */
	private mapEventToAction(eventType: AchievementEventType): string {
		const mapping: Record<AchievementEventType, string> = {
			post_created: 'posts_created',
			thread_created: 'threads_created',
			user_login: 'login_count',
			tip_sent: 'tips_sent',
			tip_received: 'tips_received',
			shoutbox_message: 'shoutbox_messages',
			like_given: 'likes_given',
			like_received: 'likes_received',
			user_mentioned: 'mentions_received',
			daily_streak: 'daily_streaks',
			wallet_loss: 'wallet_losses',
			thread_necromancy: 'thread_necromancies',
			crash_sentiment: 'crash_sentiments',
			diamond_hands: 'diamond_hands_events',
			paper_hands: 'paper_hands_events',
			market_prediction: 'market_predictions',
			thread_locked: 'threads_locked',
			custom_event: 'custom_events'
		};

		return mapping[eventType] || eventType;
	}

	/**
	 * Update user progress for a specific achievement
	 */
	private async updateUserProgress(
		userId: UserId,
		achievement: Achievement,
		eventData: any
	): Promise<void> {
		try {
			// Get current user achievement record
			const userAchievement = await this.getUserAchievement(userId, achievement.id);

			// Skip if already completed
			if (userAchievement?.isCompleted) {
				return;
			}

			// Calculate new progress
			const progress = await this.calculateProgress(achievement, userId, eventData);

			if (progress.isCompleted && !userAchievement?.isCompleted) {
				await this.completeAchievement(userId, achievement, progress);
			} else if (progress.current > 0) {
				await this.updateProgress(userId, achievement, progress);
			}
		} catch (error) {
			logger.error('ACHIEVEMENT_PROCESSOR', 'Failed to update user progress', {
				userId,
				achievementId: achievement.id,
				achievementKey: achievement.key,
				error: error instanceof Error ? error.message : String(error)
			});
		}
	}

	/**
	 * Calculate achievement progress based on trigger type
	 */
	private async calculateProgress(
		achievement: Achievement,
		userId: UserId,
		eventData: any
	): Promise<AchievementProgress> {
		const config = achievement.triggerConfig as any;

		switch (achievement.triggerType) {
			case 'count':
				return await this.calculateCountProgress(achievement, userId, config);

			case 'threshold':
				return await this.calculateThresholdProgress(achievement, userId, config);

			case 'event':
				return await this.calculateEventProgress(achievement, userId, config, eventData);

			case 'composite':
				return await this.calculateCompositeProgress(achievement, userId, config);

			case 'custom':
				return await this.calculateCustomProgress(achievement, userId, config, eventData);

			default:
				return { current: 0, target: 1, percentage: 0, isCompleted: false };
		}
	}

	/**
	 * Calculate progress for count-based achievements
	 */
	private async calculateCountProgress(
		achievement: Achievement,
		userId: UserId,
		config: any
	): Promise<AchievementProgress> {
		const action = config.action;
		const target = config.target || 1;

		// Count relevant events for this user
		const eventType = this.actionToEventType(action);
		const result = await db
			.select({ count: count() })
			.from(achievementEvents)
			.where(
				and(
					eq(achievementEvents.userId, userId),
					eq(achievementEvents.eventType, eventType),
					eq(achievementEvents.processingStatus, 'completed')
				)
			);

		const current = result[0]?.count || 0;
		const percentage = Math.min((current / target) * 100, 100);

		return {
			current,
			target,
			percentage,
			isCompleted: current >= target
		};
	}

	/**
	 * Calculate progress for threshold-based achievements
	 */
	private async calculateThresholdProgress(
		achievement: Achievement,
		userId: UserId,
		config: any
	): Promise<AchievementProgress> {
		// Similar to count but may use different data sources
		const metric = config.metric;
		const target = config.target;

		// Get current value from user data or calculated metric
		const current = await this.getUserMetric(userId, metric);
		const percentage = Math.min((current / target) * 100, 100);

		return {
			current,
			target,
			percentage,
			isCompleted: current >= target
		};
	}

	/**
	 * Calculate progress for event-based achievements (single occurrence)
	 */
	private async calculateEventProgress(
		achievement: Achievement,
		userId: UserId,
		config: any,
		eventData: any
	): Promise<AchievementProgress> {
		// Check if the event conditions are met
		const conditionsMet = await this.checkEventConditions(config.conditions || [], eventData);

		return {
			current: conditionsMet ? 1 : 0,
			target: 1,
			percentage: conditionsMet ? 100 : 0,
			isCompleted: conditionsMet
		};
	}

	/**
	 * Calculate progress for composite achievements (multiple requirements)
	 */
	private async calculateCompositeProgress(
		achievement: Achievement,
		userId: UserId,
		config: any
	): Promise<AchievementProgress> {
		const requirements = config.requirements || [];
		const operator = config.operator || 'AND';

		let completedRequirements = 0;

		for (const requirement of requirements) {
			const reqProgress = await this.calculateCountProgress(achievement, userId, requirement);
			if (reqProgress.isCompleted) {
				completedRequirements++;
			}
		}

		const isCompleted =
			operator === 'AND'
				? completedRequirements === requirements.length
				: completedRequirements > 0;

		const percentage = (completedRequirements / requirements.length) * 100;

		return {
			current: completedRequirements,
			target: requirements.length,
			percentage,
			isCompleted
		};
	}

	/**
	 * Calculate progress for custom achievements using evaluators
	 */
	private async calculateCustomProgress(
		achievement: Achievement,
		userId: UserId,
		config: any,
		eventData: any
	): Promise<AchievementProgress> {
		const evaluator = config.evaluator;

		if (!this.evaluators.canHandle(evaluator)) {
			logger.warn('ACHIEVEMENT_PROCESSOR', `Unknown evaluator: ${evaluator}`);
			return { current: 0, target: 1, percentage: 0, isCompleted: false };
		}

		const isCompleted = await this.evaluators.evaluate(evaluator, userId, config.config || {});

		return {
			current: isCompleted ? 1 : 0,
			target: 1,
			percentage: isCompleted ? 100 : 0,
			isCompleted,
			data: eventData
		};
	}

	/**
	 * Complete an achievement for a user
	 */
	private async completeAchievement(
		userId: UserId,
		achievement: Achievement,
		progress: AchievementProgress
	): Promise<void> {
		try {
			// Mark achievement as completed
			await db
				.insert(userAchievements)
				.values({
					userId,
					achievementId: achievement.id,
					currentProgress: progress.data || {},
					progressPercentage: progress.percentage.toString(),
					isCompleted: true,
					completedAt: new Date(),
					completionData: {
						completedAt: new Date().toISOString(),
						finalProgress: progress
					}
				})
				.onConflictDoUpdate({
					target: [userAchievements.userId, userAchievements.achievementId],
					set: {
						isCompleted: true,
						completedAt: new Date(),
						progressPercentage: progress.percentage.toString(),
						completionData: {
							completedAt: new Date().toISOString(),
							finalProgress: progress
						}
					}
				});

			// Distribute rewards
			await this.distributeRewards(userId, achievement);

			// Log achievement completion
			logger.info(
				'ACHIEVEMENT_COMPLETED',
				`User ${userId} completed achievement ${achievement.key}`,
				{
					userId,
					achievementId: achievement.id,
					achievementKey: achievement.key,
					achievementName: achievement.name,
					rewardXp: achievement.rewardXp,
					rewardDgt: achievement.rewardDgt
				}
			);
		} catch (error) {
			logger.error('ACHIEVEMENT_PROCESSOR', 'Failed to complete achievement', {
				userId,
				achievementId: achievement.id,
				error: error instanceof Error ? error.message : String(error)
			});
		}
	}

	/**
	 * Update achievement progress (partial completion)
	 */
	private async updateProgress(
		userId: UserId,
		achievement: Achievement,
		progress: AchievementProgress
	): Promise<void> {
		await db
			.insert(userAchievements)
			.values({
				userId,
				achievementId: achievement.id,
				currentProgress: progress.data || {},
				progressPercentage: progress.percentage.toString(),
				isCompleted: false
			})
			.onConflictDoUpdate({
				target: [userAchievements.userId, userAchievements.achievementId],
				set: {
					currentProgress: progress.data || {},
					progressPercentage: progress.percentage.toString()
				}
			});
	}

	/**
	 * Distribute rewards for completed achievement
	 */
	private async distributeRewards(userId: UserId, achievement: Achievement): Promise<void> {
		try {
			// Award XP
			if (achievement.rewardXp > 0) {
				await this.xpService.awardXP(userId, achievement.rewardXp, 'achievement_unlock');
			}

			// Award DGT (integrate with existing DGT service)
			if (achievement.rewardDgt > 0) {
				// TODO: Integrate with DGT service
				logger.info(
					'ACHIEVEMENT_REWARD',
					`Would award ${achievement.rewardDgt} DGT to user ${userId}`
				);
			}

			// Award Clout (integrate with existing Clout service)
			if (achievement.rewardClout > 0) {
				// TODO: Integrate with Clout service
				logger.info(
					'ACHIEVEMENT_REWARD',
					`Would award ${achievement.rewardClout} Clout to user ${userId}`
				);
			}

			// TODO: Handle title and badge rewards when those systems are integrated
		} catch (error) {
			logger.error('ACHIEVEMENT_PROCESSOR', 'Failed to distribute rewards', {
				userId,
				achievementId: achievement.id,
				error: error instanceof Error ? error.message : String(error)
			});
		}
	}

	/**
	 * Helper methods
	 */
	private async getUserAchievement(
		userId: UserId,
		achievementId: AchievementId
	): Promise<UserAchievement | null> {
		const result = await db
			.select()
			.from(userAchievements)
			.where(
				and(eq(userAchievements.userId, userId), eq(userAchievements.achievementId, achievementId))
			)
			.limit(1);

		return result[0] || null;
	}

	private async getUserMetric(userId: UserId, metric: string): Promise<number> {
		// Get user metrics from users table or calculate from events
		switch (metric) {
			case 'total_posts':
				const posts = await db
					.select({ count: count() })
					.from(achievementEvents)
					.where(
						and(
							eq(achievementEvents.userId, userId),
							eq(achievementEvents.eventType, 'post_created')
						)
					);
				return posts[0]?.count || 0;

			case 'total_threads':
				const threads = await db
					.select({ count: count() })
					.from(achievementEvents)
					.where(
						and(
							eq(achievementEvents.userId, userId),
							eq(achievementEvents.eventType, 'thread_created')
						)
					);
				return threads[0]?.count || 0;

			default:
				return 0;
		}
	}

	private async checkEventConditions(conditions: any[], eventData: any): Promise<boolean> {
		return conditions.every((condition) => {
			const fieldValue = this.getNestedValue(eventData, condition.field);

			switch (condition.operation) {
				case 'equals':
					return fieldValue === condition.value;
				case 'greater_than':
					return fieldValue > condition.value;
				case 'less_than':
					return fieldValue < condition.value;
				case 'contains':
					return String(fieldValue).includes(condition.value);
				case 'within_seconds':
					const timeDiff = Date.now() - new Date(fieldValue).getTime();
					return timeDiff <= condition.value * 1000;
				default:
					return false;
			}
		});
	}

	private getNestedValue(obj: any, path: string): any {
		return path.split('.').reduce((current, key) => current?.[key], obj);
	}

	private actionToEventType(action: string): AchievementEventType {
		const mapping: Record<string, AchievementEventType> = {
			posts_created: 'post_created',
			threads_created: 'thread_created',
			login_count: 'user_login',
			tips_sent: 'tip_sent',
			tips_received: 'tip_received',
			shoutbox_messages: 'shoutbox_message',
			likes_given: 'like_given',
			likes_received: 'like_received'
		};

		return mapping[action] || 'custom_event';
	}

	private async markEventProcessed(
		eventType: AchievementEventType,
		userId: UserId,
		eventData: any
	): Promise<void> {
		await db
			.update(achievementEvents)
			.set({
				processingStatus: 'completed',
				processedAt: new Date()
			})
			.where(
				and(
					eq(achievementEvents.userId, userId),
					eq(achievementEvents.eventType, eventType),
					eq(achievementEvents.processingStatus, 'pending')
				)
			);
	}
}

/**
 * Achievement Controller
 *
 * HTTP endpoints for achievement management, admin operations,
 * and user achievement tracking.
 */

import type { Request, Response } from 'express';
import type { AchievementId } from '@shared/types/ids';
import { AchievementProcessorService } from './achievement-processor.service';
import { AchievementAdminService } from './achievement-admin.service';
import { AchievementEventEmitter } from '../../../core/events/achievement-events.service';
import {
	getAllAchievementTemplates,
	getTemplateById,
	getTemplatesByTags,
	getTemplatesByCategory
} from './templates/achievement-templates';
import { logger } from '../../../core/logger';
import { db } from '@db';
import { eq, and, desc, count } from 'drizzle-orm';
import { achievements, userAchievements } from '@schema';
import type { AchievementEventType } from '@schema';

export class AchievementController {
	private processorService = new AchievementProcessorService();
	private adminService = new AchievementAdminService();

	/**
	 * Get user's achievements with progress
	 * GET /api/achievements/user/:userId
	 */
	async getUserAchievements(req: Request, res: Response): Promise<void> {
		try {
			const { userId } = req.params;
			const { completed, category, tier } = req.query;

			const whereConditions: any[] = [eq(userAchievements.userId, userId)];

			if (completed === 'true') {
				whereConditions.push(eq(userAchievements.isCompleted, true));
			} else if (completed === 'false') {
				whereConditions.push(eq(userAchievements.isCompleted, false));
			}

			const userAchievementsData = await db
				.select({
					id: userAchievements.id,
					achievementId: userAchievements.achievementId,
					currentProgress: userAchievements.currentProgress,
					progressPercentage: userAchievements.progressPercentage,
					isCompleted: userAchievements.isCompleted,
					completedAt: userAchievements.completedAt,
					achievement: {
						id: achievements.id,
						key: achievements.key,
						name: achievements.name,
						description: achievements.description,
						category: achievements.category,
						tier: achievements.tier,
						iconUrl: achievements.iconUrl,
						iconEmoji: achievements.iconEmoji,
						rewardXp: achievements.rewardXp,
						rewardDgt: achievements.rewardDgt,
						rewardClout: achievements.rewardClout,
						isSecret: achievements.isSecret,
						unlockMessage: achievements.unlockMessage
					}
				})
				.from(userAchievements)
				.innerJoin(achievements, eq(userAchievements.achievementId, achievements.id))
				.where(and(...whereConditions))
				.orderBy(desc(userAchievements.completedAt));

			res.json({
				success: true,
				data: userAchievementsData
			});
		} catch (error) {
			logger.error('ACHIEVEMENT_CONTROLLER', 'Failed to get user achievements', {
				userId: req.params.userId,
				error: error instanceof Error ? error.message : String(error)
			});
			res.status(500).json({
				success: false,
				error: 'Failed to get user achievements'
			});
		}
	}

	/**
	 * Get all available achievements
	 * GET /api/achievements
	 */
	async getAchievements(req: Request, res: Response): Promise<void> {
		try {
			const {
				category,
				tier,
				triggerType,
				isActive = 'true',
				isSecret,
				search,
				page = '1',
				limit = '50'
			} = req.query;

			const filters = {
				category: category as any,
				tier: tier as any,
				triggerType: triggerType as any,
				isActive: isActive === 'true',
				isSecret: isSecret === 'true' ? true : isSecret === 'false' ? false : undefined,
				search: search as string
			};

			const result = await this.adminService.getAchievements(
				filters,
				parseInt(page as string),
				parseInt(limit as string)
			);

			res.json({
				success: true,
				data: result.achievements,
				pagination: {
					page: parseInt(page as string),
					limit: parseInt(limit as string),
					total: result.total,
					pages: Math.ceil(result.total / parseInt(limit as string))
				}
			});
		} catch (error) {
			logger.error('ACHIEVEMENT_CONTROLLER', 'Failed to get achievements', {
				error: error instanceof Error ? error.message : String(error)
			});
			res.status(500).json({
				success: false,
				error: 'Failed to get achievements'
			});
		}
	}

	/**
	 * Get achievement statistics
	 * GET /api/achievements/stats
	 */
	async getAchievementStats(req: Request, res: Response): Promise<void> {
		try {
			const stats = await this.adminService.getAchievementStats();

			res.json({
				success: true,
				data: stats
			});
		} catch (error) {
			logger.error('ACHIEVEMENT_CONTROLLER', 'Failed to get achievement stats', {
				error: error instanceof Error ? error.message : String(error)
			});
			res.status(500).json({
				success: false,
				error: 'Failed to get achievement stats'
			});
		}
	}

	/**
	 * Get specific achievement details
	 * GET /api/achievements/:id
	 */
	async getAchievementById(req: Request, res: Response): Promise<void> {
		try {
			const { id } = req.params;
			const achievement = await this.adminService.getAchievementById(id as AchievementId);

			if (!achievement) {
				res.status(404).json({
					success: false,
					error: 'Achievement not found'
				});
				return;
			}

			res.json({
				success: true,
				data: achievement
			});
		} catch (error) {
			logger.error('ACHIEVEMENT_CONTROLLER', 'Failed to get achievement', {
				id: req.params.id,
				error: error instanceof Error ? error.message : String(error)
			});
			res.status(500).json({
				success: false,
				error: 'Failed to get achievement'
			});
		}
	}

	/**
	 * Create new achievement
	 * POST /api/achievements
	 */
	async createAchievement(req: Request, res: Response): Promise<void> {
		try {
			const achievement = await this.adminService.createAchievement(req.body);

			res.status(201).json({
				success: true,
				data: achievement
			});
		} catch (error) {
			logger.error('ACHIEVEMENT_CONTROLLER', 'Failed to create achievement', {
				body: req.body,
				error: error instanceof Error ? error.message : String(error)
			});
			res.status(400).json({
				success: false,
				error: error instanceof Error ? error.message : 'Failed to create achievement'
			});
		}
	}

	/**
	 * Update achievement
