export class LevelingService {
	private xpService: XpService;

	constructor() {
		this.xpService = new XpService();
	}

	/**
	 * Get detailed level information including rewards and visual customization
	 */
	async getLevelInfo(levelNumber: number): Promise<LevelInfo | null> {
		try {
			const levelData = await db
				.select()
				.from(levels)
				.where(eq(levels.level, levelNumber))
				.limit(1);

			if (levelData.length === 0) {
				return null;
			}

			const level = levelData[0];

			// Get next level for XP requirement calculation
			const nextLevelData = await db
				.select()
				.from(levels)
				.where(gte(levels.level, levelNumber + 1))
				.orderBy(asc(levels.level))
				.limit(1);

			return {
				level: level.level,
				name: level.name || `Level ${level.level}`,
				minXp: level.minXp,
				nextLevelXp: nextLevelData.length > 0 ? nextLevelData[0].minXp : undefined,
				iconUrl: level.iconUrl || undefined,
				rarity: level.rarity || 'common',
				frameUrl: level.frameUrl || undefined,
				colorTheme: level.colorTheme || undefined,
				animationEffect: level.animationEffect || undefined,
				unlocks: level.unlocks,
				rewards: {
					dgt: level.rewardDgt || undefined,
					titleId: level.rewardTitleId || undefined,
					badgeId: level.rewardBadgeId || undefined
				}
			};
		} catch (error) {
			logger.error('LEVELING_SERVICE', 'Error getting level info:', error);
			throw error;
		}
	}

	/**
	 * Get comprehensive user progression data
	 */
	async getUserProgression(userId: UserId): Promise<UserProgression | null> {
		try {
			// Get user basic info
			const userData = await db
				.select({
					id: users.id,
					username: users.username,
					level: users.level,
					xp: users.xp
				})
				.from(users)
				.where(eq(users.id, userId))
				.limit(1);

			if (userData.length === 0) {
				return null;
			}

			const user = userData[0];

			// Get current and next level info
			const currentLevelInfo = await this.getLevelInfo(user.level);
			const nextLevelInfo = await this.getLevelInfo(user.level + 1);

			if (!currentLevelInfo) {
				throw new Error(`Level ${user.level} not found`);
			}

			// Calculate progression
			const xpForNextLevel = nextLevelInfo ? nextLevelInfo.minXp - user.xp : 0;
			const progressPercentage = nextLevelInfo
				? ((user.xp - currentLevelInfo.minXp) / (nextLevelInfo.minXp - currentLevelInfo.minXp)) *
					100
				: 100;

			// Get recent level ups (last 30 days)
			const thirtyDaysAgo = new Date();
			thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

			const recentLevelUps = await db
				.select({ count: count() })
				.from(xpAdjustmentLogs)
				.where(
					and(
						eq(xpAdjustmentLogs.userId, userId),
						gte(xpAdjustmentLogs.createdAt, thirtyDaysAgo),
						sql`${xpAdjustmentLogs.reason} LIKE '%Level up%'`
					)
				);

			// Get weekly XP gain
			const weekAgo = new Date();
			weekAgo.setDate(weekAgo.getDate() - 7);

			const weeklyXpGain = await db
				.select({ total: sum(xpAdjustmentLogs.amount) })
				.from(xpAdjustmentLogs)
				.where(
					and(
						eq(xpAdjustmentLogs.userId, userId),
						gte(xpAdjustmentLogs.createdAt, weekAgo),
						sql`${xpAdjustmentLogs.adjustmentType} = 'add'`
					)
				);

			// Get user rank
			const higherRankedUsers = await db
				.select({ count: count() })
				.from(users)
				.where(and(sql`${users.xp} > ${user.xp}`, sql`${users.level} >= ${user.level}`));

			const rank = (higherRankedUsers[0]?.count || 0) + 1;

			// Get achievement stats
			const achievementStats = await db
				.select({ count: count() })
				.from(userAchievements)
				.where(eq(userAchievements.userId, userId));

			const recentAchievements = await db
				.select({
					achievement: achievements,
					earnedAt: userAchievements.earnedAt
				})
				.from(userAchievements)
				.innerJoin(achievements, eq(userAchievements.achievementId, achievements.id))
				.where(eq(userAchievements.userId, userId))
				.orderBy(desc(userAchievements.earnedAt))
				.limit(5);

			// Get mission stats
			const missionStats = await db
				.select({
					total: count(),
					completed: sum(sql`CASE WHEN ${userMissionProgress.isCompleted} THEN 1 ELSE 0 END`),
					streak: count() // TODO: Implement streak calculation
				})
				.from(userMissionProgress)
				.where(eq(userMissionProgress.userId, userId));

			const availableMissions = await db
				.select({ count: count() })
				.from(missions)
				.where(eq(missions.isActive, true));

			return {
				userId: user.id,
				username: user.username,
				currentLevel: user.level,
				currentXp: user.xp,
				totalXp: user.xp,
				xpForNextLevel,
				progressPercentage: Math.min(100, Math.max(0, progressPercentage)),
				levelInfo: currentLevelInfo,
				...(nextLevelInfo ? { nextLevelInfo } : {}),
				recentLevelUps: recentLevelUps[0]?.count || 0,
				weeklyXpGain: parseInt(weeklyXpGain[0]?.total || '0'),
				rank,
				achievements: {
					total: achievementStats[0]?.count || 0,
					recent: recentAchievements.map((a: { achievement: any; earnedAt: Date }) => ({
						...a.achievement,
						earnedAt: a.earnedAt
					}))
				},
				missions: {
					completed: parseInt(missionStats[0]?.completed || '0'),
					available: availableMissions[0]?.count || 0,
					streak: parseInt(missionStats[0]?.streak || '0')
				}
			};
		} catch (error) {
			logger.error('LEVELING_SERVICE', 'Error getting user progression:', error);
			throw error;
		}
	}

	/**
	 * Get leaderboard with various sorting options
	 */
	async getLeaderboard(
		type: 'level' | 'xp' | 'weekly' | 'monthly' = 'xp',
		limit: number = 50,
		offset: number = 0
	): Promise<LeaderboardEntry[]> {
		try {
			let orderBy;
			let timeFilter;

			switch (type) {
				case 'level':
					orderBy = [desc(users.level), desc(users.xp)];
					break;
				case 'weekly':
					timeFilter = new Date();
					timeFilter.setDate(timeFilter.getDate() - 7);
					orderBy = [desc(users.xp)]; // TODO: Implement weekly XP tracking
					break;
				case 'monthly':
					timeFilter = new Date();
					timeFilter.setDate(timeFilter.getDate() - 30);
					orderBy = [desc(users.xp)]; // TODO: Implement monthly XP tracking
					break;
				default:
					orderBy = [desc(users.xp)];
			}

			const leaderboardData = await db
				.select({
					userId: users.id,
					username: users.username,
					level: users.level,
					totalXp: users.xp,
					avatarUrl: users.avatarUrl
				})
				.from(users)
				.orderBy(...orderBy)
				.limit(limit)
				.offset(offset);

			// Enrich with additional data
			const enrichedData: LeaderboardEntry[] = [];

			for (let i = 0; i < leaderboardData.length; i++) {
				const user = leaderboardData[i];

				// Get weekly XP for this user (if needed)
				let weeklyXp = 0;
				if (type === 'weekly' || type === 'monthly') {
					const timeFilter = new Date();
					timeFilter.setDate(timeFilter.getDate() - (type === 'weekly' ? 7 : 30));

					const weeklyData = await db
						.select({ total: sum(xpAdjustmentLogs.amount) })
						.from(xpAdjustmentLogs)
						.where(
							and(
								eq(xpAdjustmentLogs.userId, user.userId),
								gte(xpAdjustmentLogs.createdAt, timeFilter),
								sql`${xpAdjustmentLogs.adjustmentType} = 'add'`
							)
						);

					weeklyXp = parseInt(weeklyData[0]?.total || '0');
				}

				enrichedData.push({
					userId: user.userId,
					username: user.username,
					level: user.level,
					totalXp: user.totalXp,
					weeklyXp,
					rank: offset + i + 1,
					trend: 'stable' // TODO: Implement trend calculation
				});
			}

			return enrichedData;
		} catch (error) {
			logger.error('LEVELING_SERVICE', 'Error getting leaderboard:', error);
			throw error;
		}
	}

	/**
	 * Calculate and award level up rewards
	 */
	async processLevelUp(
		userId: UserId,
		oldLevel: number,
		newLevel: number
	): Promise<{
		rewards: any[];
		unlocks: any[];
	}> {
		try {
			const rewards: any[] = [];
			const unlocks: any[] = [];

			// Process rewards for each level gained
			for (let level = oldLevel + 1; level <= newLevel; level++) {
				const levelInfo = await this.getLevelInfo(level);
				if (!levelInfo) continue;

				// Award DGT tokens
				if (levelInfo.rewards.dgt && levelInfo.rewards.dgt > 0) {
					await db.insert(transactions).values({
						userId,
						amount: levelInfo.rewards.dgt,
						type: 'LEVEL_REWARD',
						status: 'COMPLETED',
						description: `Level ${level} DGT reward`,
						metadata: { level, source: 'level_up' }
					});

					rewards.push({
						type: 'dgt',
						amount: levelInfo.rewards.dgt,
						level
					});
				}

				// Award title
				if (levelInfo.rewards.titleId) {
					await db
						.insert(userTitles)
						.values({
							userId,
							titleId: levelInfo.rewards.titleId,
							isActive: false, // User needs to manually activate
							earnedAt: new Date()
						})
						.onConflictDoNothing();

					rewards.push({
						type: 'title',
						titleId: levelInfo.rewards.titleId,
						level
					});
				}

				// Award badge
				if (levelInfo.rewards.badgeId) {
					await db
						.insert(userBadges)
						.values({
							userId,
							badgeId: levelInfo.rewards.badgeId,
							earnedAt: new Date()
						})
						.onConflictDoNothing();

					rewards.push({
						type: 'badge',
						badgeId: levelInfo.rewards.badgeId,
						level
					});
				}

				// Process unlocks
				if (levelInfo.unlocks) {
					unlocks.push({
						level,
						unlocks: levelInfo.unlocks
					});
				}
			}

			logger.info('LEVELING_SERVICE', `Processed level up rewards for user ${userId}`, {
				oldLevel,
				newLevel,
				rewardsCount: rewards.length,
				unlocksCount: unlocks.length
			});

			return { rewards, unlocks };
		} catch (error) {
			logger.error('LEVELING_SERVICE', 'Error processing level up rewards:', error);
			throw error;
		}
	}

	/**
	 * Get progression analytics for admin dashboard
	 */
	async getProgressionAnalytics(
		timeframe: 'day' | 'week' | 'month' = 'week'
	): Promise<ProgressionAnalytics> {
		try {
			const timeFilter = new Date();
			switch (timeframe) {
				case 'day':
					timeFilter.setDate(timeFilter.getDate() - 1);
					break;
				case 'week':
					timeFilter.setDate(timeFilter.getDate() - 7);
					break;
				case 'month':
					timeFilter.setDate(timeFilter.getDate() - 30);
					break;
			}

			// User distribution by level
			const userDistribution = await db
				.select({
					level: users.level,
					count: count()
				})
				.from(users)
				.groupBy(users.level)
				.orderBy(users.level);

			// Active users count
			const activeUsers = await db
				.select({ count: count() })
				.from(users)
				.where(gte(users.lastLogin, timeFilter));

			// Level ups today
			const levelUpsToday = await db
				.select({ count: count() })
				.from(xpAdjustmentLogs)
				.where(
					and(
						gte(xpAdjustmentLogs.createdAt, timeFilter),
						sql`${xpAdjustmentLogs.reason} LIKE '%Level up%'`
					)
				);

			// Mission completions
			const missionCompletions = await db
				.select({ count: count() })
				.from(userMissionProgress)
				.where(
					and(
						eq(userMissionProgress.isCompleted, true),
						gte(userMissionProgress.completedAt || userMissionProgress.updatedAt, timeFilter)
					)
				);

			// Average progression metrics
			const avgXpPerDay = await db
				.select({ avg: sql<number>`AVG(${xpAdjustmentLogs.amount})` })
				.from(xpAdjustmentLogs)
				.where(
					and(
						gte(xpAdjustmentLogs.createdAt, timeFilter),
						sql`${xpAdjustmentLogs.adjustmentType} = 'add'`
					)
				);

			// Top performers
			const topPerformers = await this.getLeaderboard('weekly', 10);

			return {
				userDistribution: userDistribution.map((d: { level: number; count: number }) => ({
					level: d.level,
					count: d.count
				})),
				averageProgression: {
					xpPerDay: avgXpPerDay[0]?.avg || 0,
					levelsPerWeek: 0.5, // TODO: Calculate actual
					completionRate: 0.8 // TODO: Calculate actual
				},
				topPerformers,
				engagementMetrics: {
					activeUsers: activeUsers[0]?.count || 0,
					levelUpsToday: levelUpsToday[0]?.count || 0,
					missionCompletions: missionCompletions[0]?.count || 0
				}
			};
		} catch (error) {
			logger.error('LEVELING_SERVICE', 'Error getting progression analytics:', error);
			throw error;
		}
	}

	/**
	 * Create or update level configurations
	 */
	async createLevel(levelData: {
		level: number;
		minXp: number;
		name?: string;
		iconUrl?: string;
		rarity?: string;
		frameUrl?: string;
		colorTheme?: string;
		animationEffect?: string;
		unlocks?: any;
		rewardDgt?: number;
		rewardTitleId?: EntityId;
		rewardBadgeId?: EntityId;
	}): Promise<LevelInfo> {
		try {
			const result = await db
				.insert(levels)
				.values(levelData)
				.onConflictDoUpdate({
					target: levels.level,
					set: levelData
				})
				.returning();

			logger.info('LEVELING_SERVICE', `Created/updated level ${levelData.level}`, levelData);

			return this.getLevelInfo(result[0].level) as Promise<LevelInfo>;
		} catch (error) {
			logger.error('LEVELING_SERVICE', 'Error creating level:', error);
			throw error;
		}
	}

	/**
	 * Get all levels configuration
	 */
	async getAllLevels(): Promise<LevelInfo[]> {
		try {
			const levelsData = await db.select().from(levels).orderBy(asc(levels.level));

			const levelInfos: LevelInfo[] = [];
			for (const level of levelsData) {
				const levelInfo = await this.getLevelInfo(level.level);
				if (levelInfo) {
					levelInfos.push(levelInfo);
				}
			}

			return levelInfos;
		} catch (error) {
			logger.error('LEVELING_SERVICE', 'Error getting all levels:', error);
			throw error;
		}
	}

	/**
	 * Calculate optimal XP curve for balanced progression
	 */
	async generateXpCurve(
		maxLevel: number = 100,
		baseXp: number = 100
	): Promise<Array<{ level: number; minXp: number }>> {
		const curve: Array<{ level: number; minXp: number }> = [];

		for (let level = 1; level <= maxLevel; level++) {
			// Exponential curve with diminishing returns
			const multiplier = Math.pow(level, 1.8) * 1.2;
			const minXp = Math.floor(baseXp * multiplier);

			curve.push({ level, minXp });
		}

		return curve;
	}

	/**
	 * Bulk import levels from XP curve
	 */
	async importXpCurve(curve: Array<{ level: number; minXp: number }>): Promise<void> {
		try {
			const levelValues = curve.map((c) => ({
				level: c.level,
				minXp: c.minXp,
				name: `Level ${c.level}`,
				rarity: this.getLevelRarity(c.level)
			}));

			await db.insert(levels).values(levelValues).onConflictDoNothing();

			logger.info('LEVELING_SERVICE', `Imported ${curve.length} levels`, {
				maxLevel: Math.max(...curve.map((c) => c.level)),
				maxXp: Math.max(...curve.map((c) => c.minXp))
			});
		} catch (error) {
			logger.error('LEVELING_SERVICE', 'Error importing XP curve:', error);
			throw error;
		}
	}

	/**
	 * Helper to determine level rarity based on level number
	 */
	private getLevelRarity(level: number): string {
		if (level >= 90) return 'mythic';
		if (level >= 75) return 'legendary';
		if (level >= 50) return 'epic';
		if (level >= 25) return 'rare';
		return 'common';
	}
}

export const levelingService = new LevelingService();

/**
 * Clout Transformer - Security-First Implementation
 * 
 * Transforms raw database clout records into role-appropriate
 * response objects with GDPR compliance and admin data protection.
 */

import type { 
  UserId,
  AchievementId,
  CloutAmount 
} from '@shared/types/ids';
// import { UserTransformer } from '../../users/transformers/user.transformer'; // TODO: Add when available

// Public clout data (leaderboards, etc.)
export interface PublicCloutAchievement {
  id: AchievementId;
  name: string;
  description?: string;
  iconUrl?: string;
  cloutReward: CloutAmount;
  isEnabled: boolean;
}

// Authenticated user viewing achievements
export interface AuthenticatedCloutAchievement extends PublicCloutAchievement {
  // User progress tracking
  userProgress?: {
    isCompleted: boolean;
    progress: number;
    maxProgress: number;
    completedAt?: string;
  };

  // Display criteria (sanitized)
  criteria: {
    type: string;
    description: string;
    target: number;
  };
}

// Admin view with full management data
export interface AdminCloutAchievement extends AuthenticatedCloutAchievement {
  // System fields
  achievementKey: string;
  criteriaType?: string;
  criteriaValue?: number;

  // Admin metadata
  createdAt: string;
  adminNotes?: string;

  // Usage statistics
  stats: {
    totalCompletions: number;
    completionRate: number;
    avgTimeToComplete?: number;
    lastAwarded?: string;
  };
}

// Clout log entries for different views
export interface PublicCloutLog {
  id: string;
  amount: CloutAmount;
  reason: string;
  timestamp: string;

  // Public user info (if enabled by user)
  user?: {
    id: UserId;
    username: string;
    avatarUrl?: string;
    level: number;
  };
}

export interface AuthenticatedCloutLog extends PublicCloutLog {
  // Enhanced context for own logs
  source: 'achievement' | 'admin_grant' | 'event' | 'system';
  achievement?: {
    id: AchievementId;
    name: string;
    iconUrl?: string;
  };
}

export interface AdminCloutLog extends AuthenticatedCloutLog {
  // Full admin data
  systemData: {
    userId: UserId;
    achievementId?: AchievementId;
    adminId?: UserId;
    ipHash?: string; // Anonymized
  };

  // Admin metadata
  reviewedBy?: UserId;
  flagged: boolean;
  flagReason?: string;
}

export class CloutTransformer {

  /**
   * Transform achievement for public consumption
   * Only shows basic achievement info for leaderboards/public displays
   */
  static toPublicAchievement(dbAchievement: any): PublicCloutAchievement {
    if (!dbAchievement) {
      throw new Error('Invalid achievement data provided to transformer');
    }

    return {
      id: dbAchievement.id as AchievementId,
      name: dbAchievement.name,
      description: this.sanitizeDescription(dbAchievement.description),
      iconUrl: dbAchievement.iconUrl || undefined,
      cloutReward: this.sanitizeCloutAmount(dbAchievement.cloutReward),
      isEnabled: dbAchievement.enabled === true
    };
  }

  /**
   * Transform achievement for authenticated users
   * Includes user progress and sanitized criteria
   */
  static toAuthenticatedAchievement(dbAchievement: any, requestingUser: any, userProgress?: any): AuthenticatedCloutAchievement {
    const publicData = this.toPublicAchievement(dbAchievement);

    return {
      ...publicData,

      // User progress (if available)
      userProgress: userProgress ? {
        isCompleted: userProgress.isCompleted === true,
        progress: userProgress.progress || 0,
        maxProgress: userProgress.maxProgress || dbAchievement.criteriaValue || 1,
        completedAt: userProgress.completedAt?.toISOString()
      } : undefined,

      // Sanitized criteria
      criteria: {
        type: this.normalizeCriteriaType(dbAchievement.criteriaType),
        description: this.generateCriteriaDescription(dbAchievement),
        target: dbAchievement.criteriaValue || 0
      }
    };
  }

  /**
   * Transform achievement for admin view
   * Includes all system data and usage statistics
   */
  static toAdminAchievement(dbAchievement: any, stats?: any): AdminCloutAchievement {
    // Get authenticated data with admin permissions
    const authenticatedData = this.toAuthenticatedAchievement(dbAchievement, { role: 'admin' });

    return {
      ...authenticatedData,

      // System fields
      achievementKey: dbAchievement.achievementKey,
      criteriaType: dbAchievement.criteriaType,
      criteriaValue: dbAchievement.criteriaValue,

      // Admin metadata
      createdAt: dbAchievement.createdAt.toISOString(),
      adminNotes: dbAchievement.adminNotes,

      // Usage statistics
      stats: {
        totalCompletions: stats?.totalCompletions || 0,
        completionRate: this.calculateCompletionRate(stats),
        avgTimeToComplete: stats?.avgTimeToComplete,
        lastAwarded: stats?.lastAwarded?.toISOString()
      }
    };
  }

  /**
   * Transform clout log for public consumption
   * Only shows basic info for public activity feeds
   */
  static toPublicCloutLog(dbLog: any): PublicCloutLog {
    return {
      id: dbLog.id,
      amount: this.sanitizeCloutAmount(dbLog.cloutEarned),
      reason: this.sanitizeReason(dbLog.reason, 'public'),
      timestamp: dbLog.createdAt.toISOString(),

      // Public user info (if enabled)
      user: dbLog.user ? {
        id: dbLog.user.id as UserId,
        username: dbLog.user.username || '[deleted]',
        avatarUrl: dbLog.user.avatarUrl,
        level: this.calculateLevel(dbLog.user.xp || 0)
      } : undefined
    };
  }

  /**
   * Transform clout log for authenticated users viewing their own logs
   */
  static toAuthenticatedCloutLog(dbLog: any, requestingUser: any): AuthenticatedCloutLog {
    const publicData = this.toPublicCloutLog(dbLog);

    return {
      ...publicData,

      // Enhanced source identification
      source: this.identifyLogSource(dbLog),

      // Achievement context (if applicable)
      achievement: dbLog.achievement ? {
        id: dbLog.achievement.id as AchievementId,
        name: dbLog.achievement.name,
        iconUrl: dbLog.achievement.iconUrl
      } : undefined
    };
  }

  /**
   * Transform clout log for admin view
   * Includes all system data and moderation info
   */
  static toAdminCloutLog(dbLog: any): AdminCloutLog {
    // Get authenticated data with admin permissions
    const authenticatedData = this.toAuthenticatedCloutLog(dbLog, { role: 'admin' });

    return {
      ...authenticatedData,

      // System tracking
      systemData: {
        userId: dbLog.userId as UserId,
        achievementId: dbLog.achievementId as AchievementId,
        adminId: dbLog.adminId as UserId,
        ipHash: this.anonymizeIP(dbLog.ipAddress)
      },

      // Admin metadata
      reviewedBy: dbLog.reviewedBy as UserId,
      flagged: dbLog.flagged === true,
      flagReason: dbLog.flagReason
    };
  }

  /**
   * Transform multiple achievements for list views
   */
  static toAchievementList(achievements: any[], requestingUser: any, viewType: 'public' | 'authenticated' | 'admin' = 'public') {
    switch (viewType) {
      case 'admin':
        return achievements.map(achievement => this.toAdminAchievement(achievement));
      case 'authenticated':
        return achievements.map(achievement => this.toAuthenticatedAchievement(achievement, requestingUser));
      default:
        return achievements.map(achievement => this.toPublicAchievement(achievement));
    }
  }

  /**
   * Transform clout logs with pagination and summary
   */
  static toCloutLogHistory(logs: any[], requestingUser: any, viewType: 'public' | 'authenticated' | 'admin' = 'public') {
    let transformedLogs;

    switch (viewType) {
      case 'admin':
        transformedLogs = logs.map(log => this.toAdminCloutLog(log));
        break;
      case 'authenticated':
        transformedLogs = logs.map(log => this.toAuthenticatedCloutLog(log, requestingUser));
        break;
      default:
        transformedLogs = logs.map(log => this.toPublicCloutLog(log));
    }

    return {
      logs: transformedLogs,
      summary: this.calculateLogSummary(logs, requestingUser)
    };
  }

  // ==========================================
  // PRIVATE UTILITY METHODS
  // ==========================================

  private static sanitizeCloutAmount(amount: any): CloutAmount {
    const parsed = parseInt(amount?.toString() || '0', 10);
    return (isNaN(parsed) ? 0 : Math.max(0, parsed)) as CloutAmount;
  }

  private static sanitizeDescription(description: string): string {
    if (!description) return '';
    return description.trim().substring(0, 200); // Limit length for security
  }

  private static sanitizeReason(reason: string, level: 'public' | 'authenticated'): string {
    if (!reason) return 'Achievement earned';

    if (level === 'public') {
      // More aggressive sanitization for public view
      return reason.replace(/admin|internal|system/gi, '').trim() || 'Achievement earned';
    }

    return reason.trim();
  }

  private static normalizeCriteriaType(criteriaType: string): string {
    const typeMap: Record<string, string> = {
      'posts_created': 'Create Posts',
      'likes_received': 'Receive Likes',
      'threads_created': 'Create Threads',
      'days_active': 'Active Days',
      'dgt_earned': 'Earn DGT',
      'tips_given': 'Give Tips'
    };

    return typeMap[criteriaType] || 'Complete Action';
  }

  private static generateCriteriaDescription(achievement: any): string {
    const { criteriaType, criteriaValue } = achievement;

    switch (criteriaType) {
      case 'posts_created':
        return `Create ${criteriaValue} posts`;
      case 'likes_received':
        return `Receive ${criteriaValue} likes`;
      case 'threads_created':
        return `Start ${criteriaValue} threads`;
      case 'days_active':
        return `Be active for ${criteriaValue} days`;
      case 'dgt_earned':
        return `Earn ${criteriaValue} DGT`;
      case 'tips_given':
        return `Give ${criteriaValue} tips`;
      default:
        return achievement.description || 'Complete the required action';
    }
  }

  private static identifyLogSource(dbLog: any): 'achievement' | 'admin_grant' | 'event' | 'system' {
    if (dbLog.achievementId) return 'achievement';
    if (dbLog.reason?.includes('Admin')) return 'admin_grant';
    if (dbLog.reason?.includes('Event') || dbLog.reason?.includes('Rain')) return 'event';
    return 'system';
  }

  private static calculateLevel(xp: number): number {
    // Basic level calculation - replace with actual level system
    return Math.floor(Math.sqrt(xp / 100)) + 1;
  }

  private static calculateCompletionRate(stats: any): number {
    if (!stats || !stats.totalUsers) return 0;
    return Math.round((stats.totalCompletions / stats.totalUsers) * 100);
  }

  private static calculateLogSummary(logs: any[], user: any) {
    const totalClout = logs.reduce((sum, log) => sum + (log.cloutEarned || 0), 0);
    const achievementLogs = logs.filter(log => log.achievementId);
    const adminLogs = logs.filter(log => log.reason?.includes('Admin'));

    return {
      totalClout,
      totalEntries: logs.length,
      fromAchievements: achievementLogs.length,
      fromAdminGrants: adminLogs.length,
      fromEvents: logs.length - achievementLogs.length - adminLogs.length
    };
  }

  private static anonymizeIP(ip?: string): string {
    if (!ip) return 'unknown';

    if (ip.includes(':')) {
      // IPv6
      const parts = ip.split(':');
      return parts.slice(0, 4).join(':') + '::***';
    } else {
      // IPv4
      const parts = ip.split('.');
      return parts.slice(0, 3).join('.') + '.***';
    }
  }
}

/**
