				.limit(1);

			if (existing.length > 0) continue;

			// Set expiration
			if (missionData.isDaily) {
				missionData.expiresAt = tomorrow;
			} else if (missionData.isWeekly) {
				missionData.expiresAt = nextWeek;
			}

			await db.insert(missions).values(missionData);
