	topSlowQueries: Array<{
		query: string;
		avgTime: number;
		count: number;
	}>;
}

interface OptimizedMessageQuery {
	roomId: RoomId;
	limit?: number;
	cursor?: number;
	direction?: 'before' | 'after';
	includeDeleted?: boolean;
	userId?: UserId;
}

export class PerformanceService {
	private static queryMetrics: QueryMetrics[] = [];
	private static readonly MAX_METRICS = 10000;
	private static connectionPool: any = null;

	/**
	 * Optimized message retrieval with intelligent caching
	 */
	static async getOptimizedMessages(options: OptimizedMessageQuery): Promise<{
		data: any[];
		meta: { count: number; hasMore: boolean; nextCursor?: number };
		fromCache: boolean;
	}> {
		const startTime = Date.now();
		const cacheKey = this.generateCacheKey('messages', options);

		// Try cache first
		const cached = ShoutboxCacheService.getCachedMessages(options.roomId);
		if (cached && this.isCacheValid(options, cached)) {
			const filtered = this.applyFiltersToCache(cached, options);

			this.recordQueryMetric({
				queryType: 'messages_cached',
				executionTime: Date.now() - startTime,
				rowsReturned: filtered.length,
				cacheHit: true,
				timestamp: new Date()
			});

			return {
				data: filtered,
				meta: {
					count: filtered.length,
					hasMore: filtered.length >= (options.limit || 50),
					nextCursor: filtered.length > 0 ? filtered[filtered.length - 1].id : undefined
				},
				fromCache: true
			};
		}

		// Database query with optimizations
		const result = await this.executeOptimizedQuery(options);

		// Cache the result
		await ShoutboxCacheService.cacheMessages(options.roomId, result.data);

		this.recordQueryMetric({
			queryType: 'messages_db',
			executionTime: Date.now() - startTime,
			rowsReturned: result.data.length,
			cacheHit: false,
			timestamp: new Date()
		});

		return {
			...result,
			fromCache: false
		};
	}

	/**
	 * Execute optimized database query
	 */
	private static async executeOptimizedQuery(options: OptimizedMessageQuery): Promise<{
		data: any[];
		meta: { count: number; hasMore: boolean; nextCursor?: number };
	}> {
		const {
			roomId,
			limit = 50,
			cursor,
			direction = 'before',
			includeDeleted = false,
			userId
		} = options;

		// Build optimized query conditions
		const conditions = [eq(shoutboxMessages.roomId, roomId)];

		if (!includeDeleted) {
			conditions.push(eq(shoutboxMessages.isDeleted, false));
		}

		if (userId) {
			conditions.push(eq(shoutboxMessages.userId, userId));
		}

		if (cursor) {
			conditions.push(
				direction === 'before'
					? sql`${shoutboxMessages.id} < ${cursor}`
					: sql`${shoutboxMessages.id} > ${cursor}`
			);
		}

		// Use prepared statement for better performance
		const query = db
			.select({
				id: shoutboxMessages.id,
				userId: shoutboxMessages.userId,
				roomId: shoutboxMessages.roomId,
				content: shoutboxMessages.content,
				createdAt: shoutboxMessages.createdAt,
				editedAt: shoutboxMessages.editedAt,
				isDeleted: shoutboxMessages.isDeleted,
				isPinned: shoutboxMessages.isPinned,
				tipAmount: shoutboxMessages.tipAmount,
				username: users.username,
				userLevel: users.level,
				userRoles: users.roles,
				usernameColor: users.usernameColor,
				avatarUrl: users.avatarUrl
			})
			.from(shoutboxMessages)
			.leftJoin(users, eq(shoutboxMessages.userId, users.id))
			.where(and(...conditions))
			.orderBy(desc(shoutboxMessages.id))
			.limit(limit + 1); // +1 to check for more results

		const results = await query;

		// Check if there are more results
		const hasMore = results.length > limit;
		const data = hasMore ? results.slice(0, limit) : results;

		// Transform to expected format
		const transformedData = data.map((row) => ({
			id: row.id,
			userId: row.userId,
			roomId: row.roomId,
			content: row.content,
			createdAt: row.createdAt,
			editedAt: row.editedAt,
			isDeleted: row.isDeleted,
			isPinned: row.isPinned,
			tipAmount: row.tipAmount,
			user: row.userId
				? {
						username: row.username || 'Unknown',
						level: row.userLevel || 1,
						roles: row.userRoles || [],
						usernameColor: row.usernameColor,
						avatarUrl: row.avatarUrl
					}
				: null
		}));

		return {
			data: transformedData,
			meta: {
				count: data.length,
				hasMore,
				nextCursor: data.length > 0 ? data[data.length - 1].id : undefined
			}
		};
	}

	/**
	 * Batch message operations for efficiency
	 */
	static async batchInsertMessages(
		messages: Array<{
			userId: UserId;
			roomId: RoomId;
			content: string;
			type?: string;
			metadata?: any;
		}>
	): Promise<{ insertedIds: number[]; errors: any[] }> {
		const startTime = Date.now();
		const insertedIds: number[] = [];
		const errors: any[] = [];

		try {
			// Use database transaction for consistency
			const result = await db.transaction(async (tx) => {
				const insertPromises = messages.map(async (message) => {
					try {
						const [inserted] = await tx
							.insert(shoutboxMessages)
							.values({
								userId: message.userId,
								roomId: message.roomId,
								content: message.content,
								createdAt: new Date(),
								isDeleted: false,
								isPinned: false
							})
							.returning({ id: shoutboxMessages.id });

						return inserted.id;
					} catch (error) {
						errors.push({ message, error });
						return null;
					}
				});

				const results = await Promise.all(insertPromises);
				return results.filter((id) => id !== null) as number[];
			});

			insertedIds.push(...result);

			// Invalidate cache for affected rooms
			const affectedRooms = new Set(messages.map((m) => m.roomId));
			affectedRooms.forEach((roomId) => {
				ShoutboxCacheService.invalidateMessages(roomId);
			});

			this.recordQueryMetric({
				queryType: 'batch_insert',
				executionTime: Date.now() - startTime,
				rowsReturned: insertedIds.length,
				cacheHit: false,
				timestamp: new Date()
			});
		} catch (error) {
			logger.error('PerformanceService', 'Batch insert failed', {
				error,
				messageCount: messages.length
			});
			errors.push({ error, messages });
		}

		return { insertedIds, errors };
	}

	/**
	 * Optimized user session management
	 */
	static async getActiveUsersInRoom(roomId: RoomId): Promise<
		Array<{
			id: EntityId;
			username: string;
			level: number;
			roles: string[];
			lastSeen: Date;
		}>
	> {
		const startTime = Date.now();
		const cacheKey = `active_users:${roomId}`;

		// Try cache first
		const cached = ShoutboxCacheService.getCachedRoom(roomId);
		if (cached && cached.onlineUsers.size > 0) {
			// Get user details for online users
			const userIds = Array.from(cached.onlineUsers);

			const users = await db
				.select({
					id: users.id,
					username: users.username,
					level: users.level,
					roles: users.roles,
					lastSeen: users.lastActiveAt
				})
				.from(users)
				.where(inArray(users.id, userIds));

			this.recordQueryMetric({
				queryType: 'active_users_optimized',
				executionTime: Date.now() - startTime,
				rowsReturned: users.length,
				cacheHit: true,
				timestamp: new Date()
			});

			return users.map((user) => ({
				id: user.id,
				username: user.username,
				level: user.level || 1,
				roles: user.roles || [],
				lastSeen: user.lastSeen || new Date()
			}));
		}

		// Fallback to recent activity query
		const recentActiveTime = new Date(Date.now() - 5 * 60 * 1000); // 5 minutes

		const activeUsers = await db
			.select({
				id: users.id,
				username: users.username,
				level: users.level,
				roles: users.roles,
				lastSeen: users.lastActiveAt
			})
			.from(users)
			.innerJoin(shoutboxMessages, eq(users.id, shoutboxMessages.userId))
			.where(
				and(eq(shoutboxMessages.roomId, roomId), gte(shoutboxMessages.createdAt, recentActiveTime))
			)
			.groupBy(users.id, users.username, users.level, users.roles, users.lastActiveAt);

		this.recordQueryMetric({
			queryType: 'active_users_db',
			executionTime: Date.now() - startTime,
			rowsReturned: activeUsers.length,
			cacheHit: false,
			timestamp: new Date()
		});

		return activeUsers.map((user) => ({
			id: user.id,
			username: user.username,
			level: user.level || 1,
			roles: user.roles || [],
			lastSeen: user.lastSeen || new Date()
		}));
	}

	/**
	 * Performance monitoring and metrics
	 */
	static getPerformanceStats(): PerformanceStats {
		const now = Date.now();
		const oneMinuteAgo = now - 60000;

		// Filter recent metrics
		const recentMetrics = this.queryMetrics.filter((m) => m.timestamp.getTime() > oneMinuteAgo);

		// Calculate statistics
		const totalQueries = recentMetrics.length;
		const avgQueryTime =
			totalQueries > 0
				? recentMetrics.reduce((sum, m) => sum + m.executionTime, 0) / totalQueries
				: 0;

		const cacheHits = recentMetrics.filter((m) => m.cacheHit).length;
		const cacheHitRate = totalQueries > 0 ? (cacheHits / totalQueries) * 100 : 0;

		const queriesPerSecond = totalQueries / 60;

		// Top slow queries
		const queryGroups = new Map<string, { totalTime: number; count: number }>();
		recentMetrics.forEach((metric) => {
			const existing = queryGroups.get(metric.queryType) || { totalTime: 0, count: 0 };
			queryGroups.set(metric.queryType, {
				totalTime: existing.totalTime + metric.executionTime,
				count: existing.count + 1
			});
		});

		const topSlowQueries = Array.from(queryGroups.entries())
			.map(([query, stats]) => ({
				query,
				avgTime: stats.totalTime / stats.count,
				count: stats.count
			}))
			.sort((a, b) => b.avgTime - a.avgTime)
			.slice(0, 5);

		return {
			avgQueryTime,
			cacheHitRate,
			queriesPerSecond,
			activeConnections: this.getActiveConnections(),
			memoryUsage: this.getMemoryUsage(),
			topSlowQueries
		};
	}

	/**
	 * Query optimization suggestions
	 */
	static analyzeQueryPerformance(): Array<{
		issue: string;
		suggestion: string;
		impact: 'low' | 'medium' | 'high';
	}> {
		const suggestions: Array<{
			issue: string;
			suggestion: string;
			impact: 'low' | 'medium' | 'high';
		}> = [];

		const stats = this.getPerformanceStats();

		// Check cache hit rate
		if (stats.cacheHitRate < 70) {
			suggestions.push({
				issue: `Low cache hit rate: ${stats.cacheHitRate.toFixed(1)}%`,
				suggestion: 'Increase cache TTL or improve cache key strategy',
				impact: 'high'
			});
		}

		// Check average query time
		if (stats.avgQueryTime > 100) {
			suggestions.push({
				issue: `High average query time: ${stats.avgQueryTime.toFixed(1)}ms`,
				suggestion: 'Optimize slow queries or add database indexes',
				impact: 'high'
			});
		}

		// Check queries per second
		if (stats.queriesPerSecond > 100) {
			suggestions.push({
				issue: `High query rate: ${stats.queriesPerSecond.toFixed(1)} QPS`,
				suggestion: 'Implement connection pooling or query batching',
				impact: 'medium'
			});
		}

		return suggestions;
	}

	/**
	 * Helper methods
	 */
	private static generateCacheKey(type: string, options: any): string {
		return `${type}:${JSON.stringify(options)}`;
	}

	private static isCacheValid(options: OptimizedMessageQuery, cached: any[]): boolean {
		// Simple validation - in production, implement more sophisticated logic
		return cached.length > 0 && (!options.limit || cached.length >= options.limit);
	}

	private static applyFiltersToCache(cached: any[], options: OptimizedMessageQuery): any[] {
		let filtered = [...cached];

		if (options.userId) {
			filtered = filtered.filter((m) => m.userId === options.userId);
		}

		if (!options.includeDeleted) {
			filtered = filtered.filter((m) => !m.isDeleted);
		}

		if (options.cursor) {
			filtered = filtered.filter((m) =>
				options.direction === 'before' ? m.id < options.cursor! : m.id > options.cursor!
			);
		}

		if (options.limit) {
			filtered = filtered.slice(0, options.limit);
		}

		return filtered;
	}

	private static recordQueryMetric(metric: QueryMetrics): void {
		this.queryMetrics.push(metric);

		// Keep only recent metrics
		if (this.queryMetrics.length > this.MAX_METRICS) {
			this.queryMetrics = this.queryMetrics.slice(-Math.floor(this.MAX_METRICS * 0.8));
		}
	}

	private static getActiveConnections(): number {
		// In production, get actual connection pool stats
		return Math.floor(Math.random() * 20) + 5;
	}

	private static getMemoryUsage(): number {
		// In production, get actual memory usage
		const used = process.memoryUsage();
		return Math.round(used.heapUsed / 1024 / 1024); // MB
	}

	/**
	 * Cleanup and maintenance
	 */
	static cleanup(): void {
		// Clean old metrics
		const oneHourAgo = Date.now() - 3600000;
		this.queryMetrics = this.queryMetrics.filter((m) => m.timestamp.getTime() > oneHourAgo);

		// Clear expired cache entries
		ShoutboxCacheService.clearExpiredEntries();

		logger.debug('PerformanceService', 'Cleanup completed', {
			metricsCount: this.queryMetrics.length
		});
	}
}

// Cleanup interval
setInterval(() => {
	PerformanceService.cleanup();
}, 300000); // Every 5 minutes

/**
 * Enhanced Chat Room Service
 *
 * Handles chat room creation, management, and access control
 * with admin configuration and multi-room support
 */

import { db } from '@db';
import type { UserId } from '@shared/types';
import { chatRooms, users, shoutboxConfig, shoutboxMessages, shoutboxUserIgnores } from '@schema';
import { eq, and, or, desc, asc, sql, gt, lt, isNull, inArray, not, count } from 'drizzle-orm';
import { logger } from '@server/src/core/logger';
import { createId } from '@paralleldrive/cuid2';
import type { RoomId, GroupId } from '@shared/types';
import { EntityId } from "@shared/types";

export interface CreateRoomData {
	name: string;
	description?: string;
	isPrivate?: boolean;
	minXpRequired?: number;
	minGroupIdRequired?: GroupId;
	order?: number;
	createdBy: number;
	accessRoles?: string[];
	themeConfig?: any;
	maxUsers?: number;
}

export interface RoomWithStats {
	id: EntityId;
	name: string;
	description: string | null;
	isPrivate: boolean;
	minXpRequired: number | null;
	minGroupIdRequired: GroupId | null;
	order: number | null;
	createdAt: Date;
	createdBy: number;
	isDeleted: boolean;
	accessRoles: string[] | null;
	themeConfig: any;
	maxUsers: number | null;

	// Stats
	messageCount: number;
	activeUsers: number;
	onlineUsers: number;
	lastActivity: Date | null;
	pinnedMessageCount: number;
}

export class RoomService {
	private static cache = new Map<string, any>();
	private static readonly CACHE_TTL = 5 * 60 * 1000; // 5 minutes

	/**
	 * Create a new chat room (admin only)
	 */
	static async createRoom(roomData: CreateRoomData): Promise<{
		success: boolean;
		room?: any;
		message: string;
		error?: string;
	}> {
		try {
			// Validate room name
			if (!roomData.name || roomData.name.length < 3 || roomData.name.length > 50) {
				return {
					success: false,
					message: 'Room name must be between 3 and 50 characters',
					error: 'INVALID_ROOM_NAME'
				};
			}

			// Check if room name already exists
			const existingRoom = await db.query.chatRooms.findFirst({
				where: and(eq(chatRooms.name, roomData.name), eq(chatRooms.isDeleted, false))
			});

			if (existingRoom) {
				return {
					success: false,
					message: 'A room with this name already exists',
					error: 'ROOM_NAME_EXISTS'
				};
			}

			// Get next order if not specified
			let order = roomData.order;
			if (!order) {
				const maxOrderResult = await db
					.select({
						maxOrder: sql<number>`COALESCE(MAX(${chatRooms.order}), 0)`
					})
					.from(chatRooms)
					.where(eq(chatRooms.isDeleted, false));

				order = (maxOrderResult[0]?.maxOrder || 0) + 1;
			}

			// Create the room
			const [newRoom] = await db
				.insert(chatRooms)
				.values({
					name: roomData.name,
					description: roomData.description || null,
					isPrivate: roomData.isPrivate || false,
					minXpRequired: roomData.minXpRequired || null,
					minGroupIdRequired: roomData.minGroupIdRequired || null,
					order: order,
					createdBy: roomData.createdBy,
					accessRoles: roomData.accessRoles || null,
					themeConfig: roomData.themeConfig || {},
					maxUsers: roomData.maxUsers || null
				})
				.returning();

			// Create room-specific configuration if provided
			if (roomData.themeConfig || roomData.maxUsers) {
				await this.createRoomConfig(newRoom.id, roomData);
			}

			logger.info('RoomService', 'Chat room created', {
				roomId: newRoom.id,
				roomName: newRoom.name,
				createdBy: roomData.createdBy
			});

			return {
				success: true,
				room: newRoom,
				message: 'Chat room created successfully'
			};
		} catch (error) {
			logger.error('RoomService', 'Error creating chat room', { error, roomData });
			return {
				success: false,
				message: 'Failed to create chat room',
				error: 'ROOM_CREATION_FAILED'
			};
		}
	}

	/**
	 * Update an existing chat room
	 */
	static async updateRoom(
		roomId: RoomId,
		updateData: Partial<CreateRoomData>,
		updatedBy: UserId
	): Promise<{
		success: boolean;
		room?: any;
		message: string;
		error?: string;
	}> {
		try {
			// Check if room exists
			const existingRoom = await db.query.chatRooms.findFirst({
				where: and(eq(chatRooms.id, roomId), eq(chatRooms.isDeleted, false))
			});

			if (!existingRoom) {
				return {
					success: false,
					message: 'Chat room not found',
					error: 'ROOM_NOT_FOUND'
				};
			}

			// If updating name, check for conflicts
			if (updateData.name && updateData.name !== existingRoom.name) {
				const nameConflict = await db.query.chatRooms.findFirst({
					where: and(
						eq(chatRooms.name, updateData.name),
						eq(chatRooms.isDeleted, false),
						not(eq(chatRooms.id, roomId))
					)
				});

				if (nameConflict) {
					return {
						success: false,
						message: 'A room with this name already exists',
						error: 'ROOM_NAME_EXISTS'
					};
				}
			}

			// Update the room
			const [updatedRoom] = await db
				.update(chatRooms)
				.set({
					name: updateData.name || existingRoom.name,
					description:
						updateData.description !== undefined
							? updateData.description
							: existingRoom.description,
					isPrivate:
						updateData.isPrivate !== undefined ? updateData.isPrivate : existingRoom.isPrivate,
					minXpRequired:
						updateData.minXpRequired !== undefined
							? updateData.minXpRequired
							: existingRoom.minXpRequired,
					minGroupIdRequired:
						updateData.minGroupIdRequired !== undefined
							? updateData.minGroupIdRequired
							: existingRoom.minGroupIdRequired,
					order: updateData.order !== undefined ? updateData.order : existingRoom.order,
					accessRoles:
						updateData.accessRoles !== undefined
							? updateData.accessRoles
							: existingRoom.accessRoles,
					themeConfig:
						updateData.themeConfig !== undefined
							? updateData.themeConfig
							: existingRoom.themeConfig,
					maxUsers: updateData.maxUsers !== undefined ? updateData.maxUsers : existingRoom.maxUsers
				})
				.where(eq(chatRooms.id, roomId))
				.returning();

			// Update room-specific configuration
			if (updateData.themeConfig || updateData.maxUsers !== undefined) {
				await this.updateRoomConfig(roomId, updateData);
			}

			// Clear cache
			this.clearRoomCache(roomId);

			logger.info('RoomService', 'Chat room updated', {
				roomId,
				updatedBy,
				changes: Object.keys(updateData)
			});

			return {
				success: true,
				room: updatedRoom,
				message: 'Chat room updated successfully'
			};
		} catch (error) {
			logger.error('RoomService', 'Error updating chat room', { error, roomId, updateData });
			return {
				success: false,
				message: 'Failed to update chat room',
				error: 'ROOM_UPDATE_FAILED'
			};
		}
	}

	/**
	 * Delete a chat room (soft delete)
	 */
	static async deleteRoom(
		roomId: RoomId,
		deletedBy: UserId
	): Promise<{
		success: boolean;
		message: string;
		error?: string;
	}> {
		try {
			// Check if room exists and is not already deleted
			const existingRoom = await db.query.chatRooms.findFirst({
				where: and(eq(chatRooms.id, roomId), eq(chatRooms.isDeleted, false))
			});

			if (!existingRoom) {
				return {
					success: false,
					message: 'Chat room not found',
					error: 'ROOM_NOT_FOUND'
				};
			}

			// Prevent deletion of default room
			if (existingRoom.name === 'degen-lounge') {
				return {
					success: false,
					message: 'Cannot delete the default chat room',
					error: 'CANNOT_DELETE_DEFAULT'
				};
			}

			// Soft delete the room
			await db
				.update(chatRooms)
				.set({
					isDeleted: true,
					deletedAt: new Date()
				})
				.where(eq(chatRooms.id, roomId));

			// Also soft delete all messages in the room
			await db
				.update(shoutboxMessages)
				.set({
					isDeleted: true,
					editedAt: new Date()
				})
				.where(eq(shoutboxMessages.roomId, roomId));

			// Clear cache
			this.clearRoomCache(roomId);
			this.clearRoomCache(); // Clear general room list cache

			logger.info('RoomService', 'Chat room deleted', {
				roomId,
				roomName: existingRoom.name,
				deletedBy
			});

			return {
				success: true,
				message: 'Chat room deleted successfully'
			};
		} catch (error) {
			logger.error('RoomService', 'Error deleting chat room', { error, roomId });
			return {
				success: false,
				message: 'Failed to delete chat room',
				error: 'ROOM_DELETION_FAILED'
			};
		}
	}

	/**
	 * Get all chat rooms with statistics
	 */
	static async getRoomsWithStats(userId?: UserId): Promise<RoomWithStats[]> {
		const cacheKey = `rooms-with-stats:${userId || 'guest'}`;

		if (this.cache.has(cacheKey)) {
			return this.cache.get(cacheKey);
		}

		try {
			// Base query for rooms
			let roomsQuery = db
				.select({
					id: chatRooms.id,
					name: chatRooms.name,
					description: chatRooms.description,
					isPrivate: chatRooms.isPrivate,
					minXpRequired: chatRooms.minXpRequired,
					minGroupIdRequired: chatRooms.minGroupIdRequired,
					order: chatRooms.order,
					createdAt: chatRooms.createdAt,
					createdBy: chatRooms.createdBy,
					isDeleted: chatRooms.isDeleted,
					accessRoles: chatRooms.accessRoles,
					themeConfig: chatRooms.themeConfig,
					maxUsers: chatRooms.maxUsers
				})
				.from(chatRooms)
				.where(eq(chatRooms.isDeleted, false));

			// If user is specified, filter rooms they can access
			if (userId) {
				const userInfo = await db.query.users.findFirst({
					where: eq(users.id, userId),
					columns: { groupId: true, xp: true }
				});

				if (userInfo) {
					roomsQuery = roomsQuery.where(
						or(
							eq(chatRooms.isPrivate, false),
							eq(chatRooms.createdBy, userId),
							and(
								eq(chatRooms.isPrivate, true),
								or(
									isNull(chatRooms.minGroupIdRequired),
									sql`${userInfo.groupId} <= ${chatRooms.minGroupIdRequired}`
								),
								or(
									isNull(chatRooms.minXpRequired),
									sql`${userInfo.xp} >= ${chatRooms.minXpRequired}`
								)
							)
						)
					);
				}
			} else {
				// Guest users can only see public rooms
				roomsQuery = roomsQuery.where(eq(chatRooms.isPrivate, false));
			}

			const rooms = await roomsQuery.orderBy(asc(chatRooms.order));

			// Get statistics for each room
			const roomsWithStats: RoomWithStats[] = await Promise.all(
				rooms.map(async (room) => {
					const stats = await this.getRoomStats(room.id);
					return {
						...room,
						...stats
					};
				})
			);

			// Cache the result
			this.cache.set(cacheKey, roomsWithStats);
			setTimeout(() => this.cache.delete(cacheKey), this.CACHE_TTL);

			return roomsWithStats;
		} catch (error) {
			logger.error('RoomService', 'Error getting rooms with stats', { error, userId });
			return [];
		}
	}

	/**
	 * Get statistics for a specific room
	 */
	static async getRoomStats(roomId: RoomId): Promise<{
		messageCount: number;
		activeUsers: number;
		onlineUsers: number;
		lastActivity: Date | null;
		pinnedMessageCount: number;
	}> {
		try {
			// Get message count
			const messageCountResult = await db
				.select({
					count: count(shoutboxMessages.id)
				})
				.from(shoutboxMessages)
				.where(and(eq(shoutboxMessages.roomId, roomId), eq(shoutboxMessages.isDeleted, false)));

			// Get pinned message count
			const pinnedCountResult = await db
				.select({
					count: count(shoutboxMessages.id)
				})
				.from(shoutboxMessages)
				.where(
					and(
						eq(shoutboxMessages.roomId, roomId),
						eq(shoutboxMessages.isDeleted, false),
						eq(shoutboxMessages.isPinned, true)
					)
				);

			// Get last activity
			const lastActivityResult = await db
				.select({
					lastActivity: sql<Date>`MAX(${shoutboxMessages.createdAt})`
				})
				.from(shoutboxMessages)
				.where(and(eq(shoutboxMessages.roomId, roomId), eq(shoutboxMessages.isDeleted, false)));

			// Get active users (users who posted in last 24 hours)
			const activeUsersResult = await db
				.select({
					count: sql<number>`COUNT(DISTINCT ${shoutboxMessages.userId})`
				})
				.from(shoutboxMessages)
				.where(
					and(
						eq(shoutboxMessages.roomId, roomId),
						eq(shoutboxMessages.isDeleted, false),
						gt(shoutboxMessages.createdAt, sql`NOW() - INTERVAL '24 hours'`)
					)
				);

			return {
				messageCount: messageCountResult[0]?.count || 0,
				activeUsers: activeUsersResult[0]?.count || 0,
				onlineUsers: 0, // Would need WebSocket connection tracking
				lastActivity: lastActivityResult[0]?.lastActivity || null,
				pinnedMessageCount: pinnedCountResult[0]?.count || 0
			};
		} catch (error) {
			logger.error('RoomService', 'Error getting room stats', { error, roomId });
			return {
				messageCount: 0,
				activeUsers: 0,
				onlineUsers: 0,
				lastActivity: null,
				pinnedMessageCount: 0
			};
		}
	}

	/**
	 * Check if user has access to a room
	 */
	static async checkRoomAccess(
		userId: UserId,
		roomId: RoomId
	): Promise<{
		hasAccess: boolean;
		reason?: string;
	}> {
		try {
			const room = await db.query.chatRooms.findFirst({
				where: and(eq(chatRooms.id, roomId), eq(chatRooms.isDeleted, false))
			});

			if (!room) {
				return { hasAccess: false, reason: 'Room not found' };
			}

			// Public rooms are accessible to all authenticated users
			if (!room.isPrivate) {
				return { hasAccess: true };
			}

			// Room creator always has access
			if (room.createdBy === userId) {
				return { hasAccess: true };
			}

			// Check user requirements
			const user = await db.query.users.findFirst({
				where: eq(users.id, userId),
				columns: { groupId: true, xp: true, roles: true }
			});

			if (!user) {
				return { hasAccess: false, reason: 'User not found' };
			}

			// Check XP requirement
			if (room.minXpRequired && user.xp && user.xp < room.minXpRequired) {
				return { hasAccess: false, reason: `Requires ${room.minXpRequired} XP` };
			}

			// Check group requirement (lower groupId = higher permission)
			if (room.minGroupIdRequired && user.groupId && user.groupId > room.minGroupIdRequired) {
				return { hasAccess: false, reason: 'Insufficient permissions' };
			}

			// Check role requirements
			if (room.accessRoles && room.accessRoles.length > 0) {
				const userRoles = user.roles || [];
				const hasRequiredRole = room.accessRoles.some((role) => userRoles.includes(role));
				if (!hasRequiredRole) {
					return { hasAccess: false, reason: 'Role requirements not met' };
				}
			}

			return { hasAccess: true };
		} catch (error) {
			logger.error('RoomService', 'Error checking room access', { error, userId, roomId });
			return { hasAccess: false, reason: 'Access check failed' };
		}
	}

	/**
	 * Get user's ignored users for a room
	 */
	static async getUserIgnoreList(userId: UserId, roomId?: RoomId): Promise<UserId[]> {
		try {
			const ignoreList = await db
				.select({
					ignoredUserId: shoutboxUserIgnores.ignoredUserId
				})
				.from(shoutboxUserIgnores)
				.where(
					and(
						eq(shoutboxUserIgnores.userId, userId.toString()),
						roomId
							? eq(shoutboxUserIgnores.roomId, roomId.toString())
							: isNull(shoutboxUserIgnores.roomId)
					)
				);

			return ignoreList.map((item) => item.ignoredUserId as UserId);
		} catch (error) {
			logger.error('RoomService', 'Error getting ignore list', { error, userId, roomId });
			return [];
		}
	}

	/**
	 * Add user to ignore list
	 */
	static async ignoreUser(
		userId: UserId,
		ignoredUserId: UserId,
		roomId?: RoomId,
		options?: {
			hideMessages?: boolean;
			hideCommands?: boolean;
			hideMentions?: boolean;
		}
	): Promise<{ success: boolean; message: string }> {
		try {
			// Check if already ignoring
			const existing = await db.query.shoutboxUserIgnores.findFirst({
				where: and(
					eq(shoutboxUserIgnores.userId, userId.toString()),
					eq(shoutboxUserIgnores.ignoredUserId, ignoredUserId.toString()),
					roomId
						? eq(shoutboxUserIgnores.roomId, roomId.toString())
						: isNull(shoutboxUserIgnores.roomId)
				)
			});

			if (existing) {
				return { success: false, message: 'User is already ignored' };
			}

			// Add to ignore list
			await db.insert(shoutboxUserIgnores).values({
				userId: userId.toString(),
				ignoredUserId: ignoredUserId.toString(),
				roomId: roomId?.toString() || null,
				hideMessages: options?.hideMessages ?? true,
				hideCommands: options?.hideCommands ?? true,
				hideMentions: options?.hideMentions ?? true
			});

			return { success: true, message: 'User added to ignore list' };
		} catch (error) {
			logger.error('RoomService', 'Error ignoring user', { error, userId, ignoredUserId, roomId });
			return { success: false, message: 'Failed to ignore user' };
		}
	}

	/**
	 * Remove user from ignore list
	 */
	static async unignoreUser(
		userId: UserId,
		ignoredUserId: UserId,
		roomId?: RoomId
	): Promise<{ success: boolean; message: string }> {
		try {
			const result = await db
				.delete(shoutboxUserIgnores)
				.where(
					and(
						eq(shoutboxUserIgnores.userId, userId.toString()),
						eq(shoutboxUserIgnores.ignoredUserId, ignoredUserId.toString()),
						roomId
							? eq(shoutboxUserIgnores.roomId, roomId.toString())
							: isNull(shoutboxUserIgnores.roomId)
					)
				);

			return { success: true, message: 'User removed from ignore list' };
		} catch (error) {
			logger.error('RoomService', 'Error unignoring user', {
				error,
				userId,
				ignoredUserId,
				roomId
			});
			return { success: false, message: 'Failed to unignore user' };
		}
	}

	/**
	 * Create room-specific configuration
	 */
	private static async createRoomConfig(roomId: RoomId, roomData: CreateRoomData): Promise<void> {
		try {
			await db.insert(shoutboxConfig).values({
				scope: 'room',
				roomId: roomId.toString(),
				themeConfig: roomData.themeConfig || {},
				maxConcurrentUsers: roomData.maxUsers || 1000,
				createdBy: roomData.createdBy.toString()
			});
		} catch (error) {
			logger.error('RoomService', 'Error creating room config', { error, roomId });
		}
	}

	/**
	 * Update room-specific configuration
	 */
	private static async updateRoomConfig(
		roomId: RoomId,
		updateData: Partial<CreateRoomData>
	): Promise<void> {
		try {
			const updateValues: any = {};

			if (updateData.themeConfig !== undefined) {
				updateValues.themeConfig = updateData.themeConfig;
			}

			if (updateData.maxUsers !== undefined) {
				updateValues.maxConcurrentUsers = updateData.maxUsers;
			}

			if (Object.keys(updateValues).length > 0) {
				updateValues.updatedAt = new Date();

				await db
					.update(shoutboxConfig)
					.set(updateValues)
					.where(
						and(eq(shoutboxConfig.scope, 'room'), eq(shoutboxConfig.roomId, roomId.toString()))
					);
			}
		} catch (error) {
			logger.error('RoomService', 'Error updating room config', { error, roomId });
		}
	}

	/**
	 * Clear room cache
	 */
	private static clearRoomCache(roomId?: RoomId): void {
		if (roomId) {
			this.cache.delete(`room:${roomId}`);
			this.cache.delete(`room-stats:${roomId}`);
		}

		// Clear rooms list cache for all users
		for (const key of this.cache.keys()) {
			if (key.startsWith('rooms-with-stats:')) {
				this.cache.delete(key);
			}
		}
	}

	/**
	 * Reorder rooms
	 */
	static async reorderRooms(roomOrders: { roomId: RoomId; order: number }[]): Promise<{
		success: boolean;
		message: string;
	}> {
		try {
			// Update all room orders in a transaction
			await db.transaction(async (tx) => {
				for (const { roomId, order } of roomOrders) {
					await tx.update(chatRooms).set({ order }).where(eq(chatRooms.id, roomId));
				}
			});

			// Clear cache
			this.clearRoomCache();

			return { success: true, message: 'Room order updated successfully' };
		} catch (error) {
			logger.error('RoomService', 'Error reordering rooms', { error, roomOrders });
			return { success: false, message: 'Failed to update room order' };
		}
	}
}

/**
 * Enhanced Shoutbox Service
 *
 * Comprehensive shoutbox system with:
 * - Command processing (/tip, /rain, /airdrop, /moderation commands)
 * - Admin configuration management
 * - User ignore system
 * - Message queuing and caching
 * - Analytics tracking
 */

import { db } from '@db';
import type { UserId } from '@shared/types';
import {
	shoutboxMessages,
	chatRooms,
	users,
	shoutboxConfig,
	shoutboxUserIgnores,
	shoutboxAnalytics,
	shoutboxBannedWords,
	customEmojis
} from '@schema';
import { eq, and, or, desc, asc, sql, gt, lt, isNull, inArray, not } from 'drizzle-orm';
import { logger } from '@server/src/core/logger';
import { createId } from '@paralleldrive/cuid2';
import type { NewShoutboxConfig, ShoutboxConfig } from '@schema/admin/shoutboxConfig';
import type { RoomId } from '@shared/types';

interface MessageContext {
	userId: UserId;
	username: string;
	roomId: RoomId;
	content: string;
	userRoles: string[];
	userLevel: number;
	ipAddress?: string;
	sessionId?: string;
}

interface CommandResult {
	success: boolean;
	message: string;
	data?: any;
	error?: string;
	broadcastData?: any;
}

export class ShoutboxService {
	private static cache = new Map<string, any>();
	private static configCache = new Map<string, ShoutboxConfig>();
	private static readonly CACHE_TTL = 5 * 60 * 1000; // 5 minutes

	/**
	 * Get shoutbox configuration with hierarchical override system
	 * Global config can be overridden by room-specific config
	 */
	static async getConfig(roomId?: RoomId): Promise<ShoutboxConfig> {
		const cacheKey = `config:${roomId || 'global'}`;

		if (this.configCache.has(cacheKey)) {
			return this.configCache.get(cacheKey)!;
		}

		try {
			// Get global config first
			let globalConfig = await db.query.shoutboxConfig.findFirst({
				where: and(eq(shoutboxConfig.scope, 'global'), isNull(shoutboxConfig.roomId))
			});

			// Create default global config if none exists
			if (!globalConfig) {
				globalConfig = await this.createDefaultConfig();
			}

			let finalConfig = globalConfig;

			// If room-specific config is requested, merge with global
			if (roomId) {
				const roomConfig = await db.query.shoutboxConfig.findFirst({
					where: and(eq(shoutboxConfig.scope, 'room'), eq(shoutboxConfig.roomId, roomId.toString()))
				});

				if (roomConfig) {
					// Merge room config over global config
					finalConfig = { ...globalConfig, ...roomConfig };
				}
			}

			this.configCache.set(cacheKey, finalConfig);

			// Auto-expire cache
			setTimeout(() => this.configCache.delete(cacheKey), this.CACHE_TTL);

			return finalConfig;
		} catch (error) {
			logger.error('ShoutboxService', 'Error loading config', { error, roomId });
			return await this.createDefaultConfig();
		}
	}

	/**
	 * Create default shoutbox configuration
	 */
	private static async createDefaultConfig(): Promise<ShoutboxConfig> {
		const defaultConfig: NewShoutboxConfig = {
