// Rate limiting for shoutbox messages (10 seconds cooldown)
const userLastMessageTime = new Map<UserId, number>();
const COOLDOWN_MS = 10000; // 10 seconds

// Check if user has access to a specific chat room
async function userHasRoomAccess(userId: UserId, roomId: RoomId): Promise<boolean> {
	try {
		// Get the room info
		const roomInfo = await db.select().from(chatRooms).where(eq(chatRooms.id, roomId)).limit(1);

		if (roomInfo.length === 0 || roomInfo[0].isDeleted) {
			return false;
		}

		const room = roomInfo[0];

		// If the room is not private, or if the user created the room, they have access
		if (!room.isPrivate || room.createdBy === userId) {
			return true;
		}

		// Check if the user meets the minimum XP requirement
		if (room.minXpRequired && room.minXpRequired > 0) {
			const userInfo = await db
				.select({ xp: users.xp })
				.from(users)
				.where(eq(users.id, userId))
				.limit(1);

			if (userInfo.length === 0 || !userInfo[0].xp || userInfo[0].xp < room.minXpRequired) {
				return false;
			}
		}

		// Check if the user meets the minimum group requirement
		if (room.minGroupIdRequired) {
			const userInfo = await db
				.select({ groupId: users.groupId })
				.from(users)
				.where(eq(users.id, userId))
				.limit(1);

			if (userInfo.length === 0 || !userInfo[0].groupId) {
				return false;
			}

			// Lower group IDs typically have higher permissions
			// Admin = 1, Moderator = 2, Regular user = 3+
			if (userInfo[0].groupId > room.minGroupIdRequired) {
				return false;
			}
		}

		return true;
	} catch (error) {
		logger.error('ShoutboxRoutes', 'Error checking room access', { err: error, roomId, userId });
		return false;
	}
}

const router = Router();

// Get available chat rooms
router.get('/rooms', async (req: Request, res: Response) => {
	try {
		const userId = userService.getUserFromRequest(req);
		const isAdminOrMod = userService.getUserFromRequest(req)
			? await canUser(
					{
						id: (userService.getUserFromRequest(req) as any).id,
						primaryRoleId: (userService.getUserFromRequest(req) as any).primaryRoleId,
						secondaryRoleIds: (userService.getUserFromRequest(req) as any).secondaryRoleIds
					},
					'canModerateChat'
				)
			: false;

		// Base query - get non-deleted rooms
		let query = db
			.select({
				id: chatRooms.id,
				name: chatRooms.name,
				description: chatRooms.description,
				isPrivate: chatRooms.isPrivate,
				minXpRequired: chatRooms.minXpRequired,
				createdAt: chatRooms.createdAt,
				createdBy: chatRooms.createdBy,
				order: chatRooms.order
			})
			.from(chatRooms)
			.where(eq(chatRooms.isDeleted, false))
			.orderBy(asc(chatRooms.order));

		// If user is not admin/mod, exclude private rooms they don't have access to
		if (!isAdminOrMod && userId) {
			// For logged-in regular users, filter rooms they can access:
			// 1. Public rooms (not private)
			// 2. Private rooms where they meet the group requirement
			query = db
				.select({
					id: chatRooms.id,
					name: chatRooms.name,
					description: chatRooms.description,
					isPrivate: chatRooms.isPrivate,
					minXpRequired: chatRooms.minXpRequired,
					createdAt: chatRooms.createdAt,
					createdBy: chatRooms.createdBy,
					order: chatRooms.order
				})
				.from(chatRooms)
				.where(
					and(
						eq(chatRooms.isDeleted, false),
						or(
							eq(chatRooms.isPrivate, false),
							and(
								eq(chatRooms.isPrivate, true),
								or(
									// User created the room
									eq(chatRooms.createdBy, userId),
									// User meets minimum group requirement (lower groupId = higher permission)
									sql`(
                  SELECT ${users.groupId} FROM ${users} 
                  WHERE ${users.id} = ${userId}
                ) <= ${chatRooms.minGroupIdRequired}`
								)
							)
						)
					)
				)
				.orderBy(asc(chatRooms.order));
		} else if (!userId) {
			// For guests, only show public rooms
			query = db
				.select({
					id: chatRooms.id,
					name: chatRooms.name,
					description: chatRooms.description,
					isPrivate: chatRooms.isPrivate,
					minXpRequired: chatRooms.minXpRequired,
					createdAt: chatRooms.createdAt,
					createdBy: chatRooms.createdBy,
					order: chatRooms.order
				})
				.from(chatRooms)
				.where(and(eq(chatRooms.isDeleted, false), eq(chatRooms.isPrivate, false)))
				.orderBy(asc(chatRooms.order));
		}

		const rooms = await query;

		// Add accessibility information for the current user
		const roomsWithAccess = rooms.map((room) => {
			const accessible = !room.isPrivate || isAdminOrMod || (userId && room.createdBy === userId);

			return {
				...room,
				accessible,
				locked: !accessible
			};
		});

		res.json(roomsWithAccess);
	} catch (error) {
		logger.error('ShoutboxRoutes', 'Error fetching chat rooms', { err: error });
		res.status(500).json({ error: 'Failed to fetch chat rooms' });
	}
});

// Moderation endpoint: Delete (soft delete) a shoutbox message with WebSocket notification
router.delete('/messages/:id', isAdminOrModerator, async (req: Request, res: Response) => {
	try {
		const messageId = req.params.id as MessageId;

		// Check if message exists
		const existingMessage = await db
			.select()
			.from(shoutboxMessages)
			.where(eq(shoutboxMessages.id, messageId))
			.limit(1);

		if (existingMessage.length === 0) {
			return res.status(404).json({ error: 'Message not found' });
		}

		// Soft delete the message (mark as deleted)
		const result = await db
			.update(shoutboxMessages)
			.set({
				isDeleted: true,
				editedAt: new Date()
			})
			.where(eq(shoutboxMessages.id, messageId))
			.returning();

		// Log the moderation action
		const moderatorId = userService.getUserFromRequest(req);
		logger.info('ShoutboxRoutes', `User ${moderatorId} deleted shoutbox message ${messageId}`, {
			moderatorId,
			messageId,
			action: 'delete_shoutbox_message'
		});

		const deletedMessage = result[0];

		// Notify connected clients about the deletion
		if (req.app && (req.app as any).wss && (req.app as any).wss.clients) {
			const clients = (req.app as any).wss.clients;
			const broadcastData = {
				type: 'chat_update',
				action: 'message_deleted',
				messageId: messageId,
				roomId: deletedMessage.roomId,
				timestamp: new Date().toISOString()
			};

			for (const client of clients) {
				if (client.readyState === 1) {
					// WebSocket.OPEN
					client.send(JSON.stringify(broadcastData));
				}
			}
		}

		res.json({
			success: true,
			message: 'Message deleted successfully',
			deletedMessage: deletedMessage
		});
	} catch (error) {
		// console.error('Error deleting shoutbox message:', error); // Original console.error removed
		const messageIdForLog = req.params.id as MessageId; // Ensure messageId is available for logging
		logger.error('ShoutboxRoutes', 'Error deleting shoutbox message', {
			err: error,
			messageId: messageIdForLog
		});
		res.status(500).json({ error: 'Failed to delete message' });
	}
});

// Get latest shoutbox messages - now with room support
router.get('/messages', async (req: Request, res: Response) => {
	try {
		const limit = req.query.limit ? parseInt(req.query.limit as string) : 50;
		const roomId = req.query.roomId ? (req.query.roomId as string) as RoomId : null;
		const currentUserId = userService.getUserFromRequest(req);

		// Check if the user has access to the specified room
		if (roomId && currentUserId) {
			const hasAccess = await userHasRoomAccess(currentUserId, roomId);
			if (!hasAccess) {
				return res.status(403).json({ error: 'You do not have access to this room' });
			}
		} else if (roomId && !currentUserId) {
			// If guest tries to access a specific room, check if it's public
			const roomInfo = await db
				.select({ isPrivate: chatRooms.isPrivate })
				.from(chatRooms)
				.where(eq(chatRooms.id, roomId))
				.limit(1);
			if (roomInfo.length === 0 || roomInfo[0].isPrivate) {
				return res.status(403).json({ error: 'You do not have access to this room' });
			}
		}

		// Variable to hold our where condition
		let whereCondition;
		let targetRoomId: RoomId | null = roomId;

		// Determine the target room ID if not explicitly provided
		if (!targetRoomId) {
			try {
				const defaultRoom = await db
					.select({ id: chatRooms.id })
					.from(chatRooms)
					.where(and(eq(chatRooms.name, 'degen-lounge'), eq(chatRooms.isDeleted, false)))
					.limit(1);
				if (defaultRoom.length > 0) {
					targetRoomId = defaultRoom[0].id;
				} else {
					// If no default room, maybe return empty or fetch from all public rooms?
					// For now, let's return empty if no room is specified and default doesn't exist.
					return res.json([]);
				}
			} catch (error) {
				logger.warn('ShoutboxRoutes', 'Error fetching default room', { err: error });
				return res.status(500).json({ error: 'Failed to determine chat room' });
			}
		}

		// Ensure we have a target room ID to filter by
		if (!targetRoomId) {
			// This case should ideally not be reached if the logic above is sound
			return res.status(400).json({ error: 'Could not determine the target chat room.' });
		}

		whereCondition = eq(shoutboxMessages.roomId, targetRoomId);

		// Perform a single query with LEFT JOIN
		const messagesWithUsers = await db
			.select({
				// Message fields
				id: shoutboxMessages.id,
				userId: shoutboxMessages.userId,
				roomId: shoutboxMessages.roomId,
				content: shoutboxMessages.content,
				createdAt: shoutboxMessages.createdAt,
				editedAt: shoutboxMessages.editedAt,
				isDeleted: shoutboxMessages.isDeleted,
				isPinned: shoutboxMessages.isPinned,
				tipAmount: shoutboxMessages.tipAmount,
				// User fields (prefixed)
				u_id: users.id,
				u_username: users.username,
				u_avatarUrl: users.avatarUrl,
				u_activeAvatarUrl: users.activeAvatarUrl,
				u_level: users.level,
				u_groupId: users.groupId,
				u_isActive: users.isActive, // Include flags to check status
				u_isBanned: users.isBanned
			})
			.from(shoutboxMessages)
			.leftJoin(users, eq(shoutboxMessages.userId, users.id))
			.where(
				and(
					whereCondition // Filter by the target room
					// Optionally hide deleted messages unless requested?
					// For now, include deleted messages and let frontend handle visibility
				)
			)
			.orderBy(desc(shoutboxMessages.createdAt))
			.limit(limit);

		// Process the results
		const messages = messagesWithUsers.map((row) => {
			let user = null;
			// Check if user exists and is active/not banned
			if (row.u_id && row.u_isActive && !row.u_isBanned) {
				user = {
					id: row.u_id,
					username: row.u_username,
					avatarUrl: row.u_avatarUrl,
					activeAvatarUrl: row.u_activeAvatarUrl,
					level: row.u_level,
					groupId: row.u_groupId
				};
			} else if (row.u_id) {
				// User exists but is inactive or banned
				user = {
					id: row.u_id,
					username: row.u_username || '[Inactive User]',
					avatarUrl: null,
					activeAvatarUrl: null,
					level: 1,
					groupId: null, // Indicate inactive/banned status
					isInactiveOrBanned: true
				};
			} else {
				// User ID was likely null in the message, or user truly deleted
				user = {
					id: row.userId, // Keep original ID if available
					username: '[Deleted User]',
					avatarUrl: null,
					activeAvatarUrl: null,
					level: 1,
					groupId: null,
					isDeleted: true
				};
			}

			return {
				id: row.id,
				roomId: row.roomId,
				content: row.content,
				createdAt: row.createdAt,
				editedAt: row.editedAt,
				isDeleted: row.isDeleted,
				isPinned: row.isPinned,
				tipAmount: row.tipAmount,
				user // Attach the processed user object
			};
		});

		res.json(messages);
	} catch (error) {
		const roomIdForLog = req.query.roomId ? (req.query.roomId as string) as RoomId : null;
		const limitForLog = req.query.limit ? parseInt(req.query.limit as string) : 50;
		logger.error('ShoutboxRoutes', 'Error fetching shoutbox messages', {
			err: error,
			roomId: roomIdForLog,
			limit: limitForLog
		});
		res.status(500).json({ error: 'Failed to fetch shoutbox messages' });
	}
});

// Post a new shoutbox message - now with room support and WebSocket broadcast
router.post('/messages', isAuthenticated, async (req: Request, res: Response) => {
	try {
		const userId = userService.getUserFromRequest(req);
		const { roomId } = req.body;

		// Check rate limiting
		const lastMessageTime = userLastMessageTime.get(userId) || 0;
		const now = Date.now();

		if (now - lastMessageTime < COOLDOWN_MS) {
			const waitTime = Math.ceil((COOLDOWN_MS - (now - lastMessageTime)) / 1000);
			return res.status(429).json({
				error: `Please wait ${waitTime} seconds before sending another message`
			});
		}

		// If roomId is provided, check if user has access to this room
		if (roomId) {
			const hasAccess = await userHasRoomAccess(userId, roomId);
			if (!hasAccess) {
				return res.status(403).json({ error: 'You do not have access to this room' });
			}
		} else {
			// If no roomId specified, get the default room (degen-lounge)
			const defaultRoom = await db
				.select()
				.from(chatRooms)
				.where(eq(chatRooms.name, 'degen-lounge'))
				.limit(1);

			if (defaultRoom.length > 0) {
				req.body.roomId = defaultRoom[0].id;
			}
		}

		// Validate message data
		const messageData = insertShoutboxMessageSchema.parse({
			...req.body,
			userId
		});

		// Insert message
		const result = await db.insert(shoutboxMessages).values(messageData).returning();
		const newMessage = result[0];

		// Update rate limit tracking
		userLastMessageTime.set(userId, now);

		// Process mentions in the shoutbox message
		try {
			await MentionsService.processMentions({
				content: messageData.content,
				mentioningUserId: userId.toString(),
				type: 'shoutbox',
				messageId: newMessage.id.toString(),
				context: `Shoutbox message in room ${newMessage.roomId}`
			});
		} catch (mentionError) {
			logger.error('ShoutboxRoutes', 'Error processing mentions for shoutbox message', {
				err: mentionError,
				messageId: newMessage.id,
				userId
			});
			// Don't fail the message creation if mentions fail
		}

		// Get user data to return with the message
		const userData = await db
			.select({
				id: users.id,
				username: users.username,
				avatarUrl: users.avatarUrl,
				activeAvatarUrl: users.activeAvatarUrl,
				level: users.level,
				groupId: users.groupId
			})
			.from(users)
			.where(eq(users.id, userId))
			.limit(1);

		const responseData = {
			...newMessage,
			user: userData[0]
		};

		// Broadcast the message to all connected WebSocket clients
		if (req.app && (req.app as any).wss && (req.app as any).wss.clients) {
			const clients = (req.app as any).wss.clients;
			const broadcastData = {
				type: 'chat_update',
				action: 'new_message',
				message: responseData,
				roomId: newMessage.roomId,
				timestamp: new Date().toISOString()
			};

			for (const client of clients) {
				if (client.readyState === 1) {
					// WebSocket.OPEN
					client.send(JSON.stringify(broadcastData));
				}
			}
		}

		res.status(201).json(responseData);
	} catch (error) {
		if (error instanceof ZodError) {
			return res.status(400).json({
				error: 'Invalid message data',
				details: error.errors
			});
		}

		const userIdForLog = userService.getUserFromRequest(req); // Ensure userId is available
		const roomIdForLog = req.body.roomId; // Get roomId from request body for logging context
		logger.error('ShoutboxRoutes', 'Error creating shoutbox message', {
			err: error,
			userId: userIdForLog,
			roomId: roomIdForLog
		});
		res.status(500).json({ error: 'Failed to create shoutbox message' });
	}
});

// Update a shoutbox message (for pinning/unpinning) - admin/mod only with WebSocket notification
router.patch('/messages/:id', isAdminOrModerator, async (req: Request, res: Response) => {
	try {
		const messageId = req.params.id as MessageId;

		// Check if message exists
		const existingMessage = await db
			.select()
			.from(shoutboxMessages)
			.where(eq(shoutboxMessages.id, messageId))
			.limit(1);

		if (existingMessage.length === 0) {
			return res.status(404).json({ error: 'Message not found' });
		}

		// Validate update data - currently only supporting isPinned
		const { isPinned } = req.body;

		// Check if isPinned is a boolean
		if (typeof isPinned !== 'boolean') {
			return res.status(400).json({ error: 'isPinned must be a boolean value' });
		}

		// Update the message
		const result = await db
			.update(shoutboxMessages)
			.set({
				isPinned,
				editedAt: new Date() // Update the edited timestamp
			})
			.where(eq(shoutboxMessages.id, messageId))
			.returning();

		// Log the moderation action
		const moderatorId = userService.getUserFromRequest(req);
		logger.info(
			'ShoutboxRoutes',
			`User ${moderatorId} ${isPinned ? 'pinned' : 'unpinned'} shoutbox message ${messageId}`,
			{
				moderatorId,
				messageId,
				action: isPinned ? 'pin_shoutbox_message' : 'unpin_shoutbox_message',
				isPinned
			}
		);

		const updatedMessage = result[0];

		// Notify connected clients about the pin/unpin action
		if (req.app && (req.app as any).wss && (req.app as any).wss.clients) {
			const clients = (req.app as any).wss.clients;
			const broadcastData = {
				type: 'chat_update',
				action: isPinned ? 'message_pinned' : 'message_unpinned',
				messageId: messageId,
				roomId: updatedMessage.roomId,
				timestamp: new Date().toISOString()
			};

			for (const client of clients) {
				if (client.readyState === 1) {
					// WebSocket.OPEN
					client.send(JSON.stringify(broadcastData));
				}
			}
		}

		res.json({
			success: true,
			message: isPinned ? 'Message pinned successfully' : 'Message unpinned successfully',
			updatedMessage: updatedMessage
		});
	} catch (error) {
		// console.error('Error updating shoutbox message:', error); // Original console.error removed
		const messageIdForLog = req.params.id as MessageId; // Ensure messageId is available
		const isPinnedForLog = req.body.isPinned; // Ensure isPinned is available
		logger.error('ShoutboxRoutes', 'Error updating shoutbox message', {
			err: error,
			messageId: messageIdForLog,
			isPinned: isPinnedForLog
		});
		res.status(500).json({ error: 'Failed to update message' });
	}
});

export default router;

import { db } from '@db';
import { userFollows, userFollowPreferences, followRequests, users } from '@schema';
import { eq, and, desc, sql, count, inArray } from 'drizzle-orm';
import type { FollowNotificationSettings, UserStats } from './follows.types';
import type { RequestId } from '@shared/types';

export class FollowsService {
	/**
	 * Follow a user
	 */
	static async followUser({
		followerId,
		followedId,
		notificationSettings = {}
	}: {
		followerId: string;
		followedId: string;
		notificationSettings?: Partial<FollowNotificationSettings>;
	}) {
		// Prevent self-following
		if (followerId === followedId) {
			throw new Error('Cannot follow yourself');
		}

		// Check if already following
		const existingFollow = await db
			.select()
			.from(userFollows)
			.where(and(eq(userFollows.followerId, followerId), eq(userFollows.followedId, followedId)))
			.limit(1);

		if (existingFollow.length > 0) {
			throw new Error('Already following this user');
		}

		// Check if follow approval is required
		const followedUserPrefs = await this.getUserFollowPreferences(followedId);

		if (followedUserPrefs.requireFollowApproval) {
			// Create follow request instead of direct follow
			const request = await db
				.insert(followRequests)
				.values({
					requesterId: followerId,
					targetId: followedId,
					message: '', // Could be extended to include a message
					isPending: true,
					isApproved: false,
					isRejected: false
				})
				.returning();

			return { type: 'request', data: request[0] };
		}

		// Create direct follow
		const follow = await db
			.insert(userFollows)
			.values({
				followerId,
				followedId,
				notifyOnPosts: notificationSettings.notifyOnPosts ?? true,
				notifyOnThreads: notificationSettings.notifyOnThreads ?? true,
				notifyOnTrades: notificationSettings.notifyOnTrades ?? false,
				notifyOnLargeStakes: notificationSettings.notifyOnLargeStakes ?? true,
				minStakeNotification: notificationSettings.minStakeNotification ?? 1000
			})
			.returning();

		return { type: 'follow', data: follow[0] };
	}

	/**
	 * Unfollow a user
	 */
	static async unfollowUser(followerId: string, followedId: string) {
		const result = await db
			.delete(userFollows)
			.where(and(eq(userFollows.followerId, followerId), eq(userFollows.followedId, followedId)))
			.returning();

		if (result.length === 0) {
			throw new Error('Follow relationship not found');
		}

		return result[0];
	}

	/**
	 * Check if user A is following user B
	 */
	static async isFollowing(followerId: string, followedId: string): Promise<boolean> {
		const result = await db
			.select({ id: userFollows.id })
			.from(userFollows)
			.where(and(eq(userFollows.followerId, followerId), eq(userFollows.followedId, followedId)))
			.limit(1);

		return result.length > 0;
	}

	/**
	 * Get user's following list
	 */
	static async getUserFollowing(userId: string, page = 1, limit = 20) {
		const offset = (page - 1) * limit;

		const following = await db
			.select({
				id: userFollows.id,
				followedAt: userFollows.createdAt,
				notificationSettings: {
					notifyOnPosts: userFollows.notifyOnPosts,
					notifyOnThreads: userFollows.notifyOnThreads,
					notifyOnTrades: userFollows.notifyOnTrades,
					notifyOnLargeStakes: userFollows.notifyOnLargeStakes,
					minStakeNotification: userFollows.minStakeNotification
				},
				user: {
					id: users.id,
					username: users.username,
					avatarUrl: users.avatarUrl,
					activeAvatarUrl: users.activeAvatarUrl,
					level: users.level,
					role: users.role,
					clout: users.clout
				}
			})
			.from(userFollows)
			.leftJoin(users, eq(userFollows.followedId, users.id))
			.where(eq(userFollows.followerId, userId))
			.orderBy(desc(userFollows.createdAt))
			.limit(limit)
			.offset(offset);

		return following;
	}

	/**
	 * Get user's followers list
	 */
	static async getUserFollowers(userId: string, page = 1, limit = 20) {
		const offset = (page - 1) * limit;

		const followers = await db
			.select({
				id: userFollows.id,
				followedAt: userFollows.createdAt,
				user: {
					id: users.id,
					username: users.username,
					avatarUrl: users.avatarUrl,
					activeAvatarUrl: users.activeAvatarUrl,
					level: users.level,
					role: users.role,
					clout: users.clout
				}
			})
			.from(userFollows)
			.leftJoin(users, eq(userFollows.followerId, users.id))
			.where(eq(userFollows.followedId, userId))
			.orderBy(desc(userFollows.createdAt))
			.limit(limit)
			.offset(offset);

		return followers;
	}

	/**
	 * Get user follow counts
	 */
	static async getUserFollowCounts(userId: string) {
		const followingCount = await db
			.select({ count: count() })
			.from(userFollows)
			.where(eq(userFollows.followerId, userId));

		const followersCount = await db
			.select({ count: count() })
			.from(userFollows)
			.where(eq(userFollows.followedId, userId));

		return {
			following: followingCount[0]?.count || 0,
			followers: followersCount[0]?.count || 0
		};
	}

	/**
	 * Get user's follow preferences
	 */
	static async getUserFollowPreferences(userId: string) {
		const preferences = await db
			.select()
			.from(userFollowPreferences)
			.where(eq(userFollowPreferences.userId, userId))
			.limit(1);

		// Return default preferences if none exist
		if (preferences.length === 0) {
			return {
				allowAllFollows: true,
				onlyFriendsCanFollow: false,
				requireFollowApproval: false,
				hideFollowerCount: false,
				hideFollowingCount: false,
				hideFollowersList: false,
				hideFollowingList: false,
				notifyOnNewFollower: true,
				emailOnNewFollower: false
			};
		}

		return preferences[0];
	}

	/**
	 * Update user's follow preferences
	 */
	static async updateUserFollowPreferences(userId: string, preferences: Partial<any>) {
		const existingPrefs = await db
			.select()
			.from(userFollowPreferences)
			.where(eq(userFollowPreferences.userId, userId))
			.limit(1);

		if (existingPrefs.length === 0) {
			// Create new preferences
			return await db
				.insert(userFollowPreferences)
				.values({
					userId,
					...preferences
				})
				.returning();
		} else {
			// Update existing preferences
			return await db
				.update(userFollowPreferences)
				.set({
					...preferences,
					updatedAt: sql`NOW()`
				})
				.where(eq(userFollowPreferences.userId, userId))
				.returning();
		}
	}

	/**
	 * Get follow requests for a user
	 */
	static async getFollowRequests(targetUserId: string) {
		const requests = await db
			.select({
				id: followRequests.id,
				message: followRequests.message,
				createdAt: followRequests.createdAt,
				requester: {
					id: users.id,
					username: users.username,
					avatarUrl: users.avatarUrl,
					activeAvatarUrl: users.activeAvatarUrl,
					level: users.level,
					role: users.role
				}
			})
			.from(followRequests)
			.leftJoin(users, eq(followRequests.requesterId, users.id))
			.where(and(eq(followRequests.targetId, targetUserId), eq(followRequests.isPending, true)))
			.orderBy(desc(followRequests.createdAt));

		return requests;
	}

	/**
	 * Respond to a follow request
	 */
	static async respondToFollowRequest(requestId: RequestId, approve: boolean) {
		// Get the request details
		const request = await db
			.select()
			.from(followRequests)
			.where(eq(followRequests.id, requestId))
			.limit(1);

		if (request.length === 0) {
			throw new Error('Follow request not found');
		}

		const requestData = request[0];

		if (!requestData.isPending) {
			throw new Error('Follow request already responded to');
		}

		// Update the request
		await db
			.update(followRequests)
			.set({
				isPending: false,
				isApproved: approve,
				isRejected: !approve,
				respondedAt: sql`NOW()`
			})
			.where(eq(followRequests.id, requestId));

		// If approved, create the follow relationship
		if (approve) {
			await db.insert(userFollows).values({
				followerId: requestData.requesterId,
				followedId: requestData.targetId,
				notifyOnPosts: true,
				notifyOnThreads: true,
				notifyOnTrades: false,
				notifyOnLargeStakes: true,
				minStakeNotification: 1000
			});
		}

		return { approved: approve, requestData };
	}

	/**
	 * Get whale candidates (users with high stats)
	 */
	static async getWhaleCandidates(limit = 20) {
		// This would typically include complex logic to identify "whales"
		// For now, we'll use level and clout as indicators
		const whales = await db
			.select({
				id: users.id,
				username: users.username,
				avatarUrl: users.avatarUrl,
				activeAvatarUrl: users.activeAvatarUrl,
				level: users.level,
				clout: users.clout,
				role: users.role,
				createdAt: users.createdAt
			})
			.from(users)
			.where(sql`${users.level} >= 25 OR ${users.clout} >= 10000`)
			.orderBy(desc(users.clout), desc(users.level))
			.limit(limit);

		return whales;
	}

	/**
	 * Get activity feed for followed users
	 */
	static async getFollowingActivity(userId: string, page = 1, limit = 20) {
		// This would integrate with other systems to show activity from followed users
		// For now, return a placeholder structure
		return {
			activities: [],
			pagination: { page, limit, hasMore: false }
		};
	}

	/**
	 * Search users for following
	 */
	static async searchUsersToFollow(query: string, currentUserId: string, limit = 10) {
		if (query.length < 1) return [];

		const searchResults = await db
			.select({
				id: users.id,
				username: users.username,
				avatarUrl: users.avatarUrl,
				activeAvatarUrl: users.activeAvatarUrl,
				level: users.level,
				role: users.role,
				clout: users.clout,
				isFollowing: sql<boolean>`EXISTS(
					SELECT 1 FROM ${userFollows} 
					WHERE ${userFollows.followerId} = ${currentUserId} 
					AND ${userFollows.followedId} = ${users.id}
				)`.as('isFollowing')
			})
			.from(users)
			.where(
				and(
					sql`LOWER(${users.username}) LIKE LOWER(${`%${query}%`})`,
					sql`${users.id} != ${currentUserId}` // Exclude current user
				)
			)
			.orderBy(desc(users.level), users.username)
			.limit(limit);

		return searchResults;
	}

	/**
	 * Update follow notification preferences
	 */
	static async updateFollowNotificationSettings(
		followerId: string,
		followedId: string,
		settings: Partial<FollowNotificationSettings>
	) {
		const result = await db
			.update(userFollows)
			.set({
				...settings,
				updatedAt: sql`NOW()`
			})
			.where(and(eq(userFollows.followerId, followerId), eq(userFollows.followedId, followedId)))
			.returning();

		if (result.length === 0) {
			throw new Error('Follow relationship not found');
		}

		return result[0];
	}
}

import { db } from '@db';
import {
	friendships,
	userFriendPreferences,
	friendGroups,
	friendGroupMembers,
	users
} from '@schema';
import { eq, and, desc, sql, count, inArray, or } from 'drizzle-orm';
import type { RequestId } from '@shared/types';

export class FriendsService {
	/**
	 * Send a friend request
	 */
	static async sendFriendRequest({
		requesterId,
		addresseeId,
		message = ''
	}: {
		requesterId: string;
		addresseeId: string;
		message?: string;
	}) {
		// Prevent self-friending
		if (requesterId === addresseeId) {
			throw new Error('Cannot send friend request to yourself');
		}

		// Check if friendship already exists
		const existingFriendship = await db
			.select()
			.from(friendships)
			.where(
				or(
					and(eq(friendships.requesterId, requesterId), eq(friendships.addresseeId, addresseeId)),
					and(eq(friendships.requesterId, addresseeId), eq(friendships.addresseeId, requesterId))
				)
			)
			.limit(1);

		if (existingFriendship.length > 0) {
			const existing = existingFriendship[0];
			if (existing.status === 'accepted') {
				throw new Error('Already friends with this user');
			} else if (existing.status === 'pending') {
				throw new Error('Friend request already pending');
			} else if (existing.status === 'blocked') {
				throw new Error('Cannot send friend request to this user');
			}
		}

		// Check addressee's friend preferences
		const addresseePrefs = await this.getUserFriendPreferences(addresseeId);

		if (!addresseePrefs.allowAllFriendRequests) {
			if (addresseePrefs.onlyMutualsCanRequest) {
				// Check if they follow each other (implement when follows service is available)
				// const areMutuals = await FollowsService.areMutualFollows(requesterId, addresseeId);
				// if (!areMutuals) throw new Error('Only mutual followers can send friend requests');
			}
		}

		// Create friend request
		const request = await db
			.insert(friendships)
			.values({
				requesterId,
				addresseeId,
				status: 'pending',
				requestMessage: message,
				allowWhispers: addresseePrefs.defaultAllowWhispers,
				allowProfileView: addresseePrefs.defaultAllowProfileView,
				allowActivityView: addresseePrefs.defaultAllowActivityView
			})
			.returning();

		return request[0];
	}

	/**
	 * Respond to a friend request
	 */
	static async respondToFriendRequest(
		requestId: RequestId,
		response: 'accept' | 'decline' | 'block'
	) {
		// Get the request
		const request = await db
			.select()
			.from(friendships)
			.where(eq(friendships.id, requestId))
			.limit(1);

		if (request.length === 0) {
			throw new Error('Friend request not found');
		}

		const requestData = request[0];

		if (requestData.status !== 'pending') {
			throw new Error('Friend request already responded to');
		}

		let newStatus: 'accepted' | 'blocked';
		if (response === 'accept') {
			newStatus = 'accepted';
		} else if (response === 'block') {
			newStatus = 'blocked';
		} else {
			// Decline - delete the request
			await db.delete(friendships).where(eq(friendships.id, requestId));
			return { action: 'declined', requestData };
		}

		// Update the request
		const updated = await db
			.update(friendships)
			.set({
				status: newStatus,
				respondedAt: sql`NOW()`,
				updatedAt: sql`NOW()`
			})
			.where(eq(friendships.id, requestId))
			.returning();

		return { action: response, friendship: updated[0] };
	}

	/**
	 * Remove a friend (unfriend)
	 */
	static async removeFriend(userId: string, friendId: string) {
		const result = await db
			.delete(friendships)
			.where(
				or(
					and(
						eq(friendships.requesterId, userId),
						eq(friendships.addresseeId, friendId),
						eq(friendships.status, 'accepted')
					),
					and(
						eq(friendships.requesterId, friendId),
						eq(friendships.addresseeId, userId),
						eq(friendships.status, 'accepted')
					)
				)
			)
			.returning();

		if (result.length === 0) {
			throw new Error('Friendship not found');
		}

		return result[0];
	}

	/**
	 * Check if two users are friends
	 */
	static async areFriends(userId1: string, userId2: string): Promise<boolean> {
		const result = await db
			.select({ id: friendships.id })
			.from(friendships)
			.where(
				and(
					or(
						and(eq(friendships.requesterId, userId1), eq(friendships.addresseeId, userId2)),
						and(eq(friendships.requesterId, userId2), eq(friendships.addresseeId, userId1))
					),
					eq(friendships.status, 'accepted')
				)
			)
			.limit(1);

		return result.length > 0;
	}

	/**
	 * Get user's friends list
	 */
	static async getUserFriends(userId: string, page = 1, limit = 20) {
		const offset = (page - 1) * limit;

		const friends = await db
			.select({
				id: friendships.id,
				friendedAt: friendships.respondedAt,
				permissions: {
					allowWhispers: friendships.allowWhispers,
					allowProfileView: friendships.allowProfileView,
					allowActivityView: friendships.allowActivityView
				},
				friend: {
					id: users.id,
					username: users.username,
					avatarUrl: users.avatarUrl,
					activeAvatarUrl: users.activeAvatarUrl,
					level: users.level,
					role: users.role,
					clout: users.clout
				}
			})
			.from(friendships)
			.leftJoin(
				users,
				or(
					and(eq(friendships.requesterId, userId), eq(users.id, friendships.addresseeId)),
					and(eq(friendships.addresseeId, userId), eq(users.id, friendships.requesterId))
				)
			)
			.where(
				and(
					or(eq(friendships.requesterId, userId), eq(friendships.addresseeId, userId)),
					eq(friendships.status, 'accepted')
				)
			)
			.orderBy(desc(friendships.respondedAt))
			.limit(limit)
			.offset(offset);

		return friends;
	}

	/**
	 * Get incoming friend requests
	 */
	static async getIncomingFriendRequests(userId: string) {
		const requests = await db
			.select({
				id: friendships.id,
				requestMessage: friendships.requestMessage,
				createdAt: friendships.createdAt,
				requester: {
					id: users.id,
					username: users.username,
					avatarUrl: users.avatarUrl,
					activeAvatarUrl: users.activeAvatarUrl,
					level: users.level,
					role: users.role
				}
			})
			.from(friendships)
			.leftJoin(users, eq(friendships.requesterId, users.id))
			.where(and(eq(friendships.addresseeId, userId), eq(friendships.status, 'pending')))
			.orderBy(desc(friendships.createdAt));

		return requests;
	}

	/**
	 * Get outgoing friend requests
	 */
	static async getOutgoingFriendRequests(userId: string) {
		const requests = await db
			.select({
				id: friendships.id,
				requestMessage: friendships.requestMessage,
				createdAt: friendships.createdAt,
				addressee: {
					id: users.id,
					username: users.username,
					avatarUrl: users.avatarUrl,
					activeAvatarUrl: users.activeAvatarUrl,
					level: users.level,
					role: users.role
				}
			})
			.from(friendships)
			.leftJoin(users, eq(friendships.addresseeId, users.id))
			.where(and(eq(friendships.requesterId, userId), eq(friendships.status, 'pending')))
			.orderBy(desc(friendships.createdAt));

		return requests;
	}

	/**
	 * Get friend counts
	 */
	static async getFriendCounts(userId: string) {
		const friendCount = await db
			.select({ count: count() })
			.from(friendships)
			.where(
				and(
					or(eq(friendships.requesterId, userId), eq(friendships.addresseeId, userId)),
					eq(friendships.status, 'accepted')
				)
			);

		const incomingRequestCount = await db
			.select({ count: count() })
			.from(friendships)
			.where(and(eq(friendships.addresseeId, userId), eq(friendships.status, 'pending')));

		return {
			friends: friendCount[0]?.count || 0,
			incomingRequests: incomingRequestCount[0]?.count || 0
		};
	}

	/**
	 * Get user's friend preferences
	 */
	static async getUserFriendPreferences(userId: string) {
		const preferences = await db
			.select()
			.from(userFriendPreferences)
			.where(eq(userFriendPreferences.userId, userId))
			.limit(1);

		// Return default preferences if none exist
		if (preferences.length === 0) {
			return {
				allowAllFriendRequests: true,
				onlyMutualsCanRequest: false,
				requireMinLevel: false,
				minLevelRequired: 1,
				autoAcceptFromFollowers: false,
				autoAcceptFromWhales: false,
				hideFriendsList: false,
				hideFriendCount: false,
				showOnlineStatus: true,
				notifyOnFriendRequest: true,
				notifyOnFriendAccept: true,
				emailOnFriendRequest: false,
				defaultAllowWhispers: true,
				defaultAllowProfileView: true,
				defaultAllowActivityView: true
			};
		}

		return preferences[0];
	}

	/**
	 * Update user's friend preferences
	 */
	static async updateUserFriendPreferences(userId: string, preferences: Partial<any>) {
		const existingPrefs = await db
			.select()
			.from(userFriendPreferences)
			.where(eq(userFriendPreferences.userId, userId))
			.limit(1);

		if (existingPrefs.length === 0) {
			// Create new preferences
			return await db
				.insert(userFriendPreferences)
				.values({
					userId,
					...preferences
				})
				.returning();
		} else {
			// Update existing preferences
			return await db
				.update(userFriendPreferences)
				.set({
					...preferences,
					updatedAt: sql`NOW()`
				})
				.where(eq(userFriendPreferences.userId, userId))
				.returning();
		}
	}

	/**
	 * Update friendship permissions
	 */
	static async updateFriendshipPermissions(
		userId: string,
		friendId: string,
		permissions: {
			allowWhispers?: boolean;
			allowProfileView?: boolean;
			allowActivityView?: boolean;
		}
	) {
		const result = await db
			.update(friendships)
			.set({
				...permissions,
				updatedAt: sql`NOW()`
			})
			.where(
				or(
					and(eq(friendships.requesterId, userId), eq(friendships.addresseeId, friendId)),
					and(eq(friendships.requesterId, friendId), eq(friendships.addresseeId, userId))
				)
			)
			.returning();

		if (result.length === 0) {
			throw new Error('Friendship not found');
		}

		return result[0];
	}

	/**
	 * Check if user can send whisper to another user
	 */
	static async canSendWhisper(senderId: string, recipientId: string): Promise<boolean> {
		// Check if they are friends
		const friendship = await db
			.select({
				allowWhispers: friendships.allowWhispers
			})
			.from(friendships)
			.where(
				and(
					or(
						and(eq(friendships.requesterId, senderId), eq(friendships.addresseeId, recipientId)),
						and(eq(friendships.requesterId, recipientId), eq(friendships.addresseeId, senderId))
					),
					eq(friendships.status, 'accepted')
				)
			)
			.limit(1);

		// If they're friends, check whisper permission
		if (friendship.length > 0) {
			return friendship[0].allowWhispers;
		}

		// If not friends, check recipient's preferences
		const recipientPrefs = await this.getUserFriendPreferences(recipientId);

		// For now, allow whispers from non-friends
		// This can be changed to enforce friends-only whispers
		return true;
	}

	/**
	 * Search users for friend requests
	 */
	static async searchUsersForFriends(query: string, currentUserId: string, limit = 10) {
		if (query.length < 1) return [];

		const searchResults = await db
			.select({
				id: users.id,
				username: users.username,
				avatarUrl: users.avatarUrl,
				activeAvatarUrl: users.activeAvatarUrl,
				level: users.level,
				role: users.role,
				clout: users.clout,
				friendshipStatus: sql<string | null>`
					CASE 
						WHEN ${friendships.status} = 'accepted' THEN 'friends'
						WHEN ${friendships.status} = 'pending' AND ${friendships.requesterId} = ${currentUserId} THEN 'request_sent'
						WHEN ${friendships.status} = 'pending' AND ${friendships.addresseeId} = ${currentUserId} THEN 'request_received'
						WHEN ${friendships.status} = 'blocked' THEN 'blocked'
						ELSE NULL
					END
				`.as('friendshipStatus')
			})
			.from(users)
			.leftJoin(
				friendships,
				or(
					and(eq(friendships.requesterId, currentUserId), eq(friendships.addresseeId, users.id)),
					and(eq(friendships.requesterId, users.id), eq(friendships.addresseeId, currentUserId))
				)
			)
			.where(
				and(
					sql`LOWER(${users.username}) LIKE LOWER(${`%${query}%`})`,
					sql`${users.id} != ${currentUserId}` // Exclude current user
				)
			)
			.orderBy(users.username)
			.limit(limit);

		return searchResults;
	}

	/**
	 * Get mutual friends between two users
	 */
	static async getMutualFriends(userId1: string, userId2: string) {
		// This is a complex query that finds users who are friends with both userId1 and userId2
		const mutualFriends = await db
			.select({
				id: users.id,
				username: users.username,
				avatarUrl: users.avatarUrl,
				activeAvatarUrl: users.activeAvatarUrl,
				level: users.level
			})
			.from(users)
			.where(
				sql`
					${users.id} IN (
						SELECT CASE 
							WHEN ${friendships.requesterId} = ${userId1} THEN ${friendships.addresseeId}
							ELSE ${friendships.requesterId}
						END as friend_id
						FROM ${friendships}
						WHERE (${friendships.requesterId} = ${userId1} OR ${friendships.addresseeId} = ${userId1})
						AND ${friendships.status} = 'accepted'
					)
					AND ${users.id} IN (
						SELECT CASE 
							WHEN ${friendships.requesterId} = ${userId2} THEN ${friendships.addresseeId}
							ELSE ${friendships.requesterId}
						END as friend_id
						FROM ${friendships}
						WHERE (${friendships.requesterId} = ${userId2} OR ${friendships.addresseeId} = ${userId2})
						AND ${friendships.status} = 'accepted'
					)
				`
			)
			.orderBy(users.username);

		return mutualFriends;
	}
}

/**
 * Social Relationships Routes
 *
 * Defines API routes for user relationships including following, blocking, etc.
 */

import { Router } from 'express';
import type { Request, Response } from 'express';
import type { UserId } from '@shared/types';
import { db } from '@db';
import { users, userRelationships } from '@schema';
import { eq, and, sql, desc, not, or, count, gt, isNull } from 'drizzle-orm';
import { isValidId } from '@shared/utils/id';

import { isAuthenticated, isAdminOrModerator, isAdmin } from '../auth/middleware/auth.middleware';
import { getUserIdFromRequest } from '@server/src/utils/auth';
import { logger } from "../../core/logger";

const router = Router();

// Get followers for a user
router.get('/:userId/followers', async (req: Request, res: Response) => {
	try {
		const userId = req.params.userId as UserId;

		if (!userId || !isValidId(userId)) {
			return res.status(400).json({ message: 'Valid user ID is required' });
		}

		// Get followers
		const followers = await db
			.select({
				id: users.id,
				username: users.username,
				avatarUrl: users.avatarUrl,
				createdAt: userRelationships.createdAt
			})
			.from(userRelationships)
			.innerJoin(users, eq(users.id, userRelationships.followerId))
			.where(
				and(
					eq(userRelationships.followingId, userId),
					eq(userRelationships.relationshipType, 'follow')
				)
			)
			.orderBy(desc(userRelationships.createdAt));

		return res.status(200).json(followers);
	} catch (error) {
		logger.error('Error fetching followers:', error);
		return res.status(500).json({ message: 'Error fetching followers' });
	}
});

// Get following for a user
router.get('/:userId/following', async (req: Request, res: Response) => {
	try {
		const userId = req.params.userId as UserId;

		if (!userId || !isValidId(userId)) {
			return res.status(400).json({ message: 'Valid user ID is required' });
		}

		// Get following
		const following = await db
			.select({
				id: users.id,
				username: users.username,
				avatarUrl: users.avatarUrl,
				createdAt: userRelationships.createdAt
			})
			.from(userRelationships)
			.innerJoin(users, eq(users.id, userRelationships.followingId))
			.where(
				and(
					eq(userRelationships.followerId, userId),
					eq(userRelationships.relationshipType, 'follow')
				)
			)
			.orderBy(desc(userRelationships.createdAt));

		return res.status(200).json(following);
	} catch (error) {
		logger.error('Error fetching following:', error);
		return res.status(500).json({ message: 'Error fetching following' });
	}
});

// Follow a user
router.post('/follow/:userId', isAuthenticated, async (req: Request, res: Response) => {
	try {
		const userId = req.params.userId as UserId;
		const followerId = getUserIdFromRequest(req);

		if (!userId || !isValidId(userId)) {
			return res.status(400).json({ message: 'Valid user ID is required' });
		}

		if (followerId === undefined) {
			return res.status(401).json({ message: 'You must be logged in to follow users' });
		}

		// Check if already following
		const existingRelationship = await db
			.select()
			.from(userRelationships)
			.where(
				and(
					eq(userRelationships.followerId, followerId),
					eq(userRelationships.followingId, userId),
					eq(userRelationships.relationshipType, 'follow')
				)
			);

		if (existingRelationship.length > 0) {
			return res.status(400).json({ message: 'You are already following this user' });
		}

		// Check if user exists
		const userExists = await db
			.select()
			.from(users)
			.where(eq(users.id, userId));

		if (userExists.length === 0) {
			return res.status(404).json({ message: 'User not found' });
		}

		// Cannot follow yourself
		if (followerId === userId) {
			return res.status(400).json({ message: 'You cannot follow yourself' });
		}

		// Create follow relationship
		const newRelationship = await db
			.insert(userRelationships)
			.values({
				followerId: followerId,
				followingId: userId,
				relationshipType: 'follow',
				isAccepted: true, // follow doesn't need acceptance
				createdAt: new Date()
			})
			.returning();

		return res.status(201).json({
			message: 'Successfully followed user',
			relationship: newRelationship[0]
		});
	} catch (error) {
		logger.error('Error following user:', error);
		return res.status(500).json({ message: 'Error following user' });
	}
});

// Unfollow a user
router.delete('/unfollow/:userId', isAuthenticated, async (req: Request, res: Response) => {
	try {
		const userId = req.params.userId as UserId;
		const followerId = getUserIdFromRequest(req);

		if (!userId || !isValidId(userId)) {
			return res.status(400).json({ message: 'Valid user ID is required' });
		}

		if (followerId === undefined) {
			return res.status(401).json({ message: 'You must be logged in to unfollow users' });
		}

		// Check if relationship exists
		const existingRelationship = await db
			.select()
			.from(userRelationships)
			.where(
				and(
					eq(userRelationships.followerId, followerId),
					eq(userRelationships.followingId, userId),
					eq(userRelationships.relationshipType, 'follow')
				)
			);

		if (existingRelationship.length === 0) {
			return res.status(400).json({ message: 'You are not following this user' });
		}

		// Delete relationship
		await db
			.delete(userRelationships)
			.where(
				and(
					eq(userRelationships.followerId, followerId),
					eq(userRelationships.followingId, userId),
					eq(userRelationships.relationshipType, 'follow')
				)
			);

		return res.status(200).json({ message: 'Successfully unfollowed user' });
	} catch (error) {
		logger.error('Error unfollowing user:', error);
		return res.status(500).json({ message: 'Error unfollowing user' });
	}
});

// Check if current user is following a user
router.get('/is-following/:userId', isAuthenticated, async (req: Request, res: Response) => {
	try {
		const userId = req.params.userId as UserId;
