	nextBillingDate: Date | null;
	autoRenew: boolean;
	totalCosmeticValue: number;
	benefits: Record<string, any>;
}

export interface CosmeticDropInfo {
	cosmeticType: 'avatar_frame' | 'badge' | 'title' | 'sticker' | 'emoji_pack' | 'profile_theme';
	cosmeticId: CosmeticId;
	cosmeticName: string;
	cosmeticValue: number;
}

/**
 * Subscription Service Class
 */
export class SubscriptionService {
	private readonly VIP_PASS_PRICE = 500; // 500 DGT
	private readonly DEGEN_PASS_PRICE = 100; // 100 DGT per month
	private readonly COSMETIC_DROP_VALUE = 120; // 120 DGT worth of cosmetics per month

	/**
	 * Purchase a subscription (VIP Pass or Degen Pass)
	 */
	async purchaseSubscription(request: SubscriptionPurchaseRequest): Promise<SubscriptionInfo> {
		const { userId, type } = request;

		try {
			// Check if user already has an active subscription of this type
			const existingSubscription = await this.getUserActiveSubscription(userId, type);
			if (existingSubscription) {
				throw new Error(`User already has an active ${type.replace('_', ' ')} subscription`);
			}

			// Determine pricing
			const price = type === 'vip_pass' ? this.VIP_PASS_PRICE : this.DEGEN_PASS_PRICE;

			// Check user's DGT balance
			const balance = await dgtService.getDGTBalance(userId);
			if (balance.balance < price) {
				throw new Error(
					`Insufficient DGT balance. Required: ${price}, Available: ${balance.balance}`
				);
			}

			// Process payment and create subscription
			const result = await db.transaction(async (tx) => {
				// Debit DGT from user
				const transaction = await dgtService.debitDGT(userId, price, {
					source: 'shop_purchase',
					shopItemId: `subscription_${type}`,
					reason: `${type.replace('_', ' ')} subscription purchase`
				});

				// Create subscription record
				const endDate = type === 'vip_pass' ? null : this.getNextMonthDate();
				const nextBillingDate = type === 'vip_pass' ? null : endDate;
				const status = type === 'vip_pass' ? 'lifetime' : 'active';

				const [subscription] = await tx
					.insert(subscriptions)
					.values({
						userId,
						type,
						status,
						pricePaid: price,
						currency: 'DGT',
						startDate: new Date(),
						endDate,
						nextBillingDate,
						autoRenew: type === 'degen_pass',
						purchaseTransactionId: transaction.id,
						benefits: this.getDefaultBenefits(type),
						metadata: { purchaseSource: 'web', transactionId: transaction.id }
					})
					.returning();

				return subscription;
			});

			logger.info('SUBSCRIPTION', `${type} purchased by user ${userId} for ${price} DGT`);

			return this.mapToSubscriptionInfo(result);
		} catch (error) {
			logger.error('SUBSCRIPTION', `Error purchasing ${type}:`, error);
			throw new Error(`Failed to purchase subscription: ${error.message}`);
		}
	}

	/**
	 * Get user's active subscription
	 */
	async getUserActiveSubscription(
		userId: string,
		type?: 'vip_pass' | 'degen_pass'
	): Promise<SubscriptionInfo | null> {
		try {
			let query = db
				.select()
				.from(subscriptions)
				.where(
					and(
						eq(subscriptions.userId, userId),
						sql`${subscriptions.status} IN ('active', 'lifetime')`,
						eq(subscriptions.isDeleted, false)
					)
				);

			if (type) {
				query = query.where(
					and(
						eq(subscriptions.userId, userId),
						eq(subscriptions.type, type),
						sql`${subscriptions.status} IN ('active', 'lifetime')`,
						eq(subscriptions.isDeleted, false)
					)
				);
			}

			const result = await query.orderBy(desc(subscriptions.createdAt)).limit(1);

			if (result.length === 0) {
				return null;
			}

			return this.mapToSubscriptionInfo(result[0]);
		} catch (error) {
			logger.error('SUBSCRIPTION', 'Error getting user subscription:', error);
			throw new Error('Failed to retrieve subscription information');
		}
	}

	/**
	 * Get all user subscriptions (active and historical)
	 */
	async getUserSubscriptions(userId: string): Promise<SubscriptionInfo[]> {
		try {
			const results = await db
				.select()
				.from(subscriptions)
				.where(and(eq(subscriptions.userId, userId), eq(subscriptions.isDeleted, false)))
				.orderBy(desc(subscriptions.createdAt));

			return results.map((sub) => this.mapToSubscriptionInfo(sub));
		} catch (error) {
			logger.error('SUBSCRIPTION', 'Error getting user subscriptions:', error);
			throw new Error('Failed to retrieve subscription history');
		}
	}

	/**
	 * Cancel a subscription
	 */
	async cancelSubscription(userId: string, subscriptionId: SubscriptionId): Promise<boolean> {
		try {
			// Verify subscription belongs to user and is active
			const subscription = await db
				.select()
				.from(subscriptions)
				.where(
					and(
						eq(subscriptions.id, subscriptionId),
						eq(subscriptions.userId, userId),
						sql`${subscriptions.status} IN ('active', 'lifetime')`
					)
				)
				.limit(1);

			if (subscription.length === 0) {
				throw new Error('Subscription not found or not active');
			}

			// VIP Pass cannot be cancelled (lifetime)
			if (subscription[0].type === 'vip_pass') {
				throw new Error('VIP Pass subscriptions cannot be cancelled (lifetime access)');
			}

			// Cancel the subscription
			await db
				.update(subscriptions)
				.set({
					status: 'cancelled',
					autoRenew: false,
					updatedAt: new Date()
				})
				.where(eq(subscriptions.id, subscriptionId));

			logger.info('SUBSCRIPTION', `Subscription ${subscriptionId} cancelled by user ${userId}`);
			return true;
		} catch (error) {
			logger.error('SUBSCRIPTION', 'Error cancelling subscription:', error);
			throw new Error(`Failed to cancel subscription: ${error.message}`);
		}
	}

	/**
	 * Renew monthly subscription
	 */
	async renewSubscription(subscriptionId: SubscriptionId): Promise<boolean> {
		try {
			const subscription = await db
				.select()
				.from(subscriptions)
				.where(eq(subscriptions.id, subscriptionId))
				.limit(1);

			if (subscription.length === 0) {
				throw new Error('Subscription not found');
			}

			const sub = subscription[0];

			// Only monthly subscriptions can be renewed
			if (sub.type !== 'degen_pass') {
				throw new Error('Only Degen Pass subscriptions can be renewed');
			}

			// Check if subscription is eligible for renewal
			if (sub.status === 'cancelled') {
				throw new Error('Cancelled subscriptions cannot be renewed');
			}

			// Check user's DGT balance
			const balance = await dgtService.getDGTBalance(sub.userId);
			if (balance.balance < this.DEGEN_PASS_PRICE) {
				// Mark subscription as expired
				await db
					.update(subscriptions)
					.set({
						status: 'expired',
						updatedAt: new Date()
					})
					.where(eq(subscriptions.id, subscriptionId));

				throw new Error(
					`Insufficient DGT balance for renewal. Required: ${this.DEGEN_PASS_PRICE}, Available: ${balance.balance}`
				);
			}

			// Process renewal payment
			await db.transaction(async (tx) => {
				// Debit DGT from user
				await dgtService.debitDGT(sub.userId, this.DEGEN_PASS_PRICE, {
					source: 'shop_purchase',
					shopItemId: 'subscription_degen_pass_renewal',
					reason: 'Degen Pass subscription renewal'
				});

				// Update subscription
				const newEndDate = this.getNextMonthDate();
				const newBillingDate = this.getNextMonthDate(newEndDate);

				await tx
					.update(subscriptions)
					.set({
						status: 'active',
						endDate: newEndDate,
						nextBillingDate: newBillingDate,
						lastPaymentDate: new Date(),
						updatedAt: new Date()
					})
					.where(eq(subscriptions.id, subscriptionId));
			});

			logger.info('SUBSCRIPTION', `Subscription ${subscriptionId} renewed for user ${sub.userId}`);
			return true;
		} catch (error) {
			logger.error('SUBSCRIPTION', 'Error renewing subscription:', error);
			throw new Error(`Failed to renew subscription: ${error.message}`);
		}
	}

	/**
	 * Process monthly cosmetic drop for Degen Pass subscribers
	 */
	async processMonthlyCosmetics(): Promise<{
		processed: number;
		failed: number;
		details: Array<{ userId: string; success: boolean; error?: string }>;
	}> {
		try {
			const currentDate = new Date();
			const currentMonth = currentDate.getMonth() + 1;
			const currentYear = currentDate.getFullYear();

			// Get all active Degen Pass subscriptions
			const activeSubscriptions = await db
				.select()
				.from(subscriptions)
				.where(
					and(
						eq(subscriptions.type, 'degen_pass'),
						eq(subscriptions.status, 'active'),
						eq(subscriptions.isDeleted, false)
					)
				);

			const results = {
				processed: 0,
				failed: 0,
				details: [] as Array<{ userId: string; success: boolean; error?: string }>
			};

			for (const subscription of activeSubscriptions) {
				try {
					// Check if cosmetic drop already processed for this month
					const existingDrop = await db
						.select()
						.from(cosmeticDrops)
						.where(
							and(
								eq(cosmeticDrops.userId, subscription.userId),
								eq(cosmeticDrops.subscriptionId, subscription.id),
								eq(cosmeticDrops.dropMonth, currentMonth),
								eq(cosmeticDrops.dropYear, currentYear)
							)
						)
						.limit(1);

					if (existingDrop.length > 0) {
						logger.info(
							'COSMETIC_DROP',
							`Cosmetic already dropped for user ${subscription.userId} this month`
						);
						continue;
					}

					// Generate random cosmetic drop
					const cosmetic = await this.generateRandomCosmetic();
					if (!cosmetic) {
						throw new Error('Failed to generate cosmetic item');
					}

					// Create cosmetic drop record
					await db.insert(cosmeticDrops).values({
						userId: subscription.userId,
						subscriptionId: subscription.id,
						dropMonth: currentMonth,
						dropYear: currentYear,
						cosmeticType: cosmetic.cosmeticType,
						cosmeticId: cosmetic.cosmeticId,
						cosmeticName: cosmetic.cosmeticName,
						cosmeticValue: cosmetic.cosmeticValue,
						claimed: false,
						metadata: { generatedAt: new Date(), dropReason: 'monthly_degen_pass' }
					});

					// Update subscription cosmetic value
					await db
						.update(subscriptions)
						.set({
							lastCosmeticDrop: new Date(),
							totalCosmeticValue: sql`${subscriptions.totalCosmeticValue} + ${cosmetic.cosmeticValue}`,
							updatedAt: new Date()
						})
						.where(eq(subscriptions.id, subscription.id));

					results.processed++;
					results.details.push({ userId: subscription.userId, success: true });

					logger.info(
						'COSMETIC_DROP',
						`Cosmetic dropped for user ${subscription.userId}: ${cosmetic.cosmeticName} (${cosmetic.cosmeticValue} DGT value)`
					);
				} catch (error) {
					results.failed++;
					results.details.push({
						userId: subscription.userId,
						success: false,
						error: error.message
					});

					logger.error(
						'COSMETIC_DROP',
						`Failed to process cosmetic drop for user ${subscription.userId}:`,
						error
					);
				}
			}

			logger.info(
				'COSMETIC_DROP',
				`Monthly cosmetic drop completed: ${results.processed} processed, ${results.failed} failed`
			);

			return results;
		} catch (error) {
			logger.error('COSMETIC_DROP', 'Error processing monthly cosmetics:', error);
			throw new Error(`Failed to process monthly cosmetic drops: ${error.message}`);
		}
	}

	/**
	 * Get user's cosmetic drop history
	 */
	async getUserCosmeticDrops(userId: string): Promise<any[]> {
		try {
			const drops = await db
				.select()
				.from(cosmeticDrops)
				.where(eq(cosmeticDrops.userId, userId))
				.orderBy(desc(cosmeticDrops.createdAt));

			return drops;
		} catch (error) {
			logger.error('SUBSCRIPTION', 'Error getting cosmetic drops:', error);
			throw new Error('Failed to retrieve cosmetic drop history');
		}
	}

	/**
	 * Check if user has specific subscription benefit
	 */
	async hasSubscriptionBenefit(userId: string, benefitKey: string): Promise<boolean> {
		try {
			const subscription = await this.getUserActiveSubscription(userId);
			if (!subscription) {
				return false;
			}

			// Check if benefit exists in subscription benefits or default benefits
			const benefits = subscription.benefits as Record<string, any>;
			return benefits[benefitKey] === true || benefits[benefitKey] === 1;
		} catch (error) {
			logger.error('SUBSCRIPTION', 'Error checking subscription benefit:', error);
			return false;
		}
	}

	/**
	 * Get expired subscriptions that need renewal processing
	 */
	async getExpiredSubscriptions(): Promise<any[]> {
		try {
			const now = new Date();

			const expired = await db
				.select()
				.from(subscriptions)
				.where(
					and(
						eq(subscriptions.type, 'degen_pass'),
						eq(subscriptions.status, 'active'),
						eq(subscriptions.autoRenew, true),
						lt(subscriptions.endDate, now),
						eq(subscriptions.isDeleted, false)
					)
				);

			return expired;
		} catch (error) {
			logger.error('SUBSCRIPTION', 'Error getting expired subscriptions:', error);
			throw new Error('Failed to retrieve expired subscriptions');
		}
	}

	/**
	 * Map database subscription to SubscriptionInfo interface
	 */
	private mapToSubscriptionInfo(sub: any): SubscriptionInfo {
		return {
			id: sub.id,
			userId: sub.userId,
			type: sub.type,
			status: sub.status,
			startDate: sub.startDate,
			endDate: sub.endDate,
			nextBillingDate: sub.nextBillingDate,
			autoRenew: sub.autoRenew,
			totalCosmeticValue: sub.totalCosmeticValue,
			benefits: sub.benefits || {}
		};
	}

	/**
	 * Get default benefits for subscription type
	 */
	private getDefaultBenefits(type: 'vip_pass' | 'degen_pass'): Record<string, any> {
		const benefits: Record<string, any> = {};

		if (type === 'vip_pass') {
			benefits.lifetime_vip = true;
			benefits.vip_badge = true;
			benefits.priority_support = true;
			benefits.advanced_forum_features = true;
		} else {
			benefits.monthly_cosmetic_drop = true;
			benefits.degen_badge = true;
			benefits.exclusive_content = true;
			benefits.enhanced_profile = true;
		}

		return benefits;
	}

	/**
	 * Generate random cosmetic item for monthly drop
	 */
	private async generateRandomCosmetic(): Promise<CosmeticDropInfo | null> {
		try {
			// Randomly select cosmetic type
			const cosmeticTypes: Array<CosmeticDropInfo['cosmeticType']> = [
				'avatar_frame',
				'badge',
				'title'
			];
			const randomType = cosmeticTypes[Math.floor(Math.random() * cosmeticTypes.length)];

			// Get available cosmetics of that type
			let cosmetic: CosmeticDropInfo | null = null;

			switch (randomType) {
				case 'avatar_frame': {
					const frames = await db
						.select()
						.from(avatarFrames)
						.where(eq(avatarFrames.isActive, true));
					if (frames.length > 0) {
						const randomFrame = frames[Math.floor(Math.random() * frames.length)];
						cosmetic = {
							cosmeticType: 'avatar_frame',
							cosmeticId: randomFrame.id,
							cosmeticName: randomFrame.name || `Avatar Frame #${randomFrame.id}`,
							cosmeticValue: this.COSMETIC_DROP_VALUE
						};
					}
					break;
				}
				case 'badge': {
					const badgeList = await db.select().from(badges).where(eq(badges.isActive, true));
					if (badgeList.length > 0) {
						const randomBadge = badgeList[Math.floor(Math.random() * badgeList.length)];
						cosmetic = {
							cosmeticType: 'badge',
							cosmeticId: randomBadge.id,
							cosmeticName: randomBadge.name,
							cosmeticValue: this.COSMETIC_DROP_VALUE
						};
					}
					break;
				}
				case 'title': {
					const titleList = await db.select().from(titles).where(eq(titles.isActive, true));
					if (titleList.length > 0) {
						const randomTitle = titleList[Math.floor(Math.random() * titleList.length)];
						cosmetic = {
							cosmeticType: 'title',
							cosmeticId: randomTitle.id,
							cosmeticName: randomTitle.title,
							cosmeticValue: this.COSMETIC_DROP_VALUE
						};
					}
					break;
				}
			}

			return cosmetic;
		} catch (error) {
			logger.error('COSMETIC_DROP', 'Error generating random cosmetic:', error);
			return null;
		}
	}

	/**
	 * Get date one month from now
	 */
	private getNextMonthDate(fromDate?: Date): Date {
		const date = fromDate ? new Date(fromDate) : new Date();
		date.setMonth(date.getMonth() + 1);
		return date;
	}
}

// Export singleton instance
export const subscriptionService = new SubscriptionService();

import { userService } from '@server/src/core/services/user.service';
/**
 * Treasury Routes
 *
 * Defines API routes for treasury operations including balance management,
 * adjustments, and settings.
 */

import { Router } from 'express';
import type { Request, Response } from 'express';
import { db } from '@db';
import { sql } from 'drizzle-orm';
import { users, transactions } from '@schema';
import { eq } from 'drizzle-orm';
import { isAdmin } from '../auth/middleware/auth.middleware';
import { getUserId } from '../auth/services/auth.service';
import { logger } from "../../core/logger";

/**
 * Format DGT amount from storage format (BIGINT with 6 decimal precision) to display format
 */
function formatDgtAmount(amount: number | bigint): number {
	return Number(amount) / 1000000;
}

/**
 * Parse DGT amount from display format to storage format
 */
function parseDgtAmount(displayAmount: number): number {
	return Math.round(displayAmount * 1000000);
}

/**
 * Helper function to adjust treasury balance (used by both /adjust and /adjust-usdt endpoints)
 */
async function adjustTreasuryBalance(
	req: Request,
	res: Response,
	params: {
		amount: number;
		type: string;
		reason?: string;
		currency: string;
	}
): Promise<Response> {
	try {
		const adminId = userService.getUserFromRequest(req);
		const { amount, type, reason, currency } = params;

		if (!amount || !type || !currency) {
			return res.status(400).json({
				message: 'Required fields: amount, type (credit/debit), currency (DGT/USDT)'
			});
		}

		if (type !== 'credit' && type !== 'debit') {
			return res.status(400).json({
				message: "Type must be 'credit' or 'debit'"
			});
		}

		if (currency !== 'DGT' && currency !== 'USDT') {
			return res.status(400).json({
				message: "Currency must be 'DGT' or 'USDT'"
			});
		}

		// Validate amount
		const amountNumeric = parseFloat(amount.toString());
		if (isNaN(amountNumeric) || amountNumeric <= 0) {
			return res.status(400).json({ message: 'Amount must be a positive number' });
		}

		// Begin transaction
		await db.transaction(async (tx) => {
			// Fetch current treasury settings
			const treasuryResult = await tx.execute(sql`
        SELECT treasury_usdt_balance, dgt_treasury_balance FROM dgt_economy_parameters LIMIT 1
      `);

			if (treasuryResult.rows.length === 0) {
				throw new Error('Treasury settings not found');
			}

			const treasurySettings = treasuryResult.rows[0];

			// Calculate new balance based on currency
			let newBalance: number;
			let previousBalance: number;
			let dbField: string;
			let amountToAdjust: number;

			if (currency === 'USDT') {
				previousBalance = Number(treasurySettings.treasury_usdt_balance) || 0;
				dbField = 'treasury_usdt_balance';
				amountToAdjust = amountNumeric;
			} else {
				// DGT
				previousBalance = Number(treasurySettings.dgt_treasury_balance) || 0;
				dbField = 'dgt_treasury_balance';
				amountToAdjust = parseDgtAmount(amountNumeric);
			}

			if (type === 'credit') {
				newBalance = previousBalance + amountToAdjust;
			} else {
				// debit
				if (previousBalance < amountToAdjust) {
					throw new Error(`Insufficient treasury ${currency} balance`);
				}
				newBalance = previousBalance - amountToAdjust;
			}

			// Update treasury balance using standard SQL for improved parameter safety
			if (dbField === 'treasury_usdt_balance') {
				await tx.execute(sql`
          UPDATE dgt_economy_parameters
          SET treasury_usdt_balance = ${newBalance}, 
              updated_at = NOW(),
              updated_by = ${adminId}
          WHERE setting_id = 1
        `);
			} else {
				await tx.execute(sql`
          UPDATE dgt_economy_parameters
          SET dgt_treasury_balance = ${newBalance}, 
              updated_at = NOW(),
              updated_by = ${adminId}
          WHERE setting_id = 1
        `);
			}

			// Create transaction record
			const transactionResult = await tx.execute(sql`
        INSERT INTO transactions (
          user_id,
          amount,
          type,
          status,
          description,
          metadata,
          is_treasury_transaction,
          currency_type,
          created_at,
          updated_at,
          confirmed_at
        ) VALUES (
          ${adminId},
          ${amountToAdjust},
          ${'TREASURY_ADJUST'},
          ${'confirmed'},
          ${type === 'credit' ? `Treasury ${currency} Credit: ${reason || 'No reason provided'}` : `Treasury ${currency} Debit: ${reason || 'No reason provided'}`},
          ${JSON.stringify({
						adjustment_type: type,
						reason: reason || 'No reason provided',
						currency,
						display_amount: amountNumeric // Store original display amount for DGT
					})},
          ${true},
          ${currency},
          NOW(),
          NOW(),
          NOW()
        ) RETURNING transaction_id
      `);

			// Create admin audit log
			await tx.execute(sql`
        INSERT INTO admin_audit_logs (
          user_id,
          action,
          entity_type,
          entity_id,
          details,
          created_at,
          ip_address
        ) VALUES (
          ${adminId},
          ${`TREASURY_${currency}_${type.toUpperCase()}`},
          ${'dgt_economy_parameters'},
          ${1},
          ${JSON.stringify({
						amount: currency === 'DGT' ? amountNumeric : amountToAdjust, // Display amount for DGT
						previous_balance:
							currency === 'DGT' ? formatDgtAmount(previousBalance) : previousBalance,
						new_balance: currency === 'DGT' ? formatDgtAmount(newBalance) : newBalance,
						reason
					})},
          NOW(),
          ${req.ip || '127.0.0.1'}
        )
      `);
		});

		return res.status(200).json({
			success: true,
			message: `Successfully ${type === 'credit' ? 'credited' : 'debited'} ${amountNumeric} ${currency} to treasury`
		});
	} catch (error: any) {
		logger.error('Error adjusting treasury balance:', error);

		if (
			error.message &&
			(error.message.includes('Insufficient treasury') ||
				error.message === 'Treasury settings not found')
		) {
			return res.status(400).json({ message: error.message });
		}

		return res.status(500).json({ message: 'Internal server error' });
	}
}

const router = Router();

// Get treasury overview (admin only)
router.get('/overview', isAdmin, async (req: Request, res: Response) => {
	try {
		// Get treasury settings
		const treasuryResult = await db.execute(sql`
      SELECT 
        dgt_treasury_balance as "dgtTreasuryBalance",
        treasury_usdt_balance as "treasuryUsdtBalance",
        treasury_wallet_address as "treasuryWalletAddress",
        min_withdrawal_amount as "minWithdrawalAmount",
        withdrawal_fee_percent as "withdrawalFeePercent",
        dgt_usdt_exchange_rate as "dgtUsdtExchangeRate",
        updated_at as "lastUpdated"
      FROM dgt_economy_parameters
      LIMIT 1
    `);

		const treasurySettings = treasuryResult.rows[0];

		if (!treasurySettings) {
			return res.status(404).json({ message: 'Treasury settings not found' });
		}

		// Get total circulating supply
		const supplyResult = await db.execute(sql`
      SELECT SUM(dgt_wallet_balance) as "circulatingSupply"
      FROM users
    `);

		const circulatingSupply = formatDgtAmount(Number(supplyResult.rows[0].circulatingSupply) || 0);

		// Get active wallet stats
		const walletStats = await db.execute(sql`
      SELECT 
        COUNT(*) as "activeWallets",
        COUNT(CASE WHEN wallet_address IS NOT NULL AND is_deleted = false THEN 1 ELSE NULL END) as "totalWalletCount",
        0 as "pendingWithdrawalCount",
        0 as "pendingWithdrawalTotal"
      FROM users
    `);

		// Get daily transaction volume - for now, just get the total with mock split
		const transactionVolumeResult = await db.execute(sql`
      SELECT 
        COALESCE(SUM(amount::numeric), 0) as "totalVolume",
        COUNT(*) as "transactionCount"
      FROM transactions
      WHERE created_at > NOW() - INTERVAL '24 hours'
        AND status = 'confirmed'
    `);

		// Simplify and split the volume temporarily until schema update
		const totalVolume = Number(transactionVolumeResult.rows[0].totalVolume) || 0;
		const mockDgtVolume = totalVolume * 0.7; // 70% DGT
		const mockUsdtVolume = totalVolume * 0.3; // 30% USDT

		// Get recent treasury transactions
		const recentTransactions = await db.execute(sql`
      SELECT
        transaction_id,
        type,
        amount,
        status,
        description,
        metadata,
        created_at,
        user_id
      FROM transactions
      WHERE is_treasury_transaction = true
      ORDER BY created_at DESC
      LIMIT 15
    `);

		// Format transactions for display
		const formattedTransactions = recentTransactions.rows.map((tx: any) => {
			let meta = {};
			let currency = 'DGT'; // Default

			try {
				// Handle different formats of metadata
				if (tx.metadata) {
					if (typeof tx.metadata === 'string') {
						meta = JSON.parse(tx.metadata);
						if (meta.currency) {
							currency = meta.currency;
						}
					} else if (typeof tx.metadata === 'object') {
						meta = tx.metadata;
						if (meta.currency) {
							currency = meta.currency;
						}
					}
				}
			} catch (e) {
				logger.warn('Error parsing transaction metadata:', e);
			}

			// Use DGT formatting for DGT, regular for USDT
			const formattedAmount =
				currency === 'USDT' ? Number(tx.amount) : formatDgtAmount(Number(tx.amount));

			return {
				id: tx.transaction_id,
				type: tx.type,
				amount: formattedAmount,
				status: tx.status,
				created_at: tx.created_at,
				description: tx.description,
				currency,
				user_id: tx.user_id,
				meta
			};
		});

		// Calculate DGT supply stats
		const totalSupply = 1000000000; // 1 billion DGT fixed supply
		const treasuryBalance = formatDgtAmount(treasurySettings.dgtTreasuryBalance);
		const dailyDgtVolume = formatDgtAmount(mockDgtVolume);

		const overview = {
			dgt_stats: {
				circulating_supply: circulatingSupply,
				total_supply: formatDgtAmount(totalSupply),
				treasury_balance: treasuryBalance,
				treasury_balance_usdt: Number(treasurySettings.treasuryUsdtBalance) || 0,
				treasury_wallet_address: treasurySettings.treasuryWalletAddress
			},
			platform_stats: {
				active_wallets: Number(walletStats.rows[0].activeWallets) || 0,
				pending_withdrawal_count: Number(walletStats.rows[0].pendingWithdrawalCount) || 0,
				pending_withdrawal_total: Number(walletStats.rows[0].pendingWithdrawalTotal) || 0
			},
			economy_settings: {
				min_withdrawal_amount: formatDgtAmount(treasurySettings.minWithdrawalAmount),
				withdrawal_fee_percent: treasurySettings.withdrawalFeePercent,
				dgt_usdt_exchange_rate: treasurySettings.dgtUsdtExchangeRate || 1,
				last_updated: treasurySettings.lastUpdated
			}
		};

		return res.status(200).json(overview);
	} catch (error) {
		logger.error('Error fetching treasury overview:', error);
		return res.status(500).json({ message: 'Internal server error' });
	}
});

// Adjust user DGT balance (admin only)
router.post('/adjust-balance', isAdmin, async (req: Request, res: Response) => {
	try {
		const adminId = userService.getUserFromRequest(req);
		const { user_id, amount, type, reason } = req.body;

		if (!user_id || !amount || !type) {
			return res.status(400).json({
				message: 'Required fields: user_id, amount, type (credit/debit)'
			});
		}

		if (type !== 'credit' && type !== 'debit') {
			return res.status(400).json({
				message: "Type must be 'credit' or 'debit'"
			});
		}

		// Check if user exists
		const userCheck = await db.execute(sql`
      SELECT user_id, username, dgt_wallet_balance 
      FROM users 
      WHERE user_id = ${user_id}
    `);

		if (userCheck.rows.length === 0) {
			return res.status(404).json({ message: 'User not found' });
		}

		const user = userCheck.rows[0];
		const dgtAmountInStorage = parseDgtAmount(amount);

		// Begin transaction
		await db.transaction(async (tx) => {
			// Update user's DGT balance
			if (type === 'credit') {
				await tx.execute(sql`
          UPDATE users
          SET dgt_wallet_balance = dgt_wallet_balance + ${dgtAmountInStorage}
          WHERE user_id = ${user_id}
        `);

				// Reduce from treasury
				await tx.execute(sql`
          UPDATE dgt_economy_parameters
          SET dgt_treasury_balance = dgt_treasury_balance - ${dgtAmountInStorage}
        `);
			} else {
				// Check if user has enough balance for debit
				if (Number(user.dgt_wallet_balance) < dgtAmountInStorage) {
					throw new Error('Insufficient user balance');
				}

				await tx.execute(sql`
          UPDATE users
          SET dgt_wallet_balance = dgt_wallet_balance - ${dgtAmountInStorage}
          WHERE user_id = ${user_id}
        `);

				// Add to treasury
				await tx.execute(sql`
          UPDATE dgt_economy_parameters
          SET dgt_treasury_balance = dgt_treasury_balance + ${dgtAmountInStorage}
        `);
			}

			// Create transaction record
			const transactionResult = await tx.execute(sql`
        INSERT INTO transactions (
          user_id,
          amount,
          type,
          status,
          description,
          metadata,
          is_treasury_transaction,
          created_at,
          updated_at
        ) VALUES (
          ${user_id},
          ${dgtAmountInStorage},
          ${'ADMIN_ADJUST'},
          ${'confirmed'},
          ${type === 'credit' ? `Admin Credit: ${reason || 'No reason provided'}` : `Admin Debit: ${reason || 'No reason provided'}`},
          ${JSON.stringify({
						adjusted_by: adminId,
						adjustment_type: type,
						reason: reason || 'No reason provided',
						display_amount: amount
					})},
          ${true},
          NOW(),
          NOW()
        ) RETURNING *
      `);

			// Create admin audit log
			await tx.execute(sql`
        INSERT INTO admin_audit_logs (
          user_id,
          action,
          entity_type,
          entity_id,
          details,
          created_at,
          ip_address
        ) VALUES (
          ${adminId},
          ${type === 'credit' ? 'TREASURY_CREDIT' : 'TREASURY_DEBIT'},
          ${'user'},
          ${user_id},
          ${JSON.stringify({
						amount,
						username: user.username,
						reason,
						transaction_id: transactionResult.rows[0].transaction_id
					})},
          NOW(),
          ${req.ip || '127.0.0.1'}
        )
      `);
		});

		return res.status(200).json({
			success: true,
			message: `Successfully ${type === 'credit' ? 'credited' : 'debited'} ${amount} DGT to user ID ${user_id}`,
			user_id,
			amount,
			type
		});
	} catch (error) {
		logger.error('Error adjusting balance:', error);

		if (error.message === 'Insufficient user balance') {
			return res.status(400).json({ message: 'Insufficient user balance for debit operation' });
		}

		return res.status(500).json({ message: 'Internal server error' });
	}
});

