	/**
	 * Retry failed webhook processing
	 */
	async retryFailedWebhook(webhookId: string): Promise<{
		success: boolean;
		message: string;
	}> {
		try {
			const webhookEvent = await db
				.select()
				.from(webhookEvents)
				.where(eq(webhookEvents.webhookId, webhookId))
				.limit(1);

			if (webhookEvent.length === 0) {
				return {
					success: false,
					message: 'Webhook event not found'
				};
			}

			const event = webhookEvent[0];
			const payload = JSON.parse(event.rawPayload);

			// Increment retry count
			const newRetryCount = (parseInt(event.retryCount) + 1).toString();
			await db
				.update(webhookEvents)
				.set({
					retryCount: newRetryCount,
					status: 'processing'
				})
				.where(eq(webhookEvents.webhookId, webhookId));

			// Reprocess the event
			const result = await this.processWebhookEvent(payload, event.signature || '');

			return result;
		} catch (error) {
			logger.error('Error retrying webhook:', error);
			return {
				success: false,
				message: `Retry error: ${error instanceof Error ? error.message : 'Unknown error'}`
			};
		}
	}

	/**
	 * Process DGT conversion for successful deposits
	 */
	private async processDGTConversion(
		userId: string,
		recordId: string,
		depositAmount: string,
		originalToken: string,
		coinUSDPrice: string
	): Promise<void> {
		try {
			const config = await walletConfigService.getConfig();

			// Check if auto-conversion is enabled
			if (!config.ccpayment.autoSwapEnabled) {
				logger.info(`DGT auto-conversion disabled for deposit ${recordId}`);
				return;
			}

			// Calculate USDT amount from deposit
			// CCPayment automatically swaps to USDT, so we use the USD value
			const usdValue = parseFloat(depositAmount) * parseFloat(coinUSDPrice);
			const usdtAmount = usdValue; // USDT is pegged 1:1 to USD

			// Check minimum deposit threshold
			if (usdtAmount < config.dgt.minDepositUSD) {
				logger.info(`Deposit ${recordId} below minimum threshold: $${usdtAmount} < $${config.dgt.minDepositUSD}`);
				return;
			}

			// Calculate DGT amount using configured rate
			const dgtAmount = usdtAmount * config.dgt.usdToDGTRate;

			// Update deposit record with DGT conversion details
			await db
				.update(depositRecords)
				.set({
					usdtAmount: usdtAmount.toString(),
					dgtAmount: dgtAmount.toString(),
					conversionRate: config.dgt.usdToDGTRate.toString(),
					originalToken: originalToken
				})
				.where(eq(depositRecords.recordId, recordId));

			// Credit DGT to user's account
			await dgtService.creditDGT(userId, dgtAmount, {
				source: 'crypto_deposit',
				originalToken: originalToken,
				usdtAmount: usdtAmount.toString(),
				depositRecordId: recordId,
				reason: `Crypto deposit conversion: ${depositAmount} ${originalToken} ‚Üí ${dgtAmount} DGT`
			});

			logger.info(`DGT conversion completed: ${depositAmount} ${originalToken} ‚Üí ${dgtAmount} DGT for user ${userId}`);
		} catch (error) {
			logger.error(`Error processing DGT conversion for deposit ${recordId}:`, error);

			// Update deposit record with conversion error
			await db
				.update(depositRecords)
				.set({
					originalToken: originalToken
					// Mark conversion as failed in a way that can be retried later
				})
				.where(eq(depositRecords.recordId, recordId));

			// Don't throw error - deposit should still be marked as successful
			// Conversion can be retried manually by admin if needed
		}
	}
}

import { userService } from '@server/src/core/services/user.service';
import type { UserId, EntityId } from '@shared/types/ids';
/**
 * Withdrawal Controller
 *
 * Handles withdrawal requests for users converting DGT to crypto
 */

import type { Request, Response, NextFunction } from 'express';
import { logger } from '../../core/logger';
import { walletService } from './wallet.service';
import { dgtService } from './dgt.service';
import { ccpaymentService } from './ccpayment.service';
import { db } from '@db';
import { withdrawalRequests, users, transactions, withdrawalStatusEnum } from '@schema';
import { eq, and, desc, sql } from 'drizzle-orm';
import { WalletError, ErrorCodes } from '../../core/errors';
import { walletConfig } from '@shared/wallet.config';
import { z } from 'zod';

// Validation schemas
const createWithdrawalSchema = z.object({
	amount: z.number().positive().min(walletConfig.LIMITS.MIN_WITHDRAWAL_USD),
	currency: z.enum(['USDT', 'BTC', 'ETH']),
	network: z.string().optional(),
	address: z.string().min(10).max(255),
	notes: z.string().max(500).optional()
});

const processWithdrawalSchema = z.object({
	action: z.enum(['approve', 'reject']),
	adminNotes: z.string().max(500).optional()
});

export class WithdrawalController {
	/**
	 * Create a withdrawal request
	 */
	async createWithdrawalRequest(req: Request, res: Response, next: NextFunction) {
		try {
			if (!userService.getUserFromRequest(req)) {
				return res.status(401).json({ error: 'User not authenticated' });
			}

			const userId = (userService.getUserFromRequest(req) as { id: UserId }).id;

			// Check if withdrawals are enabled
			if (!walletConfig.WITHDRAWALS_ENABLED) {
				return res.status(503).json({
					error: 'Withdrawals are temporarily disabled',
					code: 'SERVICE_UNAVAILABLE'
				});
			}

			// Validate request body
			const { amount, currency, network, address, notes } = createWithdrawalSchema.parse(req.body);

			// Check user level requirement
			const [user] = await db
				.select({ level: users.level })
				.from(users)
				.where(eq(users.id, userId));

			if (!user || user.level < walletConfig.REQUIREMENTS.MIN_LEVEL_TO_WITHDRAW) {
				return res.status(403).json({
					error: `Level ${walletConfig.REQUIREMENTS.MIN_LEVEL_TO_WITHDRAW} required to withdraw`,
					code: 'INSUFFICIENT_LEVEL'
				});
			}

			// Check withdrawal limits
			if (
				amount < walletConfig.LIMITS.MIN_WITHDRAWAL_USD ||
				amount > walletConfig.LIMITS.MAX_WITHDRAWAL_USD
			) {
				return res.status(400).json({
					error: `Withdrawal amount must be between $${walletConfig.LIMITS.MIN_WITHDRAWAL_USD} and $${walletConfig.LIMITS.MAX_WITHDRAWAL_USD}`,
					code: 'INVALID_AMOUNT'
				});
			}

			// Check daily limit
			const twentyFourHoursAgo = new Date(Date.now() - 24 * 60 * 60 * 1000);
			const [dailyTotal] = await db
				.select({
					total: sql<number>`COALESCE(SUM(${withdrawalRequests.amount}), 0)`
				})
				.from(withdrawalRequests)
				.where(
					and(
						eq(withdrawalRequests.userId, userId),
						sql`${withdrawalRequests.createdAt} > ${twentyFourHoursAgo}`,
						eq(withdrawalRequests.status, 'approved')
					)
				);

			if (dailyTotal && dailyTotal.total + amount > walletConfig.LIMITS.DAILY_WITHDRAWAL_LIMIT) {
				return res.status(429).json({
					error: `Daily withdrawal limit of $${walletConfig.LIMITS.DAILY_WITHDRAWAL_LIMIT} exceeded`,
					code: 'DAILY_LIMIT_EXCEEDED',
					remainingLimit: Math.max(0, walletConfig.LIMITS.DAILY_WITHDRAWAL_LIMIT - dailyTotal.total)
				});
			}

			// Calculate DGT amount needed (including fees)
			const withdrawalFeePercent = walletConfig.FEES.WITHDRAWAL_FEE_PERCENT / 100;
			const flatFee = walletConfig.FEES.WITHDRAWAL_FLAT_FEE_USD;
			const totalFeeUSD = amount * withdrawalFeePercent + flatFee;
			const totalAmountUSD = amount + totalFeeUSD;
			const dgtAmountNeeded = BigInt(
				Math.ceil((totalAmountUSD / walletConfig.DGT.PRICE_USD) * 100000000)
			);

			// Check DGT balance
			const dgtBalance = await dgtService.getUserBalance(userId);
			if (dgtBalance < dgtAmountNeeded) {
				return res.status(400).json({
					error: 'Insufficient DGT balance',
					code: 'INSUFFICIENT_BALANCE',
					required: Number(dgtAmountNeeded) / 100000000,
					available: Number(dgtBalance) / 100000000
				});
			}

			// Create withdrawal request
			const [withdrawalRequest] = await db
				.insert(withdrawalRequests)
				.values({
					userId,
					amount: Math.floor(amount * 100), // Store in cents
					walletAddress: address,
					processingFee: Math.floor(totalFeeUSD * 100), // Store in cents
					requestNotes: notes,
					status: 'pending'
				})
				.returning();

			// Deduct DGT from user's balance (hold in escrow)
			await dgtService.deductDGT(
				userId,
				dgtAmountNeeded,
				'WITHDRAWAL',
				`Withdrawal request #${withdrawalRequest.id}`,
				{ withdrawalRequestId: withdrawalRequest.id }
			);

			logger.info('WithdrawalController', 'Withdrawal request created', {
				userId,
				requestId: withdrawalRequest.id,
				amount,
				currency,
				dgtDeducted: Number(dgtAmountNeeded) / 100000000
			});

			res.json({
				success: true,
				withdrawalRequest: {
					id: withdrawalRequest.id,
					amount,
					currency,
					network: network || 'default',
					address,
					fee: totalFeeUSD,
					status: 'pending',
					createdAt: withdrawalRequest.createdAt
				}
			});
		} catch (error) {
			if (error instanceof z.ZodError) {
				return res.status(400).json({ error: 'Invalid request', details: error.errors });
			}
			logger.error('WithdrawalController', 'Error creating withdrawal request', {
				error: error instanceof Error ? error.message : String(error),
				userId: userService.getUserFromRequest(req)?.id
			});
			next(error);
		}
	}

	/**
	 * Get user's withdrawal history
	 */
	async getWithdrawalHistory(req: Request, res: Response, next: NextFunction) {
		try {
			if (!userService.getUserFromRequest(req)) {
				return res.status(401).json({ error: 'User not authenticated' });
			}

			const userId = (userService.getUserFromRequest(req) as { id: UserId }).id;
			const page = parseInt(req.query.page as string) || 1;
			const limit = parseInt(req.query.limit as string) || 10;
			const offset = (page - 1) * limit;

			const withdrawals = await db
				.select({
					id: withdrawalRequests.id,
					amount: withdrawalRequests.amount,
					status: withdrawalRequests.status,
					walletAddress: withdrawalRequests.walletAddress,
					processingFee: withdrawalRequests.processingFee,
					requestNotes: withdrawalRequests.requestNotes,
					adminNotes: withdrawalRequests.adminNotes,
					createdAt: withdrawalRequests.createdAt,
					fulfilledAt: withdrawalRequests.fulfilledAt
				})
				.from(withdrawalRequests)
				.where(eq(withdrawalRequests.userId, userId))
				.orderBy(desc(withdrawalRequests.createdAt))
				.limit(limit)
				.offset(offset);

			// Get total count
			const [countResult] = await db
				.select({ count: sql<number>`COUNT(*)` })
				.from(withdrawalRequests)
				.where(eq(withdrawalRequests.userId, userId));

			res.json({
				withdrawals: withdrawals.map((w) => ({
					...w,
					amount: w.amount / 100, // Convert from cents
					processingFee: w.processingFee / 100
				})),
				pagination: {
					page,
					limit,
					total: countResult?.count || 0,
					pages: Math.ceil((countResult?.count || 0) / limit)
				}
			});
		} catch (error) {
			logger.error('WithdrawalController', 'Error getting withdrawal history', {
				error: error instanceof Error ? error.message : String(error),
				userId: userService.getUserFromRequest(req)?.id
			});
			next(error);
		}
	}

	/**
	 * Admin: Get all withdrawal requests
	 */
	async getAllWithdrawalRequests(req: Request, res: Response, next: NextFunction) {
		try {
			const status = req.query.status as (typeof withdrawalStatusEnum.enumValues)[number];
			const page = parseInt(req.query.page as string) || 1;
			const limit = parseInt(req.query.limit as string) || 20;
			const offset = (page - 1) * limit;

			const conditions = [];
			if (status && withdrawalStatusEnum.enumValues.includes(status)) {
				conditions.push(eq(withdrawalRequests.status, status));
			}

			const withdrawals = await db
				.select({
					id: withdrawalRequests.id,
					userId: withdrawalRequests.userId,
					username: users.username,
					email: users.email,
					amount: withdrawalRequests.amount,
					status: withdrawalRequests.status,
					walletAddress: withdrawalRequests.walletAddress,
					processingFee: withdrawalRequests.processingFee,
					requestNotes: withdrawalRequests.requestNotes,
					adminNotes: withdrawalRequests.adminNotes,
					createdAt: withdrawalRequests.createdAt,
					fulfilledAt: withdrawalRequests.fulfilledAt
				})
				.from(withdrawalRequests)
				.leftJoin(users, eq(withdrawalRequests.userId, users.id))
				.where(conditions.length > 0 ? and(...conditions) : undefined)
				.orderBy(desc(withdrawalRequests.createdAt))
				.limit(limit)
				.offset(offset);

			// Get total count
			const [countResult] = await db
				.select({ count: sql<number>`COUNT(*)` })
				.from(withdrawalRequests)
				.where(conditions.length > 0 ? and(...conditions) : undefined);

			res.json({
				withdrawals: withdrawals.map((w) => ({
					...w,
					amount: w.amount / 100, // Convert from cents
					processingFee: w.processingFee / 100
				})),
				pagination: {
					page,
					limit,
					total: countResult?.count || 0,
					pages: Math.ceil((countResult?.count || 0) / limit)
				}
			});
		} catch (error) {
			logger.error('WithdrawalController', 'Error getting all withdrawal requests', {
				error: error instanceof Error ? error.message : String(error)
			});
			next(error);
		}
	}

	/**
	 * Admin: Process withdrawal request (approve/reject)
	 */
	async processWithdrawalRequest(req: Request, res: Response, next: NextFunction) {
		try {
			const adminId = (userService.getUserFromRequest(req) as { id: UserId }).id;
			const requestId = req.params.requestId as EntityId;
			const { action, adminNotes } = processWithdrawalSchema.parse(req.body);

			// Get withdrawal request
			const [withdrawalRequest] = await db
				.select()
				.from(withdrawalRequests)
				.where(eq(withdrawalRequests.id, requestId));

			if (!withdrawalRequest) {
				return res.status(404).json({ error: 'Withdrawal request not found' });
			}

			if (withdrawalRequest.status !== 'pending') {
				return res.status(400).json({
					error: 'Only pending requests can be processed',
					currentStatus: withdrawalRequest.status
				});
			}

			if (action === 'approve') {
				// TODO: Implement actual withdrawal via CCPayment
				// For now, we'll just update the status

				await db
					.update(withdrawalRequests)
					.set({
						status: 'approved',
						adminNotes,
						processedBy: adminId,
						fulfilledAt: new Date(),
						processed: true
					})
					.where(eq(withdrawalRequests.id, requestId));

				logger.info('WithdrawalController', 'Withdrawal request approved', {
					requestId,
					adminId,
					amount: withdrawalRequest.amount / 100
				});

				res.json({
					success: true,
					message: 'Withdrawal request approved',
					requestId
				});
			} else {
				// Reject and refund DGT
				await db
					.update(withdrawalRequests)
					.set({
						status: 'rejected',
						adminNotes,
						processedBy: adminId,
						processed: true
					})
					.where(eq(withdrawalRequests.id, requestId));

				// Calculate DGT to refund
				const totalAmountUSD = (withdrawalRequest.amount + withdrawalRequest.processingFee) / 100;
				const dgtToRefund = BigInt(
					Math.ceil((totalAmountUSD / walletConfig.DGT.PRICE_USD) * 100000000)
				);

				// Refund DGT to user
				await dgtService.addDGT(
					withdrawalRequest.userId,
					dgtToRefund,
					'WITHDRAWAL_REFUND',
					`Refund for rejected withdrawal #${requestId}`,
					{ withdrawalRequestId: requestId }
				);

				logger.info('WithdrawalController', 'Withdrawal request rejected', {
					requestId,
					adminId,
					dgtRefunded: Number(dgtToRefund) / 100000000
				});

				res.json({
					success: true,
					message: 'Withdrawal request rejected and DGT refunded',
					requestId
				});
			}
		} catch (error) {
			if (error instanceof z.ZodError) {
				return res.status(400).json({ error: 'Invalid request', details: error.errors });
			}
			logger.error('WithdrawalController', 'Error processing withdrawal request', {
				error: error instanceof Error ? error.message : String(error),
				requestId: req.params.requestId
			});
			next(error);
		}
	}
}

export const withdrawalController = new WithdrawalController();

/**
 * XP Event Handlers
 *
 * Centralized event handlers for XP-related actions in the system.
 * This separates event logic from service logic for better modularity.
 */

import { db } from '@db';
import {
	users,
	levels,
	badges,
	titles,
	userBadges,
	userTitles,
	notifications,
	xpAdjustmentLogs
} from '@schema';
import { eq, desc } from 'drizzle-orm';
import { logger } from '../../../core/logger';
import { dgtService } from '../../wallet/dgt.service';
import { PgTransaction } from 'drizzle-orm/pg-core';
import type { UserId } from '@shared/types';

// Re-export event types from the main events file
import { XpGainEvent, XpLossEvent, LevelUpEvent } from '../xp.events';
export { XpGainEvent, XpLossEvent, LevelUpEvent };

/**
 * Handle XP award event
 * Central function to process XP gain and trigger appropriate side effects
 */
export async function handleXpAward(
	userId: UserId,
	amount: number,
	source: string,
	reason?: string
): Promise<{
	oldXp: number;
	newXp: number;
	oldLevel: number;
	newLevel: number;
	leveledUp: boolean;
}> {
	logger.info('XP_EVENTS', `Awarding ${amount} XP to user ${userId} for ${source}`);

	try {
		// Start transaction for atomic operations
		return await db.transaction(async (tx: PgTransaction<any, any, any>) => {
			// Get current user state
			const userResult = await tx
				.select({
					id: users.id,
					xp: users.xp,
					level: users.level,
					username: users.username
				})
				.from(users)
				.where(eq(users.id, userId))
				.limit(1);

			if (userResult.length === 0) {
				throw new Error(`User ${userId} not found`);
			}

			const user = userResult[0];
			const oldXp = user.xp;
			const oldLevel = user.level;
			const newXp = oldXp + amount;

			// Update user's XP
			await tx
				.update(users)
				.set({
					xp: newXp,
					lastXpGainDate: new Date(),
					dailyXpGained: db.sql`COALESCE(daily_xp_gained, 0) + ${amount}`
				})
				.where(eq(users.id, userId));

			// Log the XP adjustment
			await tx.insert(xpAdjustmentLogs).values({
				userId,
				adminId: 1, // System user ID
				adjustmentType: 'add',
				amount,
				reason: `${reason || source} (${source})`,
				oldXp,
				newXp
			});

			// Calculate new level based on XP
			const levelsData = await tx
				.select()
				.from(levels)
				.where(db.sql`min_xp <= ${newXp}`)
				.orderBy(desc(levels.level))
				.limit(1);

			// If no levels data found, use default level 1
			const newLevel = levelsData.length > 0 ? levelsData[0].level : 1;
			const leveledUp = newLevel > oldLevel;

			// If level up occurred, handle it
			if (leveledUp) {
				await handleLevelUp(tx, userId, oldLevel, newLevel, user.username);
			}

			// Create an XP gain event (for event listeners)
			const xpEvent = new XpGainEvent(userId, amount, source);

			// Return the results
			return {
				oldXp,
				newXp,
				oldLevel,
				newLevel,
				leveledUp
			};
		});
	} catch (error) {
		logger.error(
			'XP_EVENTS',
			'Error handling XP award:',
			error instanceof Error ? error.message : String(error)
		);
		throw error;
	}
}

/**
 * Handle user level up
 * Process rewards and notifications when a user levels up
 */
export async function handleLevelUp(
	tx: any, // Transaction context
	userId: UserId,
	oldLevel: number,
	newLevel: number,
	username?: string
): Promise<{
	rewards: {
		dgt?: number;
		title?: string;
		badge?: string;
	};
}> {
	logger.info('XP_EVENTS', `User ${userId} leveled up from ${oldLevel} to ${newLevel}`);

	try {
		// Update user's level
		await tx.update(users).set({ level: newLevel }).where(eq(users.id, userId));

		// Get level data for rewards
		const levelData = await tx.select().from(levels).where(eq(levels.level, newLevel)).limit(1);

		if (levelData.length === 0) {
			throw new Error(`Level ${newLevel} not found`);
		}

		const rewards: { dgt?: number; title?: string; badge?: string } = {};

		// Process DGT reward
		if (levelData[0].rewardDgt && levelData[0].rewardDgt > 0) {
			rewards.dgt = levelData[0].rewardDgt;

			// Award DGT to user wallet using dgtService
			await dgtService.addDgt(
				userId,
				BigInt(levelData[0].rewardDgt), // Ensure amount is BigInt
				'REWARD', // DgtTransactionType
				{
					reason: 'level_up_reward',
					source: `level:${newLevel}`,
					levelAchieved: newLevel
				}
			);
		}

		// Process title reward
		if (levelData[0].rewardTitleId) {
			const titleResult = await tx
				.select()
				.from(titles)
				.where(eq(titles.id, levelData[0].rewardTitleId as number))
				.limit(1);

			if (titleResult.length > 0) {
				const title = titleResult[0];

				// Check if user already has this title
				const existingTitle = await tx
					.select()
					.from(userTitles)
					.where(eq(userTitles.userId, userId), eq(userTitles.titleId, title.id))
					.limit(1);

				// Award title if user doesn't have it
				if (existingTitle.length === 0) {
					await tx.insert(userTitles).values({
						userId,
						titleId: title.id,
						awardedAt: new Date()
					});

					rewards.title = title.name;
				}
			}
		}

		// Process badge reward
		if (levelData[0].rewardBadgeId) {
			const badgeResult = await tx
				.select()
				.from(badges)
				.where(eq(badges.id, levelData[0].rewardBadgeId as number))
				.limit(1);

			if (badgeResult.length > 0) {
				const badge = badgeResult[0];

				// Check if user already has this badge
				const existingBadge = await tx
					.select()
					.from(userBadges)
					.where(eq(userBadges.userId, userId), eq(userBadges.badgeId, badge.id))
					.limit(1);

				// Award badge if user doesn't have it
				if (existingBadge.length === 0) {
					await tx.insert(userBadges).values({
						userId,
						badgeId: badge.id,
						awardedAt: new Date()
					});

					rewards.badge = badge.name;
				}
			}
		}

		// Create notification for user
		const rewardText = [
			rewards.dgt ? `${rewards.dgt} DGT` : '',
			rewards.title ? `"${rewards.title}" title` : '',
			rewards.badge ? `"${rewards.badge}" badge` : ''
		]
			.filter(Boolean)
			.join(', ');

		const notificationMessage = rewardText
			? `Congratulations! You've reached level ${newLevel} and received ${rewardText}!`
			: `Congratulations! You've reached level ${newLevel}!`;

		await tx.insert(notifications).values({
			userId,
			type: 'achievement',
			title: 'Level Up!',
			body: notificationMessage,
			data: {
				oldLevel,
				newLevel,
				rewards
			}
		});

		// Create a LevelUp event (for event listeners)
		const levelUpEvent = new LevelUpEvent(userId, oldLevel, newLevel, 0);

		return { rewards };
	} catch (error) {
		logger.error(
			'XP_EVENTS',
			`Error handling level up for user ${userId}:`,
			error instanceof Error ? error.message : String(error)
		);
		throw error;
	}
}

/**
 * Handle XP loss
 * Process logic when a user loses XP (admin adjustment, penalty, etc.)
 */
export async function handleXpLoss(
	userId: UserId,
	amount: number,
	reason: string
): Promise<{
	oldXp: number;
	newXp: number;
	levelChanged: boolean;
	newLevel: number;
}> {
	logger.info('XP_EVENTS', `Removing ${amount} XP from user ${userId} for ${reason}`);

	try {
		return await db.transaction(async (tx: PgTransaction<any, any, any>) => {
			// Get current user state
			const userResult = await tx
				.select({
					id: users.id,
					xp: users.xp,
					level: users.level
				})
				.from(users)
				.where(eq(users.id, userId))
				.limit(1);

			if (userResult.length === 0) {
				throw new Error(`User ${userId} not found`);
			}

			const user = userResult[0];
			const oldXp = user.xp;
			const oldLevel = user.level;

			// Ensure XP doesn't go negative
			const amountToRemove = Math.min(amount, oldXp);
			const newXp = oldXp - amountToRemove;

			// Update user's XP
			await tx
				.update(users)
				.set({
					xp: newXp
				})
				.where(eq(users.id, userId));

			// Log the XP adjustment
			await tx.insert(xpAdjustmentLogs).values({
				userId,
				adminId: 1, // System user ID
				adjustmentType: 'subtract',
				amount: amountToRemove,
				reason,
				oldXp,
				newXp
			});

			// Calculate new level based on XP
			const levelsData = await tx
				.select()
				.from(levels)
				.where(db.sql`min_xp <= ${newXp}`)
				.orderBy(desc(levels.level))
				.limit(1);

			// If no levels data found, use default level 1
			const newLevel = levelsData.length > 0 ? levelsData[0].level : 1;
			const levelChanged = newLevel < oldLevel;

			// If level changed, update user's level
			if (levelChanged) {
				await tx.update(users).set({ level: newLevel }).where(eq(users.id, userId));

				// Create notification for level change
				await tx.insert(notifications).values({
					userId,
					type: 'info',
					title: 'Level Changed',
					body: `Your level has changed from ${oldLevel} to ${newLevel} due to an XP adjustment.`,
					data: {
						oldLevel,
						newLevel,
						reason
					}
				});
			}

			// Create an XP loss event (for event listeners)
			const xpEvent = new XpLossEvent(userId, amountToRemove, reason);

			return {
				oldXp,
				newXp,
				levelChanged,
				newLevel
			};
		});
	} catch (error) {
		logger.error(
			'XP_EVENTS',
			'Error handling XP loss:',
			error instanceof Error ? error.message : String(error)
		);
		throw error;
	}
}

