	async storeVerificationToken(userId: UserId, token: string): Promise<void> {
		// Create a verification token that expires in 24 hours
		const expiresAt = new Date();
		expiresAt.setHours(expiresAt.getHours() + 24);

		try {
			// First check if there's an existing token for this user and delete it
			await db.delete(verificationTokens).where(eq(verificationTokens.userId, userId));

			// Then create a new token
			await db.insert(verificationTokens).values({
				userId,
				token,
				expiresAt,
				createdAt: new Date()
			});

			logger.info(`Verification token stored for user ${userId}, expires at ${expiresAt}`);
		} catch (error) {
			logger.error('Error storing verification token:', error);
			throw error;
		}
	}

