	async publishDraft(id: Id<'id'>): Promise<Thread> {
		return db.transaction(async (tx: PgTransaction<any, any, any>) => {
			// Get the draft
			const [draft] = await tx.select().from(threadDrafts).where(eq(threadDrafts.id, id));

			if (!draft) {
				throw new Error('Draft not found');
			}

			// Create a URL-friendly slug from the title
			const slug = draft.title
				? draft.title
						.toLowerCase()
						.replace(/[^\w\s]/g, '')
						.replace(/\s+/g, '-')
				: `thread-${Date.now()}`;

			// Create the thread
			const [thread] = await tx
				.insert(threads)
				.values({
					title: draft.title || 'Untitled Thread',
					slug: `${slug}-${Date.now()}`, // Ensure uniqueness
					categoryId: draft.categoryId,
					userId: draft.userId,
					prefixId: draft.prefixId || null,
					updatedAt: new Date()
				})
				.returning();

			// Create the first post
			const [firstPost] = await tx
				.insert(posts)
				.values({
					threadId: thread.id,
					userId: draft.userId,
					content: draft.content || '',
					contentHtml: draft.contentHtml,
					isFirstPost: true,
					updatedAt: new Date()
				})
				.returning();

			// Update thread with first post info
			const [updatedThread] = await tx
				.update(threads)
				.set({
					lastPostId: firstPost.id,
					lastPostAt: firstPost.createdAt,
					postCount: 1
				})
				.where(eq(threads.id, thread.id))
				.returning();

			// Mark the draft as published
			await tx
				.update(threadDrafts)
				.set({
					isPublished: true,
					updatedAt: new Date()
				})
				.where(eq(threadDrafts.id, id));

			return updatedThread;
		});
	}

