	async setSiteSetting(
		key: string,
		value: string,
		valueType: string = 'string',
		group: string = 'general',
		description: string = '',
		isPublic: boolean = false
	): Promise<SiteSetting> {
		// Check if setting exists
		const existingSetting = await this.getSiteSetting(key);

		if (existingSetting) {
			// Update existing setting
			const [updatedSetting] = await db
				.update(siteSettings)
				.set({
					value,
					valueType,
					group,
					description,
					isPublic,
					updatedAt: new Date()
				})
				.where(eq(siteSettings.key, key))
				.returning();

			return updatedSetting;
		} else {
			// Create new setting
			const [newSetting] = await db
				.insert(siteSettings)
				.values({
					key,
					value,
					valueType,
					group,
					description,
					isPublic,
					updatedAt: new Date()
				})
				.returning();

			return newSetting;
		}
	}

	async getPublicSiteSettings(): Promise<Record<string, any>> {
		const settings = await db.select().from(siteSettings).where(eq(siteSettings.isPublic, true));

		// Convert to a key-value object
		const result: Record<string, any> = {};

		settings.forEach((setting) => {
			// Convert value based on valueType
			let parsedValue = setting.value;

			switch (setting.valueType) {
				case 'number':
					parsedValue = Number(setting.value);
					break;
				case 'boolean':
					parsedValue = setting.value === 'true';
					break;
				case 'json':
					try {
						parsedValue = JSON.parse(setting.value || '{}');
					} catch (error) {
						logger.error(`Error parsing JSON for setting ${setting.key}:`, error);
						parsedValue = {};
					}
					break;
			}

			result[setting.key] = parsedValue;
		});

		return result;
	}

